  [10603020025] = {
    id = 10603020025,
    name = "列蒂西雅"
  },
  [10603020030] = {
    id = 10603020030,
    name = "那个......学长，不好意思，事关重大，我这里相对安全一些，所以才派人让你们过来。"
  },
  [10603020035] = {id = 10603020035, name = "奥萨丁"},
  [10603020040] = {
    id = 10603020040,
    name = "奥萨丁，我问你话呢！"
  },
  [10603020045] = {
    id = 10603020045,
    name = "列蒂西雅"
  },
  [10603020050] = {
    id = 10603020050,
    name = "好了，列蒂西雅，先说正事吧。"
  },
  [10603020060] = {id = 10603020060, name = "哼！"},
  [10603020065] = {
    id = 10603020065,
    name = "列蒂西雅"
  },
  [10603020070] = {
    id = 10603020070,
    name = "学长，前几天我们在击退了银翼之后，我便前往据点管理员那里，经过一番交谈后得知了一些重要的情报。"
  },
  [10603020080] = {
    id = 10603020080,
    name = "自从灯塔被破坏之后，深渊中的魔物比起以往出现了一些反常行为。"
  },
  [10603020090] = {
    id = 10603020090,
    name = "很多据点都传出消息受到了大群魔物的袭击，特别是在相对安全的据点附近也出现了很多平时根本不会出现的魔物。"
  },
  [10603020100] = {
    id = 10603020100,
    name = "黎明要塞身处深渊可探索范围的最前线，遭遇魔物袭击不是经常发生的事吗，有什么大惊小怪的，真是的。"
  },
  [10603020110] = {
