    name = "大小姐说得没问题，身处前线的要塞对于魔物的袭击也算是家常便饭了。"
  },
  [10603020120] = {
    id = 10603020120,
    name = "只是这次无意中得到一个小道消息，说是要塞在击退了最后一次魔物袭击的时候有人感受到了数只领主级的气息。"
  },
  [10603020125] = {id = 10603020125, name = "奥萨丁"},
  [10603020115] = {id = 10603020115, name = "奥萨丁"},
