  [10603020075] = {id = 10603020075, name = "奥萨丁"},
  [10603020130] = {
    id = 10603020130,
    name = "众人听到有数只领主级时，不由得倒吸一口凉气，就连列蒂西雅也一反常态的没有吐槽。就在众人沉默不语时，菲尼斯打破了沉默。"
  },
  [10603020140] = {
