    id = 10909060200,
    name = "好耶！不知道上面有什么好东西，真是期待！"
  },
  [10909060260] = {
    id = 10909060260,
    name = "我可是好心提醒大家，你、你们这样真是让我伤心......"
  },
  [10909060270] = {
    id = 10909060270,
    name = "呵呵，让你嘴欠，要我说的话，也是一样，你个乌鸦嘴！"
  },
  [10909060280] = {id = 10909060280, name = "出发！"},
  [10909060010] = {
    id = 10909060010,
    name = "快看！谢利那个疯子，居然独自一人朝天梯去了！"
  },
  [10909060020] = {
    id = 10909060020,
    name = "你要是有她那样的实力，也可以直接过去的，哈哈......"
  },
  [10909060030] = {
    id = 10909060030,
    name = "哼，小莎才不想成为那种四肢发达头脑简单的人！"
  },
  [10909060040] = {
    id = 10909060040,
    name = "呵呵呵......"
  },
  [10909060050] = {
    id = 10909060050,
    name = "安妮斯怎么连你也笑话我！小莎生气了！"
  },
  [10909060060] = {
    id = 10909060060,
    name = "好啦、好啦，我们先看看情况吧。"
  },
  [10909060120] = {id = 10909060120, name = "不行！"},
  [10909060140] = {
    id = 10909060140,
    name = "老师要是去的话，我也要跟着一起去！"
  },
  [10909060150] = {
    id = 10909060150,
    name = "我跟他一起去吧，你们先留在这里。"
  },
  [10909060160] = {
    id = 10909060160,
    name = "安妮斯去，我就去......"
  },
  [10909060170] = {
    id = 10909060170,
    name = "你们都要去，小莎也要去！"
  },
  [10909060210] = {
    id = 10909060210,
    name = "呵呵，说不定是什么恐怖的魔物呢，哈哈哈......"
  },
