  [11001] = {id = 11001, name = "泡影"},
  [110011] = {
    id = 110011,
    name = "天梯周围纷杂的声音令尼莉莎感到痛苦，她奋力地向上攀登。\n阶梯的尽头是一扇华丽的大门，\n在那大门之后，尼莉莎看到了自己尘封已久的记忆......"
  },
  [11001010010] = {
    id = 11001010010,
    name = "出现在尼莉莎眼前的是一座盘旋而上的天梯，举目皆是耀眼的白光，下方则是被一片雾气笼罩，刚才的平台也已经消失不见了。"
  },
  [11001010020] = {
    id = 11001010020,
    name = "嗯？其他人呢......又被分开了吗？"
  },
  [11001010025] = {id = 11001010025, name = "尼莉莎"},
  [11001010030] = {
    id = 11001010030,
    name = "尼莉莎自顾自地说着，看了一眼脚下。"
  },
  [11001010040] = {
    id = 11001010040,
    name = "嘶，妈呀！这么高！不会掉下去吧？掉下去一定没命了......"
  },
  [11001010050] = {
    id = 11001010050,
    name = "看来得赶紧上去，圣物就在上面了！"
  },
  [11001010060] = {
    id = 11001010060,
    name = "如果能拿到圣物，老大一定会很开心的，呵呵，出发！"
  },
  [11001010070] = {
    id = 11001010070,
    name = "尼莉莎向天梯上方走去，天梯四周慢慢浮现出半透明的泡泡漂浮在空中，它们在四周沉沉浮浮，多彩又梦幻。"
  },
  [11001010080] = {
    id = 11001010080,
    name = "一些模糊不清的说话声在尼莉莎耳边响起，她不由得停下了脚步，警惕的环顾着四周。"
  },
  [11001010090] = {id = 11001010090, name = "谁？"},
  [11001010100] = {
    id = 11001010100,
    name = "再不出来，小莎可要出手了哦！"
  },
  [11001010110] = {
    id = 11001010110,
    name = "耳边的声音依旧存在，并没有因为她的话语而消失。"
  },
  [11001010120] = {
    id = 11001010120,
    name = "这里太诡异了，得赶紧离开这里！"
  },
  [11001010130] = {
    id = 11001010130,
    name = "她加快脚步往上方跑去，空中的泡泡越来越多，耳边的声音也越来越大，时而像温柔的喃喃细语，时而像吵杂的争吵。"
  },
  [11001010140] = {
    id = 11001010140,
    name = "真是吵死了！"
  },
  [11001010150] = {
    id = 11001010150,
    name = "都跑了这么久了，怎么还见不到出口。"
  },
  [11001010160] = {
    id = 11001010160,
    name = "这到底是个什么鬼地方！"
  },
  [11001010170] = {
    id = 11001010170,
    name = "吵死了！闭嘴！"
  },
  [11001010180] = {
    id = 11001010180,
    name = "四周的声音此时像是为了响应尼莉莎，变得愈发刺耳。尼莉莎捂着耳朵，但是这些声音就像是直接出现在脑海中，完全没用。"
  },
  [11001010190] = {
    id = 11001010190,
    name = "我让你们都闭嘴！"
  },
  [11001010200] = {
    id = 11001010200,
    name = "尼莉莎有些痛苦的抱着头蹲在地上，精神有些恍惚，嘴里也不停的喃喃自语起来。"
  },
  [11001010210] = {
    id = 11001010210,
    name = "停下......都停下......吵死了......"
  },
  [11001010220] = {
    id = 11001010220,
    name = "就在尼莉莎快坚持不住之际，吵杂的声音戛然而止。"
  },
  [11001010045] = {id = 11001010045, name = "尼莉莎"},
  [11001010055] = {id = 11001010055, name = "尼莉莎"},
  [11001010065] = {id = 11001010065, name = "尼莉莎"},
