  [11001010195] = {id = 11001010195, name = "尼莉莎"},
  [11001010215] = {id = 11001010215, name = "尼莉莎"},
  [11001020010] = {
    id = 11001020010,
    name = "这是...？！我出来了吗？"
  },
  [11001020015] = {id = 11001020015, name = "尼莉莎"},
  [11001020020] = {
    id = 11001020020,
    name = "身边是一望无际的草地，抬头则是湛蓝的天幕，大门、天梯都消失不见了，和煦的阳光洒在身上、阵阵微风轻柔着脸庞，仿佛都是在告诉尼莉莎已经离开了洞窟。"
  },
  [11001020030] = {
    id = 11001020030,
    name = "尼莉莎环顾四周并未发现任何危险，正当她放松警惕时，眼前浮现出一个半透明的泡泡，在阳光的照射下异常梦幻。"
  },
  [11001020040] = {
    id = 11001020040,
    name = "泡泡中一对年轻的夫妇，男人坐在床边有些手足无措地逗弄着正在怀里嚎啕大哭的女婴，女人则躺在床上看着男人窘迫的模样开心地笑着。"
  },
  [11001020050] = {
    id = 11001020050,
    name = "这......！！"
  },
  [11001020055] = {id = 11001020055, name = "尼莉莎"},
  [11001020060] = {
    id = 11001020060,
    name = "尼莉莎不自觉地伸手去触摸泡泡，就在指尖碰到泡泡的瞬间，整个泡泡如烟火般绽放出绚烂的水幕，消弥在空气之中。就在泡泡消弭之时，眼前又浮现出一个泡泡。"
  },
  [11001020070] = {
    id = 11001020070,
    name = "刚才还在怀中的女婴，此时已经成长为了一个可爱的小女孩。"
  },
  [11001020080] = {
    id = 11001020080,
    name = "女孩正在一块空地中练习射箭，只是每次都射不中远处的靶子，男人笑着对女孩说着什么，好像是在教导女孩，女人则是在一旁准备着食物，招呼着两人吃饭。"
  },
  [11001020090] = {
    id = 11001020090,
    name = "这一次，没有等到尼莉莎的触碰，这一只泡泡也碎掉了，水雾星星点点，如同她的愿望一般，转眼消散。"
  },
  [11001020100] = {
    id = 11001020100,
    name = "这、这些都是我的记忆吗？"
  },
  [11001020110] = {
    id = 11001020110,
    name = "头好疼......"
  },
  [11001020115] = {id = 11001020115, name = "尼莉莎"},
  [11001020120] = {
    id = 11001020120,
    name = "儿时一幕幕清晰的画面随时泡泡飞速的出现在尼莉莎眼前。脑海深处的记忆如潮水般疯狂涌出，看着眼前一幕幕的画面尼莉莎的眼泪不自觉的流了下来。头疼也愈发剧烈。"
  },
  [11001020130] = {
    id = 11001020130,
    name = "不、不要......"
  },
  [11001020140] = {
    id = 11001020140,
    name = "不要让我想起这些！"
  },
  [11001020150] = {
    id = 11001020150,
    name = "尼莉莎蜷缩在地上，头痛欲裂地呕吐起来，意识也逐渐有些模糊。"
  },
  [11001020165] = {id = 11001020165, name = "尼莉莎"},
  [11001020160] = {
    id = 11001020160,
    name = "够了......够了......"
  },
  [11001020170] = {
    id = 11001020170,
    name = "不要再给我看了！不要再窥探我的记忆了！"
  },
  [11001030010] = {
    id = 11001030010,
    name = "随着尼莉莎的一声大喊，所有泡泡全都消失了，草地、天空也都消失了，四周陷入了无尽的黑暗。"
  },
  [11001030020] = {
    id = 11001030020,
    name = "伴随着这些东西的消失，尼莉莎也感到了无比的轻松，蜷缩在黑暗中像是回到了父母的怀抱中。"
  },
  [11001030030] = {
    id = 11001030030,
    name = "好温暖......"
  },
  [11001030040] = {
    id = 11001030040,
    name = "好想睡一觉......"
  },
  [11001030050] = {
    id = 11001030050,
    name = "就睡一会......"
  },
  [11001030070] = {
    id = 11001030070,
    name = "就在尼莉莎闭上眼即将陷入沉睡之际，耳边传来了一声呼唤，这一声呼唤瞬间让她清醒过来，猛地睁开双眼，发现自己正躺在天梯上，仿佛刚才的一切只是一场梦，一阵剧烈的头痛随之而来。"
  },
  [11001030035] = {id = 11001030035, name = "尼莉莎"},
  [11001030045] = {id = 11001030045, name = "尼莉莎"},
  [11001030055] = {id = 11001030055, name = "尼莉莎"},
  [11001030065] = {id = 11001030065, name = "拉茨"},
  [11001040010] = {
    id = 11001040010,
    name = "梦吗？不对，幻境？"
  },
  [11001040015] = {id = 11001040015, name = "尼莉莎"},
  [11001040020] = {
    id = 11001040020,
    name = "该死的鬼地方！居然让小莎这么丢脸！"
  },
  [11001040030] = {
    id = 11001040030,
    name = "还好没被其他人给看到，不然小莎可没脸活下去了。"
  },
  [11001040040] = {
    id = 11001040040,
    name = "不过......哎......"
  },
  [11001040050] = {
    id = 11001040050,
    name = "啪——啪——！"
  },
  [11001040060] = {
    id = 11001040060,
    name = "尼莉莎拍了拍自己的脸颊。"
  },
  [11001040070] = {
    id = 11001040070,
    name = "加油！小莎！"
  },
