  [11002] = {id = 11002, name = "舞者"},
  [110021] = {
    id = 110021,
    name = "天梯上方的道路阻断，蒂娜只得向下方走去。\n穿过回荡着无数杂乱声音的阶梯，蒂娜看到了一扇和家里相似的木门。\n当蒂娜打开木门，便无法再掌控自己的身体，\n只得如同傀儡般在舞台上面对着无数黑影起舞......"
  },
  [11002010010] = {
    id = 11002010010,
    name = "蒂娜踏上天梯，一阵耀光后已经站在晶莹如水晶般的天梯上，周围的景色也只剩下空荡荡的一片，整个空间除了天梯之外，就只有漂浮在空中的绚烂流光。"
  },
  [11002010020] = {
    id = 11002010020,
    name = "眼前的天梯已经塌陷无法向上攀登了，蒂娜回头看了看身后，映入眼帘的则是一条笔直向下的水晶台阶。"
  },
  [11002010030] = {
    id = 11002010030,
    name = "是让我往下走吗？"
  },
  [11002010045] = {id = 11002010045, name = "蒂娜"},
  [11002010040] = {
    id = 11002010040,
    name = "先去看看吧......"
  },
  [11002010035] = {id = 11002010035, name = "蒂娜"},
  [11002010050] = {
    id = 11002010050,
    name = "蒂娜不紧不慢的往下走去，每走到一块新的台阶时脚下都会向四周荡漾出一丝丝光晕，空间中的流光也缓缓开始流动起来，耳边隐隐传来一丝细语，蒂娜不由得警惕了起来。"
  },
  [11002010130] = {
    id = 11002010130,
    name = "耳畔传来了杂乱的声音，仿佛许许多多的人忙碌着，路过她的身边，又离她远去。但她身边除了翻腾的流光，始终空无一人。"
  },
  [11002010190] = {
    id = 11002010190,
    name = "随随着不断的往下走去，耳畔的声音也在不停的切换。"
  },
  [11002010200] = {
    id = 11002010200,
    name = "看来这里可以读取我的记忆。"
  },
  [11002010210] = {
    id = 11002010210,
    name = "应该是陷入幻境了，精神系魔法吗？"
  },
  [11002010220] = {
    id = 11002010220,
    name = "蒂娜想到之前娜米莉的事情后，心中不由得更加警惕。深吸了一口气，调整好状态后，准备继续下去看看。"
  },
  [11002010230] = {
    id = 11002010230,
    name = "呼......一定要注意精神状态，千万不能迷失了自己。"
  },
  [11002010240] = {
    id = 11002010240,
    name = "蒂娜继续往下走去，耳畔再次传来各种交谈说话声。不知道走了多久，脚下如水晶般剔透的阶梯已经变得浑浊，绚烂的流光也像是被墨水污染了一般逐渐变成了黑色。"
  },
  [11002010250] = {
    id = 11002010250,
    name = "耳畔的声音也出现了各式各样的争吵声，愈发的刺耳。蒂娜额头上已经出现了一些汗珠。"
  },
  [11002010270] = {
    id = 11002010270,
    name = "得加快速度了......"
  },
  [11002010280] = {
    id = 11002010280,
    name = "说罢蒂娜加快了步伐，此时台阶和流光已经完全变为了黑色，就在蒂娜精神有些恍惚之际，耳畔吵杂的声音戛然而止。"
  },
  [11002010290] = {
    id = 11002010290,
    name = "结束了吗？"
  },
  [11002010310] = {id = 11002010310, name = "！！！"},
  [11002010320] = {
    id = 11002010320,
    name = "谁？是谁？"
  },
  [11002010330] = {id = 11002010330, name = "出来！"},
  [11002010340] = {
    id = 11002010340,
    name = "空间再度恢复寂静，刚才那个声音也未再出现。"
  },
