  [11006010220] = {
    id = 11006010220,
    name = "那、那个，哥哥你们知道兽潮相关的记载吗？"
  },
  [11006010225] = {id = 11006010225, name = "灵遥"},
  [11006010230] = {
    id = 11006010230,
    name = "兽潮吗？以前在公会看到过相关的记载，但是具体内容记不清了......"
  },
  [11006010235] = {id = 11006010235, name = "菲尼斯"},
  [11006010240] = {
    id = 11006010240,
    name = "有所耳闻，你这次来迷雾之都和兽潮有关系？"
  },
  [11006010250] = {
    id = 11006010250,
    name = "嗯，大叔说迷雾之都这里圣物的传言和兽潮有关系，才让我过来看看......"
  },
  [11006010260] = {
    id = 11006010260,
    name = "那......陆大叔现在不会在黎明要塞吧？"
  },
  [11006010255] = {id = 11006010255, name = "灵遥"},
  [11006010270] = {
    id = 11006010270,
    name = "哥哥，你怎么知道？"
  },
  [11006010275] = {id = 11006010275, name = "灵遥"},
  [11006010280] = {
    id = 11006010280,
    name = "我们来迷雾之都前，就收到消息说黎明要塞出现了大规模的魔物袭击，并且还有可能存在领主级魔物。"
  },
  [11006010285] = {id = 11006010285, name = "菲尼斯"},
  [11006010290] = {
    id = 11006010290,
    name = "再结合你说的消息，大致能推测出来陆大叔就在黎明要塞。"
  },
  [11006010300] = {
    id = 11006010300,
    name = "不过以陆大叔的实力，就算有领主级魔物也没什么问题吧？"
  },
  [11006010310] = {
    id = 11006010310,
    name = "嗯，如果只是领主级魔物的话，大叔都能应付，只是离开前大叔告诉我说实际上感受到了一个特殊的魔物气息，这个气息远超领主级魔物。"
  },
  [11006010315] = {id = 11006010315, name = "灵遥"},
  [11006010325] = {id = 11006010325, name = "菲尼斯"},
  [11006010320] = {
    id = 11006010320,
    name = "远超领主级魔物的气息......"
  },
  [11006010330] = {
    id = 11006010330,
    name = "而且最近我也隐约感受到大叔好像有什么心事，总是拿着他的玉佩发呆，所以......"
  },
  [11006010335] = {id = 11006010335, name = "灵遥"},
  [11006010340] = {
    id = 11006010340,
    name = "所以你想让我过去看看陆大叔？"
  },
  [11006010350] = {
    id = 11006010350,
    name = "嗯，虽然大叔收留了我们几个姐妹，但是大叔有什么心事都从来不和我们说......所以我才想让哥哥找大叔聊聊......"
  },
  [11006010355] = {id = 11006010355, name = "灵遥"},
  [11006010360] = {
    id = 11006010360,
    name = "呵呵，放心好了，不会有什么事的，晚上回去我和大家一起商量一下，先吃饭吧。"
  },
  [11006010370] = {
    id = 11006010370,
    name = "说着你摸了摸灵遥的头。"
  },
  [11006010380] = {id = 11006010380, name = "嗯。"},
  [11006010385] = {id = 11006010385, name = "灵遥"},
  [11006020010] = {
    id = 11006020010,
    name = "众人酒足饭饱之后，列蒂西雅拉着众人到附近去闲逛了，只留下了你和菲尼斯。"
  },
  [11006020020] = {
    id = 11006020020,
    name = "呵呵，看来列蒂西雅很懂嘛。"
  },
  [11006020025] = {id = 11006020025, name = "菲尼斯"},
  [11006020030] = {
    id = 11006020030,
    name = "走吧，换个地方聊，兽潮你应该知道一些其他的信息吧。"
  },
  [11006030015] = {id = 11006030015, name = "菲尼斯"},
  [11006030010] = {
    id = 11006030010,
    name = "以前我在公会查阅资料的时候，看到过兽潮相关的记载。"
  },
  [11006030020] = {
    id = 11006030020,
    name = "兽潮的出现没有固定时间和规律，最近的一次记录也是几百年之前了。"
  },
  [11006030030] = {
    id = 11006030030,
    name = "之后出于好奇，我找老头子问过兽潮的事情，其中有一个很重要的信息，他有提到每次兽潮的出现都会伴随着灾厄级魔物......"
  },
  [11006030025] = {id = 11006030025, name = "菲尼斯"},
  [11006030035] = {id = 11006030035, name = "菲尼斯"},
  [11006030040] = {
    id = 11006030040,
    name = "灾厄级？！你确定？"
  },
  [11006030050] = {
    id = 11006030050,
    name = "不确定，不过结合灵遥说的内容，不管会不会出现灾厄级，至少都是比领主级还要强的魔物......"
  },
  [11006030060] = {
    id = 11006030060,
    name = "你接下来打算怎么办？"
  },
  [11006030070] = {
    id = 11006030070,
    name = "我应该还是会去一趟黎明要塞，不管是为了陆大叔和灵遥，还是奥萨丁和娜米莉，都得去一趟。"
  },
  [11006030080] = {
    id = 11006030080,
    name = "你想自己一个人去？"
  },
  [11006030090] = {
    id = 11006030090,
    name = "嗯，我不在的时候，你做为临时队长。"
  },
  [11006030100] = {
    id = 11006030100,
    name = "你可饶了我吧，我可不是当队长的料。"
  },
  [11006030110] = {
    id = 11006030110,
    name = "不过确实要去一趟，奥萨丁那小子自保应该没问题，不过娜米莉得赶紧将她带走。"
  },
  [11006030120] = {
    id = 11006030120,
    name = "还是我去吧！"
  },
  [11006030130] = {
    id = 11006030130,
    name = "不行，太危险了！"
  },
  [11006030140] = {
    id = 11006030140,
    name = "两人争论了一阵也没得出结论。"
  },
  [11006030160] = {
    id = 11006030160,
    name = "呵呵，看来咱俩是谁都没办法说服谁。"
  },
  [11006030170] = {
    id = 11006030170,
    name = "先回去吧，既然大家是一个团队的，也听听他们的意见。"
  },
  [11006030180] = {
    id = 11006030180,
    name = "......要是给他们说了，那......"
  },
  [11006030190] = {
    id = 11006030190,
    name = "呵呵，走吧。"
  },
  [11006030200] = {id = 11006030200, name = "......"},
