  [11005011170] = {
    id = 11005011170,
    name = "安妮斯，别理他们。"
  },
  [11005011175] = {id = 11005011175, name = "蒂娜"},
  [11005011180] = {
    id = 11005011180,
    name = "没想到你们居然还能活着来到这里。"
  },
  [11005011330] = {
    id = 11005011330,
    name = "列蒂西雅可是我们队......可是我们团队的！"
  },
  [11005010210] = {
