  [10302010070] = {
    id = 10302010070,
    name = "西蒙话语未尽，一只魔物便从暗影中窜出，朝西蒙扑了过去。"
  },
  [10302010080] = {
    id = 10302010080,
    name = "啊！我的腿！"
  },
  [10302010090] = {
    id = 10302010090,
    name = "好痛......好痛......！"
  },
  [10302010100] = {
    id = 10302010100,
    name = "怎么回事？"
  },
  [10302010110] = {
    id = 10302010110,
    name = "吼——！！！！"
  },
  [10302010120] = {
    id = 10302010120,
    name = "魔物？！刀足兽！"
  },
  [10302010130] = {
    id = 10302010130,
    name = "利尔，快救、救我......"
  },
  [10302010140] = {
    id = 10302010140,
    name = "西蒙，你坚持一下......"
  },
  [10302010150] = {
    id = 10302010150,
    name = "利尔，我们赶紧走吧，刀足兽越来越多了，再不走就......"
  },
  [10302010160] = {
    id = 10302010160,
    name = "该死，这里怎么会突然出现这么多刀足兽！"
  },
  [10302010170] = {
    id = 10302010170,
    name = "我们先对付刀足兽，再想办法救西蒙逃走。"
  },
  [10302010180] = {
    id = 10302010180,
    name = "西蒙被刀足兽偷袭，倒在了血泊之中。另外两人吃力地应付蜂拥而至的刀足兽，二人逐渐被包围起来。"
  },
  [10302010190] = {
    id = 10302010190,
    name = "利尔，别管西蒙了，我们先撤吧！再不走就来不及了！"
  },
  [10302010200] = {
    id = 10302010200,
    name = "远处还有源源不断的刀足兽正在袭来，利尔瞟了一眼身边的同伴，趁同伴不注意时偷袭了对方。"
  },
  [10302010210] = {
    id = 10302010210,
    name = "啊！！利、利尔，你干什么？！"
  },
  [10302010220] = {
    id = 10302010220,
    name = "你就牺牲一下吧！要不然我们都得死！"
  },
  [10302010230] = {
    id = 10302010230,
    name = "血腥味瞬间吸引了众多的刀足兽注意，利尔趁机赶紧逃走了。"
  },
  [10302010240] = {
    id = 10302010240,
    name = "不、不要啊！我不想死！啊......"
  },
