  [100011] = {
    id = 100011,
    name = "“老熟人”突然现身，告诉了你一个有关物资采集的消息。"
  },
  [100021] = {
    id = 100021,
    name = "你与菲尼斯正在守夜，“老熟人”突然到访。\n不过他并未现身，只是给了你有关无底裂隙的提示。"
  },
  [100031] = {
    id = 100031,
    name = "由于灯塔被毁，你与众人商议后，决定先在深渊搜集情报。"
  },
  [100041] = {
    id = 100041,
    name = "附近圈落正在举办团队对抗赛，你与众人商量后决定去见识一番。"
  },
  [100051] = {
    id = 100051,
    name = "有人希望能与你们结伴而行，尼莉莎和菲尼斯对此表现得异常期待。"
  },
  [100061] = {
    id = 100061,
    name = "瑟蕾突然到访，将贵重的徽章送给了你。\n在被你猜到身份后，她希望你能帮忙保密。"
  },
  [100071] = {
    id = 100071,
    name = "为了寻找徽章素材，你带领众人向更强大的敌人发起了挑战。"
  },
  [100081] = {
    id = 100081,
    name = "列蒂西雅最近心事重重，在你的追问下，她说出了自己想要前往遗迹之地的打算。"
  },
  [10202010240] = {
    id = 10202010240,
    name = "你见利尔等人还敢口出狂言，当即厉声呵斥了他们。几人被你的气势所慑，便灰溜溜地离开了。"
  },
  [20100] = {
    id = 20100,
    name = "守伊之约"
  },
  [201001] = {
    id = 201001,
    name = "几经辗转，你终于找到了伊涅利丝秘密基地所需要的魔法石，\n但每个同伴都推诿有事，你只能独自前往她的秘密基地。"
  },
  [20100010010] = {
    id = 20100010010,
    name = "离上次误打误撞踏足山谷已经过去了不少时日，你与同伴们一起重筑伊涅利丝的秘密基地，却始终差一个屋顶的魔法石，几经辗转，终于在一个行商手中买到了差别不大的一块。"
  },
  [20100010030] = {
    id = 20100010030,
    name = "真是稀罕玩意儿，总算拿到手了。"
  },
  [20100010035] = {id = 20100010035, name = "尼莉莎"},
  [20100010040] = {
    id = 20100010040,
    name = "还算有点本事，竟然知道他狮子大开口，漫天要价。"
  },
  [20100010045] = {id = 20100010045, name = "蒂娜"},
  [20100010055] = {id = 20100010055, name = "尼莉莎"},
  [20100010050] = {
    id = 20100010050,
    name = "当然，只有小莎教育别人的份儿，想坑我，门儿都没有。"
  },
  [20100010060] = {
    id = 20100010060,
    name = "这里离那座山谷不远，正好，我们给她送过去吧。"
  },
  [20100010070] = {
    id = 20100010070,
    name = "小莎不要去，好不容易才到了据点，让我好好睡一觉。"
  },
  [20100010075] = {id = 20100010075, name = "尼莉莎"},
  [20100010080] = {
    id = 20100010080,
    name = "队长，你知道的，要是睡不好，小莎就发挥不了真正的实力，可怜的小莎遇到魔物就没有反抗的力量，这副可爱的身体也会被魔物吃掉的。"
  },
  [20100010085] = {id = 20100010085, name = "尼莉莎"},
  [20100010090] = {
    id = 20100010090,
    name = "......那你休息吧，菲尼斯。"
  },
  [20100010100] = {
    id = 20100010100,
    name = "你忘了吗，今天我约了费尔斯叔叔见面，抱歉啊，我的队长。"
  },
  [20100010105] = {id = 20100010105, name = "菲尼斯"},
  [20100010110] = {
    id = 20100010110,
    name = "我也有些事，今天不能奉陪了。"
  },
  [20100010120] = {
    id = 20100010120,
    name = "咦，蒂娜小姐也有事要忙？那就只有安妮斯陪你去咯。"
  },
  [20100010130] = {
    id = 20100010130,
    name = "我，我......默尔曼院长说托人带了信给我，在附近的营地，蒂娜答应陪我去。对不起，老师。"
  },
  [20100010140] = {
    id = 20100010140,
    name = "尼莉莎。"
  },
  [20100010150] = {
    id = 20100010150,
    name = "啊，小莎快累晕了，小莎动不了了，小莎睡着了Zzz......"
  },
  [20100010160] = {
    id = 20100010160,
    name = "哎呀，看来送魔法石的事只能劳烦队长了。"
  },
  [20100010170] = {
    id = 20100010170,
    name = "没关系，这种小事我去去就回。"
  },
  [20100010180] = {
    id = 20100010180,
    name = "辛苦了，队长！早点回来哦！今天蒂娜计划做一桌子好吃的呢！"
  },
  [20100010190] = {
    id = 20100010190,
    name = "我不记得有这个计划。"
  },
  [20100010200] = {
    id = 20100010200,
    name = "别那么小气嘛，好不容易能好好休息两天，不做点好吃的犒劳自己怎么行？"
  },
  [20100010210] = {
    id = 20100010210,
    name = "这个提议不错，我们一起做好吃的等老师回来吧。"
  },
  [20100010220] = {id = 20100010220, name = "好耶！"},
  [20100010230] = {id = 20100010230, name = "......"},
  [20100010115] = {id = 20100010115, name = "蒂娜"},
  [20100010125] = {id = 20100010125, name = "菲尼斯"},
  [20100010135] = {id = 20100010135, name = "安妮斯"},
  [20100010145] = {id = 20100010145, name = "蒂娜"},
