  [20101] = {id = 20101, name = "觅影踪"},
  [201011] = {
    id = 201011,
    name = "你到达了伊涅利丝的秘密基地，却只看到了一封信。\n你依据信上地点寻找伊涅利丝，山路上发现一张染血的公告，以及一个寡言的少女......"
  },
  [20101010010] = {
    id = 20101010010,
    name = "到了。这次应该能“交差”了吧。"
  },
  [20101010020] = {
    id = 20101010020,
    name = "你到达了目的地，眼前的山壁无声无息的屹立在峡谷口，连阳光也无法穿透。你拿出伊涅利丝给你的信物，找到了那层光幕，轻轻扣响了结界。"
  },
  [20101020010] = {
    id = 20101020010,
    name = "结界犹如湖水的涟漪漾开，阳光从山峰上漏下谷涧，落在斑驳的树梢上影影绰绰，空寂的山谷有花香袭来。"
  },
  [20101020020] = {
    id = 20101020020,
    name = "花开了啊。"
  },
  [20101020030] = {
    id = 20101020030,
    name = "伊涅利丝小姐！"
  },
  [20101020040] = {
    id = 20101020040,
    name = "无人回应。你走向那座崭新的石屋，轻轻敲了敲门。"
  },
  [20101020050] = {
    id = 20101020050,
    name = "伊涅利丝小姐？"
  },
  [20101020060] = {
    id = 20101020060,
    name = "依旧无人回应。"
  },
  [20101020070] = {
    id = 20101020070,
    name = "出去了？打扰了。"
  },
  [20101030010] = {
    id = 20101030010,
    name = "你推门而入，淡淡的清香迎面扑来，窗外摇曳的守伊花在阳光下轻轻拂动。"
  },
  [20101030020] = {
    id = 20101030020,
    name = "这里倒是一眼就能看到花开了。"
  },
  [20101030030] = {
    id = 20101030030,
    name = "你环视了一圈，发现桌台上留着一封信，信封上什么都没有写，但画了一朵守伊花。"
  },
  [20101030053] = {
    id = 20101030053,
    name = "不知道你会不会来，权且当作你会吧。\n毕竟，离说好的“修缮成原来的模样”，还差了一件东西。"
  },
  [20101030063] = {
    id = 20101030063,
    name = "我去寻找父亲的遗物了，\n它就在那座长得像狮头的山顶上，那是我幼时父亲常带我去的地方，\n我相信它一定还在那里，我要把它带回来。"
  },
  [20101030073] = {
    id = 20101030073,
    name = "山谷安室利处，有吃有喝，你要是来了，可以留下等我回来。"
  },
  [20101030090] = {
    id = 20101030090,
    name = "长得像狮头的山？"
  },
  [20101030100] = {
    id = 20101030100,
    name = "你思索了片刻，还是决定去寻找伊涅利丝。"
  },
  [20101030110] = {
    id = 20101030110,
    name = "如果真的照她所说，那座山一定充满危险，否则，也不会让遗物一直留在那山上......"
  },
  [20101030120] = {
    id = 20101030120,
    name = "你看了一眼窗外的繁花盛景，转身离开了山谷。"
  },
  [20101030040] = {
    id = 20101030040,
    name = "是留给我的？"
  },
  [20101030083] = {
    id = 20101030083,
    name = "别误会，我的意思是，那些花很寂寞，你陪陪它们也热闹一点......"
  },
  [20101040010] = {
    id = 20101040010,
    name = "是前面那座了。"
  },
  [20101040020] = {
    id = 20101040020,
    name = "抬头望去，你看见森林附近唯一的一座山峰。"
  },
  [20101040030] = {
    id = 20101040030,
    name = "山腰以下被迷雾遮蔽住，只露出半个“头”，脖颈处高低错落，仿佛一头雄狮趴在那里，等候着猎物的靠近。"
  },
  [20101040040] = {
    id = 20101040040,
    name = "浊炁......"
  },
  [20101040050] = {
    id = 20101040050,
    name = "你有不好的预感，没走多少步，就看到了一大片干涸的血迹。"
  },
  [20101040060] = {
    id = 20101040060,
    name = "血迹四周没有打斗的痕迹，而是延伸着山脚的方向。"
  },
  [20101040070] = {
    id = 20101040070,
    name = "看起来是有人逃向了这里，然后被人救走了。"
  },
  [20101040080] = {
    id = 20101040080,
    name = "等等，这是......"
  },
  [20101040090] = {
    id = 20101040090,
    name = "你拾起血迹旁边一张残破的公告单。"
  },
  [20101040100] = {
    id = 20101040100,
    name = "“警告，近日山上魔物活动频繁，数量激增，有不可预知的危机，诸位探险者请勿进山冒险，以免造成不必要的伤亡。公会敬上”"
  },
  [20101040110] = {
    id = 20101040110,
    name = "你皱了皱眉，加快了脚步。"
  },
  [20101050010] = {
    id = 20101050010,
