  [11102040295] = {id = 11102040295, name = "尼莉莎"},
  [11102040300] = {id = 11102040300, name = "......"},
  [11102040305] = {id = 11102040305, name = "蒂娜"},
  [11102040310] = {
    id = 11102040310,
    name = "灾厄级，如果里面真的是灾厄级，恐怕我们......"
  },
  [11102040315] = {id = 11102040315, name = "菲尼斯"},
  [11102040320] = {
    id = 11102040320,
    name = "是啊，我让灵遥前去调查，就是想要在探险者闯入前确认里面的情况。"
  },
  [11102040325] = {id = 11102040325, name = "陆玄"},
  [11102040330] = {
    id = 11102040330,
    name = "可惜变化实在太快，灵遥也晚了一步......幸好里面只有一只小家伙。"
  },
  [11102040335] = {id = 11102040335, name = "陆玄"},
  [11102040340] = {
    id = 11102040340,
    name = "小家伙......呵呵......"
  },
  [11102040345] = {id = 11102040345, name = "尼莉莎"},
  [11102040350] = {
    id = 11102040350,
    name = "竟然如此凶险，但此前探险者们却纷纷传言那里有圣物出世，而且也出现了异象。"
  },
  [11102040360] = {
    id = 11102040360,
    name = "魔物的老巢出现圣物传闻，这也正是我疑惑的原因，这件事的背后恐怕不简单。"
  },
  [11102040365] = {id = 11102040365, name = "陆玄"},
  [11102040370] = {
    id = 11102040370,
    name = "和那魔物战斗时，隐约听到了灵魂献祭几个字，并且它能吞噬人类和同类的能量来提升实力，陆大叔知道吗？"
  },
  [11102040380] = {
    id = 11102040380,
    name = "陆玄轻抚了一下腰间的玉佩，顿了顿才开口。"
  },
  [11102040390] = {
    id = 11102040390,
    name = "有一些猜想，不过也只是猜想，一切只能走一步看一步。"
  },
  [11102040400] = {
    id = 11102040400,
    name = "如此说来，原本蛰伏在那个藏宝洞中的灾厄级魔物，便是引发这次兽潮的罪魁祸首？"
  },
  [11102040395] = {id = 11102040395, name = "陆玄"},
  [11102040410] = {
    id = 11102040410,
    name = "其实在好几个月之前，兽潮就有了些许征兆。"
  },
  [11102040415] = {id = 11102040415, name = "陆玄"},
  [11102040425] = {id = 11102040425, name = "陆玄"},
  [11102040435] = {id = 11102040435, name = "陆玄"},
  [11102040420] = {
    id = 11102040420,
    name = "最开始只是一小撮魔物突袭探险队，利奥波德就当作了一起意外处理，结果频次越来越高，规模越来越大，加之公会和高塔的人也纷纷进驻黎明要塞。"
  },
  [11102040430] = {
    id = 11102040430,
    name = "一切迹象都表明这里即将爆发巨大的危机。"
  },
  [11102040440] = {
    id = 11102040440,
    name = "相信你们也感受到了，现在要塞中的繁盛景象只是暴风雨前的宁静，很快这里就不安全了。"
  },
  [11102040445] = {id = 11102040445, name = "陆玄"},
  [11102040450] = {
    id = 11102040450,
    name = "嗯，我们就是为此而来的，能和陆大叔并肩作战，我求之不得。"
  },
  [11102040460] = {
    id = 11102040460,
    name = "你？那她们呢？"
  },
  [11102040465] = {id = 11102040465, name = "陆玄"},
  [11102040470] = {
    id = 11102040470,
    name = "我跟着老师。"
  },
  [11102040475] = {id = 11102040475, name = "安妮斯"},
  [11102040480] = {
    id = 11102040480,
    name = "凑热闹的事，小莎怎么能错过呢？"
  },
  [11102040485] = {id = 11102040485, name = "尼莉莎"},
