  },
  [11106020170] = {
    id = 11106020170,
    name = "滚、滚过来了！！"
  },
  [11106020175] = {
    id = 11106020175,
    name = "恐惧的探险者"
  },
  [11106020180] = {
    id = 11106020180,
    name = "窥探魔眼......好多窥探魔眼！！"
  },
  [11106020185] = {
    id = 11106020185,
    name = "害怕的探险者"
  },
  [11106020190] = {
    id = 11106020190,
    name = "不知是弥漫在空气中的血腥味还是耀眼的火光吸引了魔物，它们接近后更加亢奋，一只只眼睛释放出一道道魔法电光，将所见的一切击碎。"
  },
  [11106020200] = {
    id = 11106020200,
    name = "快、快跑！"
  },
  [11106020205] = {
    id = 11106020205,
    name = "恐惧的探险者"
  },
  [11106020210] = {
    id = 11106020210,
    name = "此时，探险者们才惊惧地扔掉了手中的战利品，全力城门方向奔逃。"
  },
  [11106020220] = {
    id = 11106020220,
    name = "小家伙们，看这里！"
  },
  [11106020225] = {id = 11106020225, name = "灵遥"},
  [11106020230] = {
    id = 11106020230,
    name = "危急时刻，灵遥点燃一支火把，向着远处用力甩去。火把掉落在一片干枯的植物上，窥探魔眼瞬间被熊熊燃起的火焰所吸引。"
  },
  [11106020240] = {
