    name = "害怕的探险者"
  },
  [11106020305] = {
    id = 11106020305,
    name = "慌张的探险者"
  },
  [11106020310] = {id = 11106020310, name = "呜——"},
  [11106020320] = {
    id = 11106020320,
    name = "不要慌张，不要走散！"
  },
  [11106020325] = {
    id = 11106020325,
    name = "冷静的探险者"
  },
  [11106020330] = {
    id = 11106020330,
    name = "有人在大声呼喊，可是没有人听他的劝告。魔物愈加逼近，恐怖的声音回响在空旷的沼泽，如万鬼齐哭。"
  },
  [11106020340] = {id = 11106020340, name = "吼——"},
  [11106020350] = {
    id = 11106020350,
    name = "兽潮背后，还有领主级魔物的吼叫声。"
  },
  [11106020360] = {
    id = 11106020360,
    name = "......你们先走！"
  },
  [11106020370] = {
    id = 11106020370,
    name = "老师，你别过去，太危险了！"
  },
  [11106020375] = {id = 11106020375, name = "安妮斯"},
  [11106020380] = {
    id = 11106020380,
    name = "我是你们的队长。"
  },
  [11106020390] = {
    id = 11106020390,
    name = "老师......"
  },
  [11106020395] = {id = 11106020395, name = "安妮斯"},
  [11106020400] = {
