  },
  [11107010020] = {id = 11107010020, name = "老师！"},
  [11107010025] = {id = 11107010025, name = "安妮斯"},
  [11107010030] = {
    id = 11107010030,
    name = "你将魔法力灌入指环，激活了一道透明的屏障，那屏障延伸扩张，将大部分魔物暂时阻挡在了外面。"
  },
  [11107010040] = {
    id = 11107010040,
    name = "趁现在！"
  },
  [11107010050] = {
    id = 11107010050,
    name = "未来得及拦下的魔物也被你们很快斩杀。"
  },
  [11107010060] = {
    id = 11107010060,
    name = "这道防御结界撑不了多久时间，你们先走！"
  },
  [11107010070] = {
    id = 11107010070,
    name = "沼泽上的人们早已连滚带爬地逃了。他们跌跌撞撞地跑向黎明要塞，如同在外被猛兽追杀的雏鸟，拼命回到安全的巢穴寻求庇护。"
  },
  [11107010080] = {
    id = 11107010080,
    name = "不好，魔物又要冲过来了！"
  },
  [11107010085] = {id = 11107010085, name = "希尔"},
  [11107010090] = {
    id = 11107010090,
    name = "挤成一团的魔物疯狂地撞击屏障，很快屏障就出现了一道道龟裂的痕迹。"
  },
  [11107010100] = {
    id = 11107010100,
    name = "哥哥，你也快走！"
  },
  [11107010105] = {id = 11107010105, name = "灵遥"},
  [11107010110] = {
    id = 11107010110,
    name = "你说的撑不了多久，原来只有这么一点时间的吗？！"
  },
  [11107010115] = {id = 11107010115, name = "尼莉莎"},
  [11107010120] = {
    id = 11107010120,
    name = "——咔嚓！一声清脆的碎裂声，响彻在沼泽地上空，夜色中，结界的光幕炸开了一朵璀璨的烟花。"
  },
  [11107010130] = {
    id = 11107010130,
    name = "跑！不要停下来！"
  },
  [11107010140] = {
    id = 11107010140,
    name = "结界破碎，魔物疯狂又兴奋地涌了过来。眼看灵遥几人就要被魔物攻击，你想转身回去救援，被菲尼斯扯住了手臂。"
  },
  [11107010150] = {
    id = 11107010150,
    name = "别过去！"
  },
  [11107010155] = {id = 11107010155, name = "菲尼斯"},
  [11107010160] = {
    id = 11107010160,
    name = "空中忽然响起沉闷的嗡鸣声，巨大的阴影从天而降！一面盾牌直插入地，霸道地竖起了一面临时的「墙」，暂时阻拦了那群涌动的魔物。"
  },
  [11107010170] = {
    id = 11107010170,
    name = "——轰！！"
  },
  [11107010180] = {
    id = 11107010180,
    name = "是裁决团！裁决团来救我们了！"
  },
  [11107010185] = {
    id = 11107010185,
    name = "兴奋的探险者"
  },
  [11107010190] = {
    id = 11107010190,
    name = "呼......还以为要被碾成肉饼了。"
  },
  [11107010195] = {id = 11107010195, name = "尼莉莎"},
  [11107010200] = {
    id = 11107010200,
    name = "不止是裁决团探险队，更多的人出现了。黎明要塞的方向，飞来了五光十色的魔法之光，领头的是一群着装统一的人，他们穿着公会和高塔的制服，组织有序，在指挥下抗击兽潮。"
  },
  [11107010210] = {
    id = 11107010210,
    name = "这么多人！是不是可以反攻了！反攻！"
  },
  [11107010215] = {id = 11107010215, name = "艾莉亚"},
  [11107010220] = {
    id = 11107010220,
    name = "艾莉亚别冲动，先听哥哥怎么说。"
  },
  [11107010225] = {id = 11107010225, name = "赛珂"},
  [11107010230] = {
    id = 11107010230,
    name = "奥萨丁在他们身侧不远处，面色沉重地疾步而行，看到你们的时候，眉头一松，跑了过来。"
  },
  [11107010240] = {
    id = 11107010240,
    name = "学长，我来了！好险，差一点赶不上了！"
  },
  [11107010245] = {id = 11107010245, name = "奥萨丁"},
  [11107010250] = {
    id = 11107010250,
    name = "你这个家伙，来得也太慢了！"
  },
  [11107010255] = {
    id = 11107010255,
    name = "列蒂西雅"
  },
  [11107010260] = {
    id = 11107010260,
    name = "抱歉，学长，管理处发生了些事，我们来晚了。"
  },
  [11107010265] = {id = 11107010265, name = "奥萨丁"},
  [11107010270] = {
    id = 11107010270,
    name = "不用道歉，你们来得正好。"
  },
  [11107010280] = {
    id = 11107010280,
    name = "有这么多精英团队，足够对付它们了。"
  },
  [11107010285] = {id = 11107010285, name = "菲尼斯"},
  [11107010290] = {
    id = 11107010290,
    name = "怎么还有公会和......高塔的人？"
  },
  [11107010295] = {id = 11107010295, name = "蒂娜"},
  [11107010300] = {
    id = 11107010300,
    name = "公会、高塔和要塞三方会议决定，不管有什么恩怨都先放在一边，打退兽潮后再说。"
  },
  [11107010305] = {id = 11107010305, name = "奥萨丁"},
  [11107010310] = {
    id = 11107010310,
    name = "——吼！！"
  },
  [11107010320] = {
    id = 11107010320,
    name = "这时，远处领主级魔物也跟随兽海显出了身形，它缓缓直起庞大身躯，舒展肢体。隔着数百米的距离，仿佛能够看到这只魔物的身躯，庞大的轮廓散着淡淡的红光，显得格外恐怖。"
  },
  [11107010330] = {
    id = 11107010330,
    name = "是领主级魔物！是荒芜公爵！"
  },
  [11107010340] = {
    id = 11107010340,
    name = "奥萨丁大人，这里就交给你了！我再去申请支援！"
  },
  [11107010345] = {
    id = 11107010345,
    name = "公会成员"
  },
  [11107010335] = {
    id = 11107010335,
    name = "公会成员"
  },
  [11107010350] = {
    id = 11107010350,
    name = "看着公会成员匆匆回城的背影，一个高塔服饰的人发出了讥讽的笑。"
  },
  [11107010360] = {
    id = 11107010360,
    name = "怂货，没见过这种场面，是尿裤子了吧。"
  },
  [11107010365] = {
    id = 11107010365,
    name = "高塔成员"
  },
  [11107010370] = {id = 11107010370, name = "闭嘴。"},
  [11107010375] = {id = 11107010375, name = "蓓姬"},
  [11107010380] = {
    id = 11107010380,
    name = "照陆玄大人和费尔斯大人说的那样，荒芜公爵由裁决团负责，其他的就交给公会和蓓姬大人了。"
  },
  [11107010385] = {id = 11107010385, name = "奥萨丁"},
  [11107010395] = {
    id = 11107010395,
    name = "公会干部"
  },
  [11107010390] = {
    id = 11107010390,
    name = "我们尽力而为，就怕有人会不守信用，用刀剑对着我们背后来一刀。"
  },
  [11107010400] = {
    id = 11107010400,
    name = "呵呵，既然我们三方暂时达成了合作，高塔当然不会做背信弃义的那一方。"
  },
  [11107010405] = {id = 11107010405, name = "蓓姬"},
  [11107010410] = {
    id = 11107010410,
    name = "两大势力加入支援后就立刻去部署行动，这时奥萨丁忽然正色站在了你面前，神色紧张。"
  },
  [11107010420] = {id = 11107010420, name = "学长。"},
  [11107010425] = {id = 11107010425, name = "奥萨丁"},
  [11107010430] = {
    id = 11107010430,
    name = "干什么，还不去杀掉那个大块头？"
  },
  [11107010435] = {
    id = 11107010435,
    name = "列蒂西雅"
  },
  [11107010440] = {
    id = 11107010440,
    name = "学长，请学长和我一起，对付荒芜公爵！"
  },
  [11107010445] = {id = 11107010445, name = "奥萨丁"},
  [11107010450] = {id = 11107010450, name = "！！！"},
  [11107010455] = {id = 11107010455, name = "安妮斯"},
  [11107010460] = {
    id = 11107010460,
    name = "我希望在我的背后，是我能信任的人。而且，我一直梦想，有一天能和学长并肩作战！拜托了，学长！"
  },
  [11107010465] = {id = 11107010465, name = "奥萨丁"},
  [11107010470] = {
    id = 11107010470,
    name = "你一时怔住了。"
  },
  [11107010480] = {
    id = 11107010480,
    name = "的确，要对付兽潮中的刚进化的领主级魔物，裁决团需要一个绝对不被打扰的环境。"
  },
  [11107010490] = {
    id = 11107010490,
    name = "而创造这个环境的条件需要一个非常信任的小队保驾护航，我们是目前最合适的人选。"
  },
  [11107010500] = {
    id = 11107010500,
    name = "还算你有点眼光，算上我一个。"
  },
  [11107010505] = {
    id = 11107010505,
    name = "列蒂西雅"
  },
  [11107010510] = {
    id = 11107010510,
    name = "啊，那是荒芜公爵啊，真、真、真的要上吗？"
  },
  [11107010515] = {id = 11107010515, name = "尼莉莎"},
  [11107010520] = {
    id = 11107010520,
    name = "要吸引荒芜公爵的注意力，那是非常危险的事啊~尼莉莎小姐害怕的话躲起来也没关系哦。"
  },
  [11107010525] = {id = 11107010525, name = "菲尼斯"},
  [11107010530] = {
    id = 11107010530,
    name = "哼，少用激将法。"
  },
  [11107010535] = {id = 11107010535, name = "尼莉莎"},
  [11107010545] = {id = 11107010545, name = "奥萨丁"},
  [11107010540] = {
    id = 11107010540,
    name = "我知道学长的......放心，冲锋陷阵的事交给我！我会把荒芜公爵引到那个沼泽前方，学长只需要拦下周围的魔物，不让他们靠近就行！"
  },
  [11107010550] = {
    id = 11107010550,
    name = "斩杀荒芜公爵这件事，我想和学长一起完成！"
  },
  [11107010555] = {id = 11107010555, name = "奥萨丁"},
  [11107010560] = {
    id = 11107010560,
    name = "好。就由我们拦下那些魔物，不让它们影响你们的战斗。"
  },
  [11107010570] = {
    id = 11107010570,
    name = "我、我会用尽全力，将领主级魔物的头颅献给学长的！"
  },
  [11107010580] = {
    id = 11107010580,
    name = "啊？这个就不用了......"
  },
  [11107010575] = {id = 11107010575, name = "奥萨丁"},
  [11107010590] = {
    id = 11107010590,
    name = "裁决探险团，出发！"
  },
  [11107010595] = {id = 11107010595, name = "奥萨丁"},
  [11107010600] = {
    id = 11107010600,
    name = "很有干劲啊。"
  },
  [11107010605] = {id = 11107010605, name = "菲尼斯"},
  [11107010610] = {
    id = 11107010610,
    name = "我们也上吧，大家小心。"
  },
  [11107010620] = {
    id = 11107010620,
    name = "无数的魔物如同黑色浪潮一道道朝人海涌去，双方撞击之下是魔法刀剑的碰撞声，是不断翻涌的滔天血浪，在公会与高塔的掩护下，裁决探险队已经杀进了兽海中，临近在领主级魔物身前。"
  },
  [11107010630] = {
    id = 11107010630,
    name = "跟上他们！"
  },
  [11107010640] = {
    id = 11107010640,
    name = "好高......"
  },
  [11107010645] = {id = 11107010645, name = "尼莉莎"},
  [11107010650] = {
    id = 11107010650,
    name = "前方巨大的魔物身影笼罩而下，还没靠近就感觉到了压迫的气息。"
  },
  [11107010660] = {
    id = 11107010660,
    name = "一旦奥萨丁和荒芜公爵对战，我们必须拦住周围的魔物，不能让它们接近裁决团的作战范围。"
  },
  [11107010670] = {
    id = 11107010670,
    name = "是，我明白了。"
  },
  [11107010675] = {id = 11107010675, name = "安妮斯"},
  [11107010680] = {
    id = 11107010680,
    name = "安妮斯，你站在我身后，一定要跟紧我。"
  },
  [11107010685] = {id = 11107010685, name = "蒂娜"},
