    name = "不，蒙娜小姐最宝贵的财富，在这里。"
  },
  [20211030475] = {id = 20211030475, name = "菲尼斯"},
  [20211030480] = {
    id = 20211030480,
    name = "菲尼斯指了指蒙娜的脑子，蒙娜捂紧了头。"
  },
  [20211030490] = {
    id = 20211030490,
    name = "你们要摘掉我的脑子？！"
  },
  [20211030500] = {
    id = 20211030500,
    name = "她真的......没问题吗？"
  },
  [20211030510] = {
    id = 20211030510,
    name = "蒙娜小姐常年在玛蒙图书馆，博览群书，无所不知，她的才学，可以让你重建十个农场。"
  },
  [20211030520] = {
    id = 20211030520,
    name = "原来是夸我呢，哈哈，小意思啦。"
  },
  [20211030495] = {id = 20211030495, name = "蒙娜"},
  [20211030525] = {id = 20211030525, name = "蒙娜"},
  [20211030515] = {id = 20211030515, name = "菲尼斯"},
  [20211030505] = {id = 20211030505, name = "哈克斯"},
  [20211030530] = {
    id = 20211030530,
    name = "既然是这样，你就过来签字吧。"
  },
  [20211030540] = {
    id = 20211030540,
    name = "为什么？他们怎么都不签？"
  },
  [20211030550] = {
    id = 20211030550,
    name = "他们是他们，你有钱吗？"
  },
  [20211030560] = {
    id = 20211030560,
    name = "蒙娜摇摇头。"
  },
  [20211030570] = {
    id = 20211030570,
