  },
  [20211030580] = {
    id = 20211030580,
    name = "蒙娜继续摇头。"
  },
  [20211030590] = {
    id = 20211030590,
    name = "有担保人吗？"
  },
  [20211030600] = {
    id = 20211030600,
    name = "蒙娜再次摇头。"
  },
  [20211030610] = {
    id = 20211030610,
    name = "那就对了，咱们以后还要仰仗蒙娜小姐的才学，帮大家重建农场呢。蒙娜小姐，请签字吧？"
  },
  [20211030620] = {
