  [20206010018] = {id = 20206010018, name = "打弹珠"},
  [20206010019] = {id = 20206010019, name = "PART 06"},
  [20206030070] = {
    id = 20206030070,
    name = "猜测而已，以他的行事作风，不可能不关注我们。"
  },
  [20201010315] = {id = 20201010315, name = "尼莉莎"},
  [20301] = {
    id = 20301,
    name = "心声不期而然"
  },
  [20301010010] = {
    id = 20301010010,
    name = "这是一间奇怪的小屋。"
  },
  [20301010020] = {
    id = 20301010020,
    name = "从外面看起来，它只有四人合抱的大树那么宽，但里面却竟然十分地宽敞，甚至还有三层楼阁。"
  },
  [20301010030] = {
    id = 20301010030,
    name = "精密的齿轮木窗，旁边放了两张桌椅。角落的延展床铺上，你和鬼侍刚刚醒来。"
  },
  [2030121] = {id = 2030121, name = "别传"},
  [2030122] = {id = 2030122, name = "Side Story"},
  [2030123] = {
    id = 2030123,
    name = "心有灵犀"
  },
  [2030124] = {
    id = 2030124,
    name = "The Heart is Connected"
  },
  [20301010040] = {
    id = 20301010040,
    name = "不是要找材料吗？那就一起吧。"
  },
  [20301010045] = {id = 20301010045, name = "鬼侍"},
  [20301010050] = {
    id = 20301010050,
    name = "你和鬼侍将镜子打碎后陷入了梦境，弗拉克以“为吉米找材料”为条件，将你们救醒，是时候付出报酬了。"
  },
  [20301010060] = {
    id = 20301010060,
    name = "弗拉克拍了拍清单，伸手一拉手边的绳子，一套机械的悬空桌椅缓缓落下。"
  },
  [20301010070] = {
    id = 20301010070,
    name = "别着急啊，那位姐姐，回来我们坐下慢慢说。"
  },
  [20301010075] = {id = 20301010075, name = "弗拉克"},
  [20301010080] = {
    id = 20301010080,
    name = "哇，小朋友，你家到底还藏了多少神奇的东西？！你师父真了不起！"
  },
  [20301010085] = {id = 20301010085, name = "尼莉莎"},
  [20301010090] = {
    id = 20301010090,
    name = "这个房间是我自己改造的，师父只是将整体的空间扩大了而已。"
  },
  [20301010095] = {id = 20301010095, name = "弗拉克"},
  [20301010100] = {
    id = 20301010100,
    name = "看不出来你还挺厉害的嘛！竟然想到把桌子椅子都放在天花板上面。"
  },
  [20301010105] = {id = 20301010105, name = "尼莉莎"},
  [20301010110] = {
    id = 20301010110,
    name = "奇技淫巧，不务正业。"
  },
  [20301010115] = {id = 20301010115, name = "？？？"},
  [20301010120] = {
    id = 20301010120,
    name = "耳边出现了一个不耐烦的声音，你愣了愣，看向屋中所有人，大家对这个声音都没有反应，一一围着桌子坐了下来。"
  },
  [20301010130] = {
    id = 20301010130,
    name = "难道刚才是宝物的魔法残留的幻觉？"
  },
  [20301010140] = {
    id = 20301010140,
    name = "这些都是小意思啦，吉米才是我最得意的作品！"
  },
  [20301010145] = {id = 20301010145, name = "弗拉克"},
  [20301010150] = {
    id = 20301010150,
    name = "弗拉克拍了拍金属球吉米，吉米在桌子上滚了一圈，又转了回来。"
  },
  [20301010160] = {
    id = 20301010160,
    name = "可惜，它还少一个自主寻路的功能，我想把它再好好地升级一下。"
  },
  [20301010165] = {id = 20301010165, name = "弗拉克"},
  [20301010170] = {
    id = 20301010170,
    name = "他把吉米放上了桌子正中心，一道光柱从上而下，照在吉米上，一道吉米的魔法幻影不断放大，清晰地展示了精密的内外部结构。"
  },
  [20301010180] = {
    id = 20301010180,
    name = "好神奇啊。"
  },
  [20301010185] = {id = 20301010185, name = "安妮斯"},
  [20301010195] = {id = 20301010195, name = "尼莉莎"},
  [20301010190] = {
    id = 20301010190,
    name = "这就是魔偶肚子里的样子吗？看起来比普通的傀儡复杂多了。"
  },
  [20301010200] = {
    id = 20301010200,
    name = "尼莉莎小姐真是见多识广，那么不常见的魔偶也能遇到。"
  },
  [20301010205] = {id = 20301010205, name = "菲尼斯"},
  [20301010210] = {
    id = 20301010210,
    name = "小莎虽然年纪不大，但探险的经验丰富啊。"
  },
  [20301010215] = {id = 20301010215, name = "尼莉莎"},
  [20301010220] = {
    id = 20301010220,
    name = "通常的魔偶的内部都是魔法结构，大多都会看到魔法符纹，或者包裹着魔法晶体，随主人的意志行动，这个里面的材料……完全看不到魔法的痕迹。"
  },
  [20301010225] = {id = 20301010225, name = "蒂娜"},
  [20301010230] = {
    id = 20301010230,
    name = "不不，吉米有它自己的意志，我们不是主从关系，它是我最好的朋友！"
  },
  [20301010235] = {id = 20301010235, name = "弗拉克"},
  [20301010240] = {
    id = 20301010240,
    name = "“砰、砰！”吉米开心地在原地弹跳了一下，砸到了弗拉克的脸。"
  },
  [20301010250] = {id = 20301010250, name = "嗷！"},
  [20301010255] = {id = 20301010255, name = "弗拉克"},
  [20301010260] = {id = 20301010260, name = "……"},
  [20301010270] = {
    id = 20301010270,
    name = "你没事吧？"
  },
  [20301010275] = {id = 20301010275, name = "安妮斯"},
  [20301010280] = {
    id = 20301010280,
    name = "哈哈，没事，它只是太开心了。"
  },
  [20301010285] = {id = 20301010285, name = "弗拉克"},
  [20301010290] = {
    id = 20301010290,
    name = "它看起来不太像开心的样子。"
  },
  [20301010300] = {
    id = 20301010300,
    name = "那是因为我临时拆掉了它的一些零件，所以它现在变得笨笨的，但整体精神状态很不错。"
  },
  [20301010305] = {id = 20301010305, name = "弗拉克"},
  [20301010315] = {id = 20301010315, name = "？？？"},
  [20301010310] = {
    id = 20301010310,
    name = "魔偶能有什么精神状态？真是慢死了，能不能快点结束，要做什么赶紧说吧！"
  },
  [20301010320] = {
    id = 20301010320,
    name = "你眨了眨眼，再次听到了那个声音，声音没有方向与来源，仿佛是在耳边说话......"
  },
  [20301010330] = {
    id = 20301010330,
    name = "你忽然想到了什么，心头一震，看向了场上的每个人。"
  },
  [20301010340] = {
    id = 20301010340,
    name = "叽叽喳喳的尼莉莎，严肃倾听的蒂娜，茫然不解的安妮斯，最后落在面无表情的鬼侍身上。"
  },
  [20301010350] = {
    id = 20301010350,
    name = "会是她吗？那个和梦境里一样的感觉......"
  },
  [20301010360] = {id = 20301010360, name = "呵呵。"},
  [20301010365] = {id = 20301010365, name = "菲尼斯"},
  [20301010370] = {
    id = 20301010370,
    name = "在笑什么？"
  },
  [20301010380] = {
    id = 20301010380,
    name = "（小声）一会儿找材料的时候，我们分成两组吧？"
  },
  [20301010385] = {id = 20301010385, name = "菲尼斯"},
  [20301010390] = {
    id = 20301010390,
    name = "怎么？你有线索了？"
  },
  [20301010400] = {
    id = 20301010400,
    name = "（小声）我看你好像有话和鬼侍小姐说，想给队长创造机会。"
  },
  [20301010405] = {id = 20301010405, name = "菲尼斯"},
  [20301010410] = {id = 20301010410, name = "滚吧。"},
  [20301010420] = {
    id = 20301010420,
    name = "由于菲尼斯的打岔，你错过了探究声音来源的机会，继续听弗拉克侃侃而谈。说起擅长的机械领域，弗拉克两只眼睛都熠熠生光。"
  },
  [20301010430] = {
    id = 20301010430,
    name = "除了窥探魔眼的“眼珠”和无垢者的双翼，其他的材料都应该能在圈落买到。"
  },
  [20301010435] = {id = 20301010435, name = "弗拉克"},
  [20301010440] = {
    id = 20301010440,
    name = "等等，窥探魔眼的“眼珠”这东西不能吃还有毒，你拿来干嘛？"
  },
  [20301010445] = {id = 20301010445, name = "尼莉莎"},
  [20301010450] = {
    id = 20301010450,
    name = "师父说过，窥探魔眼的眼珠是完美的六面瞳，有绝对的觉察力和区辨力，是替换吉米视觉部分的最好材料！"
  },
  [20301010455] = {id = 20301010455, name = "弗拉克"},
  [20301010460] = {
    id = 20301010460,
    name = "能不能买到你们不用担心，师父有一家常去的店，在此之前，我已经把材料单都寄过去了，你们跟我去买回来就行。"
  },
  [20301010465] = {id = 20301010465, name = "弗拉克"},
  [20301010470] = {
    id = 20301010470,
    name = "这么简单？"
  },
  [20301010475] = {id = 20301010475, name = "蒂娜"},
  [20301010480] = {
    id = 20301010480,
    name = "那就快去买吧，别在这里说一些有用没用的废话了，真是磨磨蹭蹭的，什么时候才能离开这里去买东西，真不习惯突然有这么多人啊......"
  },
  [20301010485] = {id = 20301010485, name = "？？？"},
  [20301010490] = {
    id = 20301010490,
    name = "又出现了，这个声音....."
  },
  [20301010500] = {
    id = 20301010500,
    name = "这是怎么回事？难道和那个碎掉的镜子有关？"
  },
  [20301010510] = {
    id = 20301010510,
    name = "你正思索的时候，听到安妮斯叫你的声音。"
  },
  [20301010520] = {
    id = 20301010520,
    name = "老师觉得如何？"
  },
  [20301010525] = {id = 20301010525, name = "安妮斯"},
  [20301010530] = {
    id = 20301010530,
    name = "抱歉，吉米太神奇了，刚才看得有些出神。"
  },
  [20301010540] = {
    id = 20301010540,
    name = "真的是看吉米？"
  },
  [20301010545] = {id = 20301010545, name = "菲尼斯"},
  [20301010550] = {
    id = 20301010550,
    name = "你能不能闭嘴？"
  },
  [20301010560] = {
    id = 20301010560,
    name = "安妮斯说，咱们先去圈落，把能买的都买了，剩下买不到的，再想办法去猎杀魔物。"
  },
  [20301010565] = {id = 20301010565, name = "尼莉莎"},
  [20301010570] = {
    id = 20301010570,
    name = "可以，这样也能查漏补缺。"
  },
  [20301010580] = {
    id = 20301010580,
    name = "那就这么决定了。"
  },
  [20301010585] = {id = 20301010585, name = "蒂娜"},
  [20301010590] = {
    id = 20301010590,
    name = "就这么决定了？都不问问我的意见？果然探险者就是一群粗鲁的人！"
  },
  [20301010595] = {id = 20301010595, name = "？？？"},
  [20301010600] = {
    id = 20301010600,
    name = "那个，鬼侍小姐觉得这个计划如何？"
  },
  [20301010610] = {
    id = 20301010610,
    name = "嗯，我同意"
  },
  [20301010615] = {id = 20301010615, name = "鬼侍"},
  [20301010620] = {
    id = 20301010620,
    name = "鬼侍不冷不热地回应了一句，她的神情冷傲，气质清冷绝尘，一看就是让人难以接近的类型。加上这样的回答，不禁让你陷入了自我怀疑。"
  },
  [20301010630] = {
    id = 20301010630,
    name = "......不是她？是我的错觉吗。"
  },
  [20301010640] = {
    id = 20301010640,
    name = "一边猜想着鬼侍是否有这么丰富的内心活动，一边思考着镜子对你造成的后遗症，你跟着众人一起离开了维克多的小屋。"
  },
  [20301010650] = {
    id = 20301010650,
    name = "也因此，你没有注意到安妮斯在最后叫住了鬼侍。"
  },
  [20301010660] = {
    id = 20301010660,
    name = "请等一下，鬼侍小姐。"
  },
  [20301010665] = {id = 20301010665, name = "安妮斯"},
  [20301010670] = {
    id = 20301010670,
    name = "鬼侍侧头回看，眼中露出不解，但很快又被冰雪掩盖。"
  },
  [20301010680] = {
    id = 20301010680,
    name = "鬼侍小姐，这是你掉的东西吗？"
  },
  [20301010685] = {id = 20301010685, name = "安妮斯"},
  [20301010690] = {
    id = 20301010690,
    name = "那是一对吊坠，本应是炫目的白金与紫水晶的色彩，此刻却失去了往日的光辉，显得黯淡而缺乏生气。"
  },
  [20301010700] = {
    id = 20301010700,
    name = "鬼侍微微瞪大了眼，下意识摸了摸自己的胸口，确定了是自己的东西不见了，但那惊慌也只是一瞬间，还没被安妮斯发现就被隐藏起来了。"
  },
  [20301010710] = {
    id = 20301010710,
    name = "是我的，谢谢你。"
  },
  [20301010715] = {id = 20301010715, name = "鬼侍"},
  [20301010720] = {
    id = 20301010720,
    name = "她拿回吊坠，死死拽在手中，面不改色地转身离开小屋。"
  },
  [20301010730] = {
    id = 20301010730,
    name = "吓死我了！吓死我了！幸好她帮我拿了回来，不然真丢了，我该怎么办？我会回去找吧，但我能找到吗？应该可以吧......算了，不想这么多了，拿回来就好......"
  },
  [20301010735] = {id = 20301010735, name = "？？？"},
  [20301010740] = {
    id = 20301010740,
    name = "你忽地停了下来，忍住没回头看后方的鬼侍。"
  },
  [20301010750] = {
    id = 20301010750,
    name = "怎么不走了？"
  },
  [20301010755] = {id = 20301010755, name = "菲尼斯"},
  [20301010760] = {
    id = 20301010760,
    name = "你......有没有听到什么奇怪的声音？"
  },
  [20301010770] = {
    id = 20301010770,
    name = "魔物？这片属于维克多大师的区域，很安全的。"
  },
  [20301010775] = {id = 20301010775, name = "菲尼斯"},
  [20301010780] = {
    id = 20301010780,
    name = "没事，应该是我听错了。"
  },
  [20301010790] = {
    id = 20301010790,
    name = "看来，确实只有我一个人能听到。应该......不是错觉吧。"
  },
  [20302010018] = {
    id = 20302010018,
    name = "沉默震耳欲聋"
  },
  [20302010019] = {id = 20302010019, name = "PART 02"},
  [20302010010] = {
    id = 20302010010,
    name = "踏入塞卢圈落，第一眼就看见狭长而蜿蜒的街道。"
  },
  [20302010020] = {
    id = 20302010020,
    name = "狭窄的街道两旁是错落有致的石屋，有的石屋的墙壁上镶嵌着散发着微光的矿石，有的由魔骨搭建，与先前所见的圈落大相径庭。"
  },
  [20302] = {
    id = 20302,
    name = "沉默震耳欲聋"
  },
  [20302010030] = {
    id = 20302010030,
    name = "没想到才隔一年多，这里已经变成这样了！"
  },
  [20302010035] = {id = 20302010035, name = "尼莉莎"},
  [20302010040] = {
    id = 20302010040,
    name = "小莎之前来过这里吗？"
  },
  [20302010045] = {id = 20302010045, name = "安妮斯"},
  [20302010050] = {
    id = 20302010050,
    name = "是啊，别看这个圈落不大，这里有好多奇奇怪怪的小东西呢，是别的圈落都买不到的。"
  },
  [20302010060] = {
    id = 20302010060,
    name = "那是因为我师父在附近，这周围的探险者受师父的影响，也开始学习和研制许多魔法道具和机械的战斗魔偶。"
  },
  [20302010065] = {id = 20302010065, name = "弗拉克"},
  [20302010075] = {id = 20302010075, name = "弗拉克"},
  [20302010070] = {
    id = 20302010070,
    name = "当然，他们都没有我做得好，毕竟我是师父亲口夸奖有天赋的徒弟呢。"
  },
  [20302010080] = {
    id = 20302010080,
    name = "正午时分，市场成为最繁忙的地方，探险者带着新鲜的魔物材料来以物易物，而圈落的居民则通过收购材料，制作自我研发的魔导器具。"
  },
  [20302010090] = {
    id = 20302010090,
    name = "唔唔，好香啊，那边有好吃的！"
  },
  [20302010095] = {id = 20302010095, name = "尼莉莎"},
  [20302010100] = {
    id = 20302010100,
    name = "喂，你别乱跑啊。"
  },
  [20302010105] = {id = 20302010105, name = "蒂娜"},
  [20302010110] = {
    id = 20302010110,
    name = "啊，那个，老师......"
  },
  [20302010115] = {id = 20302010115, name = "安妮斯"},
  [20302010120] = {
    id = 20302010120,
    name = "弗拉克，你说的那家店铺在哪里？"
  },
  [20302010130] = {
    id = 20302010130,
    name = "弗拉克指了指尼莉莎跑远的地方。"
  },
  [20302010140] = {
    id = 20302010140,
    name = "她去的地方正好是市场中心，差不多需要采购的材料都会在那边，另外那边还有很多有趣的东西，很适合像你们这样没有魔法道具护身的探险者哦。"
  },
  [20302010145] = {id = 20302010145, name = "弗拉克"},
  [20302010150] = {
    id = 20302010150,
    name = "哼，借用外物的都是弱者，真正的强者才不需要那些旁门左道的东西，我才不想......嗯，要不还是看看，万一遇到了好东西....."
  },
  [20302010155] = {id = 20302010155, name = "？？？"},
  [20302010160] = {
    id = 20302010160,
    name = "那就一起去看看吧。"
  },
  [20302010170] = {id = 20302010170, name = "嗯。"},
  [20302010175] = {id = 20302010175, name = "安妮斯"},
  [20302010180] = {
    id = 20302010180,
    name = "你们在熙熙攘攘的街道穿梭，目光不停地在各个摊位上扫视，前面传来了尼莉莎惊叹的声音。"
  },
  [20302010190] = {
    id = 20302010190,
    name = "天哪，好好吃！这个朽木兽的头怎么可以这么好吃！像蘑菇一样软软糯糯的！"
  },
  [20302010195] = {id = 20302010195, name = "尼莉莎"},
  [20302010200] = {
    id = 20302010200,
    name = "少见多怪，这种街边的东西，一定很不干净，也不知道是不是杀了魔物有没有洗手就做的食物，天啊，看看她手里的油渍，看看她狼吞虎咽的样子，简直不像一个淑女！"
  },
  [20302010205] = {id = 20302010205, name = "？？？"},
  [20302010210] = {
    id = 20302010210,
    name = "随随便便就在路边买食物吃，你还真不害怕，还有，你的吃相太难看了"
  },
  [20302010215] = {id = 20302010215, name = "蒂娜"},
  [20302010220] = {
    id = 20302010220,
    name = "你看了眼鬼侍和蒂娜，忽然觉得她们之前也许有很多话可以聊。"
  },
  [20302010230] = {
    id = 20302010230,
    name = "这是塞卢圈落有名的店，最擅长的就是朽木兽的烹制了。没尝过的话，一定不要错过！对了，你们先在这里逛逛，我去前面拐角的店等你们。"
  },
  [20302010235] = {id = 20302010235, name = "弗拉克"},
  [20302010240] = {
    id = 20302010240,
    name = "很好吃哦！再来三块！"
  },
  [20302010245] = {id = 20302010245, name = "尼莉莎"},
  [20302010250] = {
    id = 20302010250,
    name = "尼莉莎付了钱，端来了三大块黄褐色的食物，上面淋着不知道什么植物做的酱，虽然卖相很差，还有正在摆动的触角，但闻起来令人食欲大增。"
  },
  [20302010260] = {
    id = 20302010260,
    name = "快快，你们也来尝尝。"
  },
  [20302010265] = {id = 20302010265, name = "尼莉莎"},
  [20302010275] = {id = 20302010275, name = "安妮斯"},
  [20302010270] = {
    id = 20302010270,
    name = "我就不客气了。"
  },
  [20302010280] = {
    id = 20302010280,
    name = "安妮斯，你要是觉得不舒服，一定要告诉我。"
  },
  [20302010285] = {id = 20302010285, name = "蒂娜"},
  [20302010290] = {
    id = 20302010290,
    name = "蒂娜，你不会是害怕吧？这跟虫子面包差不多，来嘛，来嘛。"
  },
  [20302010295] = {id = 20302010295, name = "尼莉莎"},
  [20302010300] = {
    id = 20302010300,
    name = "不了，我不饿。"
  },
  [20302010305] = {id = 20302010305, name = "蒂娜"},
  [20302010310] = {
    id = 20302010310,
    name = "那给队长和鬼侍小姐吧！"
  },
  [20302010315] = {id = 20302010315, name = "尼莉莎"},
  [20302010320] = {
    id = 20302010320,
    name = "什么，我，我吗......我必须保持我的形象，怎么能吃那种还在摆动的东西，我不能吃。对，这种东西有什么好吃的，不吃也没关系！可是好像真的很香......不行不行！"
  },
  [20302010325] = {id = 20302010325, name = "？？？"},
  [20302010330] = {
    id = 20302010330,
    name = "鬼侍小姐，难得来这里一趟，就一起尝尝吧。"
  },
  [20302010340] = {
    id = 20302010340,
    name = "嗯，谢谢。"
  },
  [20302010345] = {id = 20302010345, name = "鬼侍"},
  [20302010350] = {
    id = 20302010350,
    name = "尝尝就好，不用勉强，不喜欢的话，队长可以吃两份。"
  },
  [20302010355] = {id = 20302010355, name = "蒂娜"},
  [20302010360] = {
    id = 20302010360,
    name = "嗯，还不错的。"
  },
  [20302010365] = {id = 20302010365, name = "鬼侍"},
  [20302010370] = {
    id = 20302010370,
    name = "啊！浓郁醇厚，带着微微的咸香和一丝不易察觉的甜味，恰到好处地衬托出朽木兽的鲜美！我的味蕾在欢呼，这个深渊，竟然还有这样美味的食物！"
  },
  [20302010375] = {id = 20302010375, name = "？？？"},
  [20302010380] = {
    id = 20302010380,
    name = "咳咳咳......"
  },
  [20302020010] = {
    id = 20302020010,
    name = "路过的朋友！瞧一瞧！看一看！小店新作震撼登场，小巧便携，威力无穷！"
  },
  [20302020020] = {
    id = 20302020020,
    name = "由维克多大师亲自指点改进，最新魔法道具，助你轻松猎杀魔物！"
  },
  [20302020030] = {
    id = 20302020030,
    name = "卖魔物角、魔物骨、魔物肉，收新鲜魔植液，源晶碎片。"
  },
  [20302020015] = {
    id = 20302020015,
    name = "神采飞扬的摊主"
  },
  [20302020025] = {
    id = 20302020025,
    name = "气定神闲的摊主"
  },
  [20302020035] = {
    id = 20302020035,
    name = "有气无力的摊主"
  },
  [20302020040] = {
    id = 20302020040,
    name = "摊主们扯着嗓子吆喝着自己的商品。火光在魔法灯盏中跳跃，映照着那些奇形怪状的魔法道具。"
  },
  [20302020050] = {
    id = 20302020050,
    name = "空气中弥漫着各种奇异的味道，有魔法药水的刺鼻气息，有陈旧皮革的腐朽味道，还有不知名草药的清幽芳香。"
  },
  [20302020060] = {
    id = 20302020060,
    name = "探险者的脸上或是充满了期待，或是带着谨慎地审视，他们在摊位间穿梭，不时有人停下来，与摊主激烈地讨价还价，手势比划得夸张而急切。"
  },
  [20302020070] = {
    id = 20302020070,
    name = "快看这个！这面镜子，是不是比那个魔物脑袋上的好玩多了！安妮斯，你看！"
  },
  [20302020075] = {id = 20302020075, name = "尼莉莎"},
  [20302020080] = {
    id = 20302020080,
    name = "尼莉莎按下了镜子旁边的一个按钮，镜子里的安妮斯和尼莉莎瞬间换上了一套华丽的贵族长裙。"
  },
  [20302020090] = {
    id = 20302020090,
    name = "啊，小莎真可爱啊！"
  },
  [20302020095] = {id = 20302020095, name = "尼莉莎"},
  [20302020100] = {
    id = 20302020100,
    name = "你们在看什么？"
  },
  [20302020105] = {id = 20302020105, name = "蒂娜"},
  [20302020115] = {id = 20302020115, name = "？？？"},
  [20302020110] = {
    id = 20302020110,
    name = "镜子？这又是什么镜子，有那么神奇吗？"
  },
  [20302020120] = {
    id = 20302020120,
    name = "鬼侍不动声色地朝几人的方向挪了一步，正好可以在镜子里看见远处的自己。"
  },
  [20302020130] = {
    id = 20302020130,
    name = "这面镜子呀，你看，按下这个，就能在镜子里看到你的另一种装扮！"
  },
  [20302020135] = {id = 20302020135, name = "尼莉莎"},
  [20302020140] = {
    id = 20302020140,
    name = "尼莉莎兴冲冲地按下了另一个按钮，镜子里的四名女孩唰地换上了新的装扮。"
  },
  [20302020150] = {
    id = 20302020150,
    name = "啊啊啊啊！"
  },
  [20302020155] = {id = 20302020155, name = "众人"},
  [20302020160] = {
    id = 20302020160,
    name = "什么破镜子！不会有人看到吧！该死，如果被人看到......啊！我该怎么办！要不要把这条街的人都杀掉？！"
  },
  [20302020170] = {
    id = 20302020170,
    name = "或者只杀掉这个葫芦脸摊主好了，不......杀人是不对的......不行，不行......"
  },
  [20302020175] = {id = 20302020175, name = "？？？"},
  [20302020165] = {id = 20302020165, name = "？？？"},
  [20302020180] = {
    id = 20302020180,
    name = "镜子被高高抛起，你眼疾手快地接下了它，莫名地看着挤在一团的几人，甚至连鬼侍都双手抱紧了自己的身体。"
  },
  [20302020190] = {
    id = 20302020190,
    name = "完了完了，那家伙拿到了镜子，不就知道我们刚才装扮的样子了吗！不行，不可以！不要看！"
  },
  [20302020195] = {id = 20302020195, name = "？？？"},
  [20302020200] = {
    id = 20302020200,
    name = "......你们到底看到了什——"
  },
  [20302020210] = {
    id = 20302020210,
    name = "感觉到旁边有人靠近，趁鬼侍抢走镜子前，你装作不经意地低头看了一眼：镜子里你一丝不挂，只在关键部位贴上了时下流行的玫瑰花。"
  },
  [20302020220] = {
    id = 20302020220,
    name = "你一言不发，立刻关掉了镜子还给了摊主。"
  },
  [20302020230] = {
    id = 20302020230,
    name = "嗯？什么好东西？"
  },
  [20302020235] = {id = 20302020235, name = "菲尼斯"},
  [20302020240] = {id = 20302020240, name = "呵呵。"},
  [20302020250] = {
    id = 20302020250,
    name = "突然理解了那位小姐心中想杀人的冲动。"
  },
  [20302020260] = {
    id = 20302020260,
    name = "这位小姐喜欢刚才那面镜子吗？便宜卖哦！"
  },
  [20302020265] = {
    id = 20302020265,
    name = "神采飞扬的摊主"
  },
  [20302020270] = {
    id = 20302020270,
    name = "冷静，冷静！虽然很想把镜子毁掉，但我要给合作伙伴一个好印象，再说没有人看到我刚才镜子里的样子。对，没有人！"
  },
  [20302020275] = {id = 20302020275, name = "？？？"},
  [20302020280] = {
    id = 20302020280,
    name = "我想看看别的。"
  },
  [20302020285] = {id = 20302020285, name = "鬼侍"},
  [20302020290] = {
    id = 20302020290,
    name = "摊主眉飞色舞地介绍起他其他的魔法道具，有把人装进泡泡里的魔杖，有将不同的元素进行变形的玩偶，还有轻轻摇晃，就能助眠美梦的铃铛......"
  },
  [20302020300] = {
    id = 20302020300,
    name = "喔，小姐喜欢这个铃铛？这里面我们包含了三个梦境：第一个是有数不清的美食，第二个是有花不完的金币，第三个是斩杀领主级魔王的瞬间！"
  },
  [20302020305] = {
    id = 20302020305,
    name = "神采飞扬的摊主"
  },
  [20302020310] = {
    id = 20302020310,
    name = "不用了。"
  },
  [20302020315] = {id = 20302020315, name = "鬼侍"},
  [20302020320] = {
    id = 20302020320,
    name = "鬼侍的神色间流露出些许落寞之意，把铃铛交还给摊主之后，目光转向了你，旋即又恢复了那仿若冰霜的不冷不热之态。"
  },
  [20302020330] = {
    id = 20302020330,
    name = "队长，逛得够久了，我们还是快些把材料买回去吧。"
  },
  [20302020335] = {id = 20302020335, name = "鬼侍"},
  [20302020340] = {
    id = 20302020340,
    name = "好。你们呢，还想再逛逛吗？"
  },
  [20302020350] = {
    id = 20302020350,
    name = "不逛了！"
  },
  [20302020355] = {
    id = 20302020355,
    name = "尼莉莎&安妮斯"
  },
  [20302020360] = {id = 20302020360, name = "......"},
  [20302020365] = {id = 20302020365, name = "蒂娜"},
  [20302030010] = {
    id = 20302030010,
    name = "啊，是你们。来得正好，付钱吧！"
  },
  [20302030015] = {id = 20302030015, name = "弗拉克"},
  [20302030020] = {
    id = 20302030020,
    name = "弗拉克手里抱了一个大木箱子，箱子里都是奇离古怪的魔物残肢，你大致看了一眼，大多是清单上的东西。"
  },
  [20302030030] = {
    id = 20302030030,
    name = "这不就是平时猎杀魔物都不要的垃圾，能值多少钱？"
  },
  [20302030035] = {id = 20302030035, name = "尼莉莎"},
  [20302030040] = {
    id = 20302030040,
    name = "不贵，也就两千欧泊。"
  },
  [20302030045] = {id = 20302030045, name = "弗拉克"},
  [20302030050] = {
    id = 20302030050,
    name = "敲诈啊！"
  },
  [20302030055] = {id = 20302030055, name = "尼莉莎"},
  [20302030060] = {
    id = 20302030060,
    name = "呵，区区两千欧泊都没法支付吗？果然是一群穷鬼，这次算我倒霉，回溯镜拿不到就算了，还陪这群傻瓜耽搁这么久的时间......呵，我还在期待什么？"
  },
  [20302030065] = {id = 20302030065, name = "？？？"},
  [20302030070] = {
    id = 20302030070,
    name = "弗拉克和尼莉莎已经理论了起来，一板一眼地讨论着魔物残肢的价格，鬼侍不温不火的声音插了进来。"
  },
  [20302030080] = {
    id = 20302030080,
    name = "我来付吧。"
  },
  [20302030085] = {id = 20302030085, name = "鬼侍"},
  [20302030090] = {
    id = 20302030090,
    name = "不用，鬼侍小姐。这是我的责任。"
  },
  [20302030100] = {
    id = 20302030100,
    name = "我的同伴未经你的同意就答应了与弗拉克的交易，这对你来说是不公平的，所以这个承诺，应该由我来完成。"
  },
  [20302030110] = {
    id = 20302030110,
    name = "哼，还挺有责任心的，那面镜子碎掉的时候也是，第一时间保护我......"
  },
  [20302030120] = {
    id = 20302030120,
    name = "镜子！糟了！刚才那面换衣装的镜子，最后是他拿在手里了吧！他是不是看到了！那种装扮......那种装扮......可恶，色狼！下流！"
  },
  [20302030125] = {id = 20302030125, name = "？？？"},
  [20302030115] = {id = 20302030115, name = "？？？"},
  [20302030130] = {
    id = 20302030130,
    name = "不，我什么都没看到！"
  },
  [20302030140] = {id = 20302030140, name = "！！！"},
  [20302030145] = {id = 20302030145, name = "鬼侍"},
  [20302030150] = {
    id = 20302030150,
    name = "鬼侍猛地看向了你，面容瞬间失去了血色，她的嘴唇微微颤抖着，似乎想要说些什么，却又仿佛被这突如其来的变故紧紧扼住了咽喉。"
  },
  [20302030160] = {id = 20302030160, name = "！！！"},
  [20302030170] = {
    id = 20302030170,
    name = "她听到了？！"
  },
  [20302030180] = {
    id = 20302030180,
    name = "你......你刚才在跟我说话？"
  },
  [20302030190] = {
    id = 20302030190,
    name = "你......是不是......能听到......我的声音？"
  },
  [20302030200] = {
    id = 20302030200,
    name = "你们就这样对视着，四周的空气仿佛也凝固了，原本应该有的喧嚣和嘈杂此刻全都消失不见，只剩下一片震耳欲聋的沉默。"
  },
  [20302030210] = {
    id = 20302030210,
    name = "........................"
  },
  [20302030185] = {id = 20302030185, name = "鬼侍"},
  [20302030195] = {id = 20302030195, name = "鬼侍"},
  [20302030215] = {id = 20302030215, name = "鬼侍"},
  [20302030220] = {
    id = 20302030220,
    name = "仿佛隔了漫长的时间，你们才慢慢找回市集的声音，听到了店家认输后的妥协。"
  },
  [20302030230] = {
    id = 20302030230,
    name = "看清楚了吗？别再被骗啦，小朋友！"
  },
  [20302030240] = {
    id = 20302030240,
    name = "原来这些材料这么容易就能获得啊。"
  },
  [20302030250] = {
    id = 20302030250,
    name = "尼莉莎好好跟店主理论了一番，终于将价格打到了成本价。"
  },
  [20302030260] = {
    id = 20302030260,
    name = "可此时，你与鬼侍却无心再讨论付钱的问题。"
  },
  [20302030270] = {
    id = 20302030270,
    name = "都买好了？可以走了吗？"
  },
  [20302030275] = {id = 20302030275, name = "鬼侍"},
  [20302030280] = {
    id = 20302030280,
    name = "还差好几种材料啊，可惜不巧，那家的店主今天恰好不在，你们先回我家住一晚吧，明天再来。"
  },
  [20302030285] = {id = 20302030285, name = "弗拉克"},
  [20302030290] = {
    id = 20302030290,
    name = "......嗯。"
  },
  [20302030295] = {id = 20302030295, name = "鬼侍"},
  [20302030300] = {
    id = 20302030300,
    name = "鬼侍捏紧了手心，怒而转身离去。"
  },
  [20302030310] = {id = 20302030310, name = "唉。"},
  [20302030320] = {
    id = 20302030320,
    name = "怎么了，老师？"
  },
  [20302030325] = {id = 20302030325, name = "安妮斯"},
  [20302030330] = {
    id = 20302030330,
    name = "如果你和人产生了误会......该怎么办？"
  },
  [20302030340] = {
    id = 20302030340,
    name = "好好和那个人说清楚吧，既然是误会，那就是可以说清楚的。嗯？老师和谁产生误会了呢？"
  },
  [20302030345] = {id = 20302030345, name = "安妮斯"},
  [20302030350] = {
    id = 20302030350,
    name = "没有。随便问问，走吧。"
  },
  [20302030235] = {id = 20302030235, name = "尼莉莎"},
  [20302030245] = {id = 20302030245, name = "弗拉克"},
  [20303010018] = {
    id = 20303010018,
    name = "试探你来我往"
  },
  [20303010019] = {id = 20303010019, name = "PART 03"},
  [20303010010] = {
    id = 20303010010,
    name = "我先去处理材料啦，一层有厨房，楼上第二层有房间，你们可以随意。"
  },
  [20303010015] = {id = 20303010015, name = "弗拉克"},
  [20303010020] = {
    id = 20303010020,
    name = "弗拉克用脚踩了踩一处机关，小屋地下出现了一道楼梯，他抱着材料噔噔蹬跑了下去。"
  },
  [20303010030] = {
    id = 20303010030,
    name = "欸？我能下去看看吗？"
  },
  [20303010035] = {id = 20303010035, name = "尼莉莎"},
  [20303010040] = {
    id = 20303010040,
    name = "别去了，随便看技术师的秘密是一件很冒犯的事。"
  },
  [20303010045] = {id = 20303010045, name = "蒂娜"},
  [20303010050] = {
    id = 20303010050,
    name = "我买了很多食材，小莎、蒂娜，我们一起做晚餐吧。"
  },
  [20303010055] = {id = 20303010055, name = "安妮斯"},
  [20303010060] = {
    id = 20303010060,
    name = "好欸，小莎又要大展身手了！"
  },
  [20303010065] = {id = 20303010065, name = "尼莉莎"},
  [20303010070] = {
    id = 20303010070,
    name = "鬼侍小姐，你也来吗？"
  },
  [20303010075] = {id = 20303010075, name = "安妮斯"},
  [20303010080] = {
    id = 20303010080,
    name = "我吗？不用了，我想单独和你们的队长谈谈。"
  },
  [20303010085] = {id = 20303010085, name = "鬼侍"},
  [20303010090] = {
    id = 20303010090,
    name = "......鬼侍小姐，我们到外面去说吧。"
  },
  [20303010100] = {id = 20303010100, name = "？？？"},
  [20303010105] = {id = 20303010105, name = "安妮斯"},
  [20303010110] = {
    id = 20303010110,
    name = "咦，有情况哦。"
  },
  [20303010115] = {id = 20303010115, name = "尼莉莎"},
  [20303010120] = {
    id = 20303010120,
    name = "能有什么情况？难道还能找队长赔那面打碎的镜子？"
  },
  [20303010125] = {id = 20303010125, name = "蒂娜"},
  [20303010130] = {
    id = 20303010130,
    name = "哼哼，以小莎的直觉，事情一定没有这么简单。"
  },
  [20303010135] = {id = 20303010135, name = "尼莉莎"},
  [20303010140] = {
    id = 20303010140,
    name = "我，我去给大家做饭。"
  },
  [20303010145] = {id = 20303010145, name = "安妮斯"},
  [20303010150] = {
    id = 20303010150,
    name = "安妮斯走进了厨房。蒂娜见尼莉莎想往外跑，抓了她的衣服拎了回来。"
  },
  [20303010160] = {
    id = 20303010160,
    name = "我们也去帮忙。"
  },
  [20303010170] = {
    id = 20303010170,
    name = "喂喂，你难道不想知道队长和鬼侍小姐之间，有什么不能说的秘密吗？"
  },
  [20303010180] = {
    id = 20303010180,
    name = "我没探听他人隐私的兴趣。"
  },
  [20303010190] = {
    id = 20303010190,
    name = "那可是队长欸！我们不听他们说什么，就站远远的，看看也不行？"
  },
  [20303010200] = {id = 20303010200, name = "......"},
  [20303010210] = {
    id = 20303010210,
    name = "来来来！"
  },
  [20303010220] = {id = 20303010220, name = "......"},
  [20303010225] = {id = 20303010225, name = "菲尼斯"},
  [20303010165] = {id = 20303010165, name = "蒂娜"},
  [20303010185] = {id = 20303010185, name = "蒂娜"},
  [20303010205] = {id = 20303010205, name = "蒂娜"},
  [20303010175] = {id = 20303010175, name = "尼莉莎"},
  [20303010195] = {id = 20303010195, name = "尼莉莎"},
  [20303010215] = {id = 20303010215, name = "尼莉莎"},
  [20303020010] = {
    id = 20303020010,
    name = "夜晚的风轻轻拂过，河水在月光下泛着银色的光芒。"
  },
  [20303020020] = {
    id = 20303020020,
    name = "你跟在鬼侍身后，默默无言地走着。她的背影本是寂寥的，似乎随时会融于夜色中，略显迟疑的脚步却又让那个身影变得生动了起来。"
  },
  [20303020030] = {
    id = 20303020030,
    name = "由于维克多大师居住在这里，附近几乎不会有魔物靠近，此时此刻，只有你们的脚步声和轻微的呼吸声，氛围微妙而压抑。"
  },
  [20303020040] = {
    id = 20303020040,
    name = "不久后，鬼侍终于停在了河边，她沉默地看着河水，有飞虫轻轻点过水面，荡起一圈圈细微的涟漪。"
  },
  [20303020050] = {id = 20303020050, name = "......"},
  [20303020060] = {
    id = 20303020060,
    name = "你不知道该如何开口，这件事从头到尾都是一个意外，但却将她的秘密毫无保留地暴露在你的眼前。"
  },
  [20303020070] = {
    id = 20303020070,
    name = "你......真的可以听到我说话？"
  },
  [20303020075] = {id = 20303020075, name = "鬼侍"},
  [20303020080] = {
    id = 20303020080,
    name = "她抬头看向了你，这一刻，你全身的每寸皮肤都爆发着危机感，甚至怀疑她对你产生了杀意。"
  },
  [20303020090] = {
    id = 20303020090,
    name = "但你还是轻轻地点了点头，既然欺瞒已经成了既定的事实，就不能再继续欺瞒下去。"
  },
  [20303020100] = {
    id = 20303020100,
    name = "呵呵......我以为你会否认。如果你是那种矢口狡赖的无耻之徒，我想我正好杀了你。"
  },
  [20303020105] = {id = 20303020105, name = "鬼侍"},
  [20303020110] = {
    id = 20303020110,
    name = "你如释重负。"
  },
  [20303020120] = {
    id = 20303020120,
    name = "看来鬼侍小姐表面不近人情，实际却是一个通情达理的人。"
  },
  [20303020130] = {
    id = 20303020130,
    name = "抱歉，鬼侍小姐。会出现这样的情况，我也是始料未及。"
  },
  [20303020140] = {
    id = 20303020140,
    name = "......你是什么时候能听到我说话的？"
  },
  [20303020145] = {id = 20303020145, name = "鬼侍"},
  [20303020150] = {
    id = 20303020150,
    name = "应该是，我们苏醒之后。"
  },
  [20303020160] = {id = 20303020160, name = "！！！"},
  [20303020165] = {id = 20303020165, name = "鬼侍"},
  [20303020170] = {
    id = 20303020170,
    name = "你几乎可以肯定，那面镜子，是造成你能听到鬼侍心声的原因之一，但却始终想不通为什么。"
  },
  [20303020180] = {
    id = 20303020180,
    name = "几十步之外，尼莉莎、蒂娜和菲尼斯躲在一棵树后，悄悄看着你与鬼侍的对话。"
  },
  [20303020190] = {
    id = 20303020190,
    name = "哇哇，竟然走了这么远，我就知道有猫腻。"
  },
  [20303020200] = {
    id = 20303020200,
    name = "队长在道歉诶，是为了那个打碎的宝物吗？队长为我们付出太多了。"
  },
  [20303020210] = {
    id = 20303020210,
    name = "尼莉莎小姐的耳朵很灵敏嘛。"
  },
  [20303020220] = {
    id = 20303020220,
    name = "当然，我可是优秀的猎影，啊起风了，糟了，听不清了。"
  },
  [20303020230] = {
    id = 20303020230,
    name = "奇怪，鬼侍为什么一句话也不说。"
  },
  [20303020240] = {
    id = 20303020240,
    name = "也许被风吹散了呢。"
  },
  [20303020250] = {
    id = 20303020250,
    name = "都闭嘴，不看就回去。"
  },
  [20303020255] = {id = 20303020255, name = "蒂娜"},
  [20303020245] = {id = 20303020245, name = "菲尼斯"},
  [20303020215] = {id = 20303020215, name = "菲尼斯"},
  [20303020235] = {id = 20303020235, name = "尼莉莎"},
  [20303020225] = {id = 20303020225, name = "尼莉莎"},
  [20303020205] = {id = 20303020205, name = "尼莉莎"},
  [20303020195] = {id = 20303020195, name = "尼莉莎"},
  [20303020260] = {
    id = 20303020260,
    name = "尼莉莎立马闭紧了嘴巴，三人为防止被你们注意到，只浅浅地探出了一个头。"
  },
  [20303020270] = {
    id = 20303020270,
    name = "你的确没有注意到那三个人，因为鬼侍正不停地在心里说着话，可不管有多慌张和震惊，她都能保证平静如水的面色。"
  },
  [20303020280] = {
    id = 20303020280,
    name = "所以从早上苏醒那一刻开始，你就能听到我所有的心声？！"
  },
  [20303020285] = {id = 20303020285, name = "鬼侍"},
  [20303020290] = {
    id = 20303020290,
    name = "大概......吧。"
  },
  [20303020300] = {
    id = 20303020300,
    name = "鬼侍的身体一僵，脸上闪过一丝慌乱。"
  },
  [20303020310] = {
    id = 20303020310,
    name = "那个时候，我在心里催促弗拉克，嫌弃你们的时候......你都听见了？"
  },
  [20303020315] = {id = 20303020315, name = "鬼侍"},
  [20303020320] = {
    id = 20303020320,
    name = "嗯，那时，我也不确定，我以为是我的幻听。"
  },
  [20303020330] = {
    id = 20303020330,
    name = "在圈落的时候，你听到了我的心声，才邀请我一起逛街的？"
  },
  [20303020335] = {id = 20303020335, name = "鬼侍"},
  [20303020340] = {
    id = 20303020340,
    name = "嗯，直到那时我才确定，你的声音不是错觉。"
  },
  [20303020350] = {
    id = 20303020350,
    name = "鬼侍别过了头去，没有再看你。"
  },
  [20303020360] = {
    id = 20303020360,
    name = "......我想吃朽木兽的时候，你......也听见了？"
  },
  [20303020365] = {id = 20303020365, name = "鬼侍"},
  [20303020370] = {id = 20303020370, name = "嗯。"},
  [20303020380] = {
    id = 20303020380,
    name = "那，那面换装镜子里的东西呢？你看到了......吧？"
  },
  [20303020385] = {id = 20303020385, name = "鬼侍"},
  [20303020390] = {
    id = 20303020390,
    name = "当时不知道的，在听到你说话之后就......看到了。"
  },
  [20303020400] = {
    id = 20303020400,
    name = "闭嘴！你别再说话了！"
  },
  [20303020405] = {id = 20303020405, name = "鬼侍"},
  [20303020410] = {
    id = 20303020410,
    name = "鬼侍彻底转过了身去，生怕自己的心思再被你窥探一般，垂在两旁的手紧紧握着，指节微微泛白。"
  },
  [20303020420] = {id = 20303020420, name = "抱歉。"},
  [20303020430] = {
    id = 20303020430,
    name = "你温和的致歉在鬼侍耳边响起，她猛地转身看向你。"
  },
  [20303020440] = {
    id = 20303020440,
    name = "颤动的睫毛下，水晶般的眼瞳直直注视着你，那一眼仿若承载了万般复杂的情绪，是无措与不安，还有某种莫名的期许。"
  },
  [20303020450] = {
    id = 20303020450,
    name = "你......再说一句，用心声。"
  },
  [20303020455] = {id = 20303020455, name = "鬼侍"},
  [20303020460] = {
    id = 20303020460,
    name = "鬼侍小姐，我无意偷听你的心事，真的很抱歉。"
  },
  [20303020470] = {
    id = 20303020470,
    name = "那一瞬间，你看见鬼侍眼中的光芒淡去了，掩盖了失落后，再次恢复了那张面无表情的脸庞。"
  },
  [20303020480] = {
    id = 20303020480,
    name = "你既然能听到我的声音，为什么不早点告诉我？"
  },
