    name = "我去吧，没问题。"
  },
  [20306010405] = {id = 20306010405, name = "鬼侍"},
  [20306010395] = {id = 20306010395, name = "鬼侍"},
  [20306010410] = {
    id = 20306010410,
    name = "老师呢？"
  },
  [20306010415] = {id = 20306010415, name = "安妮斯"},
  [20306010420] = {
    id = 20306010420,
    name = "既然有鬼面魔蛛的存在，据点里就不止一只魔物。"
  },
  [20306010430] = {
    id = 20306010430,
    name = "一路由鬼侍小姐带队，蒂娜和菲尼斯，你们将鬼面魔蛛引走，另一路就由我、安妮斯、弗拉克去取材料，小莎去制高点观察情况，以便随时支援。"
  },
  [20306010440] = {
    id = 20306010440,
    name = "没问题，队长！"
  },
  [20306010445] = {id = 20306010445, name = "尼莉莎"},
  [20306010450] = {
    id = 20306010450,
    name = "好的，老师。"
  },
  [20306010455] = {id = 20306010455, name = "安妮斯"},
  [20306010460] = {
    id = 20306010460,
    name = "引开鬼面魔蛛后，只要看到信号，就立刻撤退，千万不要恋战。"
  },
  [20306010470] = {
    id = 20306010470,
    name = "当然，我也想早些回去，享受河边宁静的时光。"
  },
  [20306010475] = {id = 20306010475, name = "菲尼斯"},
  [20306010490] = {
    id = 20306010490,
    name = "呵呵，那就靠你了。"
  },
  [20306010480] = {
    id = 20306010480,
    name = "你的这个队友看起来不太靠得住。"
  },
  [20306010485] = {id = 20306010485, name = "鬼侍"},
  [20306020010] = {
    id = 20306020010,
    name = "这次有你们帮忙，一定不会再失败了。"
  },
  [20306020015] = {id = 20306020015, name = "弗拉克"},
  [20306020020] = {
    id = 20306020020,
    name = "要是失败了怎么办？"
  },
  [20306020025] = {id = 20306020025, name = "菲尼斯"},
  [20306020030] = {
    id = 20306020030,
    name = "你这个乌鸦嘴能不能闭上。"
  },
  [20306020035] = {id = 20306020035, name = "蒂娜"},
  [20306020040] = {
    id = 20306020040,
    name = "我们出发吧。"
  },
  [20306020045] = {id = 20306020045, name = "鬼侍"},
  [20306020050] = {
    id = 20306020050,
    name = "鬼侍抬头看了看不远处高耸的巨型魔物，快速谨慎走出了掩体，蒂娜也无心再和菲尼斯说话，三人随即消失在了视野中。"
  },
  [20306020060] = {
    id = 20306020060,
    name = "不愧是无论何时都能保持镇定的鬼侍小姐，真可靠啊！"
  },
  [20306020065] = {id = 20306020065, name = "弗拉克"},
  [20306020070] = {
    id = 20306020070,
    name = "老师，我们……"
  },
  [20306020075] = {id = 20306020075, name = "安妮斯"},
  [20306020080] = {
    id = 20306020080,
    name = "我们再等等，小莎已经找到制高点了，会随时给我们信号。"
  },
  [20306020090] = {id = 20306020090, name = "吼！！"},
  [20306020100] = {
    id = 20306020100,
    name = "鬼面魔蛛发现他们了！"
  },
  [20306020105] = {id = 20306020105, name = "弗拉克"},
  [20306020110] = {
    id = 20306020110,
    name = "一阵雷霆般的咆哮震响，轰隆隆的脚步声甚至让你能感觉到地面的震动，而后数十只魔物齐齐吼叫，它们紧紧跟在巨兽身后，蜂拥而出，你们屏住呼吸，等着魔物的远去。"
  },
  [20306020120] = {
    id = 20306020120,
    name = "不知鬼侍用了什么办法，仅仅隔了半分钟，据点中的魔物竟然全被引了出去。"
  },
  [20306020130] = {
    id = 20306020130,
    name = "没魔物了，我们出去吗？"
  },
  [20306020135] = {id = 20306020135, name = "弗拉克"},
  [20306020140] = {
    id = 20306020140,
    name = "不忙，你看。"
  },
  [20306020150] = {
    id = 20306020150,
    name = "一只魔物拖着长长的尾巴，刚好从弗拉克的脚边略过。"
  },
  [20306020160] = {
    id = 20306020160,
    name = "魔物被引出来了，你们快点！"
