  },
  [20306020190] = {
    id = 20306020190,
    name = "好厉害！"
  },
  [20306020195] = {id = 20306020195, name = "弗拉克"},
  [20306020200] = {id = 20306020200, name = "嗯。"},
  [20306020205] = {id = 20306020205, name = "安妮斯"},
  [20306020210] = {id = 20306020210, name = "带路。"},
  [20306020220] = {
    id = 20306020220,
    name = "弗拉克立刻回神，带你找到刚才鬼面魔蛛留守的地方。"
  },
  [20306020230] = {
    id = 20306020230,
    name = "老师，在那里！"
  },
  [20306020235] = {id = 20306020235, name = "安妮斯"},
  [20306020240] = {
    id = 20306020240,
    name = "被压成粉末的废墟上，一块火褐色皮革的盒子尤其显眼，你准备伸手去拿时，突然从粉末中窜出来一只宛如骨妖的魔物！"
  },
  [20306020250] = {
    id = 20306020250,
    name = "是凶镰！"
  },
  [20306020255] = {id = 20306020255, name = "安妮斯"},
  [20306020260] = {
    id = 20306020260,
    name = " 嘶！！！那魔物发出了一声尖锐的威胁声，仿佛在警告你们不要靠近。与此同时，鬼面魔蛛的咆哮也轰然响起，震撼着周围的每一寸空气，让人不寒而栗。"
  },
  [20306020270] = {
    id = 20306020270,
    name = "你们拿到了吗？！"
  },
  [20306020275] = {id = 20306020275, name = "鬼侍"},
  [20306020280] = {
    id = 20306020280,
    name = "还没有。"
  },
  [20306020290] = {
    id = 20306020290,
    name = "锵——你的武器斩向凶镰，却毫无动静，反而它灵活地摆尾，朝你们来了个大甩击，被安妮斯用屏障及时挡下！"
  },
  [20306020300] = {
    id = 20306020300,
    name = "老师小心！"
  },
  [20306020305] = {id = 20306020305, name = "安妮斯"},
  [20306020310] = {
    id = 20306020310,
    name = "还要多久？！"
  },
  [20306020320] = {
    id = 20306020320,
    name = "判断失误，这里留下的魔物，和鬼面魔蛛的实力相当。"
  },
  [20306020330] = {
    id = 20306020330,
    name = "那怎么办？！鬼面魔蛛好像知道了什么，在拼命往回赶，我们快拦不住了！"
  },
  [20306020340] = {
    id = 20306020340,
    name = "锵——你无法在兼顾保护两个人的情况下，全心全意地战斗，三人也被凶镰逼得离盒子越来越远。"
  },
  [20306020350] = {
    id = 20306020350,
    name = "弗拉克 ，我拖住魔物，你用吉米去拿盒子！"
  },
  [20306020335] = {id = 20306020335, name = "鬼侍"},
  [20306020315] = {id = 20306020315, name = "鬼侍"},
  [20306020380] = {
    id = 20306020380,
    name = "吉米，吉米，喂，别怕啊！"
  },
  [20306020370] = {
    id = 20306020370,
    name = "弗拉克将吉米抛了出去，吉米又原地飞了回来，仿佛不愿意再接近那个地方。"
  },
  [20306020360] = {
    id = 20306020360,
    name = "哦，好的！"
  },
  [20306020365] = {id = 20306020365, name = "弗拉克"},
  [20306020385] = {id = 20306020385, name = "弗拉克"},
  [20306020390] = {
    id = 20306020390,
    name = "拿到了吗？咳咳……"
  },
  [20306020400] = {
    id = 20306020400,
    name = "我们立刻撤退，你们也别再纠缠，放那家伙回来！"
  },
  [20306020410] = {
    id = 20306020410,
    name = "你察觉到了形势的逆转，意识到事情远比预期的要糟糕，毫不犹豫地下达了紧急撤离的命令。"
  },
  [20306020420] = {
    id = 20306020420,
    name = "安妮斯，弗拉克，我们先撤！鬼面魔蛛要回来了！"
  },
  [20306020430] = {
    id = 20306020430,
    name = "震颤的脚步声越来越近，安妮斯立刻洒出了强效驱魔粉，虽然只能短暂阻拦凶镰很短的时间，但也足够让你们撤离。"
  },
  [20306020395] = {id = 20306020395, name = "鬼侍"},
  [20307010010] = {
    id = 20307010010,
    name = "哇，刚才差点就被魔物们包成三明治吃掉了！"
  },
  [20307010015] = {id = 20307010015, name = "尼莉莎"},
  [20307010018] = {
    id = 20307010018,
    name = "默契相得益彰"
  },
  [20307010019] = {id = 20307010019, name = "PART 07"},
  [20307010020] = {
    id = 20307010020,
    name = "幸好尼莉莎小姐发现那大家伙不对劲，帮我们拖延了时间。"
  },
  [20307010025] = {id = 20307010025, name = "菲尼斯"},
  [20307] = {
    id = 20307,
    name = "默契相得益彰"
  },
  [203071] = {
    id = 203071,
    name = "你们撤退后重新商量作战计划，决定第二次出击。\n战斗到关键的时刻，你和鬼侍靠心声传递配合出手，\n共同击杀了魔物，拿回了材料。"
  },
  [20307010030] = {
    id = 20307010030,
    name = "那只魔物就像能感知到你们在行动。我们才将它引走没多远，它就想回去，鬼侍小姐为了拦住它，还受了轻伤。"
  },
  [20307010035] = {id = 20307010035, name = "蒂娜"},
  [20307010040] = {
    id = 20307010040,
    name = "受了伤？"
  },
  [20307010050] = {
    id = 20307010050,
    name = "我没事。"
  },
  [20307010055] = {id = 20307010055, name = "鬼侍"},
  [20307010060] = {
    id = 20307010060,
    name = "都怪菲尼斯的乌鸦嘴，明明很完美的计划，竟然也发生了意外。"
  },
  [20307010070] = {
    id = 20307010070,
    name = "不不，是我的原因，吉米的指令出了问题，才让这次行动失败的。"
  },
  [20307010080] = {
    id = 20307010080,
    name = "不过队长撤离得真是及时啊，你们刚走，那个大家伙就爬回据点了。"
  },
  [20307010065] = {id = 20307010065, name = "尼莉莎"},
  [20307010075] = {id = 20307010075, name = "弗拉克"},
  [20307010085] = {id = 20307010085, name = "尼莉莎"},
  [20307010090] = {
    id = 20307010090,
    name = "你不是负责发信号的吗？"
  },
  [20307010095] = {id = 20307010095, name = "蒂娜"},
  [20307010100] = {
    id = 20307010100,
    name = "当时打得多激烈呀，哪有时间发信号，要不是小莎，你身上还多一个血窟窿呢。"
  },
  [20307010105] = {id = 20307010105, name = "尼莉莎"},
  [20307010110] = {
    id = 20307010110,
    name = "还好队长和鬼侍小姐有默契，几乎同时选择了撤退，要不咱们几个都得受伤。"
  },
  [20307010115] = {id = 20307010115, name = "菲尼斯"},
  [20307010120] = {
    id = 20307010120,
    name = "老师的判断很准确，发现无法越过凶镰拿取材料之后，就立刻让我们离开了。"
  },
  [20307010125] = {id = 20307010125, name = "安妮斯"},
  [20307010130] = {
    id = 20307010130,
    name = "……这种巧合可一不可再，这次有鬼侍小姐相助，下次就没那么幸运了。"
  },
  [20307010140] = {id = 20307010140, name = "哼。"},
  [20307010150] = {
    id = 20307010150,
    name = "好了，我们还是讨论一下，怎么样在两只魔物手里拿回那个盒子吧。"
  },
  [20307010145] = {id = 20307010145, name = "尼莉莎"},
  [20307010135] = {id = 20307010135, name = "蒂娜"},
  [20307010160] = {
    id = 20307010160,
    name = "一个鬼面魔蛛已经很棘手，加上一个凶镰的“阵地战”，又该如何攻破呢？你们集体陷入了沉思。"
  },
  [20307010170] = {
    id = 20307010170,
    name = "为什么不直接杀掉？"
  },
  [20307010175] = {id = 20307010175, name = "鬼侍"},
  [20307010180] = {
    id = 20307010180,
    name = "以鬼面魔蛛的实力，分成两组根本无法立刻击杀，如果让它们有机会汇合，又会陷入困境。"
  },
  [20307010185] = {id = 20307010185, name = "蒂娜"},
  [20307010190] = {
    id = 20307010190,
    name = "如果咱们集中人力，把鬼面魔蛛引出来先杀掉呢？"
  },
  [20307010195] = {id = 20307010195, name = "尼莉莎"},
  [20307010200] = {
    id = 20307010200,
    name = "恐怕不行，从上次战斗情况来看，两只魔物之间是有感应的，在鬼面魔蛛意识到危机时候，凶镰也会立刻赶来支援。"
  },
  [20307010210] = {
    id = 20307010210,
    name = "那就让它无法支援。"
  },
  [20307010215] = {id = 20307010215, name = "鬼侍"},
  [20307010220] = {
    id = 20307010220,
    name = "也就是说，还是需要人去牵制凶镰。"
  },
  [20307010230] = {
    id = 20307010230,
    name = "我去吧，一个人够了。"
  },
  [20307010235] = {id = 20307010235, name = "鬼侍"},
  [20307010240] = {
    id = 20307010240,
    name = "鬼侍小姐……"
  },
  [20307010245] = {id = 20307010245, name = "安妮斯"},
  [20307010250] = {
    id = 20307010250,
    name = "如果我们能在短时间内杀掉鬼面魔蛛——"
  },
  [20307010255] = {id = 20307010255, name = "菲尼斯"},
  [20307010260] = {
    id = 20307010260,
    name = "太冒险了。"
  },
  [20307010270] = {
    id = 20307010270,
    name = "对于你们而言，击杀一只鬼面魔蛛，不应该是很简单的事吗？"
  },
  [20307010280] = {
    id = 20307010280,
    name = "凶镰的攻击性非常强，倘若再有任何魔物加入这场混战，无疑会将你置身于危险的境地。"
  },
  [20307010290] = {
    id = 20307010290,
    name = "你是对我没有信心？还是对自己没有信心？"
  },
  [20307010300] = {
    id = 20307010300,
    name = "如果发生意外——"
  },
  [20307010310] = {
    id = 20307010310,
    name = "如果发生意外，你会来救我吗？"
  },
  [20307010320] = {id = 20307010320, name = "会。"},
  [20307010275] = {id = 20307010275, name = "鬼侍"},
  [20307010295] = {id = 20307010295, name = "鬼侍"},
  [20307010315] = {id = 20307010315, name = "鬼侍"},
  [20307010330] = {
    id = 20307010330,
    name = "你毫无迟疑地回答。鬼侍的话点亮了你的思绪，你与鬼侍无拘无碍的心声传递，就是可以随时修正计划的绝佳利器。"
  },
  [20307010340] = {
    id = 20307010340,
    name = "连队长都认为冒险的事，那一定很危险了。"
  },
  [20307010345] = {id = 20307010345, name = "尼莉莎"},
  [20307010350] = {
    id = 20307010350,
    name = "虽然冒险，但可以一试。"
  },
  [20307010360] = {id = 20307010360, name = "啊？"},
  [20307010365] = {id = 20307010365, name = "尼莉莎"},
  [20307010370] = {id = 20307010370, name = "……"},
  [20307010375] = {id = 20307010375, name = "鬼侍"},
  [20307010380] = {
    id = 20307010380,
    name = "鬼侍朝你看了一眼，嘴角微微上扬，眼角也微微弯起，趁你看过去之前恢复了原状。"
  },
  [20307020010] = {
    id = 20307020010,
    name = "再次回到废墟之外，里面的鬼面魔蛛正在进食。"
  },
  [20307020020] = {
    id = 20307020020,
    name = "在鬼面魔蛛看来，这是它的领地，比它弱小的魔物，都是自己蓄养的食物。"
  },
  [20307020030] = {
    id = 20307020030,
    name = "咔嚓，一只魔物被咬断了身体，其余的魔物如飞沙般崩散开，纷纷躲到了墙角里。"
  },
  [20307020040] = {
    id = 20307020040,
    name = "据说，在魔物进食的时候打扰它们，是最危险的时候。"
  },
  [20307020050] = {
    id = 20307020050,
    name = "鬼面魔珠好像受伤了？看它的样子，正在用进食疗伤。"
  },
  [20307020060] = {
    id = 20307020060,
    name = "没错，之前在拦阻鬼面魔蛛时，鬼侍小姐对它发起了攻击，斩断了它的一条腿。"
  },
  [20307020070] = {
    id = 20307020070,
    name = "那我们准备行动吧。"
  },
  [20307020080] = {
    id = 20307020080,
    name = "队长，你认真的？"
  },
  [20307020090] = {
    id = 20307020090,
    name = "难道尼莉莎小姐想挑战一下难度，等到它痊愈再行动？"
