  [20307020105] = {id = 20307020105, name = "尼莉莎"},
  [20307020055] = {id = 20307020055, name = "安妮斯"},
  [20307020065] = {id = 20307020065, name = "蒂娜"},
  [20307020095] = {id = 20307020095, name = "菲尼斯"},
  [20307020120] = {
    id = 20307020120,
    name = "鬼侍小姐。"
  },
  [20307020130] = {
    id = 20307020130,
    name = "我已经准备好了，凶镰就在我的前方不远处。"
  },
  [20307020135] = {id = 20307020135, name = "鬼侍"},
  [20307020140] = {
    id = 20307020140,
    name = "一击即中，鬼面魔蛛转身“望”向了你的位置，发出一声怒吼，随即多只长满倒刺的巨足踏碎了地面的砖石，飞快地朝你冲了过来！"
  },
  [20307020150] = {
    id = 20307020150,
    name = "尼莉莎。"
  },
  [20307020160] = {
    id = 20307020160,
