  },
  [20307020175] = {id = 20307020175, name = "尼莉莎"},
  [20307020170] = {
    id = 20307020170,
    name = "看小莎来烧、烤、大虫子！"
  },
  [20307020180] = {
    id = 20307020180,
    name = "陷阱燃起一片火海，鬼面魔珠在火海中剧烈晃动起来，你没有转身去看，而是疾速奔向与队友约定的目的地——"
  },
  [20307020190] = {
    id = 20307020190,
    name = "那个会让它陨身糜骨的葬身之地。"
  },
  [20307020200] = {
    id = 20307020200,
    name = "准备战斗！"
  },
  [20307020210] = {
    id = 20307020210,
    name = "随着你的命令一下，鬼面魔蛛从火海中一跃而出，尖啸着朝你们扑了上来！"
  },
  [20307020220] = {
    id = 20307020220,
    name = "蒂娜提剑迎了上去，长剑在空气中划过一道冷光，与与蛛腿碰撞，发出铮铮的声响。"
  },
  [20307020230] = {
    id = 20307020230,
    name = "菲尼斯身形鬼魅，巧妙地避开毒液，趁机跃到鬼面魔蛛的背上，手中的剑狠狠刺下！"
  },
  [20307020240] = {
    id = 20307020240,
    name = "吼！！鬼面魔蛛痛苦地抖动着身体，将菲尼斯甩了出去，锋利的蛛腿闪烁着寒光，在踏上菲尼斯的那一刻，被他躲了过去。"
  },
  [20307020250] = {
    id = 20307020250,
    name = "这家伙的壳真硬啊。"
  },
  [20307020255] = {id = 20307020255, name = "菲尼斯"},
  [20307020260] = {id = 20307020260, name = "别大意"},
  [20307020270] = {
    id = 20307020270,
    name = "凶镰行动了，我先拦下它。"
  },
  [20307020280] = {
    id = 20307020280,
    name = "知道了，我们会尽快。"
  },
  [20307020275] = {id = 20307020275, name = "鬼侍"},
  [20307020290] = {
    id = 20307020290,
    name = "鬼面魔蛛吃痛，彻底发狂。巨大的身躯高高扬起，口中发出令人毛骨悚然的嘶叫声。锋利的蛛腿疯狂地挥舞着，带起一阵阵凌厉的风声。"
  },
  [20307020300] = {
    id = 20307020300,
    name = "吉米，你别害怕，你是一个勇敢的魔偶！冲啊！欸？喂，喂，别跑！"
  },
  [20307020305] = {id = 20307020305, name = "弗拉克"},
  [20307020310] = {
    id = 20307020310,
    name = "大虫子，再尝尝小莎的这招！给你加点料！"
  },
  [20307020315] = {id = 20307020315, name = "尼莉莎"},
  [20307020320] = {
    id = 20307020320,
    name = "尼莉莎搭弓射出了一列爆破箭，接着在鬼面魔蛛身上炸裂，你似乎闻到了一股食物烤焦的味道。"
  },
  [20307020330] = {
    id = 20307020330,
    name = "蒂娜趁机而上，狠狠地斩下了鬼面魔蛛的又一条腿！"
  },
  [20307020340] = {
    id = 20307020340,
    name = "好了吗？"
  },
  [20307020350] = {id = 20307020350, name = "很快！"},
  [20307020360] = {
