  [20309010095] = {id = 20309010095, name = "尼莉莎"},
  [20309010100] = {
    id = 20309010100,
    name = "嘭！一股强大的冲击力从地下室喷薄而出，一个灰头土脸的身影从滚滚浓烟中冲了出来。"
