  },
  [20309010145] = {id = 20309010145, name = "弗拉克"},
  [20309010150] = {
    id = 20309010150,
    name = "眼看他兴奋地再次跑进了地下工坊，不到几秒，又垂头丧气地爬了上来。"
  },
  [20309010160] = {
    id = 20309010160,
    name = "唉，工坊好些东西被炸毁了，又要重新采购了。"
  },
  [20309010165] = {id = 20309010165, name = "弗拉克"},
  [20309010170] = {
    id = 20309010170,
    name = "既然如此，先吃早餐吧，一会儿大家一起帮你收拾。"
  },
  [20309010180] = {
    id = 20309010180,
    name = "咦？队长，你竟然会做早餐吗？"
  },
  [20309010185] = {id = 20309010185, name = "尼莉莎"},
  [20309010190] = {
    id = 20309010190,
    name = "不是我，是鬼侍小姐准备的。"
  },
  [20309010200] = {
    id = 20309010200,
    name = "咚咚咚，大门被敲响了，众人正在猜测是谁的时候，鬼侍前去打开了门。"
  },
  [20309010210] = {
    id = 20309010210,
    name = "鬼侍小姐，这是您预定的早餐。"
  },
  [20309010215] = {id = 20309010215, name = "送餐员"},
  [20309010220] = {
