  },
  [20309010225] = {id = 20309010225, name = "鬼侍"},
  [20309010230] = {
    id = 20309010230,
    name = "鬼侍给送餐员两块银币，提了一大盒吃食放上了桌，食盒分了多层，每一层都装着精致的面包，以及稀有的糕点。"
