  },
  [20309010255] = {id = 20309010255, name = "弗拉克"},
  [20309010245] = {id = 20309010245, name = "尼莉莎"},
  [20309010260] = {
    id = 20309010260,
    name = "你看了鬼侍一眼，她朝你点点头。"
  },
  [20309010270] = {
    id = 20309010270,
    name = "队长不想同我出去，要和你们一起吃。"
  },
  [20309010275] = {id = 20309010275, name = "鬼侍"},
  [20309010280] = {
    id = 20309010280,
    name = "呵呵，鬼侍小姐是说，大家昨日都辛苦了，让我早上去圈落，找大厨准备了一桌早餐。"
  },
  [20309010295] = {id = 20309010295, name = "菲尼斯"},
  [20309010290] = {
