    name = "尼莉莎&安妮斯"
  },
  [20309010380] = {id = 20309010380, name = "！！！"},
  [20309010390] = {
    id = 20309010390,
    name = "尼莉莎惊掉了手中的食物，安妮斯愣愣地看着两人，茫然又无措。"
  },
  [20309010400] = {
    id = 20309010400,
    name = "我只是对果酱塔的做法很感兴趣，多看了两眼。"
  },
  [20309010410] = {
    id = 20309010410,
    name = "是这样啊。"
  },
  [20309010420] = {
    id = 20309010420,
    name = "那你喜欢吃什么？"
  },
  [20309010430] = {
    id = 20309010430,
    name = "不用麻烦了，鬼侍小姐，深渊里没有这么讲究，不挨饿已经很好了。"
  },
  [20309010440] = {
    id = 20309010440,
    name = "叫我柯莱特尔，你不是已经知道了吗？"
  },
  [20309010450] = {
    id = 20309010450,
    name = "你顿了顿，对于昨晚呼之欲出的名字，现在却怎么也没办法再开口。"
  },
  [20309010460] = {
    id = 20309010460,
    name = "不过，习惯贵族生活的柯莱特尔大小姐，怎么会成为鬼牌的鬼侍呢？"
  },
  [20309010470] = {
    id = 20309010470,
    name = "你想知道这个答案，但直觉让你没有询问——每个人都有自己的秘密，除非让她主动告诉你。"
  },
  [20309010480] = {
    id = 20309010480,
