  [20309010515] = {id = 20309010515, name = "尼莉莎"},
  [20309010520] = {
    id = 20309010520,
    name = "我记得队长最爱吃炸酥的小鱼，中午你给大家露一手，让鬼侍小姐和弗拉克也一起尝尝你的厨艺！"
