  [20309020065] = {id = 20309020065, name = "安妮斯"},
  [20309020035] = {id = 20309020035, name = "尼莉莎"},
  [20309020055] = {id = 20309020055, name = "尼莉莎"},
  [20309020045] = {id = 20309020045, name = "蒂娜"},
  [20309020080] = {
    id = 20309020080,
    name = "随着魔法阵光芒一闪，“轰隆”一声巨响，河水瞬间掀起巨大的浪花，鱼儿被炸得四处逃窜。"
  },
  [20309020090] = {
    id = 20309020090,
    name = "哈哈哈，太好玩啦！"
  },
  [20309020095] = {id = 20309020095, name = "尼莉莎"},
  [20309030010] = {
    id = 20309030010,
    name = "鬼侍小姐，你想修好这一对吊坠？"
  },
  [20309030020] = {
    id = 20309030020,
    name = "嗯，有办法吗？"
  },
  [20309030030] = {
    id = 20309030030,
    name = "有是有，可我没有办法复刻里面的魔法符文，那种古老的符文，只有德高望重的言灵大师才能做得到吧。"
  },
  [20309030040] = {id = 20309030040, name = "......"},
  [20309030050] = {
    id = 20309030050,
    name = "不过你要是让我只恢复外观的话，还是挺容易的。"
  },
  [20309030060] = {
    id = 20309030060,
    name = "......能恢复它们原本的样子，也好。"
  },
  [20309030070] = {
    id = 20309030070,
    name = "你确定吗？这样就算修复完成，也只能是一对普通的装饰品了。"
  },
  [20309030080] = {
    id = 20309030080,
    name = "嗯，拜托了。"
  },
  [20309030015] = {id = 20309030015, name = "弗拉克"},
  [20309030035] = {id = 20309030035, name = "弗拉克"},
  [20309030055] = {id = 20309030055, name = "弗拉克"},
  [20309030075] = {id = 20309030075, name = "弗拉克"},
  [20309030025] = {id = 20309030025, name = "鬼侍"},
  [20309030045] = {id = 20309030045, name = "鬼侍"},
  [20309030065] = {id = 20309030065, name = "鬼侍"},
  [20309030085] = {id = 20309030085, name = "鬼侍"},
  [20309010415] = {id = 20309010415, name = "鬼侍"},
  [20309010425] = {id = 20309010425, name = "鬼侍"},
  [20309010445] = {id = 20309010445, name = "鬼侍"},
  [20310010010] = {
    id = 20310010010,
    name = "我说队长，明明你随便叫一个，她们都愿意和你出来，怎么只拉着我一起？"
  },
  [20310010015] = {id = 20310010015, name = "菲尼斯"},
  [20310010018] = {
    id = 20310010018,
    name = "冷夜风雨茫茫"
  },
  [20310010019] = {id = 20310010019, name = "PART 10"},
  [20310] = {
    id = 20310,
    name = "冷夜风雨茫茫"
  },
  [203101] = {
    id = 203101,
    name = "伙伴们一起帮弗拉克重新布置工坊。\n你在寻找最后一道材料的同时，断断续续听到鬼侍遇险，\n匆忙赶过去救人时，却再也无法收到鬼侍的讯息......"
  },
  [20310010020] = {
    id = 20310010020,
    name = "不是你，难道让那些女孩子们做苦力？"
  },
  [20310010030] = {
    id = 20310010030,
    name = "我想不只是这个原因吧？"
  },
  [20310010040] = {
    id = 20310010040,
    name = "你没理他，朝最近的小摊走去，购置了一堆工坊的小零件，那是弗拉克重组工坊需要的东西。"
  },
  [20310010050] = {
    id = 20310010050,
    name = "队长，你要有什么困扰可以尽管告诉我，我擅长为人排忧解难，尤其是感情问题。"
  },
  [20310010060] = {
    id = 20310010060,
    name = "你能为我分忧的最好办法，就是闭上你的嘴。"
  },
  [20310010070] = {
    id = 20310010070,
    name = "菲尼斯耸耸肩，接过你递来的一大筐金属零件和奇奇怪怪的矿石，跟在你旁边继续走着。"
  },
  [20310010080] = {
    id = 20310010080,
    name = "维克多大师涉猎广泛，没想到他的弟子却只专注于机械傀儡的设计。"
  },
  [20310010090] = {
    id = 20310010090,
    name = "这也是弗拉克天赋的地方，他还这么年少，前途无量。"
  },
  [20310010100] = {
    id = 20310010100,
    name = "我们认识的时候，也差不多他这个年纪吧？"
  },
  [20310010110] = {
    id = 20310010110,
    name = "你老了，这么快就在回忆过去了。"
  },
  [20310010120] = {
    id = 20310010120,
    name = "不，我只是在想，当时你把我当成了鬼，就像尼莉莎小姐第一次看见鬼侍小姐一样。"
  },
  [20310010130] = {
    id = 20310010130,
    name = "你心中一凛，停下了脚步，认真地看向他。"
  },
  [20310010140] = {
    id = 20310010140,
    name = "你到底想说什么？"
  },
  [20310010150] = {
    id = 20310010150,
    name = "你知道鬼牌是什么组织吗？"
  },
  [20310010160] = {
    id = 20310010160,
    name = "鬼牌的确很神秘，但这与鬼侍小姐无关。"
  },
  [20310010170] = {
    id = 20310010170,
    name = "看来队长有秘密瞒着我们啊。"
  },
  [20310010180] = {
    id = 20310010180,
    name = "......你别问了，我答应过她，不会告诉第三个人。"
  },
  [20310010190] = {
    id = 20310010190,
    name = "好吧，我没有要为难队长的意思。我只是想说，希望队长理智一点，不要色令智昏。"
  },
  [20310010200] = {
    id = 20310010200,
    name = "我什么时候——"
  },
  [20310010210] = {
    id = 20310010210,
    name = "队长忘了，打完鬼面魔蛛之后，你转身就不见了，我们可是追了很久，才赶上你的步伐啊。"
  },
  [20310010220] = {
    id = 20310010220,
    name = "......那时候情况紧急，没有来得及告诉你们。我——"
  },
  [20310010230] = {
