  [20310020010] = {
    id = 20310020010,
    name = "一道闪电划过天空，随即是震耳欲聋的雷鸣。豆大的雨点开始砸落下来，打在了鬼侍的身上。"
  },
  [20310020020] = {
    id = 20310020020,
