  },
  [203111] = {
    id = 203111,
    name = "你找到鬼侍，将她救起到了山洞。\n两人围火夜谈，鬼侍说起了自己真实的经历，\n你才知道她性格大变的原因，并向她重新做出了承诺。"
  },
  [20311010020] = {
    id = 20311010020,
    name = "鬼侍小姐！你在哪里？我马上到了！"
  },
  [20311010030] = {id = 20311010030, name = "啊——"},
  [20311010035] = {id = 20311010035, name = "鬼侍"},
  [20311010040] = {
    id = 20311010040,
    name = "听到鬼侍短暂的惊呼声，你喘着粗气，脚步踉跄地来到湖泊旁，想要在湖面上找到些什么。"
  },
  [20311010050] = {
    id = 20311010050,
    name = "然而，湖面被雨滴打得千疮百孔，什么都没有。"
  },
  [20311010060] = {
    id = 20311010060,
    name = "你在湖边吗？！我看不到你！"
  },
  [20311010070] = {
    id = 20311010070,
    name = "湖边的树木在风雨中剧烈摇晃，树枝被折断，树叶被打得七零八落。"
  },
  [20311010080] = {
    id = 20311010080,
    name = "鬼侍小姐！鬼侍小姐！听到了回应我！鬼侍小姐！"
  },
  [20311010090] = {
    id = 20311010090,
    name = "你声嘶力竭地呼喊着，在如瀑的雨声中却显得那般微弱。"
  },
  [20311010100] = {
    id = 20311010100,
    name = "猛然间，你死盯向了那片湖泊，没有丝毫的犹豫，毅然跳了下去！"
  },
  [20311020010] = {
    id = 20311020010,
    name = "已至深夜，山洞外的雨还在淅淅沥沥地下着，打在洞口的石头上，溅起一片片的水花。"
  },
  [20311020020] = {
    id = 20311020020,
    name = "鬼侍仍是没醒过来，昏迷中的她一直双眉轻蹙，你回想着这几日与她相处的点滴，发现她好像没有真正的开心过。"
  },
  [20311020030] = {id = 20311020030, name = "......"},
  [20311020040] = {
    id = 20311020040,
    name = "你深呼出一口气，想到在水里发现鬼侍的样子，依然心有余悸。"
  },
  [20311020050] = {
    id = 20311020050,
    name = "她就那样静静地躺在湖里，失去了挣扎的力气，一点点地下沉，银色的头发交织散乱，像是早已死了许久的怨鬼。"
  },
  [20311020060] = {
    id = 20311020060,
    name = "你将她救起来后，做了该做的一切，她还是没有苏醒的迹象，只能先将她抱至附近的山洞避雨。"
  },
  [20311020070] = {
    id = 20311020070,
    name = "温暖的火焰在山洞中升腾而起。火光摇曳，映照着鬼侍惨白又脆弱的脸庞。"
  },
  [20311020080] = {id = 20311020080, name = "......"},
  [20311020090] = {
    id = 20311020090,
    name = "脆弱吗？你摇了摇头，一个单挑无垢者的人，怎么会让你产生这样的想法。"
  },
  [20311020100] = {
    id = 20311020100,
    name = "哥哥......"
  },
  [20311020105] = {id = 20311020105, name = "鬼侍"},
  [20311020110] = {
    id = 20311020110,
    name = "鬼侍小姐？"
  },
  [20311020120] = {
    id = 20311020120,
    name = "你凑近了些，鬼侍的双眼仍然紧闭，仿佛在噩梦中难以挣脱。"
  },
  [20311020130] = {
    id = 20311020130,
    name = "此时你才发现她的肩膀竟被魔物那尖锐的利器深深刺穿，伤口足有半掌长，边缘参差不齐，血水不断往外渗着。"
  },
  [20311020140] = {
    id = 20311020140,
    name = "形势危急，抱歉了。"
  },
  [20311020150] = {
    id = 20311020150,
    name = "你迅速为鬼侍治疗包扎，手上的动作不敢有丝毫迟疑。"
  },
  [20311020160] = {
    id = 20311020160,
    name = "鬼侍眉头紧皱，似被疼痛惊醒，缓缓地睁开了眼睛。"
  },
  [20311020170] = {id = 20311020170, name = "哥哥？"},
  [20311020175] = {id = 20311020175, name = "鬼侍"},
  [20311020180] = {
    id = 20311020180,
    name = "鬼侍小姐，是我。"
  },
  [20311020190] = {
    id = 20311020190,
    name = "鬼侍的眼中茫然了片刻，才缓缓聚焦起来。"
  },
  [20311020200] = {
    id = 20311020200,
    name = "是你啊。"
  },
  [20311020210] = {
    id = 20311020210,
    name = "嗯。外面的雨还很大，我们恐怕要在这里休息，等雨停了再走。"
  },
  [20311020220] = {
    id = 20311020220,
    name = "嗯，谢谢你。"
  },
  [20311020230] = {
    id = 20311020230,
    name = "那个魔法是不是失效了，我听不清你那时的声音。"
  },
  [20311020240] = {
    id = 20311020240,
    name = "嗯，我也听不见鬼侍小姐的心声了。"
  },
  [20311020250] = {
    id = 20311020250,
    name = "这样啊......这样也好，总被你知道我在想什么，有时难免尴尬。"
  },
  [20311020260] = {
    id = 20311020260,
    name = "她虽说着好，但你却感觉到了她的不舍与失落，如同你的心情一般。"
  },
  [20311020270] = {
    id = 20311020270,
    name = "那个......你能转过去一下吗？我的衣服很湿，感觉很不舒服。"
  },
  [20311020280] = {
    id = 20311020280,
    name = "抱歉，是我的疏忽，这是我的外套。另外，我搭个架子，你可以搭衣服。"
  },
  [20311020290] = {
    id = 20311020290,
    name = "鬼侍点点头。你搭好了木架背过身，就听到身后窸窸窣窣的声音。你将心思放空，望着山洞外的雨。大雨如注，似乎没有停下来的架势。"
  },
  [20311020300] = {
    id = 20311020300,
    name = "好了，谢谢。"
  },
