  [20311030060] = {
    id = 20311030060,
    name = "我也不知道。但幸运的是，有关于你，一向都很准。"
  },
  [20311030080] = {
    id = 20311030080,
    name = "你说的是那个地牢？那是普尔兰少爷最害怕的地方。"
  },
  [20311030100] = {
    id = 20311030100,
    name = "嗯？什么意思？"
  },
  [20311030037] = {
    id = 20311030037,
    name = "如果我没有落水呢？"
  },
  [20311030057] = {
    id = 20311030057,
    name = "你的直觉一向很准吗？"
  },
  [20311030077] = {
    id = 20311030077,
    name = "是啊，连在梦里，你都能第一时间找到我。"
  },
  [20311030097] = {
    id = 20311030097,
    name = "不是，哥哥从来没有去过那里，那是我最害怕的地方。"
  },
  [20311030117] = {
    id = 20311030117,
    name = "梦，都是假的。"
  },
  [20311030015] = {id = 20311030015, name = "鬼侍"},
  [20311030035] = {id = 20311030035, name = "鬼侍"},
  [20311030055] = {id = 20311030055, name = "鬼侍"},
  [20311030075] = {id = 20311030075, name = "鬼侍"},
  [20311030095] = {id = 20311030095, name = "鬼侍"},
  [20311030115] = {id = 20311030115, name = "鬼侍"},
  [20311040020] = {
    id = 20311040020,
    name = "她黯然垂了下了睫毛，石壁上的身影落寞而寂寥。"
  },
  [20311040010] = {
    id = 20311040010,
    name = "也许我还在期待那个梦吧，期待有个人，能随时听到我的呼唤。"
  },
  [20311030120] = {
    id = 20311030120,
    name = "火焰噼里啪啦地燃烧着，隔着衣物，你看不到她的表情。"
  },
  [20311030137] = {
    id = 20311030137,
    name = "你知道为什么梦境会从那一天开始吗？"
  },
  [20311030147] = {
    id = 20311030147,
    name = "因为，那是普尔兰家族罹难的一日。"
  },
  [20311030157] = {
    id = 20311030157,
    name = "我的父亲、母亲，都死在了那场宴会中。"
  },
  [20311030167] = {
    id = 20311030167,
    name = "还有哥哥......\n他为了让我走，将最后的生机留给了我，让我在地牢里等他。"
  },
  [20311030177] = {
    id = 20311030177,
    name = "我怕黑，哥哥就陪我说话，他让我千万不要离开地牢......\n后来，他也不说话了。"
  },
  [20311030187] = {
    id = 20311030187,
    name = "我在暗无天日的地牢里待了一天又一天，\n直到老鼠咬穿了地牢的门，我才看到了一丝光亮。"
  },
  [20311030197] = {
    id = 20311030197,
    name = "我以为是哥哥来接我了，当我开门的时候，也的确看见了他。"
  },
  [20311030207] = {
    id = 20311030207,
    name = "原来他一直守在地牢的门口，身上插满了刀剑......\n那时，他就那样倒在了我面前......"
  },
  [20311030210] = {
    id = 20311030210,
    name = "鬼侍还记得那具僵硬的尸体，浸血的水晶吊坠被死死地捏在他的手心，她放声大哭，哭到头发都褪去了原本的颜色。"
  },
  [20311030227] = {
    id = 20311030227,
    name = "我总是习惯地依靠所有人，仗着家人的宠爱，肆无忌惮地依赖着......"
  },
  [20311030237] = {
    id = 20311030237,
    name = "等到失去一切的那天，却发现自己什么也做不到。"
  },
  [20311030240] = {
    id = 20311030240,
    name = "你张了张嘴，试图安慰她，她却轻声笑了笑。"
  },
  [20311030257] = {
    id = 20311030257,
    name = "后来，我花了几年的时间报了仇......"
  },
  [20311030267] = {
    id = 20311030267,
    name = "你觉得我做得对吗？"
  },
  [20311030270] = {
    id = 20311030270,
    name = "那是你的人生，别人无权置喙。"
  },
  [20311030287] = {
    id = 20311030287,
    name = "是啊，你不是哥哥。\n我早知道的，但为什么，我还是忍不住想靠近你......"
  },
  [20311030290] = {
    id = 20311030290,
    name = "鬼侍站起了身，赤脚朝你走来，火焰跳动，石壁上映出她纤细的身影。"
  },
  [20311030135] = {id = 20311030135, name = "鬼侍"},
  [20311030145] = {id = 20311030145, name = "鬼侍"},
  [20311030155] = {id = 20311030155, name = "鬼侍"},
  [20311030165] = {id = 20311030165, name = "鬼侍"},
  [20311030175] = {id = 20311030175, name = "鬼侍"},
  [20311030185] = {id = 20311030185, name = "鬼侍"},
  [20311030195] = {id = 20311030195, name = "鬼侍"},
  [20311030205] = {id = 20311030205, name = "鬼侍"},
  [20311030285] = {id = 20311030285, name = "鬼侍"},
  [20311030265] = {id = 20311030265, name = "鬼侍"},
  [20311030255] = {id = 20311030255, name = "鬼侍"},
  [20311030235] = {id = 20311030235, name = "鬼侍"},
  [20311030225] = {id = 20311030225, name = "鬼侍"},
  [20311040015] = {id = 20311040015, name = "鬼侍"},
  [20311040030] = {
    id = 20311040030,
    name = "我曾认为自己不再需要依靠任何人，但在快要死掉的那一刻，还是希望有人能拉住我的手，救我离开无尽的黑暗......如果那场梦，没有醒来就好了......"
  },
  [20311040035] = {id = 20311040035, name = "鬼侍"},
  [20311040040] = {
    id = 20311040040,
    name = "别怕，我在。"
  },
  [20311040050] = {
    id = 20311040050,
    name = "如果你需要，我会拉着你的手，不会让你留在黑暗中。"
  },
  [20311040060] = {
    id = 20311040060,
    name = "那影子晃动了一瞬，仿若不可置信。"
  },
  [20311040070] = {
    id = 20311040070,
    name = "无论我在哪里，你都能找到我吗？"
  },
  [20311040075] = {id = 20311040075, name = "鬼侍"},
  [20311040080] = {
    id = 20311040080,
    name = "嗯，不管你在哪儿，只要你呼唤我，我就一定能找到你。"
  },
  [20311040090] = {
    id = 20311040090,
    name = "......谢谢你。"
  },
  [20311040095] = {id = 20311040095, name = "鬼侍"},
  [20311040100] = {
    id = 20311040100,
    name = "鬼侍缓缓坐了下来，背靠已经烤干的衣物，轻轻靠近了你。"
  },
  [20311040110] = {
    id = 20311040110,
    name = "我终于重新遇到了，愿意继续陪我做梦的人......"
  },
  [20311040115] = {id = 20311040115, name = "鬼侍"},
  [20311040120] = {
    id = 20311040120,
    name = "你感觉到了身后传来的温热，挺直了背脊一动不动。等了许久，鬼侍都没有动静。"
  },
  [20311040130] = {
    id = 20311040130,
    name = "你微微侧了侧身子，看着石壁上交错的影子，感觉到肩上一沉，原来鬼侍早已轻阖眼帘，倚着你安然睡去。"
  },
  [20311040140] = {
    id = 20311040140,
    name = "晚安，柯莱特尔。祝你好梦。"
  },
  [20312010010] = {
    id = 20312010010,
    name = "天亮时分，你和鬼侍背着无垢者的双翼回到了维克多的小屋。"
  },
  [20312010018] = {
    id = 20312010018,
    name = "唯有明月长留"
  },
  [20312010019] = {id = 20312010019, name = "PART 12"},
  [20312] = {
    id = 20312,
    name = "唯有明月长留"
  },
  [203121] = {
    id = 203121,
    name = "弗拉克的吉米修复完成并升级成功。\n你们醒来后，弗拉克将鬼侍赠送的吊坠转交给你，而鬼侍已经不知所踪。"
  },
  [20312010020] = {
    id = 20312010020,
    name = "哟，队长，欢迎回来。"
  },
  [20312010025] = {id = 20312010025, name = "菲尼斯"},
  [20312010035] = {id = 20312010035, name = "尼莉莎"},
  [20312010030] = {
    id = 20312010030,
    name = "哇哦，队长，你们竟然不声不响就把最后一个材料拿到了！"
  },
  [20312010040] = {
    id = 20312010040,
    name = "去找无垢者，怎么不叫我们？"
  },
  [20312010050] = {
    id = 20312010050,
    name = "老师，你们没有受伤吧？"
  },
  [20312010045] = {id = 20312010045, name = "蒂娜"},
  [20312010055] = {id = 20312010055, name = "安妮斯"},
  [20312010060] = {
    id = 20312010060,
    name = "你们怎么都在门口？"
  },
  [20312010070] = {
    id = 20312010070,
    name = "因为队长一晚上没回来，有人一晚上都没睡觉！"
  },
  [20312010080] = {
    id = 20312010080,
    name = "谁说要去找队长，过河的时候，那小身板差点被河水冲走了？"
  },
  [20312010090] = {id = 20312010090, name = "哼。"},
  [20312010085] = {id = 20312010085, name = "蒂娜"},
  [20312010075] = {id = 20312010075, name = "尼莉莎"},
  [20312010095] = {id = 20312010095, name = "尼莉莎"},
  [20312010100] = {
    id = 20312010100,
    name = "大家早啊~怎么都在这里站着？"
  },
  [20312010105] = {id = 20312010105, name = "弗拉克"},
  [20312010110] = {
    id = 20312010110,
    name = "弗兰克推门而出，打着哈欠睡眼朦胧，看样子是刚睡醒。"
  },
  [20312010120] = {
    id = 20312010120,
    name = "鬼侍小姐独自猎杀了无垢者，把双翼带回来了。"
  },
  [20312010130] = {
    id = 20312010130,
    name = "啊！太棒了！我就差这最后一个材料！"
  },
  [20312010135] = {id = 20312010135, name = "弗拉克"},
  [20312010140] = {
    id = 20312010140,
    name = "嗯，双翼在我这里。"
  },
  [20312010145] = {id = 20312010145, name = "鬼侍"},
  [20312010180] = {id = 20312010180, name = "好。"},
  [20312010150] = {
    id = 20312010150,
    name = "太好了！啊对了，你找我修的东西修好了。"
  },
  [20312010160] = {
    id = 20312010160,
    name = "这么快？"
  },
  [20312010170] = {
    id = 20312010170,
    name = "当然了，我可是师父的弟子！我带你去拿吧！"
  },
  [20312010155] = {id = 20312010155, name = "弗拉克"},
  [20312010175] = {id = 20312010175, name = "弗拉克"},
  [20312010165] = {id = 20312010165, name = "鬼侍"},
  [20312010185] = {id = 20312010185, name = "鬼侍"},
  [20312010190] = {
    id = 20312010190,
    name = "鬼侍走后，同伴们凑上来围着你聊天。"
  },
  [20312010205] = {id = 20312010205, name = "尼莉莎"},
  [20312010215] = {id = 20312010215, name = "蒂娜"},
  [20312010200] = {
    id = 20312010200,
    name = "队长，你这次要挨批评哦！"
  },
  [20312010210] = {
    id = 20312010210,
    name = "抛下队友，独断专行。"
  },
  [20312010220] = {
    id = 20312010220,
    name = "老师，大家都很担心你。"
  },
  [20312010225] = {id = 20312010225, name = "安妮斯"},
  [20312010230] = {
    id = 20312010230,
    name = "这次是我的错，当时情势危急，没有来得及通知大家。菲尼斯，你没跟她们说？"
  },
  [20312010240] = {
    id = 20312010240,
    name = "我是告诉了她们，你不会有危险，可她们并不相信。"
  },
  [20312010245] = {id = 20312010245, name = "菲尼斯"},
  [20312010250] = {
    id = 20312010250,
    name = "就在这时，你忽然察觉到有一股视线在注视着你，那视线温柔清透，似乎还藏着不舍与眷恋。然而，当你看过去的时候，却发现没有人。"
  },
  [20312010270] = {
    id = 20312010270,
    name = "那个方向，是鬼侍小姐？"
  },
  [20312010260] = {id = 20312010260, name = "......"},
  [20312010280] = {
    id = 20312010280,
    name = "对啊，队长，你是怎么知道鬼侍小姐杀无垢者去了？她真的一个人把无垢者干掉了？"
  },
  [20312010290] = {
    id = 20312010290,
    name = "好了，看队长的样子一夜没睡，先让他去休息。"
  },
  [20312010285] = {id = 20312010285, name = "尼莉莎"},
  [20312010295] = {id = 20312010295, name = "蒂娜"},
  [20312010300] = {
    id = 20312010300,
    name = "老师，我做了早餐，吃一些再睡吧。"
  },
  [20312010305] = {id = 20312010305, name = "安妮斯"},
  [20312010310] = {
    id = 20312010310,
    name = "好，先进屋吧。"
  },
  [20312020010] = {
    id = 20312020010,
    name = "好耶！大功告成！"
  },
  [20312020015] = {id = 20312020015, name = "弗拉克"},
  [20312020020] = {
    id = 20312020020,
    name = "好耶！让我看看！"
  },
  [20312020025] = {id = 20312020025, name = "尼莉莎"},
  [20312020040] = {
    id = 20312020040,
    name = "无垢者的双翼被修剪成了小巧玲珑的形状，薄如蝉翼，吉米俏皮地在空中转了个圈，喷出一道瑰丽的烟雾，烟雾缓缓成型，变成一朵绽放的玫瑰。"
  },
  [20312020030] = {
    id = 20312020030,
    name = "尼莉莎刚说完，吉米就飞了起来。"
  },
  [20312020050] = {
    id = 20312020050,
    name = "哈哈，弗拉克，吉米是在向你表白吗？"
  },
  [20312020060] = {
    id = 20312020060,
    name = "嗯嗯，这是吉米开心的表现！"
  },
  [20312020055] = {id = 20312020055, name = "尼莉莎"},
  [20312020065] = {id = 20312020065, name = "弗拉克"},
  [20312020070] = {
    id = 20312020070,
    name = "老师，来吃点东西吧。"
  },
  [20312020075] = {id = 20312020075, name = "安妮斯"},
  [20312020080] = {
    id = 20312020080,
    name = "这条鱼，是安妮斯说特意留给你和鬼侍小姐的。"
  },
  [20312020090] = {
    id = 20312020090,
    name = "弗拉克，鬼侍小姐呢？"
  },
  [20312020095] = {id = 20312020095, name = "安妮斯"},
  [20312020085] = {id = 20312020085, name = "蒂娜"},
  [20312020105] = {id = 20312020105, name = "弗拉克"},
  [20312020100] = {
    id = 20312020100,
    name = "哦。鬼侍小姐啊，她刚刚走了。"
  },
  [20312020110] = {id = 20312020110, name = "走了？"},
  [20312020120] = {
    id = 20312020120,
    name = "是啊，她还让给我把这个送给你，在你问起她的时候。"
  },
  [20312020125] = {id = 20312020125, name = "弗拉克"},
  [20312020130] = {
    id = 20312020130,
    name = "弗拉克拿出了一块透明的玻璃盒子，里面静静地躺着一枚月光石吊坠。"
  },
  [20312030010] = {
    id = 20312030010,
    name = "现在就要走吗？"
  },
  [20312030015] = {id = 20312030015, name = "弗拉克"},
  [20312030020] = {
    id = 20312030020,
    name = "嗯，既然交易结束，吊坠也修复完成，我也该离开了。"
  },
  [20312030030] = {
    id = 20312030030,
    name = "哦，那你不和他们打个招呼吗？"
  },
  [20312030040] = {
    id = 20312030040,
    name = "不用了。"
  },
  [20312030050] = {
    id = 20312030050,
    name = "鬼侍走了几步，又回头打开盒子，她想了想，留下了紫水晶的吊坠，将月光般的另一枚递给了弗拉克。"
  },
  [20312030060] = {
    id = 20312030060,
    name = "帮我把这个交给队长吧。"
  },
  [20312030070] = {
    id = 20312030070,
    name = "要说些什么吗，我帮你带话。"
  },
  [20312030080] = {
    id = 20312030080,
    name = "不需要了，他会明白的。"
  },
  [20312030025] = {id = 20312030025, name = "鬼侍"},
  [20312030045] = {id = 20312030045, name = "鬼侍"},
  [20312030065] = {id = 20312030065, name = "鬼侍"},
  [20312030085] = {id = 20312030085, name = "鬼侍"},
  [20312030035] = {id = 20312030035, name = "弗拉克"},
  [20312030075] = {id = 20312030075, name = "弗拉克"},
  [20312040010] = {
    id = 20312040010,
    name = "我还记得梦里你送给我的吊坠吗，里面有星星的吊坠。"
  },
  [20312040015] = {id = 20312040015, name = "鬼侍"},
  [20312040020] = {
    id = 20312040020,
    name = "不是我，是你的哥哥送的。"
  },
  [20312040030] = {
    id = 20312040030,
    name = "知道那时候我为什么没有根据真实的回忆选月亮，而是选有星星的项链吗？"
  },
  [20312040040] = {
    id = 20312040040,
    name = "为什么？"
  },
  [20312040050] = {
    id = 20312040050,
    name = "因为哥哥说过，有月亮在的时候，星星就不用辛苦地发光了。"
  },
  [20312040060] = {
    id = 20312040060,
    name = "呵呵......"
  },
  [20312040070] = {
    id = 20312040070,
    name = "那个时候，我已经认出了你不是哥哥，但我想试试，试试能不能改变那个结局。"
  },
  [20312040080] = {
    id = 20312040080,
    name = "所以我把月亮给了你......"
  },
  [20312040090] = {
    id = 20312040090,
    name = "谢谢你，送我的一场美梦。就算只有须臾片刻，却也让我不再迷惘地去寻求过去。"
  },
  [20312040100] = {
    id = 20312040100,
    name = "谢谢你，队长。"
  },
