  [80226111] = {
    id = 80226111,
    name = "不同种类的魔物正凑在一起活动，或低声呜咽，或高声咆哮。这副光景，像极了探险者们的聚会。"
  },
  [80226112] = {
    id = 80226112,
    name = "攻击、防御、协助......能力各异的魔物彼此协作，对探险者而言着实算不上好消息。"
  },
  [80226113] = {
    id = 80226113,
    name = "魔物构筑了一个严密的包围圈，静静等候猎物的到来。当然，它们并不知道下一个出现的是猎物还是猎人。"
  },
  [80226121] = {
    id = 80226121,
    name = "不管魔物还是人类，表里不一的家伙应付起来总是会很棘手。"
  },
  [80226122] = {
    id = 80226122,
    name = "遇上魔物并非什么好事，不过考虑到魔物所能带来的素材，似乎也算不上完全的坏事。"
  },
  [80226123] = {
    id = 80226123,
    name = "攻击才是最好的防守，抢先一步击溃敌人比任何防御手段都更加有效。"
  },
  [80226124] = {
    id = 80226124,
    name = "比起盲目的攻击，有时候严密的防守更加有效。当敌人锐气消磨殆尽，自然来到你的回合。"
  },
  [80226131] = {
    id = 80226131,
    name = "天空阴云密布，看起来将会有一场暴雨。你正打算寻找躲避的地方，一群魔物出现在了大地尽头。"
  },
  [80226132] = {
    id = 80226132,
    name = "夜幕降临，无数蛰伏的魔物开始活跃，困顿疲乏的探险者成为了它们最心仪的目标。"
  },
  [80226133] = {
    id = 80226133,
    name = "比起看得见的威胁，那些隐藏于暗中，蓄势待发的敌人才更加危险。"
  },
  [80226134] = {
    id = 80226134,
    name = "不管何时何地，“看热闹”永远是一件令人愉悦的事，只是如果不注意距离，很容易被卷入其中，引火烧身。"
  },
  [80226135] = {
    id = 80226135,
    name = "占尽地利的魔物排列着整齐的阵型向你冲来，这一战凶险异常，小心为上吧。"
  },
  [80226141] = {
    id = 80226141,
    name = "偶然的相遇令双方都始料未及，开阔的地势也来不及作出更多部署。赌上探险者的荣誉，堂堂正正地战斗吧。"
  },
  [80226142] = {
    id = 80226142,
    name = "面对不知为何仓皇奔逃的魔物，避让确实是最安全的选择。但如果能追击，或许可以彻底消除这些潜在的威胁。"
  },
  [80226143] = {
    id = 80226143,
    name = "一群魔物跟在你们后方，虽然没有主动进攻，但也没有离去的意思，似乎正在等待猎物露出破绽。"
  },
  [80226144] = {
    id = 80226144,
    name = "不达目的，决不罢休。这是优秀探险者所坚守的信条，也是眼前这些目露凶光的魔物的本能。"
  },
  [80226145] = {
    id = 80226145,
    name = "只要穿过这片区域便能看到目的地了。不过游荡在大地尽头的那些家伙，不会轻易让人通过。"
  },
  [80226146] = {
    id = 80226146,
    name = "对魔物而言，深渊的每个角落都是乐园。与探险者相比，它们更像是此地的主人。"
  },
  [80226147] = {
    id = 80226147,
    name = "对魔物而言，这里是它们的领地，任何贸然闯入的存在都是敌人。即便只是路过，它们也绝不会允许。"
  },
  [80226148] = {
    id = 80226148,
    name = "所有的恐惧都源于力量不足，对魔物而言也是如此。当它们对自身力量感到自信时，面对任何敌人都会毫不犹豫地发起攻击。"
  },
  [80226151] = {
    id = 80226151,
    name = "面对到访的探险者，深渊有着自己的接待方式，比如远处呼啸而来的魔物群。"
  },
  [80226211] = {
    id = 80226211,
    name = "作为魔物中的强大个体，只要它们联合行动便能无所畏惧，无所不能。至少在败亡前，它们是这样认为的。"
  },
  [80226221] = {
    id = 80226221,
    name = "进攻、协助、防守，若能三者兼顾，必定会成为对手最为头疼的存在。"
  },
  [80226222] = {
    id = 80226222,
    name = "一群魔物正以一种罕见的方式聚拢起来，似乎想要以此变得更加强大。"
  },
  [80226231] = {
    id = 80226231,
    name = "狭缝中喷发热气宛如大地的怒火，奔涌而来的魔物便是这片荒野愤怒的证明。"
  },
  [80226232] = {
    id = 80226232,
    name = "每前进一步都会承受更猛烈的攻击，而后退则将面临覆灭的危险。是时候放手一搏了。"
  },
  [80226241] = {
    id = 80226241,
    name = "魔物释放的攻击宛如划破夜空的流星，只是这些“流星”无法实现愿望，反而会带来无尽灾殃。"
  },
  [80226251] = {
    id = 80226251,
    name = "真正的危险通常并非来自个体力量强大的魔物，那些不起眼的家伙往往更具威胁。"
  },
  [80226252] = {
    id = 80226252,
    name = "魔物中存在不少生存能力极强的家伙，当它们成群出现时，足以令任何探险者头疼。"
  },
  [80226253] = {
    id = 80226253,
    name = "猛烈的攻击，险恶的环境，难以突破的魔物防线，前行的每一步都艰难异常。"
  },
  [80226311] = {
    id = 80226311,
