  [80233001] = {
    id = 80233001,
    name = "带领魔物群奔袭冲锋的领主级魔物，脾气暴躁，破坏欲极强。\n巨大的身躯及磅礴的力量能轻易摧毁山石、建筑，对探险者们构筑的防御工事拥有极大威胁。"
  },
