  [80265101] = {
    id = 80265101,
    name = "平坦大道，畅行无忧。带着轻松的心情出发吧，好好享受这趟远途。"
  },
  [80265102] = {
    id = 80265102,
    name = "蜿蜒小径，草木清幽。留意路边的荆棘和周围活动魔物，其他没什么可担心的。"
  },
  [80265103] = {
    id = 80265103,
    name = "山道盘旋，曲折坎坷。欣赏沿途风景时，也别忘了当心脚下。"
  },
  [80265104] = {
    id = 80265104,
    name = "步道泥泞，荆棘丛生。这趟远途会比想象中更加艰苦，前行的每一步都必须保持警惕。"
  },
  [80265105] = {
    id = 80265105,
    name = "道路艰险，魔物环伺。前进变得异常困难，若没有足够的勇气与决心，放弃才是最明智的选择。"
  },
  [80265106] = {
    id = 80265106,
    name = "路途凶险，灾祸连绵。想要抵达终点，除了绝对的实力，还要足够的运气。"
  },
  [80265107] = {
    id = 80265107,
    name = "险恶绝地，变幻无常。在这里，好运或许比实力更加重要。"
  },
  [80265108] = {
    id = 80265108,
    name = "光明褪去，黑暗笼罩。这是魔物的乐园，是探险者的绝境，前行的每一步都凶险异常。"
  },
  [80265109] = {
    id = 80265109,
    name = "万物凋零，生机难寻。所谓的禁域，除了无人愿意前往，也可以看作无人归还。"
  },
