  [80278011] = {
    id = 80278011,
    name = "他们游走于深渊各地，知晓许多小道消息。当然，探险者最感兴趣的，是他们收集的各种奇珍异宝"
  },
  [80278012] = {
    id = 80278012,
    name = "每一次偶然的邂逅都是命运的必然，擦肩而过或是命运转折，往往只在一瞬之间。"
  },
  [80278013] = {
    id = 80278013,
    name = "历经时间验证的隐蔽场所，存放着不少先行者留下的物资，安心休整吧。"
  },
  [80279101] = {
    id = 80279101,
    name = "孤独的老人摊开决绝的信，抱着盒子落下了石门，他在回忆里徘徊，微笑着点燃了余生。"
  },
  [80279102] = {
    id = 80279102,
    name = "痛苦悔恨的墓志铭旁，留下了一本记载着真相的日记，那些林林总总的过往，终随时间湮灭在烈火青山里。"
  },
  [80279103] = {
    id = 80279103,
    name = "任务失败了。属于阿列科的信件与盒子被埋在高处，脚下晦暗的深渊正张着巨大的裂口，所见得到的只有空虚与黑暗。"
  },
  [80279104] = {
    id = 80279104,
    name = "忆梦珠捕捉着梦境，唤醒了阿列科眼中的光，有关于她的记忆如野草一般疯长。梦境破碎的那一刻，他如梦初醒，再不敢死去。"
  },
