  [80820001] = {
    id = 80820001,
    name = "深渊传来的求援信息打破了往日的宁静，或是出于责任、原则，或者曾经的梦魇，前往深渊已成为必然的选择。"
  },
  [80820002] = {
    id = 80820002,
    name = "满怀恶意之人从来不会在乎道德、规则，为了达到自己的目的，他们会不择手段。"
  },
  [80820003] = {
    id = 80820003,
    name = "恶徒们时常纠集在一起，欺压善良之人取乐，但当灾祸来临，他们便会毫不犹豫地抛弃同伙，将损人利己的本性展露无遗。"
  },
  [80820004] = {
    id = 80820004,
    name = "危险常在毫无防备之际降临，无论圈落据点，无论旷野山林，没有任何地方能绝对安全，这便是深渊的法则。"
  },
  [80820005] = {
    id = 80820005,
    name = "利益能引发无数纠纷，但比利益所引发的纠纷更加棘手的，则是不同理念之间的冲突。"
  },
  [80820006] = {
    id = 80820006,
    name = "残酷的深渊中依然存在着诸多真诚和善良，这些美好就如同黑暗中的灯火，为迷惘之人指引方向。"
  },
  [80820007] = {
    id = 80820007,
    name = "一些人有着绝不退让的骄傲，但有时候，为了更加重要的存在，不得不选择妥协。"
  },
  [80820008] = {
    id = 80820008,
    name = "在巨大的利益面前，强者充分展现出了自己的权势和力量，而弱者也为了些许的可能拼尽全力。"
  },
  [80820009] = {
    id = 80820009,
    name = "人心远比深渊更神秘、更难以揣测。或许只是一时贪念，也或许是蓄谋已久，只要契机出现，熟悉之人也将变得陌生。"
  },
  [80820010] = {
    id = 80820010,
    name = "藏匿于记忆深处的遗憾和黑暗，阻碍了前行的脚步，即便侥幸从噩梦中醒来，等在前方的，还有更加残酷的考验。"
  },
  [80820011] = {
    id = 80820011,
    name = "兽潮将至，黎明要塞中一片祥和。人们似乎并不畏惧这传闻中的灾祸，反而将其视作猎杀魔物的绝佳机会。"
  },
  [80821001] = {
    id = 80821001,
    name = "宽广无垠的深渊中，栖息着各种奇妙、凶猛的魔物，沉睡着无数令当今人们趋之若鹜的宝物、财富......"
  },
