  [80832001] = {
    id = 80832001,
    name = "几经辗转，你终于找到了伊涅利丝秘密基地所需要的魔法石，但每个同伴都推诿有事，你只能独自前往她的秘密基地。"
  },
  [80832002] = {
    id = 80832002,
    name = "你到达了伊涅利丝的秘密基地，却只看到了一封信。你依据信上地点寻找伊涅利丝，山路上发现一张染血的公告，以及一个寡言的少女......"
  },
  [80832003] = {
    id = 80832003,
    name = "你担忧伊涅利丝上山，发现魔物明显增多，找到伊涅利丝时，她正被魔物围攻......"
  },
  [80832004] = {
    id = 80832004,
    name = "你与伊涅利丝、莫妮卡遇到了源源不断的魔物群，你让两人暂时撤退。回撤过程中，却遇到更多的魔物围攻......"
  },
  [80832005] = {
    id = 80832005,
    name = "你在山洞没有发现魔物，在各种猜测后，莫妮卡采集了很多“蘑菇”，你们也在山洞底部找到了需要的勃罗特花。"
  },
  [80832006] = {
    id = 80832006,
    name = "你与伊涅利丝、莫妮卡重新上山，点燃了迷烟，一路魔物群都睡着了过去，没想到半途却出了意外......"
  },
  [80832007] = {
    id = 80832007,
    name = "你和伊涅利丝一路逃到崖边，正在快速通过藤蔓索桥时，魔物将藤蔓撞断了......"
  },
  [80832008] = {
    id = 80832008,
    name = "你和伊涅利丝在山中寻找出路，终于与莫妮卡重新相聚。"
  },
  [80832009] = {
    id = 80832009,
    name = "在与莫妮卡道别后，伊涅利丝带你回到了秘密基地，共赏守伊花开。"
  },
  [80832010] = {
    id = 80832010,
    name = "圈落在举办一年一度的收获祭活动，你前赴黑羽的约定。"
  },
  [80832011] = {
    id = 80832011,
    name = "在大会现场，蒙娜慌乱地到处找队伍，被各队嫌弃。蒙娜看到你后，死缠烂打，你和黑羽同意她加入了队伍。主办方说破坏田地的魔物又在聚集了，让大家去除魔。"
  },
  [80832012] = {
    id = 80832012,
    name = "看起来很简单的除魔任务，由于蒙娜与黑羽无法配合，导致花了很多时间。最后打得非常狼狈，灰头土脸，踩线才完成任务。"
  },
  [80832013] = {
    id = 80832013,
    name = "休息日，大家疑惑菲尼斯的去向时，黑羽在蛋糕店打工被众人发现。黑羽因为被同伴调侃娇羞成怒，你帮忙解围，接着了解了黑羽打工的原因。"
  },
  [80832014] = {
    id = 80832014,
    name = "次日早上，蒙娜苏醒后想恶作剧，但结果却出人意料。第二天的晋级赛要求为农作物收集肥料，你们就如何收集肥料进行探讨。"
  },
  [80832015] = {
    id = 80832015,
    name = "你们跑到山中收集肥料，但收效甚微。而后蒙娜决定去偷大型魔物的排泄物，遭到一致拒绝。蒙娜为证明自己将黑羽拐跑了。你们正打算走另一条路时，听到她的求救声，赶去救两人。"
  },
  [80832016] = {
    id = 80832016,
    name = "蒙娜偷大型魔物的排泄物被发现，你们赶到现场救下被魔物吞掉的蒙娜。在一通手忙脚乱后，终于成功带回最优质的排泄物。在被主办方刁难时，菲尼斯出现解围，晋级决赛。你遭到菲尼斯的调侃，菲尼斯暗示下一场比赛的内容。"
  },
  [80832017] = {
    id = 80832017,
    name = "休息日众人去黑羽的店中消费，尼莉莎故意为难黑羽，黑羽不得不屈服......"
  },
  [80832018] = {
    id = 80832018,
    name = "第三日早上，蒙娜苏醒后继续恶作剧，结果依旧出人意料。第三天的晋级赛要求为收割农作物制作器具，你们对此展开了探讨。"
  },
  [80832019] = {
    id = 80832019,
    name = "你们在挑选魔物的时候出现了争论，一番讨论后，众人决定去猎杀最强的魔物。尽管再次出现了小意外，你们最终还是战胜了巨型魔物，剃下了它最硬的坚骨。"
  },
  [80832020] = {
    id = 80832020,
    name = "众人用心打造了一把奇怪的可以切割收获粮草的器具，并提前庆祝。但在使用的时候再次出了意外，引起了大混乱。你们不光失去了所有分数，还面临赔钱的遭遇。最后菲尼斯出现为大家解决了困境，但结局令人哭笑不得。"
  },
  [80832021] = {
    id = 80832021,
    name = "弗拉克说起升级吉米的材料，你们商议后决定分头行动，在商议过程中，你忽然听到鬼侍的声音，但鬼侍没理你，你认为是错觉。"
  },
  [80832022] = {
    id = 80832022,
    name = "你们一起逛街买材料，在看到奇趣魔法道具时，你再次听到了鬼侍的吐槽，你在听到多次后不小心回应吐槽，你与鬼侍面面相觑......"
  },
  [80832023] = {
    id = 80832023,
    name = "回到住处后，鬼侍拉你单独说话，在你和鬼侍双方试探之后发现的确能听到对方的心声，你们想可能和之前毁掉的宝物有关......"
  },
  [80832024] = {
    id = 80832024,
    name = "第二天，鬼侍和你结伴上街，期间她口是心非，心口不一，你们逛了一圈，找到了昨日的商铺，买到了材料。"
  },
  [80832025] = {
    id = 80832025,
    name = "弗拉克重组吉米，大家一起吃饭，交谈中，尼莉莎发现你和鬼侍奇奇怪怪的......"
  },
  [80832026] = {
    id = 80832026,
    name = "维克多在某个废弃的据点居住时，曾经留下了窥探魔眼的“眼珠”，而那里现在被一只巨型魔物占据，你们打算将材料偷到手就走，但却发生了意外......"
  },
  [80832027] = {
    id = 80832027,
    name = "你们撤退后重新商量作战计划，决定第二次出击，战斗到关键的时刻，你和鬼侍靠心声传递配合出手，共同击杀了魔物，拿回了材料。"
  },
  [80832028] = {
    id = 80832028,
    name = "得到材料回去后，你看见鬼侍在河边和弗拉克说话，鬼侍问起镜子的事，等弗拉克离开后，你重提了梦境里的故事。"
  },
  [80832029] = {
    id = 80832029,
    name = "弗拉克又修补了吉米并重新测试，但仍缺最后一个材料。而这时，伙伴们发现鬼侍对你的态度发生了明显改变......"
  },
  [80832030] = {
    id = 80832030,
    name = "伙伴们一起帮弗拉克重新布置工坊，你在寻找最后一道材料的同时，断断续续听到鬼侍遇险，匆忙赶过去救人，却再也无法收到鬼侍的讯息......"
  },
  [80832031] = {
    id = 80832031,
    name = "你找到鬼侍，将她救起到了山洞。两人围火夜谈，鬼侍说起了自己真实的经历，你才知道她性格大变的原因，并向她重新做出了承诺。"
  },
  [80832032] = {
    id = 80832032,
    name = "弗拉克的吉米修复完成并升级成功，你们醒来后，弗拉克将鬼侍赠送的吊坠转交给你，而鬼侍已经不知所踪。"
  },
