  [80885002] = {
    id = 80885002,
    name = "银色的光洒向大地，明澈的涧流承载着白花的记忆，\n流过月光、丛林、萤火......收集一颗颗碎落的繁星。"
  },
  [80885003] = {
    id = 80885003,
    name = "迷惘的脚步声停下了，燃起了一团照亮后人的灯火。\n伙伴们吟唱着祈祷的歌声：「愿你终会达成，内心所愿......」"
  },
  [80885004] = {
    id = 80885004,
    name = "这是一座古老的巨型石像，被风撞击的声音如千万年的咒语，\n仿佛能唤醒本源的共鸣，洞悉生命的意义......"
  },
  [80885005] = {
    id = 80885005,
    name = "一颗发光的树，树洞流出荧光，\n里面用刀刻下了探险者的字迹 \n「晶红赤玉，催生抗争的勇气。」"
  },
  [80885006] = {
    id = 80885006,
    name = "远处传来一阵歌声。在泉水池塘边吟唱的诗人拿着竖琴，\n仿如幻象般消失在水瀑中，留下一页未尽的诗歌。"
  },
  [80885007] = {
    id = 80885007,
    name = "前方有块石碑，上方铭刻 \n「从世界开始之前的世界，你比山川历久，比亘古永恒......」\n你将手放上去，似乎能听见古老的回响。"
  },
  [80885008] = {
    id = 80885008,
    name = "这是一处绝涧。\n灰暗的天空露出紫色的穹顶。瀑布后的刻纹若隐若现，\n上方预言 「地狱之门始启」。"
  },
  [80885009] = {
    id = 80885009,
    name = "远方传来的异乡音乐。\n不知是何人奏响，继而响起淅沥的雨声，\n一片朦胧里，你眼前浮现一段奇异的文字。"
  },
  [80885010] = {
    id = 80885010,
    name = "捡到了一本《深渊年鉴》，这是记载深渊历史的文献，\n通常以铜板印刷的方式，流行在学者们的手中。"
  },
  [80885011] = {
    id = 80885011,
    name = "你原地休息，很快进入了梦境。\n一切绮丽的想象滋长，有乐声在梦中清唱，\n是少年时无虑快乐的时光。"
  },
  [80885012] = {
    id = 80885012,
    name = "疾风吹过，片片落叶坠向大地，它们挣扎扭动身姿，\n在黑暗完全降临之前，发出残留的绝响，\n如一首连绵起伏的乐章。"
  },
  [80885013] = {
    id = 80885013,
    name = "眼前是一条奔涌不息的暗河，\n它从辉煌中来，卷集了腐朽气味，\n伴随孤独的叹息，终于没入空虚。"
  },
  [80885014] = {
    id = 80885014,
    name = "一只断裂的哨笛躺在河边，\n河水冲刷着，发出不规则的声响，\n用奇妙的乐律，浇灌死者的灵魂。"
  },
  [80885015] = {
    id = 80885015,
    name = "你走入了旷野中的巨石阵。\n道道闪电划破暗夜，隆隆雷声震耳欲聋，\n被烧灼的焦木将断裂声定格。"
  },
  [80885016] = {
    id = 80885016,
    name = "魆黑的风吹断死去的枯树，\n你抬头眺望，野鸦哀鸣响彻山谷，\n它们流浪徘徊，唱着失去家园的挽歌。"
  },
  [80885017] = {
    id = 80885017,
    name = "月华皎洁，雾气漫过树木丛林，\n暗黑里涌现出顽强的生命，\n万物鸣奏着勇气的旋律，断断续续传到耳边......"
  },
  [80885018] = {
    id = 80885018,
    name = "你走进废弃的村落，衣衫褴褛的探险者低声和歌，\n他们坐在篝火旁，唱着一段段故事，怀念回不去的家乡。"
  },
  [80885019] = {
    id = 80885019,
    name = "眼前是一条清澈的河流，\n你泡在水中冲刷了满身的疲惫，静静躺在水面，浑然迷蒙间，\n水波浪潮声仿佛欢快的舞曲。"
  },
  [80885020] = {
    id = 80885020,
    name = "一页发光的乐谱躺在废墟上，\n当你触碰时，脑中响起了一段旋律，\n仿佛带你回到了厮杀的战场......"
  },
  [80885021] = {
    id = 80885021,
    name = "夜深休憩，你进入了噩梦的世界，\n时钟不停在转动，交融成亦真亦幻的影像，\n直到黎明，才等到休止的音符响起。"
  },
  [80885022] = {
    id = 80885022,
    name = "前方是嘈杂的临时营地，探险者们围着冒着泡沫的啤酒，\n哼着流传的小调，满是冒险和史诗的味道。"
  },
  [80885023] = {
    id = 80885023,
    name = "一处风洞传出了呼声。\n你仔细聆听，风声遥远尖锐刺耳，\n假想出远古的号角，在阴暗角落觊觎光明。"
  },
  [80885024] = {
    id = 80885024,
    name = "午夜，林中奏响凄切的啼鸣，如同魔鬼的召唤，\n歌咏般将悲伤和绝望蔓延开来。"
  },
  [80885025] = {
    id = 80885025,
    name = "你到了一处火热的岩浆，落石被炽热的浪潮瞬间吞没，\n掀起的热潮发出炙烈的呼啸，几乎震破你的耳膜。"
  },
  [80885026] = {
    id = 80885026,
    name = "你走过一个无名的坟堆，几个探险者在坟前哀叹，\n有人拿出古提琴，静静弹唱一曲悲伤的镇魂歌。"
  },
  [80885027] = {
    id = 80885027,
    name = "走入幽暗的荆棘谷，你感受到阴沉的风声，\n如悲怨的哭泣，高亢地旋律，像一段释放罪恶的咒语。"
  },
  [80885028] = {
    id = 80885028,
    name = "走过一片混乱的场地，这里似乎经过了战斗的洗礼，\n金石相交声仿佛还未停歇，耳畔是锋刃的嘶鸣与回响。"
  },
  [80885029] = {
    id = 80885029,
    name = "深渊迎来了黎明，冒险的人们重新踏上旅程，\n有人用树叶吹响了曲调，你轻声相和，直到乐声远去。"
  },
  [80885030] = {
    id = 80885030,
    name = "寂静的荒野，暴风雪即将到来，\n你躲在安宁的角落，聆听远处的轰鸣，\n那是即将掩埋世界的声音。"
  },
  [80885031] = {
    id = 80885031,
    name = "据点外有歌声响起，是拾荒的孩童唱着不知名的民谣，\n他们以此讨好巡逻的护卫，乞讨能让人饱腹的食物。"
  },
  [80885032] = {
    id = 80885032,
    name = "这是一座斑驳腐朽的石塔，\n上面记载了月光女神为了夜晚不再漆黑，\n不惜以身殉塔点亮鸣泉的潮汐之歌。"
  },
  [80885033] = {
    id = 80885033,
    name = "下雨了，你靠着巨大的岩壁避雨，\n跌落的雨滴敲击出摄人心魄的音符。\n像是亡者的祷告，砸碎深渊仅有的生机。"
  },
  [80885034] = {
    id = 80885034,
    name = "魔物正为深渊带来毁灭，英雄必将挺身而出......\n游吟诗人在各地唱着相似的歌谣，如过往，如预言。"
  },
  [80885035] = {
    id = 80885035,
    name = "刻录着旅途中所聆听音乐的礼盒。\n兑换后即可在「事迹-音乐-旅途轶事」中获得对应音乐。"
  },
  [80885036] = {
