    name = "我方成员携带的控制效果使敌方目标处于控制状态时，对以该目标为中心召唤「落雷」。\n「落雷：对以控制目标为中心且半径160的圆形范围内的敌方全体目标造成基于我方全体成员基础攻击力总和%d%%的伤害，并使受到伤害的敌方目标陷入眩晕状态，持续%d秒」"
  },
  [80970025] = {
    id = 80970025,
    name = "每触发1次「落雷」，「关卡增益」累计层数增加1层。"
  },
  [80980011] = {id = 80980011, name = "坚冰"},
  [80980012] = {
    id = 80980012,
    name = "每1只敌方魔物以自身为中心且半径为80的圆形范围内每存在1只其他魔物时，免伤提升%d%%，最大可提升至%d%%。"
  },
  [80980021] = {id = 80980021, name = "崩裂"},
  [80980022] = {
    id = 80980022,
    name = "若我方成员的「普攻」「战技」「总攻技」为范围技能，则施放时使受到攻击的敌方目标附加「崩裂」效果。\n「崩裂：自身死亡时，以自身为中心且半径为80的圆形范围内全部魔物直接造成等同于自身%d%%生命上限的伤害。」"
  },
  [80980031] = {id = 80980031, name = "重生"},
  [80980032] = {
    id = 80980032,
    name = "敌方魔物死亡后，会在战场中随机位置复活重生，并恢复自身%d%%生命上限的生命值，同一魔物一场战斗最多触发1次。"
  },
  [80980041] = {id = 80980041, name = "引燃"},
  [80980042] = {
    id = 80980042,
    name = "我方成员对敌方目标造成伤害时使受到攻击的敌方目标附加「引燃」效果，可以叠加。\n「引燃：每隔%s秒，携带者会损失等同于自身%d%%生命上限的生命值。」"
  },
  [80980051] = {id = 80980051, name = "蔓生"},
  [80980052] = {
    id = 80980052,
    name = "战场中所有魔物能够相互分担伤害，随着魔物越多，单只魔物受到的伤害会越低。"
  },
  [80980061] = {id = 80980061, name = "轰炸"},
  [80980062] = {
    id = 80980062,
    name = "若我方成员的「普攻」「战技」「总攻技」为范围技能，则施放时受到攻击的魔物数量越多，造成的伤害越高。"
  },
