  [82000001] = {
    id = 82000001,
    name = "由地面上多种宝石矿物加工而成的贵重晶石，通体晶莹剔透，色泽绚丽，在人类社会中拥有极高价值。"
  },
  [82000002] = {
    id = 82000002,
    name = "源矿加工而成的珍贵结晶，由于成品率极低，而且原料只存在于那些超强大魔物的巢穴，开采难度极大，在人类社会中始终拥有高昂且稳定的价值。"
  },
  [82000003] = {
    id = 82000003,
    name = "日常贸易中的通用货币，虽然不及欧泊、源晶贵重，但仍然拥有稳定的价值。"
  },
  [82000004] = {
    id = 82000004,
    name = "浊炁会不断侵蚀人类的精神、躯体，使人的理性逐渐丧失。倘若理性彻底消散，人类或许将在深渊迷失。"
  },
  [82000005] = {
    id = 82000005,
    name = "每一位声名远扬的队长都经历了无数风雨挫折，一步一步，从懵懂走向成熟，从资深迈向传说。"
  },
  [82000006] = {
    id = 82000006,
    name = "不因成功而自满，不因失败而沉沦，懂得及时吸取经验教训的探险者在深渊中才能走得更远。"
  },
  [82000007] = {
    id = 82000007,
    name = "即便只是微不足道的小事也有它存在的意义，探险凭证会如实记录队长们的每一次努力。"
  },
  [82000008] = {
    id = 82000008,
    name = "如同商店街促销活动一样，达成公会指定的目标便能获得公会发放的积点。当然，这些积点的作用也和商店街的印章一样，换取奖励而已。"
  },
  [82000009] = {
    id = 82000009,
    name = "不管任何事，精力充沛时才能做得更好。特别是在深渊中行动，没有精力时就好好休息吧，逞强可算不上明智。"
  },
  [82000010] = {id = 82000010, name = "-"},
  [82000011] = {
    id = 82000011,
    name = "感谢探险者为深渊探索付出的努力，从深渊收集到的每一份情报，都将成为人类探索事业的基石。"
  },
  [82000013] = {
    id = 82000013,
    name = "每天处理适量的探险事务就足够了，对优秀的探险者而言，休息也是修行中的重要一环。"
  },
  [82000014] = {
    id = 82000014,
    name = "击败强大的魔物是探险者的浪漫，荣耀属于敢向它们发起挑战的勇敢者。"
  },
  [82000017] = {
    id = 82000017,
    name = "一份拟定好的探索合约。"
  },
  [82000018] = {
    id = 82000018,
    name = "秘银制作的深渊凭证，颇受各个探险队欢迎。"
  },
  [82000019] = {
    id = 82000019,
    name = "黑曜制作的深渊凭证，颇受各个探险队欢迎。"
  },
  [82000020] = {
    id = 82000020,
    name = "远途动态积分"
  },
  [82000021] = {
    id = 82000021,
    name = "传说吸收了黑夜破晓时的力量，能够破除恶瘴，驱散黑暗。采集后放置一段时间便会因力量流失自然消散。"
  },
  [82000201] = {
    id = 82000201,
    name = "每一位踏足深渊的探险者，都犹如点亮深渊长夜的星火。或许还不够耀眼，但漫天繁星闪烁，已足以驱散黑暗。"
  },
  [82000202] = {
    id = 82000202,
    name = "不屈的探险者们犹如璀璨的星光，穿破重重迷雾照亮深渊大地，为后来者们指引前行的方向。"
  },
  [82000301] = {
    id = 82000301,
    name = "加工欧泊时的边角料，虽然不及欧泊贵重，但毕竟产自于优质的宝石矿脉，并不算常见，对绝大部分人而言依然拥有一定的价值。"
  },
  [82000302] = {
    id = 82000302,
    name = "源晶加工成品率极低，因此大量的边角料被加工为晶玉。虽然价值明显降低，但也颇受人们青睐。"
  },
  [82000311] = {
    id = 82000311,
    name = "深渊中相对常见的一种野草，富含水分和能量，口感极佳。不过本身含有微量毒性，倘若一次摄入过多，会出现致幻、麻痹等副作用。"
  },
  [82000312] = {
    id = 82000312,
    name = "经历过残酷战斗，经受了风雨洗礼，依然残存于世间的坚硬兽骨。如果不是材质过于粗糙，或许它也能成为象征永恒的物品吧。"
  },
  [82000313] = {
    id = 82000313,
    name = "主要产自极光平原的一种矿物素材，用途广泛。因其在黑暗环境中能持续发光的特性，一些有钱人也喜欢将其作为装饰物。"
  },
  [82000314] = {
    id = 82000314,
    name = "产自混沌山谷的特殊矿物，据说只要掌握对应的方法，便能从中提取出各种贵重素材，颇受工匠以及喜欢赌运气的家伙们欢迎。"
  },
  [82000315] = {
    id = 82000315,
    name = "原本只是苍青山岭中一种相对常见的玉石，但在商人的精心包装之下成为了深受追捧的宝石，不少人都以拥有一件高岭青玉的饰品为荣。"
  },
  [82000316] = {
    id = 82000316,
    name = "炎流谷地最具代表性的素材，除了表面炎流般的纹路，本身不断散发的灼热气息也给人一种身临其境的感觉，目前主要被用于冶炼、锻造领域。"
  },
  [82000317] = {
    id = 82000317,
    name = "当山石碎裂，当誓言消散，它都静静地躺在深渊地底，任凭时间流逝，不曾挪移，直到它独到的质感和香味引起了人们的注意。"
  },
  [82000401] = {
    id = 82000401,
    name = "人们常说，追逐心仪的对象时需要从对方的喜好、兴趣等方面入手，如果连这些基础情报也不清楚，盲目地追逐只会白费力气。"
  },
  [82000402] = {
    id = 82000402,
    name = "有关于心仪目标的情报固然越多越好，但如果不懂得把握重点，不会取舍，只会让自己疲于应对，陷入被动。"
  },
  [82000403] = {
    id = 82000403,
    name = "有了关于心仪目标的足够情报，对这些情报也进行了重点分析判断，那么剩下的便是把握时机、主动出击了。"
  },
  [82001001] = {
    id = 82001001,
    name = "盛放于山谷中的粉色花朵，据说是约定和幸福的象征。"
  },
  [82001002] = {
    id = 82001002,
    name = "以圈落农场的收获物制作的穗状纪念品，寓意着丰收与希望。"
  },
  [82001003] = {
    id = 82001003,
    name = "工艺精湛的魔力传导元件，深受许多工匠和商人的喜爱。"
  },
  [82010001] = {
    id = 82010001,
    name = "记录着探险者战斗过程的特殊物品，只需以注入少量魔力便能将其内记录的内容重现。"
  },
  [82010101] = {
    id = 82010101,
    name = "与徽章相性极佳的深渊矿物，只需通过一些简单的方式便能将其内蕴藏的力量转移到徽章之中。"
  },
  [82010102] = {
    id = 82010102,
    name = "与徽章相性极佳的深渊矿物，只需通过一些简单的方式便能将其内蕴藏的力量转移到徽章之中。"
  },
  [82010103] = {
    id = 82010103,
    name = "与徽章相性极佳的深渊矿物，只需通过一些简单的方式便能将其内蕴藏的力量转移到徽章之中。"
  },
  [82010104] = {
    id = 82010104,
    name = "与徽章相性极佳的深渊矿物，只需通过一些简单的方式便能将其内蕴藏的力量转移到徽章之中。"
  },
  [82020001] = {
    id = 82020001,
    name = "坚硬，是银斑石给予人们的第一印象，或许最初注意到它的人，单纯只是想要寻找一块合适的、能砸开干粮中坚果的物品而已。"
  },
  [82020002] = {
    id = 82020002,
    name = "舔舐起来有一种刺激性的味道，但紧接着便能感受到一股淡淡的香甜，深受不少探险者和魔物喜欢，至于是谁模仿谁，早已无从考证。"
  },
  [82020004] = {
    id = 82020004,
    name = "拥有比相同大小的木材更轻的重量，每当大风刮来，一些较小的松风石破片便会随风飘散。或许最初发现它的人便是在大风中感受到了它的存在。"
  },
  [82020005] = {
    id = 82020005,
    name = "初期探索深渊的探险者们从深渊中带出了无数外形奇异的物品，对他们来说，形象越是怪异的存在，便越有可能带来财富。"
  },
  [82020006] = {
    id = 82020006,
    name = "因其坚硬的质地，最初主要被作为建筑原料，虽然现在主要用途已经发生改变，但在修筑一些重要工事时，依然会是人们的首选。"
  },
  [82020007] = {
    id = 82020007,
    name = "在不少典籍中，矛头石精都是勇气、温暖的象征，但对大部分探险者而言，它只是一种素材而已。"
  },
  [82020009] = {
    id = 82020009,
    name = "这类矿物除了轻盈的特质，燃点也很低。即便在冰天雪地中也能轻易点燃，而且燃烧效率极高，毫不逊色于优质的木炭。"
  },
  [82020010] = {
    id = 82020010,
    name = "奇异的外形让它很早便受到了探险者们的关注，只是那时候人们最感兴趣的是它蜿蜒扭曲的外壳。"
  },
  [82020011] = {
    id = 82020011,
    name = "人类对深渊素材的利用过程并不轻松，不过只要知晓了其中存在强大的力量，那么无论如何坚硬的外壳也阻挡不住人类对其力量的渴望。"
  },
  [82020012] = {
    id = 82020012,
    name = "深渊中并不缺少质地坚硬的矿物，真正让这类素材备受人们关注的原因，是其坚硬表层下所蕴藏的强大力量。"
  },
  [82020013] = {
    id = 82020013,
    name = "这类矿物坚硬的外壳将其内的物质包裹得严严实实，致使无人能轻易断定其真正的价值，为此吸引了无数商人和喜欢赌运气的家伙。"
  },
  [82020014] = {
    id = 82020014,
    name = "赤玉灵石吸引人们的固然是它所蕴藏的强大力量，但即便没有那份力量，凭借自身冬暖夏凉的特性，或许依然会受到人们追捧。"
  },
  [82020015] = {
    id = 82020015,
    name = "最初发现锋芒灵石时，人们曾将它比作绽放于幽暗深渊的希望，然而随着越来越多贵重素材的发现，关于它的故事已无人再提起。"
  },
  [82020016] = {
    id = 82020016,
    name = "在矿物中的力量尚未被发现时，这些物品通常被商人作为深渊特产贩售至世界各地。而且为了增加价值，商人们往往还会准备诸多动人故事。"
  },
  [82020020] = {
    id = 82020020,
    name = "因自身轻量、易燃的特性，最初主要被作为燃料，然而当人们发现其内蕴藏着的强大力量之后，火堆中便再难看到它的踪影。"
  },
  [82020021] = {
    id = 82020021,
    name = "一些分布在特殊区域中的此类矿物，表面在风沙的长期侵蚀下会逐渐碎裂，致使品相看起来很差，经验不足的探险者很容易将其忽略。"
  },
  [82020022] = {
    id = 82020022,
    name = "每当有风吹过，凹凸不平的表面便会发出呜呜的声音。平时或许还好，但如果在寂静的夜里，成片呼啸灵石所发出的呜咽声足以让人感到恐惧。"
  },
  [82020023] = {
    id = 82020023,
    name = "据说那扭曲的外壳是此类矿物力量的具现，外壳越是别致的素材，所蕴藏的力量会更加纯粹。"
  },
  [82020024] = {
    id = 82020024,
    name = "矿物素材虽然没有生命，但却和无数生灵一样，在不同的环境中会拥有不同的表现。只不过生灵将差异隐藏在了内部，而矿物则显露在了外壳。"
  },
  [82020025] = {
    id = 82020025,
    name = "当灵石内的力量消散时，外壳也会随之破碎，人们曾尝试了无数种方法，想要保留其极具特色的外壳，但均未能如愿。"
  },
  [82020026] = {
    id = 82020026,
    name = "利用深渊矿物并非人类的特权，魔物也早就注意到了这些质地坚硬的优质矿物。"
  },
  [82020027] = {
    id = 82020027,
    name = "现有资料对盾面石精的记录颇为详细，但却没有任何记录提到它名称的由来。或许真如传闻那样，它的名称只是发现者的一时兴起。"
  },
  [82020029] = {
    id = 82020029,
    name = "材质固然轻盈，但却拥有不逊色于金属矿物的坚硬度。因此一些探险者在构建营地时也会考虑使用这类可以轻易搬动、拆卸的素材。"
  },
  [82020030] = {
    id = 82020030,
    name = "从深渊中带回地面奇异物品并不在少数，但只有那些具备出色特性，或者蕴藏力量的素材，才能留在探险者的财富名录之中。"
  },
  [82030101] = {
    id = 82030101,
    name = "人们常说进攻才是最好的防御。"
  },
  [82030102] = {
    id = 82030102,
    name = "人们常说进攻才是最好的防御。\n这句话没错，但在深渊中，盲目出击绝非明智的决定。"
  },
  [82030103] = {
    id = 82030103,
    name = "人们常说进攻才是最好的防御。\n这句话没错，但在深渊中，盲目出击绝非明智的决定。\n我愿以此身躯、以此意志化作铁壁，成为最忠诚的守护者。"
  },
  [82030201] = {
    id = 82030201,
    name = "犹豫令人迷惘，恐惧让人却步。"
  },
  [82030202] = {
    id = 82030202,
    name = "犹豫令人迷惘，恐惧让人却步。\n前行的阻碍从来不是未知和危险，而是没有做好觉悟的自己。"
  },
  [82030203] = {
    id = 82030203,
    name = "犹豫令人迷惘，恐惧让人却步。\n前行的阻碍从来不是未知和危险，而是没有做好觉悟的自己。\n直面内心，自然能无惧艰险，勇往直前。"
  },
  [82030401] = {
    id = 82030401,
    name = "翱翔吧，如同振翅的飞鸟。"
  },
  [82030402] = {
    id = 82030402,
    name = "翱翔吧，如同振翅的飞鸟。\n即便雨横风狂，即便雷霆密布，也当无所畏惧。"
  },
  [82030403] = {
    id = 82030403,
    name = "翱翔吧，如同振翅的飞鸟。\n即便雨横风狂，即便雷霆密布，也当无所畏惧。\n聆听内心的指引，向着憧憬的方向。"
  },
  [82030501] = {
    id = 82030501,
    name = "名望是身份的象征，财富是地位的基石。"
  },
  [82030502] = {
    id = 82030502,
    name = "名望是身份的象征，财富是地位的基石。\n但这些被许多人所渴求的存在，只会干扰你的思考。"
  },
  [82030503] = {
    id = 82030503,
    name = "名望是身份的象征，财富是地位的基石。\n但这些被许多人追逐的存在，只会干扰你的思考。\n不过也无需抗拒，保持谦卑，坦然面对一切诱惑和灾难、一切美好和幸运。"
  },
  [82040001] = {
    id = 82040001,
    name = "并不起眼的书册，记录着一些基础的修行方法。"
  },
  [82040002] = {
    id = 82040002,
    name = "修行的方法对大多数探险者而言都不是秘密，只是在实际应用中时常忽略一些细节，混淆一些步骤。"
  },
  [82040003] = {
    id = 82040003,
    name = "很多人都在追寻让技艺精进的技巧，其实，哪有那么多技巧。十年磨一剑，将每个步骤都做到极致，技艺自然会有所精进。"
  },
  [82070001] = {
    id = 82070001,
    name = "如此醒目的存在总感觉会被人惦记，果然还是找个足够安全的地方存放才能放心。"
  },
  [82070002] = {
    id = 82070002,
    name = "如此贵重的信物足以吸引诸多探险者，不过也会吸引不少心怀不轨之人吧？不对，那些名门望族的家里放着那么多贵重物品也没事，只要低调一些，应该不会被人注意。"
  },
  [82070003] = {
    id = 82070003,
    name = "让无数人渴求的存在，握住它的感觉就如同握住了无数个梦想。呵呵，这就是掌控一切的感觉吗？再多来一些吧！"
  },
  [82070004] = {
    id = 82070004,
    name = "尚未雕琢成型的信物坯体，虽然还无法发挥作用，但已足以令无数探险者为之心动。"
  },
  [82070101] = {
    id = 82070101,
    name = "每一个动人心弦的故事都开始于一次美好而难忘的相遇，和「%s」相遇的见证，在某些重要时刻或许可以发挥巨大作用。"
  },
  [82080101] = {
    id = 82080101,
    name = "核心内醒目的红光闪烁，犹如跃动在深渊中的焰火。"
  },
  [82080102] = {
    id = 82080102,
    name = "灼热的核心上还残存着深渊狂暴的力量，这份力量足以带来巨大的灾难，也足以带来更多的可能。"
  },
  [82080103] = {
    id = 82080103,
    name = "自然的意志萦绕其中，以最原始、最纯粹的力量守护着持有之人。"
  },
  [82080104] = {
    id = 82080104,
    name = "湛蓝的核心是那坚定意志的见证，无论前路有怎样的危险坎坷，这份前行之心也犹如永恒的坚冰，绝不动摇。"
  },
  [82080105] = {
    id = 82080105,
    name = "如骄阳，如烈火，无论何时都在绽放自己的光芒，引得无数人瞩目。"
  },
  [82080106] = {
    id = 82080106,
    name = "所谓的守护正义，是以身作则，是英勇无畏，绝非什么冠冕堂皇的口号。"
  },
  [82080107] = {
    id = 82080107,
    name = "力量没有对错和善恶，使用力量的人，才能决定着那份力量的评价。"
  },
  [82080108] = {
    id = 82080108,
    name = "核心上洁白无瑕的水晶，见证着崇高的意志。"
  },
  [82080109] = {
    id = 82080109,
    name = "核心散发出的璀璨光芒，足以穿破深渊中的层层迷障。"
  },
  [82081101] = {
    id = 82081101,
    name = "早期涉足深渊的探险者中，虽然大部分都是冲着黄金宝石而去，但也有部分人更热衷于那些形象奇异的物品。"
  },
  [82081102] = {
    id = 82081102,
    name = "当那些奇异的素材可以带来财富的消息传开，探险者们便将渴求财富的目光放到了几乎所有深渊物品上。"
  },
  [82081103] = {
    id = 82081103,
    name = "随着越来越多的奇异素材被带出深渊，无数工匠技师、商人骗子也纷纷涌入玛蒙之地，随着这些人的到来，素材的利用也有了更多可能。"
  },
  [82081104] = {
    id = 82081104,
    name = "火焰般的晶石如同那吸引飞蛾的烛火，引诱着无数探险者。明知前行之路充满危险，但在名利的驱使下，依然前赴后继。"
  },
  [82081105] = {
    id = 82081105,
    name = "深渊中从来不缺悲惨的故事，但那远不足以让探险者们止步，只要还活着，只要希望不灭，就依然拥有改变命运的机会。"
  },
  [82081106] = {
    id = 82081106,
    name = "勇敢者不会停止对未知的探索，即便为此拼尽全力，即便为此身形陨落，那份向往未知的信念也将继续燃烧，指引后来之人。"
  },
  [82081107] = {
    id = 82081107,
    name = "发现元素之力所凝聚而成的晶石后，人们尝试了许多利用的方法，但并不顺利，直到一枚徽章偶然和这些素材放到了一起。"
  },
  [82081108] = {
    id = 82081108,
    name = "在人们地不断尝试下，深渊素材的最佳利用方式逐渐被发现，不过对人类而言，这并不代表结束，而是全新的开始。"
  },
  [82081109] = {
    id = 82081109,
    name = "探险者利用深渊的力量向着更广阔的区域前行，探寻未知和神秘，而深渊也为探险者们准备了更加凶险的考验和更加丰厚的恩典。"
  },
  [82090001] = {
    id = 82090001,
    name = "无需精致的礼物，也无需动人的话语，你与心仪的探险者之间有时候只差一张邀请函的距离。"
  },
  [82090002] = {
