  [80750002] = {
    id = 80750002,
    name = "狂化形态"
  },
  [85011011] = {
    id = 85011011,
    name = "你这家伙，以前怎么说都不为所动，现在竟然主动找我，呵呵~我能拒绝么？开玩笑的，放心，我会陪你一起。"
  },
  [85021011] = {
    id = 85021011,
    name = "真是悠闲啊，我的队长大人~又去哪逍遥快活了？下次叫上我啊，一个人玩可没什么意思~"
  },
  [85031011] = {
    id = 85031011,
    name = "这深渊中虽说少了各种舞会，但每天和魔物斗智斗勇倒也不会无聊，只是在深渊呆久了，不知道会不会有哪家的小姐在想我......"
  },
  [85031012] = {
    id = 85031012,
    name = "当初我们说好要一起去深渊探险，可是我却违背了约定。如今我们以这样的方式实现，真是命运无常......"
  },
  [85041011] = {
    id = 85041011,
    name = "真是令人愉悦的感觉，连容貌也似乎英俊了几分。"
  },
  [85041012] = {
    id = 85041012,
    name = "这样的体验可以成为不错的谈资吧，呵呵呵，等我回去一定要和美丽的小姐们好好聊聊。"
  },
  [85041013] = {
    id = 85041013,
    name = "虽然我对力量提升的原理有些兴趣，但我更想先找个地方试试现在的实力。"
  },
  [85051011] = {
    id = 85051011,
    name = "对于深渊，我了解的并不多，不过，我只要考虑如何战斗就可以了吧？"
  },
  [85051012] = {
    id = 85051012,
    name = "不管怎样的决定我都没问题。嗯，这大概就是一名优秀新人探险者的自我修养了吧。"
  },
  [85051013] = {
    id = 85051013,
    name = "险恶的深渊、凶猛的魔物、复杂的人际......哈啊，最近操了不少心，额头上似乎都有皱纹了。"
  },
  [85051014] = {
    id = 85051014,
    name = "团队作战中最重要的是信任，没有足够的信任每个人都很难发挥出自己的全力，有些令人头疼啊~"
  },
  [85051015] = {
    id = 85051015,
    name = "没人可以改变过去，以“过去”的经验指引我们的未来，才是我们现在所能做到的事。"
  },
  [85061011] = {
    id = 85061011,
    name = "也是时候出去活动一下了。"
  },
  [85071011] = {id = 85071011, name = "喝！"},
  [85071012] = {id = 85071012, name = "咿呃！"},
  [85071013] = {id = 85071013, name = "咿呀！"},
  [85081011] = {
    id = 85081011,
    name = "看得到么。"
  },
  [85081012] = {
    id = 85081012,
    name = "有破绽——"
  },
  [85091011] = {
    id = 85091011,
    name = "已经没有逃跑的机会了。"
  },
  [85091012] = {
    id = 85091012,
    name = "别担心，马上就结束。"
  },
  [85101011] = {id = 85101011, name = "唔。"},
  [85101012] = {id = 85101012, name = "呃。"},
  [85101013] = {id = 85101013, name = "呜啊~"},
  [85111011] = {
    id = 85111011,
    name = "太受关注了么，真是头疼啊~"
  },
  [85121011] = {
    id = 85121011,
    name = "轮到我了么......"
  },
  [85131011] = {
    id = 85131011,
    name = "还算不错，差不多刚热身的程度，呵呵呵~"
  },
  [85141011] = {
    id = 85141011,
    name = "竟然失手了......哼。"
  },
  [85151011] = {
    id = 85151011,
    name = "今年还是老规矩？呵呵，虽然深渊中条件有限，但我在附近圈落发现了一个不错的店，就去那庆祝吧。"
  },
  [85161011] = {
    id = 85161011,
    name = "大家都去准备新年物资了，打算今天好好庆祝一下。我？呵呵，我这不是在此恭候我们的队长大人吗？"
  },
  [85171011] = {
    id = 85171011,
    name = "以前每到这种节日，我收到的礼物足以把院子填满，可是现在......唉，如果这深渊中的魔物也懂得欣赏本公子的魅力就好了......"
  },
  [85181011] = {
    id = 85181011,
    name = "圈落那边好像在举办庆典，一起去看看？——如果可以我当然希望和美丽的小姐一起，这不是只有我们俩么，要不我去找身衣服给你换上？"
  },
  [85191011] = {
    id = 85191011,
    name = "这种日子，我家里的邮箱估计已经被各种邀请函给塞满了。还是我够义气吧，推掉了无数美丽小姐的邀约来陪你。"
  },
  [85201011] = {id = 85201011, name = "Elpis"},
  [85201012] = {
    id = 85201012,
    name = "Elpis（我没念错吧？）"
  },
  [85011021] = {
    id = 85011021,
    name = "我是露露，一名正在追寻深渊传说的探险者！如果有什么有趣的事，一定要告诉我哦！"
  },
  [85021021] = {
    id = 85021021,
    name = "我把最近听过的故事都整理好了，一起去探访这些故事的线索吧。"
  },
  [85031021] = {
    id = 85031021,
    name = "我知道深渊并非只有传奇精彩的故事，在我刚到深渊不久我就已经知道了。可是，正因为有那些悲伤的事，美好和幸福的传说才更加令人向往。"
  },
  [85031022] = {
    id = 85031022,
    name = "我第一次踏上玛蒙之地时，就被港口的繁华惊呆了。我从没想过一个地方能聚集那么多人，拥有那么多建筑......相比之下，我的故乡完全就是小乡村嘛。"
  },
  [85041021] = {
    id = 85041021,
    name = "虽然有想过探险者提升实力会付出一些代价，但这代价是不是有点高了？"
  },
  [85041022] = {
    id = 85041022,
    name = "露露的努力得到认可了吗？呵呵~我会继续努力，争取得到更多的提升。"
  },
  [85041023] = {
    id = 85041023,
    name = "我达到这种程度，所花费的代价就已经超出了想象，那些传说中的人物，不管怎么想，肯定都算得上大富豪吧？！"
  },
  [85051021] = {
    id = 85051021,
    name = "从小我就对深渊充满了向往，渴望有朝一日自己也能和各种话剧的主人公一样，在深渊创造自己的故事。"
  },
  [85051022] = {
    id = 85051022,
    name = "对地面上的人来说，深渊是个拥有无数传说的地方，可是实际到了这里才会发现一切并没有那么容易。"
  },
  [85051023] = {
    id = 85051023,
    name = "提升实力需要花费不少代价，每天还要面对各种危险......呼~探险者的生活根本没有故事中的那么悠闲浪漫。"
  },
  [85051024] = {
    id = 85051024,
    name = "我竟然也能有这样的经历！我一直以为只有故事的主人公才能遇到这样的事。"
  },
  [85051025] = {
    id = 85051025,
    name = "比起那些虚无缥缈的梦想，对现在的我而言，能够和大家一起探险，就已经很满足了。"
  },
  [85061021] = {
    id = 85061021,
    name = "来啦来啦~"
  },
  [85071021] = {id = 85071021, name = "喝咿！"},
  [85071022] = {id = 85071022, name = "呀啊。"},
  [85071023] = {id = 85071023, name = "豁！"},
  [85081021] = {
    id = 85081021,
    name = "嘿嘿~看招！"
  },
  [85081022] = {
    id = 85081022,
    name = "一下就好。"
  },
  [85091021] = {
    id = 85091021,
    name = "这一招，能承受住吗~"
  },
  [85091022] = {
    id = 85091022,
    name = "看好了~不许逃哦！"
  },
  [85101021] = {id = 85101021, name = "哇啊~"},
  [85101022] = {id = 85101022, name = "欸！"},
  [85101023] = {id = 85101023, name = "呜欸。"},
  [85111021] = {
    id = 85111021,
    name = "太活跃果然容易被盯上。"
  },
  [85121021] = {
    id = 85121021,
    name = "故事里，都是......骗人的......"
  },
  [85131021] = {
    id = 85131021,
    name = "耶！真是完美的胜利，就和故事里那些主人公一样！"
  },
  [85141021] = {
    id = 85141021,
    name = "故事里的主人公也会遇到挫折，我们还不能放弃。"
  },
  [85151021] = {
    id = 85151021,
    name = "唔...想起来今天是什么日子了？诶？！你不会忘记了吧？今天可是你的生日！我已经准备好蛋糕了，当然还有话剧，呵呵呵，请期待吧！"
  },
  [85161021] = {
    id = 85161021,
    name = "在我的家乡，新年是全年里最热闹的一天。在这天，大家不管有多忙，都会放下手中的事聚集到一起表演话剧和歌舞，庆祝新年。"
  },
  [85171021] = {
    id = 85171021,
    name = "啊，已经这个日子了吗！唔，来不及做点心了......圈落里今天应该会非常热闹，一起去逛逛怎么样，就像“约会”那样！你该不会害羞了吧？呵呵呵~"
  },
  [85181021] = {
    id = 85181021,
    name = "哇哇，圈落里竟然那么热闹，这可是在深渊里诶，竟然还有那么多人！在我的家乡可完全看不到这么热闹的场面，我们快去和大家一起庆祝吧。"
  },
  [85191021] = {
    id = 85191021,
    name = "在节日交换礼物的习俗，我还是到了深渊才知道——欸？你也是？真的吗？呵呵，那我们今天就按我们喜欢的方式来庆祝吧！"
  },
  [85011031] = {
    id = 85011031,
    name = "探险者奥菲利亚，为了追寻深渊的秘密而来。"
  },
  [85021031] = {
    id = 85021031,
    name = "虽然我很少有机会泡茶，但对这方面的手艺还挺自信的，要试试吗？"
  },
  [85031031] = {
    id = 85031031,
    name = "我曾目睹那些盲目崇拜深渊之人所造成的惨状，为了不让那样的悲剧重演，我会努力将深渊的秘密呈现在世人眼前。"
  },
  [85031032] = {
    id = 85031032,
    name = "对于未知的存在，世人总是充满敬畏。这并没有错，错的是利用这份敬畏之心，行不轨之事的人。"
  },
  [85041031] = {
    id = 85041031,
    name = "宽容是一种强大而仁慈的力量，足以为混乱的深渊带来救赎。"
  },
  [85041032] = {
    id = 85041032,
    name = "作为领主的女儿，所拥有的并非只有荣耀，还应肩负为领地人民奉献一切的责任。"
  },
  [85041033] = {
    id = 85041033,
    name = "我现在和大家一样，只是一名探险者，一名愿意为了探索深渊付出全力的探险者。"
  },
  [85051031] = {
    id = 85051031,
    name = "我不想再有人经历我所经历过的灾难，为此不管需要付出多少努力我也愿意。"
  },
  [85051032] = {
    id = 85051032,
    name = "我并不喜欢战斗，可是为了故乡和人民，我已经做好觉悟！"
  },
  [85051033] = {
    id = 85051033,
    name = "故乡的灾难虽然已暂时平息，但如果不解开深渊的秘密，那些惨痛的过去，总有一天会卷土重来。"
  },
  [85051034] = {
    id = 85051034,
    name = "为了能拥有从容面对各种灾难的实力，不管会有多么艰苦，我也绝不会放弃。"
  },
  [85051035] = {
    id = 85051035,
    name = "故乡、人民......肩负着许多事物前行确实会有些累，不过正因为有那么多期待，前进的每一步才更有意义，不是吗。"
  },
  [85061031] = {
    id = 85061031,
    name = "由我来协助大家。"
  },
  [85071031] = {id = 85071031, name = "嘿伊！"},
  [85071032] = {id = 85071032, name = "咿呀！"},
  [85071033] = {id = 85071033, name = "呀啊。"},
  [85081031] = {
    id = 85081031,
    name = "尽在掌握。"
  },
  [85081032] = {
    id = 85081032,
    name = "交给我吧。"
  },
  [85091031] = {
    id = 85091031,
    name = "请见证我的决心和意志！"
  },
  [85091032] = {
    id = 85091032,
    name = "我们没有停下的理由。"
  },
  [85101031] = {id = 85101031, name = "呃啊。"},
  [85101032] = {id = 85101032, name = "呜啊~"},
  [85101033] = {id = 85101033, name = "啊~"},
  [85111031] = {
    id = 85111031,
    name = "只是这种程度......"
  },
  [85121031] = {
    id = 85121031,
    name = "......故乡......还在等我......"
  },
  [85131031] = {
    id = 85131031,
    name = "只要有不放弃，无论怎样的阻碍都无法阻止我们前行。"
  },
  [85141031] = {
    id = 85141031,
    name = "前进的路上总会有许多风雨，一时的失利并不算什么。"
  },
  [85151031] = {
    id = 85151031,
    name = "在我们那儿，生日时的烤馅饼是必不可少的，来尝尝吧。馅料吗？嗯......忘记具体数量了，呵呵~因为我们认为馅料越多祝福便越多，所以这周围的各种食材我都添加了一些。"
  },
  [85161031] = {
    id = 85161031,
    name = "新年快乐，来试试这杯特制的新年红茶吧，当然，茶点就没办法了。唔~真想念故乡的美味茶点啊。"
  },
  [85171031] = {
    id = 85171031,
    name = "试试这份点心么？味道应该不会差，这是我小时候向家里的厨师学习的。一直在想要怎样表达谢意，果然还是美味的点心最合适吧。"
  },
  [85181031] = {
    id = 85181031,
    name = "看到庆典上大家所展露的笑容，我便更加确信了自己努力的意义。为了守护这份美好和幸福，我会拼尽全力！"
  },
  [85191031] = {
    id = 85191031,
    name = "唔......礼物的数量似乎还不够，我马上再去采购一些。这种重要的日子，我不想让任何人留下遗憾。"
  },
  [85011041] = {
    id = 85011041,
    name = "小莎虽然经验丰富，值得信赖，但也不太擅长那些很危险或者太麻烦的事~如果遇到了可以尽管拜托别人，小莎不会介意哒~"
  },
  [85021041] = {
    id = 85021041,
    name = "这么舒服的天气，再多休息一会儿也没关系吧？肯定不会耽误进度的，毕竟有“厉害”的蒂娜娜，她会努力的。"
  },
  [85031041] = {
    id = 85031041,
    name = "虽然还没找到什么宝贝，但这趟旅途还是蛮有意思的，要是拉茨也在就好了......也不知道她最近怎么样，给她留个消息吧。"
  },
  [85031042] = {
    id = 85031042,
    name = "闲下来好无聊，找谁玩呢......凶巴巴的蒂娜肯定在修行，菲尼斯么......虽然看上去笑眯眯的，但总感觉会被吃的连骨头都不剩，还是不要。算了，去找安妮斯吧，安妮斯——"
  },
  [85041041] = {
    id = 85041041,
    name = "一名优秀的探险者果然不管怎么低调，都一定会发光的，嘻嘻。"
  },
  [85041042] = {
    id = 85041042,
    name = "如果提升实力是为了对付更加凶猛的敌人，小莎不介意把这样的机会让给别人哦~"
  },
  [85041043] = {
    id = 85041043,
    name = "优秀的人固然有义务承当更多事务，但小莎觉得引导普通人去做些特别的事，也是优秀者的责任。"
  },
  [85051041] = {
    id = 85051041,
    name = "就实力而言，小莎还是很自信的，不管是战斗还是逃跑，都没问题~"
  },
  [85051042] = {
    id = 85051042,
    name = "有些凶巴巴的家伙，小莎并不怕她，只、只是不想和她一般见识罢了。"
  },
  [85051043] = {
    id = 85051043,
    name = "在深渊中行动，最重要的不是力量，而是脑子，一味追求力量的人只是莽夫而已。"
  },
  [85051044] = {
    id = 85051044,
    name = "选择比努力更重要，所以小莎的每一个决定都是最正确的选择！嗯，也许有一两次失误？大概~"
  },
  [85051045] = {
    id = 85051045,
    name = "小莎的生活很简单，休息、吃饭、闯祸——啊！如果是可爱的小莎，就算闯祸也是可以被原谅的吧？嘿嘿~"
  },
  [85061041] = {
    id = 85061041,
    name = "诶~又要工作了吗？"
  },
  [85071041] = {id = 85071041, name = "哈~"},
  [85071042] = {id = 85071042, name = "呀~"},
  [85071043] = {id = 85071043, name = "嗖~"},
  [85081041] = {
    id = 85081041,
    name = "谁也别想逃。"
  },
  [85081042] = {
    id = 85081042,
    name = "乖乖投降吧~"
  },
  [85091041] = {
    id = 85091041,
    name = "差不多该结束了。"
  },
  [85091042] = {
    id = 85091042,
    name = "这一次会正中红心！"
  },
  [85101041] = {id = 85101041, name = "呀啊！"},
  [85101042] = {id = 85101042, name = "呜呃。"},
  [85101043] = {id = 85101043, name = "呃啊。"},
  [85111041] = {
    id = 85111041,
    name = "可恶，竟然追着小莎不放。"
  },
  [85121041] = {
    id = 85121041,
    name = "说好了一起探险......对不起......"
  },
  [85131041] = {
    id = 85131041,
    name = "小莎亲自出手，胜利还不是轻轻松松~"
  },
  [85141041] = {
    id = 85141041,
    name = "可、可恶！竟然这么狡猾，大家快撤！"
  },
  [85151041] = {
    id = 85151041,
    name = "生日得好好庆祝才行，小莎上一次生日，是和拉茨在港口的一间酒馆中度过的，当时还遇到了“请客”的热心探险者呢~"
  },
  [85161041] = {
    id = 85161041,
    name = "新年也没有什么特别的不同，无非就是把去年没完成的事情丢到了今年而已，这种天气，还是更适合好好休息。"
  },
  [85171041] = {
    id = 85171041,
    name = "队长，猜猜我准备了什么~——锵锵，没错，正是甜甜的点心，送给你！ 呵呵，谢谢队长长期以来的关照，这可是专门为队长准备的，快尝尝吧。"
  },
  [85181041] = {
    id = 85181041,
    name = "这样热闹的日子，休息和探险都太浪费时间了，和大家一起庆祝、巡游，才是今天最该做的事。"
  },
  [85191041] = {
    id = 85191041,
    name = "不管怎样的节日，和重要的人一起度过才会更有意义，大家一起去庆祝吧！（拉茨在做什么呢，如果，大家能在一起就好了）"
  },
  [85011051] = {
    id = 85011051,
    name = "圣歌所属、慈悲者，应邀而来。愿我们的慈悲，可以成为堕落之人最后的救赎。"
  },
  [85021051] = {
    id = 85021051,
    name = "救赎堕落之人是我的使命，但探险队的事务我也不会懈怠，今天也请让我和您一起努力~"
  },
  [85031051] = {
    id = 85031051,
    name = "圣歌的理念是为深渊带来更多爱与正义，但具体要怎样做并没有明确说明，所以只要符合这个理念的行动我们都可以自主决定。"
  },
  [85031052] = {
    id = 85031052,
    name = "祈祷是我每天都会做的事，不过我祈祷的对象并非遥不可及的神明，而是自己的内心。"
  },
  [85041051] = {
    id = 85041051,
    name = "真正的慈悲是守护弱者、惩戒罪恶，绝非一味的纵容和忍让。"
  },
  [85041052] = {
    id = 85041052,
    name = "如果只有战斗才能贯彻我们的慈悲，那么、我会竭尽所有的力量去战斗！"
  },
  [85041053] = {
    id = 85041053,
    name = "无论是这个世界的未来还是我们自己的未来，都应该由我们自己决定，也只能由我们自己决定！"
  },
  [85051051] = {
    id = 85051051,
    name = "对那些濒临堕落之人，我会全力引导他们，真正能拯救他们的人只有他们自己，但如果已无可救药，我会毫不犹豫的为他们送上终结。"
  },
  [85051052] = {
    id = 85051052,
    name = "圣歌的成员们都在为了各自的理念和职责行动着，我也绝不能停下，必须更加努力才能无愧于大家的信任。"
  },
  [85051053] = {
    id = 85051053,
    name = "我出生于深渊，母亲费了很大的代价才将我带回地面，我本不应再回到这里......可是，我想要传承母亲的意志，必须回到这里。"
  },
  [85051054] = {
    id = 85051054,
    name = "那些罪恶之人只要诚心忏悔，我都会给他们改过的机会......但他们如果继续作恶、不思悔改，那么、我将不再留情。"
  },
  [85051055] = {
    id = 85051055,
    name = "这个混沌的世界中，武力只能带来短暂而脆弱的安宁，只有人们内心深处的善良和希望，才能为这个世界带来真正的救赎。"
  },
  [85061051] = {
    id = 85061051,
    name = "请放心，我已做好准备。"
  },
  [85071051] = {id = 85071051, name = "嘿！"},
  [85071052] = {id = 85071052, name = "呀！"},
  [85071053] = {id = 85071053, name = "哈！"},
  [85081051] = {
    id = 85081051,
    name = "忏悔吧！"
  },
  [85081052] = {
    id = 85081052,
    name = "不可饶恕。"
  },
  [85091051] = {
    id = 85091051,
    name = "我们的意志、不可阻挡！"
  },
  [85091052] = {
    id = 85091052,
    name = "绝不放过任何罪恶。"
  },
  [85101051] = {id = 85101051, name = "呃啊。"},
  [85101052] = {id = 85101052, name = "呃。"},
  [85101053] = {id = 85101053, name = "呀啊。"},
  [85111051] = {
    id = 85111051,
    name = "暴力、不会让我妥协。"
  },
  [85121051] = {
    id = 85121051,
    name = "如果、能为大家争取一些时间......"
  },
  [85131051] = {
    id = 85131051,
    name = "心怀慈悲之心，才能无往不胜。"
  },
  [85141051] = {
    id = 85141051,
    name = "我的力量还不足以贯彻我们的意志，必须变得更加强大才行。"
  },
  [85151051] = {
    id = 85151051,
    name = "生日是一个人最值得纪念的日子，不管什么时候也绝不能敷衍。所以我和大家准备了一个庆祝会，希望您喜欢~"
  },
  [85161051] = {
    id = 85161051,
    name = "新年快乐~请问、您今天有空吗？我准备去参加圣歌的新年聚会，如果您有时间，我想邀请您和我一起......可以？谢、谢谢！"
  },
  [85171051] = {
    id = 85171051,
    name = "我自己做的一些点心，请问可以收下吗？那个、非常感谢您对我的支持和关照，我知道这些远不足以表达对您的谢意，今后我会更加努力回应您的期待。"
  },
  [85181051] = {
    id = 85181051,
    name = "圈落的庆典已经开始了，一起去逛逛吗？你的表情似乎在说这很奇怪？呵呵，对我们而言，热闹的庆典是宣扬圣歌的好机会，所以只要有时间都不会错过。"
  },
  [85191051] = {
    id = 85191051,
    name = "每到这个节日，我们便会将之前在各地收到的捐赠物品整理好，将它们送到最需要的人手中。对圣歌而言，这是一种传递爱与善良的方式，也是我们纪念这个节日的方式。"
  },
  [85011061] = {
    id = 85011061,
    name = "我是诺玛，一名物种研究员，当然，也是探险者，如果有魔物相关的事情，或许我可以帮上忙。"
  },
  [85021061] = {
    id = 85021061,
    name = "抱歉队长，请稍等一下，我正在撰写一份魔物解剖报告，马上就能完成。"
  },
  [85031061] = {
    id = 85031061,
    name = "深渊中的物种和地面完全不同，几乎每一种都有自己独特的构造，如果不亲自解剖、取证，很错过很多发现。"
  },
  [85031062] = {
    id = 85031062,
    name = "现有的许多魔物研究记录都存在缺失、或者靠主观推测得出结论的情况，这给研究者带来了许多麻烦，所以编撰一本没有错误的物种记录很有必要。"
  },
  [85041061] = {
    id = 85041061,
    name = "变强的感觉......不坏，但会有一种想要解剖查看原理的冲动。"
  },
  [85041062] = {
    id = 85041062,
    name = "请不用害怕，我的解剖刀是为魔物准备的，当然，如果面对敌人，它们也不会犹豫。"
  },
  [85041063] = {
    id = 85041063,
    name = "“解剖者”吗......对探险者而言，能拥有这样的称呼似乎也很不错。"
  },
  [85051061] = {
    id = 85051061,
    name = "对研究者而言，能够获得一手素材非常重要，这也是我成为探险者的原因。"
  },
  [85051062] = {
    id = 85051062,
    name = "重复做一件事看起来会很枯燥，可是，当你能感受到其中的微妙变化后，会非常享受。"
  },
  [85051063] = {
    id = 85051063,
    name = "解剖只是探索世界的一种手段，就和使用其他工具一样，并没有什么特别。"
  },
  [85051064] = {
    id = 85051064,
    name = "魔物的声音和形象都可能具用欺骗性，唯独它们的身体绝不会撒谎，它们会清楚的告诉我有关魔物的一切......"
  },
  [85051065] = {
    id = 85051065,
    name = "距离一份完整的物种记录还很遥远，但只要努力去做，终有一天一定可以实现。"
  },
  [85061061] = {
    id = 85061061,
    name = "这次会有新发现么。"
  },
  [85071061] = {id = 85071061, name = "喝咿！"},
  [85071062] = {id = 85071062, name = "呀啊！"},
  [85071063] = {id = 85071063, name = "哈！"},
  [85081061] = {
    id = 85081061,
    name = "放弃吧。"
  },
  [85081062] = {
    id = 85081062,
    name = "弱点暴露了——"
  },
  [85091061] = {
    id = 85091061,
    name = "感谢您为研究做出的贡献，再见。"
  },
  [85091062] = {
    id = 85091062,
    name = "抱歉，这一切，都是为了研究！"
  },
  [85101061] = {id = 85101061, name = "呃——"},
  [85101062] = {id = 85101062, name = "啊。"},
  [85101063] = {id = 85101063, name = "呃啊。"},
  [85111061] = {
    id = 85111061,
    name = "我必须要......坚持住才行。"
  },
  [85121061] = {
    id = 85121061,
    name = "为研究付出一切，是研究者的宿命。"
  },
  [85131061] = {
    id = 85131061,
    name = "呼~多亏了大家，素材保存完好。"
  },
  [85141061] = {
    id = 85141061,
    name = "别管我，我的素材还没拿到——"
  },
  [85151061] = {
    id = 85151061,
    name = "生日是今天么？抱歉，我常常会因为沉迷研究搞混不少事情......不过我有准备礼物——魔物标本，这是我加入探险队后做的第一个，送给你。"
  },
  [85161061] = {
    id = 85161061,
    name = "去年收集到的新物种资料并不算多，不过我很满足，这代表现在已发现区域中的物种我基本收集齐了，有些期待去新的地方了。"
  },
  [85171061] = {
    id = 85171061,
    name = "那个......队长，可以请你收下这个吗？只是一些点心，感谢您长久以来对我研究的鼓励和帮助。"
  },
  [85181061] = {
    id = 85181061,
    name = "以前有一次在圈落中被孩子们带着参加节日巡游，当他们得知我就是那个“解剖者”后，全都吓着跑开了......那之后我便尽量避免类似的活动。"
  },
  [85191061] = {
    id = 85191061,
    name = "故事中说今天会有人骑着驯鹿跑遍世界，这种生物果然是幻想出来的吧，如果真有这样的生物，它的身体构造会是怎样的呢。"
  },
  [85011071] = {
    id = 85011071,
    name = "本小姐便是大名鼎鼎的“仲裁侦探”马尔可，把重要的案子都拿过来。对了，这里办事利索的人员名单，也帮本小姐整理一份。"
  },
  [85021071] = {
    id = 85021071,
    name = "这些人选都太普通了，就没有别的推荐了么？找一个称心如意的助手，真是比找一件圣物还难。"
  },
  [85031071] = {
    id = 85031071,
    name = "任何一件小事，都有可能演变为足以影响世界的重大事件！而仲裁侦探，则是让这一切都消弭于萌芽的存在。"
  },
  [85031072] = {
    id = 85031072,
    name = "作为仲裁侦探，本小姐所奉行的准则当然是以理服人，只有面对那些冥顽不灵的家伙时，才会使用一些特别的手段。"
  },
  [85041071] = {
    id = 85041071,
    name = "对侦探而言最重要的智慧，本小姐已经足够了，现在需要的是更多力量。"
  },
  [85041072] = {
    id = 85041072,
    name = "智慧负责推理，而力量负责执行，二者兼具才足以称作“仲裁侦探”。"
  },
  [85041073] = {
    id = 85041073,
    name = "只要我们不断去解决各种案件，“仲裁侦探”的名号总有一天会传遍深渊。"
  },
  [85051071] = {
    id = 85051071,
    name = "无论多小的细节都绝不能放过，解决棘手案件的关键往往是那些容易被忽略的事物。"
  },
  [85051072] = {
    id = 85051072,
    name = "力量对推理确实不会有太多帮助，但可以让本小姐的想法得以更好的贯彻。"
  },
  [85051073] = {
    id = 85051073,
    name = "为了贯彻正义，无论怎样的手段都应该被允许，那些在背后议论本小姐行事作风的人，我会找他们好好聊聊。"
  },
  [85051074] = {
    id = 85051074,
    name = "推理、仲裁的能力固然是判断仲裁侦探是否优秀的标准，但对本小姐而言，真正的优秀可远不止这些，比如......能否拥有一位得力助手。"
  },
  [85051075] = {
    id = 85051075,
    name = "每一次结束都是另一个新的开始，不管完成了多少案件，都只是过去，必须尽快将目光放到下一件事情上。"
  },
  [85061071] = {
    id = 85061071,
    name = "诶，新案件？我助手还没找到！"
  },
  [85071071] = {id = 85071071, name = "嘿咿~"},
  [85071072] = {id = 85071072, name = "喝！"},
  [85071073] = {id = 85071073, name = "呀啊！"},
  [85081071] = {
    id = 85081071,
    name = "线索已经明了。"
  },
  [85081072] = {
    id = 85081072,
    name = "请接受调查。"
  },
  [85091071] = {
    id = 85091071,
    name = "大罪！不可饶恕的大罪！"
  },
  [85091072] = {
    id = 85091072,
    name = "冥顽不灵，去地狱忏悔吧！"
  },
  [85101071] = {id = 85101071, name = "呃啊！"},
  [85101072] = {id = 85101072, name = "啊！"},
  [85101073] = {id = 85101073, name = "切！"},
  [85111071] = {
    id = 85111071,
    name = "本小姐绝不会向罪恶屈服！"
  },
  [85121071] = {
    id = 85121071,
    name = "罪恶的力量，如此强大了么......"
  },
  [85131071] = {
    id = 85131071,
    name = "对本小姐而言这没什么特别的，仲裁侦探才不会因为这点小事感到骄傲。"
  },
  [85141071] = {
    id = 85141071,
    name = "哼！如果本小姐有助手协助，怎么可能让这群罪恶之徒胡作非为。"
  },
  [85151071] = {
    id = 85151071,
    name = "为什么知道你生日？对仲裁侦探而言这难道不是理所应当的事么。看在你平时努力帮我寻找助手的份上，本小姐就破例为你庆祝一下这个特别的日子吧。"
  },
  [85161071] = {
    id = 85161071,
    name = "过去的一年解决了不少案件，不过还远远不够，今年的目标是100件，所以没时间休息了，得抓紧时间找到助手帮忙解决才行。"
  },
  [85171071] = {
    id = 85171071,
    name = "桌子上的东西？那，那是本小姐做的点心，不小心多做了一点，就当感谢你的帮忙，提前支付的一点报酬吧。"
  },
  [85181071] = {
    id = 85181071,
    name = "外面好像有很多人聚集......说不定里面会有优秀助手的人选，这种机会绝对不能错过，我们也赶紧去看看吧！"
  },
  [85191071] = {
    id = 85191071,
    name = "越是热闹的节日，越容易发生案件，仲裁侦探可没时间放松。如果能拥有一位得力助手，说不定偶尔也会有些享受节日的时间。"
  },
  [85011081] = {
    id = 85011081,
    name = "为什么会是我呢，优秀的探险者还有很多......不管出于怎样的目的，我会按约定做好自己该做的事。"
  },
  [85021081] = {
    id = 85021081,
    name = "今天的行程安排是怎样的，我想提前做些准备。"
  },
  [85031081] = {
    id = 85031081,
    name = "明明是个危机四伏、尔虞我诈的世界，却依然令无数人向往......果然只要存在利益，便不会缺少前赴后继的追逐者。"
  },
  [85031082] = {
    id = 85031082,
    name = "深渊世界并没有故事中那么美好，但它存在着所谓的奇迹，我还停留于此，便是在渴求奇迹，渴求着再见一眼已无法归来的人。"
  },
  [85041081] = {
    id = 85041081,
    name = "如果是为了我能更好的应对战斗，您所做的已经足够了。"
  },
  [85041082] = {
    id = 85041082,
    name = "战斗，是我现在唯一能做到的事，我会全力战斗，直至找到我所渴求的奇迹。"
  },
  [85041083] = {
    id = 85041083,
    name = "我并没有太多欲望，自然也不会有那么多犹豫和畏惧。"
  },
  [85051081] = {
    id = 85051081,
    name = "我成为探险者的回忆并不美好，但正因为那段经历，我才能更清晰的认识这个世界。"
  },
  [85051082] = {
    id = 85051082,
    name = "魔物的凶恶固然令人畏惧，但在深渊中，比魔物更可怕的，却是人心。"
  },
  [85051083] = {
    id = 85051083,
    name = "我并非想谢绝您的好意，只是经历了太多虚伪和欺骗......我不想再有那样的经历。"
  },
  [85051084] = {
    id = 85051084,
    name = "无论遭遇怎样的困难、危险，亦或者感到悲伤、绝望，我都学会了微笑面对......无法改变这个世界，只能改变自己。"
  },
  [85051085] = {
    id = 85051085,
    name = "我明白自己所渴求的奇迹有多么虚无缥缈，但如果选择放弃，我会找不到自己存在的意义。"
  },
  [85061081] = {
    id = 85061081,
    name = "我已准备完成。"
  },
