  [85101081] = {id = 85101081, name = "呃。"},
  [85101082] = {id = 85101082, name = "呃啊~"},
  [85101083] = {id = 85101083, name = "呜啊~"},
  [85111081] = {
    id = 85111081,
    name = "意料之中的问题，没关系。"
  },
  [85121081] = {
    id = 85121081,
    name = "这次，可以......见到了吗......"
  },
  [85131081] = {
    id = 85131081,
    name = "我所做的准备，足以应对更复杂的战局。"
  },
  [85141081] = {
    id = 85141081,
    name = "无论如何准备，也无法应对诡变的战局吗......"
  },
  [85151081] = {
    id = 85151081,
    name = "生日是对生命的记录， 所以拥有了特别的意义。这种特别的日子，值得我们感恩和庆祝。"
  },
  [85161081] = {
    id = 85161081,
    name = "又是没有任何发现的一年，虽然我已知晓奇迹不太可能出现，但依然会在每个早晨醒来的时候，祈祷奇迹。"
  },
  [85171081] = {
    id = 85171081,
    name = "在你的身上，我感受到了和以前遇到的那些人相似的感觉，可是又不太一样......总之，谢谢您的关照。"
  },
  [85181081] = {
    id = 85181081,
    name = "我找不到和人群一起欢呼庆祝的理由，至少在我明白哪些人是我可以完全信任的之前。"
  },
  [85191081] = {
    id = 85191081,
    name = "每年这个时候，我都会许同一个愿望，可是实现愿望的地方终究只存在于故事中。"
  },
  [85011091] = {
    id = 85011091,
    name = "探险者烈风，为追寻强者之道而来，如果有任何线索，请务必告诉我。作为回报，我可以为你解决任何不违背我信念的问题。"
  },
  [85021091] = {
    id = 85021091,
    name = "我们什么时候出发，我想尽快找个对手验证一下近期的修行成果。"
  },
  [85031091] = {
    id = 85031091,
    name = "这里的生存法则，和我的家族很像。大家都狂热的崇尚力量，拥有力量的人可以拥有一切，名誉、财富、权力......不过，又似乎有些不同。"
  },
  [85031092] = {
    id = 85031092,
    name = "我想要成为强者并非是为了财富和名望，家族中的生活让我早已厌倦了那一切，我只是渴望突破自己的极限，追寻契合自己信念的未来。"
  },
  [85041091] = {
    id = 85041091,
    name = "力量是成为强者的基础，但不应该是强者的全部。"
  },
  [85041092] = {
    id = 85041092,
    name = "我所追寻的强者之道，是永不放弃的信念，毫不妥协的坚持，绝不后退的意志。"
  },
  [85041093] = {
    id = 85041093,
    name = "我的实力又有了新的突破，但我的信念和意志，还需要更多磨练。"
  },
  [85051091] = {
    id = 85051091,
    name = "如果没有坚定的信念，盲目追求力量，最终只会沦为欲望的奴隶。"
  },
  [85051092] = {
    id = 85051092,
    name = "我的童年并不美好，不过也正因如此，我才明白只有变得足够强大才能选择自己的未来。"
  },
  [85051093] = {
    id = 85051093,
    name = "真正的强者，不会沉迷于那些和修行无关的事，不管何时都应当朝着自己的目标全力以赴。"
  },
  [85051094] = {
    id = 85051094,
    name = "衡量强者的重要标准并非是他所拥有的力量，而在于他对待弱者的方式。"
  },
  [85051095] = {
    id = 85051095,
    name = "我所追寻的强者之道的确有些严苛，但如果没有自己的信念，拥有再强大的力量，也只能算得上莽夫。"
  },
  [85061091] = {id = 85061091, name = "走吧。"},
  [85071091] = {id = 85071091, name = "呵！"},
  [85071092] = {id = 85071092, name = "呀！"},
  [85071093] = {id = 85071093, name = "喝啊！"},
  [85081091] = {
    id = 85081091,
    name = "绝不妥协。"
  },
  [85081092] = {
    id = 85081092,
    name = "绝不后退。"
  },
  [85091091] = {
    id = 85091091,
    name = "不负强者的荣耀。"
  },
  [85091092] = {
    id = 85091092,
    name = "见证我的意志。"
  },
  [85101091] = {id = 85101091, name = "唔。"},
  [85101092] = {id = 85101092, name = "呃。"},
  [85101093] = {id = 85101093, name = "啊。"},
  [85111091] = {
    id = 85111091,
    name = "哼~这样就想让我停下？"
  },
  [85121091] = {
    id = 85121091,
    name = "我的选择，错了吗......"
  },
  [85131091] = {
    id = 85131091,
    name = "强者不会满足于微小胜利，继续挑战更强的对手吧。"
  },
  [85141091] = {
    id = 85141091,
    name = "前行的路上总会布满荆棘，只有不放弃的人，才能算得上强者。"
  },
  [85151091] = {
    id = 85151091,
    name = "今天是你的生日吗？之前虽然有听说，但我并不知道这边庆祝生日的习俗，也不知道该准备些什么......所以，如果有希望我做的事，请尽管开口。"
  },
  [85161091] = {
    id = 85161091,
    name = "对我而言新年并没有什么不同，在成为强者的路上，没有任何一天可以松懈。你说享受节日也是强者的一种修行？怎么可能！嗯——或许也有点道理......那、那我尝试一下，今天和你一起去庆祝新年吧。"
  },
  [85171091] = {
    id = 85171091,
    name = "代表感谢的日子吗？原来还有这样的节日，对那些有恩与自己的人心怀感激，这一点也非常契合我的强者之道。感谢你一直以来对我的支持和关照！"
  },
  [85181091] = {
    id = 85181091,
    name = "确实是很热闹的日子，不过对我的修行似乎并没有什么帮助。对我而言，任何事情都只分为两个类型，“与修行有关”和“与修行无关”。"
  },
  [85191091] = {
    id = 85191091,
    name = "已经这个点了吗，得抓紧时间去狩猎，准备节日用的物资——嗯，为什么狩猎？今天不是狩猎的节日吗，难道这里没有这样的习惯？"
  },
  [85011101] = {
    id = 85011101,
    name = "您好，我是探险者罗宾，非常感谢您的邀请，很乐意为您效力——“孤高游侠”，没、没错，是是是我本人。"
  },
  [85021101] = {
    id = 85021101,
    name = "物资分配、行动计划、探险准备......我该先处理哪个呢？抱歉，好像已经问了你很多次。虽然我已经逐渐熟悉了这些事务，但事务太多时依然会手忙脚乱。"
  },
  [85031101] = {
    id = 85031101,
    name = "“孤高游侠”，我真的很喜欢这个称呼，可是这是因为意外才获得的称号，感觉现在的自己配不上，呼~好烦恼。"
  },
  [85031102] = {
    id = 85031102,
    name = "在这竟然能看到这本游记，这可是我最喜欢的书！我想要成为探险者，很大程度是受了它的影响，而且这本书主角的名字和我很像！"
  },
  [85041101] = {
    id = 85041101,
    name = "如果以孤高游侠作为目标，不管实力还是经历我都还差的很远。"
  },
  [85041102] = {
    id = 85041102,
    name = "如果能掌握更强的实力，或许我就可以和那些传奇游侠一样，游历四方，行侠仗义。"
  },
  [85041103] = {
    id = 85041103,
    name = "竟然有种“我也算故事主角”的感觉，嘿嘿~虽然还只是错觉，但我会朝着这个方向继续努力。"
  },
  [85051101] = {
    id = 85051101,
    name = "如果没有成为探险者，或许我现在正按照家人的安排，做一些在他们看来还不错的工作吧。"
  },
  [85051102] = {
    id = 85051102,
    name = "多亏了大家的关照我才能走到这一步，今后我也会继续努力，不辜负大家的期待。"
  },
  [85051103] = {
    id = 85051103,
    name = "我很不擅长做决定，从小到大，所有的一切都是父母和师父为我安排好的，就连刚到深渊的时候，我也是模仿游记里的主人公在行动。"
  },
  [85051104] = {
    id = 85051104,
    name = "受到太多的期待会让我感到不安，所以来到这里之前我才会选择独行，不过也因此才获得了“孤高游侠”的称号，一切都好像注定的一样。"
  },
  [85051105] = {
    id = 85051105,
    name = "成为传奇游侠是我的梦想，但我也明白自己和他们之间的差距......不过，感觉只要和这里的大家一起努力，总有一天一定可以实现。"
  },
  [85061101] = {
    id = 85061101,
    name = "嗯，交给我。"
  },
  [85071101] = {id = 85071101, name = "喝呀！"},
  [85071102] = {id = 85071102, name = "噫呃！"},
  [85071103] = {id = 85071103, name = "呀喝！"},
  [85081101] = {id = 85081101, name = "退下！"},
  [85081102] = {
    id = 85081102,
    name = "接招——"
  },
  [85091101] = {
    id = 85091101,
    name = "胆大的尽管来吧！"
  },
  [85091102] = {
    id = 85091102,
    name = "无需手下留情。"
  },
  [85101101] = {id = 85101101, name = "呃啊。"},
  [85101102] = {id = 85101102, name = "呀啊。"},
  [85101103] = {id = 85101103, name = "呜啊~"},
  [85111101] = {
    id = 85111101,
    name = "唔，大意了——"
  },
  [85121101] = {
    id = 85121101,
    name = "......哈啊~可以不用逞强了......"
  },
  [85131101] = {
    id = 85131101,
    name = "对游侠而言，这样的胜利微不足道。"
  },
  [85141101] = {
    id = 85141101,
    name = "如果我不逞强，及时将内心的不安说出来，会不会有不同的结局......"
  },
  [85151101] = {
    id = 85151101,
    name = "我听说今天是你的生日，所以准备了一些饭菜，当然，也有蛋糕和果汁。因为不知道你会喜欢什么，所以就什么都准备了一些。"
  },
  [85161101] = {
    id = 85161101,
    name = "在这种重要的日子里好好休息也是游侠的修行之一，为什么？唔，据书上所说，游侠的修行应当融入生活，而非一味苦修，要学会在对应的时间做对应的事。"
  },
  [85171101] = {
    id = 85171101,
    name = "抱歉，我，那个，失败了......我尝试了好多次，可是做出来的点心依然不行。所以，可以用这本我最爱的游记作为替代么，谢谢你长久以来的关照。"
  },
  [85181101] = {
    id = 85181101,
    name = "故事里，游侠们总是会非常享受各种节日，可是我却完全做不到——让我去参加这样热闹的活动，我会感到不安和紧张......"
  },
  [85191101] = {
    id = 85191101,
    name = "在我来深渊之前，每年这个时候我们一家都会坐在火炉边，分享一年来各自的收获。我感觉非常有趣，要试试吗？那么先从我开始吧......"
  },
  [85011111] = {
    id = 85011111,
    name = "您好~我是米哈尔，今天起正式加入您的探险队，如果有任何我能帮上忙的事，请尽管吩咐！那么，今后请多多指教了~"
  },
  [85021111] = {
    id = 85021111,
    name = "物资都已准备完毕，随时可以出发——诶，辛苦？那个、只是一点力所能及的小事而已，请不必客气。"
  },
  [85031111] = {
    id = 85031111,
    name = "一夜暴富啊~呵呵，不错的愿望，想必深渊中的许多人都有这样的愿望吧......只是，对一些人而言，一夜暴富或许并不算什么好事。"
  },
  [85031112] = {
    id = 85031112,
    name = "有人将深渊视作实现愿望的圣地，有人则将此看作孕育罪恶的漩涡。但在我看来，深渊、就只是深渊而已，既不是什么圣地，也并非什么漩涡。"
  },
  [85041111] = {
    id = 85041111,
    name = "不管前方是美好还是丑恶，我都会继续前行。"
  },
  [85041112] = {
    id = 85041112,
    name = "不沉溺于过去，不期望于未来，如此、才能把握住现在。"
  },
  [85041113] = {
    id = 85041113,
    name = "这种前所未有的体验我一定不会忘记！呵呵，今后也请继续关照。"
  },
  [85051111] = {
    id = 85051111,
    name = "那是一种如美梦般的感觉......虽然很快就醒了，但如果不是那场美梦，我很难拥有现在的一切。"
  },
  [85051112] = {
    id = 85051112,
    name = "我并不清楚深渊中是否拥有我想要的答案，但我很喜欢现在正经历着的一切。"
  },
  [85051113] = {
    id = 85051113,
    name = "不管做任何事都要竭尽全力才行，即便结果不一定完美，但至少可以让自己在未来不会后悔。"
  },
  [85051114] = {
    id = 85051114,
    name = "全力以赴是我现在唯一能做到的事，不管会遭遇怎样的困难挫折，我也绝不会放弃。"
  },
  [85051115] = {
    id = 85051115,
    name = "对我而言，现在所经历的每一刻都是无可替代的存在，所以我会全力去享受，绝不给自己留下任何遗憾。"
  },
  [85061111] = {
    id = 85061111,
    name = "那么、一起前进吧！"
  },
  [85071111] = {id = 85071111, name = "呀啊。"},
  [85071112] = {id = 85071112, name = "喝啊！"},
  [85071113] = {id = 85071113, name = "哈！"},
  [85081111] = {
    id = 85081111,
    name = "无处可逃。"
  },
  [85081112] = {
    id = 85081112,
    name = "一击毙命！"
  },
  [85091111] = {
    id = 85091111,
    name = "前行之路、由我来开辟！"
  },
  [85091112] = {
    id = 85091112,
    name = "无人可以阻挡我们。"
  },
  [85101111] = {id = 85101111, name = "呜呃。"},
  [85101112] = {id = 85101112, name = "呃啊。"},
  [85101113] = {id = 85101113, name = "呃呃。"},
  [85111111] = {
    id = 85111111,
    name = "还不到放弃的时候。"
  },
  [85121111] = {
    id = 85121111,
    name = "我的旅途......到此为止了吗......"
  },
  [85131111] = {
    id = 85131111,
    name = "所有阻碍都已清除完毕，去下一个地点吧。"
  },
  [85141111] = {
    id = 85141111,
    name = "旅途漫漫，难免遭遇荆棘坎坷。"
  },
  [85151111] = {
    id = 85151111,
    name = "今天是你的生日吧？怎么知道的？呵呵，秘密！我知道一个不错的地方，一起去庆祝吧！"
  },
  [85161111] = {
    id = 85161111,
    name = "小时候每到新年我都会在门框上划一条线，看看自己今年长高了多少。那时候最大的心愿便是赶快长大，减轻母亲的负担......"
  },
  [85171111] = {
    id = 85171111,
    name = "节日快乐~非常感谢您长久以来的关照。给，一点儿小礼物——呵呵，只是一些点心，用母亲教我的方法做的，希望能合你的口味~"
  },
  [85181111] = {
    id = 85181111,
    name = "没想到这种节日在深渊中也会举办得如此热闹，我们可不能错过了！一起去看看吧！呵呵，不知道深渊里是否也会有巡游的队伍。"
  },
  [85191111] = {
    id = 85191111,
    name = "又到这个日子了吗......此前我在各地游历时，曾见识过一些极具地域特色的庆祝方式，和寻常的庆典完全不同——想知道详细？呵呵，那我们今天就聊聊这个好了！"
  },
  [85011121] = {
    id = 85011121,
    name = "鬼姬向您问好~作为鬼牌之人竟然也能收到邀请，呵呵呵~阁下真是有趣......"
  },
  [85021121] = {
    id = 85021121,
    name = "深渊中从来不缺好玩的事情，不过想要找到心仪的事物，更多时候需要我们亲自导演才行~"
  },
  [85031121] = {
    id = 85031121,
    name = "无论贪婪还是慷慨，罪恶亦或仁慈，深渊这个舞台都会将它们完美呈现出来~每一个来到深渊的人，不管是否愿意，都已经成为了这舞台上的一员。"
  },
  [85031122] = {
    id = 85031122,
    name = "对鬼牌而言这个世界会变得怎样并不重要，我们所渴求的只有那一出出有趣的戏剧~所以只要深渊这个舞台还在，便永远会存在观众的席位。"
  },
  [85041121] = {
    id = 85041121,
    name = "竟然会做出这样的举动，呵呵，非常有趣~阁下所渴求的会是什么呢？"
  },
  [85041122] = {
    id = 85041122,
    name = "解开鬼姬的束缚......很有趣的选择！希望阁下在将来不会后悔这一刻所做的决定。"
  },
  [85041123] = {
    id = 85041123,
    name = "鬼姬最擅长的便是导演各种好戏，就让在下为阁下献上更多有趣的表演吧。"
  },
  [85051121] = {
    id = 85051121,
    name = "鬼牌中一些人偶尔会指责在下对那些有趣的事情干预过多，可是如果不亲自参与会少很多乐趣吧？虽然都是观众，但理念上仍有不少差距呢~"
  },
  [85051122] = {
    id = 85051122,
    name = "阁下觉得自己已经了解鬼姬了吗？呵呵，不，你不了解！不过也不必了解，有时候知道太多只会为我们增加无谓的烦恼。"
  },
  [85051123] = {
    id = 85051123,
    name = "在寻找乐趣方面，鬼牌中很少有人能比得上鬼魅。有时候她只需要稍加引导，便能将一些无聊的小事演变为有趣的戏剧~"
  },
  [85051124] = {
    id = 85051124,
    name = "这深渊中有无数有趣的事情，见证它们便是鬼姬存在的意义，无论现在将来，这一点永远都不会改变。"
  },
  [85051125] = {
    id = 85051125,
    name = "杰姬，多么遥远的名字，在下以为再也不会听到......呵呵，不过比起杰姬，还是鬼姬更适合在下。"
  },
  [85061121] = {
    id = 85061121,
    name = "好戏、开场——"
  },
  [85071121] = {id = 85071121, name = "喝——"},
  [85071122] = {id = 85071122, name = "嘿！"},
  [85071123] = {id = 85071123, name = "呀！"},
  [85081121] = {
    id = 85081121,
    name = "尽管挣扎吧~"
  },
  [85081122] = {
    id = 85081122,
    name = "降临——"
  },
  [85091121] = {
    id = 85091121,
    name = "这场表演该进入高潮了。"
  },
  [85091122] = {
    id = 85091122,
    name = "鬼姬才是这场闹剧主宰！"
  },
  [85101121] = {id = 85101121, name = "呃。"},
  [85101122] = {id = 85101122, name = "唔呃。"},
  [85101123] = {id = 85101123, name = "呃啊。"},
  [85111121] = {
    id = 85111121,
    name = "这么渴望和鬼姬一起狂欢吗？"
  },
  [85121121] = {
    id = 85121121,
    name = "鬼姬......谢幕了吗......"
  },
  [85131121] = {
    id = 85131121,
    name = "一场闹剧——呵呵呵，不过也算是为鬼姬带来了不少乐趣。"
  },
  [85141121] = {
    id = 85141121,
    name = "搞砸了？不，是我们太过沉迷，被卷入了这场闹剧~"
  },
  [85151121] = {
    id = 85151121,
    name = "生日？呵呵呵，这可是个值得庆祝的好日子~就让鬼姬为阁下献上一场盛宴吧——还请放心，只是一场单纯的生日宴会，不会有打扰您雅兴的剧目。"
  },
  [85161121] = {
    id = 85161121,
    name = "在这种寓意着美好、希望的节日中，依然会有无数精彩的好戏上演~毕竟人越聚集的地方，越容易出现矛盾纷争，这种节日果然很棒啊！"
  },
  [85171121] = {
    id = 85171121,
    name = "鬼姬在准备点心，毕竟点心和这个日子非常匹配吧？为什么会这样做？是啊，为什么呢？呵呵呵~或许鬼姬就应该做一些让人无法预料的事吧？"
  },
  [85181121] = {
    id = 85181121,
    name = "平日里鬼姬不管在哪出现都会成为所有人的焦点，可是这种节日里，似乎所有人都已经忘记了鬼姬，真是无聊的节日......"
  },
  [85191121] = {
    id = 85191121,
    name = "诶，竟然会给鬼姬赠送礼物，阁下也总能做出一些让鬼姬难以捉摸的事~我现在可以打开吗？确定？"
  },
  [85011131] = {
    id = 85011131,
    name = "请问这份邀请上的地址是这里吗？看来没走错。我是汐，来自真理之钥，如果有和魔法奥秘相关的事请尽管交给我！"
  },
  [85021131] = {
    id = 85021131,
    name = "我在整理近期的感悟记录，有几处关键的地方还是没能想明白，可以帮我参考一下吗？"
  },
  [85031131] = {
    id = 85031131,
    name = "我学习魔法不久便产生了“魔法是如何诞生的”这种疑问，这也是真理之钥的大家都渴望了解的问题。我相信只要大家继续努力，一定可以找到这个问题的答案。"
  },
  [85031132] = {
    id = 85031132,
    name = "最初我得知真理之钥的目标时非常惊讶，那是我第一次遇到和我拥有相似理想的组织，所以在加入时我完全没有犹豫。"
  },
  [85041131] = {
    id = 85041131,
    name = "我有一种即将产生新领悟的感觉，只差一点了，再一点......"
  },
  [85041132] = {
    id = 85041132,
    name = "每一次力量的变化都能获得一些感悟，虽然不一定能起到很大的作用，但这种感觉我很喜欢。"
  },
  [85041133] = {
    id = 85041133,
    name = "情感！是人的情感！我好像又明白了一些，从某些方面来讲魔法便是我们的一种情感的具现！"
  },
  [85051131] = {
    id = 85051131,
    name = "对于同类型魔法的研究我确实有了一些收获，但对于不同魔法间的共通性和特性我依然收获甚微，今后这方面的研究还要更努力才行。"
  },
  [85051132] = {
    id = 85051132,
    name = "无论武技还是魔法，对它们的了解每多一分，实力自然会变得更强。所以不管采用怎样的方式修行，理解和领悟才是最重要的。"
  },
  [85051133] = {
    id = 85051133,
    name = "故事书中的魔法师都拥有无所不能的力量，可是我自己掌握后才发现完全是骗人的，呵呵，不过如果不是那些魔法师，我也不会学习魔法。"
  },
  [85051134] = {
    id = 85051134,
    name = "“从何开始，到哪结束”，只要能想清楚这两个问题，或许我便能找到解开魔法奥秘的钥匙。"
  },
  [85051135] = {
    id = 85051135,
    name = "对魔法的研究越是深入，便越发明白自己的渺小，我甚至开始怀疑自己能否在这深渊中找到想要的答案。"
  },
  [85061131] = {
    id = 85061131,
    name = "我会好好把握这次机会。"
  },
  [85071131] = {id = 85071131, name = "呀~"},
  [85071132] = {id = 85071132, name = "哈啊。"},
  [85071133] = {id = 85071133, name = "嘿呀。"},
  [85081131] = {
    id = 85081131,
    name = "我会加油。"
  },
  [85081132] = {
    id = 85081132,
    name = "交给我吧。"
  },
  [85091131] = {
    id = 85091131,
    name = "见识一下我的全力吧。"
  },
  [85091132] = {
    id = 85091132,
    name = "信念才是最强大的魔法！"
  },
  [85101131] = {id = 85101131, name = "噫呃。"},
  [85101132] = {id = 85101132, name = "呀啊。"},
  [85101133] = {id = 85101133, name = "呃啊。"},
  [85111131] = {
    id = 85111131,
    name = "请别分心，我还可以继续。"
  },
  [85121131] = {
    id = 85121131,
    name = "终于发现了问题，可是......"
  },
  [85131131] = {
    id = 85131131,
    name = "很完美的作战，我似乎也又有了些感悟，先记录一下......"
  },
  [85141131] = {
    id = 85141131,
    name = "对不起，都怪我没能把握好时机......"
  },
  [85151131] = {
    id = 85151131,
    name = "听说今天是你的生日，所以大家一起筹备了庆祝会，你会参加吧？如果少了主角，那庆祝会就没有意义了。"
  },
  [85161131] = {
    id = 85161131,
    name = "在我家新年是非常重要的日子，家人之间会彼此分享各自过去一年的收获，畅想新一年的愿望。来到深渊后我也依然坚持着这样的习惯，不过分享对象从家人变成了同伴。"
  },
  [85171131] = {
    id = 85171131,
    name = "非常抱歉，我尝试了很多次，也无法做出美味的甜点。比起魔法，厨艺要困难太多了......非常感谢你对我的关照！"
  },
  [85181131] = {
    id = 85181131,
    name = "平时真理之钥的同伴都在为了目标各自努力，只有这种热闹的节日才会聚到一起庆祝。今年一起去吧，大家每次都能想出些奇怪的比试，会很有趣哦！"
  },
  [85191131] = {
    id = 85191131,
    name = "这样的节日里，真理之钥的大家也会交换礼物，不过我们的礼物都是彼此修行时获得的感悟。我们在阅读后也不会保留，而是传递给下一个成员，这样便能确保大家拥有最大收获。"
  },
  [85011141] = {
    id = 85011141,
    name = "我是狄莉丝，有麻烦尽管找我，只要报酬合适，不管怎样的问题都能给你摆平。"
  },
  [85021141] = {
    id = 85021141,
    name = "不能打架不能赚钱的每一分钟都令人觉得煎熬，快收拾好出发吧。"
  },
  [85031141] = {
    id = 85031141,
    name = "赚钱是人生一大乐趣，打架也是人生的一大乐趣，两种乐趣叠加，便是我所追求的最高乐趣。"
  },
  [85031142] = {
    id = 85031142,
    name = "在深渊中，拳头才是最大的道理，强者可以拥有一切，而弱者无论如何歇斯底里也不会有人在意。"
  },
  [85041141] = {
    id = 85041141,
    name = "干得不错！我就喜欢这种直接的做事方式。"
  },
  [85041142] = {
    id = 85041142,
    name = "解放力量的感觉真是令人愉悦，赶紧为我安排更强的对手吧。"
  },
  [85041143] = {
    id = 85041143,
    name = "这疯狂混乱的世界里，唯有力量是永恒的真理！"
  },
  [85051141] = {
    id = 85051141,
    name = "所谓的守护、梦想......如果没有足够的力量，都只是奢望。"
  },
  [85051142] = {
    id = 85051142,
    name = "战斗是一种享受，但也要挑选好对手，和更强的对手战斗，才能突破自己的极限。"
  },
  [85051143] = {
    id = 85051143,
    name = "在深渊中活下去，并非是强者的专利，但强者可以选择要怎样活下去。"
  },
  [85051144] = {
    id = 85051144,
    name = "每个人都有野心和欲望，敢于直面自己内心的人，才能算得上强者。"
  },
  [85051145] = {
    id = 85051145,
    name = "我家老头子教会了我钱财和力量都是不可或缺的东西，所以我会在活着的每一天全力去享受战斗和财富。"
  },
  [85061141] = {
    id = 85061141,
    name = "大闹一场吧！"
  },
  [85071141] = {id = 85071141, name = "呀——"},
  [85071142] = {id = 85071142, name = "喝诶！"},
  [85071143] = {id = 85071143, name = "噫呃！"},
  [85081141] = {
    id = 85081141,
    name = "轮到我了！"
  },
  [85081142] = {
    id = 85081142,
    name = "没人能躲开。"
  },
  [85091141] = {
    id = 85091141,
    name = "逃吧逃吧，再逃远一些。"
  },
  [85091142] = {
    id = 85091142,
    name = "再不挣扎就要结束咯~"
  },
  [85101141] = {id = 85101141, name = "呃。"},
  [85101142] = {id = 85101142, name = "嘁。"},
  [85101143] = {id = 85101143, name = "啊。"},
  [85111141] = {
    id = 85111141,
    name = "就是这种感觉，呵呵哈哈哈！"
  },
  [85121141] = {
    id = 85121141,
    name = "也算是尽兴了吧......"
  },
  [85131141] = {
    id = 85131141,
    name = "就只有这点儿能耐吗？嘁，真是无聊。"
  },
  [85141141] = {
    id = 85141141,
    name = "这笔账，我会让他们会加倍奉还！"
  },
  [85151141] = {
    id = 85151141,
    name = "生日么，也算是特别的日子。一起去喝点吧，顺便带你和我那些“老朋友”活动一下。放心，有我在，不会出什么问题。"
  },
  [85161141] = {
    id = 85161141,
    name = "对我而言，活着的每一天都是新的，毕竟在这个混乱疯狂的世界，谁也无法预知未来，能好好享受当下才是最重要的事。"
  },
  [85171141] = {
    id = 85171141,
    name = "感谢的日子？不，我们之间似乎没有什么要感谢的吧？不过如果能请我喝一杯，说些好听的话也不是不行~"
  },
  [85181141] = {
    id = 85181141,
    name = "据说这一天，那些离去的人都会回来看看......嘁，纯粹就是胡扯，我每年倒好的酒，可从来没有被动过......"
  },
  [85191141] = {
    id = 85191141,
    name = "又是所有人都去享受节日的日子么？真是太闲了，找两个家伙切磋一下吧。......放心，我会手下留情，毕竟大家都是“同伴”嘛~"
  },
  [85011151] = {
    id = 85011151,
    name = "我是猎魔人炎狼，用你们的话来说，叫做探险者。不过，我只是为了魔物而来。"
  },
  [85021151] = {
    id = 85021151,
    name = "请不用在意我的存在，如有需要，我和格拉墨随时可以行动。"
  },
  [85031151] = {
    id = 85031151,
    name = "猎魔人并非只是一个名号，它更是一种责任，一种信念。我继承了这个名号，自然会竭尽全力，背负它所承载的一切。"
  },
  [85031152] = {
    id = 85031152,
    name = "格拉墨是历代猎魔人的武器，不过比起武器，它更像是一名伙伴。虽然偶尔会闹点小脾气，但在危急时永远是最可靠的存在。"
  },
  [85041151] = {
    id = 85041151,
    name = "我和格拉墨之间似乎更有默契了，是错觉么。"
  },
  [85041152] = {
    id = 85041152,
    name = "解放格拉墨的封印，真是大胆的选择——既然如此，便让你见识一下我们的全力。"
  },
  [85041153] = {
    id = 85041153,
    name = "我能感受到格拉墨的兴奋，它正在渴求战斗，渴求魔物的血肉。"
  },
  [85051151] = {
    id = 85051151,
    name = "猎魔人的职责是斩杀魔物和罪恶，肃清深渊，守护人类世界。"
  },
  [85051152] = {
    id = 85051152,
    name = "修行、战斗，再修行、再战斗......这便是我现在所拥有的一切。"
  },
  [85051153] = {
    id = 85051153,
    name = "师父拯救了弱小落魄的我，我也会用他给予我的力量，去拯救更多的人。"
  },
  [85051154] = {
    id = 85051154,
    name = "每个人都有自己拯救和守护的方式，我和格拉墨的方式，便是斩杀更多的魔物。"
  },
  [85051155] = {
    id = 85051155,
    name = "只要还有罪恶和魔物，我和格拉墨便将一往无前。"
  },
  [85061151] = {
    id = 85061151,
    name = "格拉墨，该出发了。"
  },
  [85071151] = {id = 85071151, name = "喝！"},
  [85071152] = {id = 85071152, name = "哈！"},
  [85071153] = {id = 85071153, name = "嘿！"},
  [85081151] = {
    id = 85081151,
    name = "发现猎物。"
  },
  [85081152] = {
    id = 85081152,
    name = "猎杀执行。"
  },
  [85091151] = {
    id = 85091151,
    name = "格拉墨——解放！"
  },
  [85091152] = {
    id = 85091152,
    name = "终结，降临！"
  },
  [85101151] = {id = 85101151, name = "呃啊~"},
  [85101152] = {id = 85101152, name = "唔-"},
  [85101153] = {id = 85101153, name = "唔啊~"},
  [85111151] = {
    id = 85111151,
    name = "格拉墨，你愤怒了吗。"
  },
  [85121151] = {
    id = 85121151,
    name = "还是......那么不堪......"
  },
  [85131151] = {
    id = 85131151,
    name = "只是微不足道的胜利，格拉墨在期待下一次出场。"
  },
  [85141151] = {
    id = 85141151,
    name = "格拉墨，你的不甘和愤怒，我明白。"
  },
  [85151151] = {
    id = 85151151,
    name = "生日么，让格拉墨给你唱首歌如何？——开玩笑的，格拉墨可不会说话。嗯？开玩笑不适合我？"
  },
  [85161151] = {
    id = 85161151,
    name = "又已是新年，我和格拉墨仍未给这深渊带来变化......请别误会，我只是在感慨，并非急于求成。难得的节日，一起去走走吧。"
  },
  [85171151] = {
    id = 85171151,
    name = "对我而言，多余的杂念只会影响我修行的速度。——我并非在否定节日的意义，只是要变得更强，就必须把握每一分钟。"
  },
  [85181151] = {
    id = 85181151,
    name = "人类真是奇怪又矛盾的生物，一边对未知感到畏惧，一边又憧憬未知的存在。"
  },
  [85191151] = {
    id = 85191151,
    name = "我独自流浪的时候，非常喜欢这种日子，因为可以相对轻松的乞讨到食物......意外吗？呵呵，对那个时候的我来说，不挨饿就已是最大的愿望，根本不敢奢求现在的生活。"
  },
  [85011161] = {
    id = 85011161,
    name = "我是薇，很高兴能收到您的邀请，从现在起我就是您的新成员了，还请多多指教。"
  },
  [85021161] = {
    id = 85021161,
    name = "红茶和点心已经准备好了，每次行动前吃饱喝足，才会更有干劲不是吗？呵呵~"
  },
  [85031161] = {
    id = 85031161,
    name = "所谓的“重要收获”就是一种、一种只可意会的存在。可能是数之不尽的财富，也可能是名扬深渊的功绩，亦或者其他的存在。总之，在找到前我也不知道具体会是什么。"
  },
  [85031162] = {
    id = 85031162,
    name = "我从小就听过许多关于深渊的故事，但是当自己真的来到深渊后，才切实感受到了它的存在。虽说这里和故事中存在巨大的差异，但并没有改变我对它的憧憬。"
  },
  [85041161] = {
    id = 85041161,
    name = "我并没有那么多远大的理想，能拥有现在这样可以帮上大家的力量，我就已经很满足了。"
  },
  [85041162] = {
    id = 85041162,
    name = "属于我的“重要收获”，一定会是我们所共同期待的存在，就由我们一起去追寻它的所在吧。"
  },
  [85041163] = {
    id = 85041163,
    name = "家人和同伴才是我最重要的存在，我所做的一切，都是为了能更好的守护自己重要的人。"
  },
  [85051161] = {
    id = 85051161,
    name = "战斗之外的事我也还算自信，如果有需要可以随时交给我。"
  },
  [85051162] = {
    id = 85051162,
    name = "我对魔法只能算得上略有研究，还称不上优秀，这也是我到深渊历练的目的之一。"
  },
  [85051163] = {
    id = 85051163,
    name = "深渊世界虽然残酷，但我们拥有可以彼此信任的同伴，因此也会有很多温柔和期待。"
  },
  [85051164] = {
    id = 85051164,
    name = "对我来说，盲目追求力量，并不是一个明智的决定，只有适合自己的，才算是最好的。"
  },
  [85051165] = {
    id = 85051165,
    name = "我所寻找的“重要收获”已经有了一些眉目——是什么？呵呵，现在还不能说，等我得到它的时候再告诉你。"
  },
  [85061161] = {
    id = 85061161,
    name = "大家一起努力吧~"
  },
  [85071161] = {id = 85071161, name = "嗯哼~"},
  [85071162] = {id = 85071162, name = "嘿——"},
  [85071163] = {id = 85071163, name = "呀啊~"},
  [85081161] = {
    id = 85081161,
    name = "交给我。"
  },
  [85081162] = {
    id = 85081162,
    name = "要认真咯~"
  },
  [85091161] = {
    id = 85091161,
    name = "大家的身后，由我来守护。"
  },
  [85091162] = {
    id = 85091162,
    name = "不必担心，我会处理好一切。"
  },
  [85101161] = {id = 85101161, name = "呃啊。"},
  [85101162] = {id = 85101162, name = "哈啊。"},
  [85101163] = {id = 85101163, name = "呃唔。"},
  [85111161] = {
    id = 85111161,
    name = "请别分心，我还可以继续。"
  },
  [85121161] = {
    id = 85121161,
    name = "果然......还是会有遗憾......"
  },
  [85131161] = {
    id = 85131161,
    name = "能和大家彼此信任、彼此依靠，胜利必然会属于我们。"
  },
  [85141161] = {
    id = 85141161,
    name = "敌人的情况超出预料太多，大家带好受伤的同伴，马上撤退。"
  },
  [85151161] = {
    id = 85151161,
    name = "今天是您的生日吧？呵呵~作为您的成员，掌握这种基本信息还是很有必要的。我准备了一些蛋糕和美食，一起来享用吧~"
  },
  [85161161] = {
    id = 85161161,
    name = "新年快乐~今天有什么安排呢？交给我？呵呵，那、我们去附近的圈落逛逛吧，听说那里有话剧和杂技表演，而且还有许多美味的小吃。"
  },
  [85171161] = {
    id = 85171161,
    name = "来，请先把眼睛闭上。张嘴——好吃么？呵呵，这是我自己做的甜点，您能喜欢真是太好了！在深渊中能遇到您是我最幸运的事，今后还请继续关照。"
  },
  [85181161] = {
    id = 85181161,
    name = "除了少数重要事务，其余杂务我已经处理完毕，今天好好享受庆典吧。节日装扮和糖果我也准备好了，随时可以出发。"
  },
  [85191161] = {
    id = 85191161,
    name = "大家的礼物我已经准备好了，请确认一下吧。多了一份？不，一份也没多。要现在打开吗，还是等大家的礼物分发完成之后......？"
  },
  [85011171] = {
    id = 85011171,
    name = "贵安~我是芙蕾雅，一名刚踏上旅途的探险者，非常开心能收到您的邀请，今后还请多多关照。"
  },
  [85021171] = {
    id = 85021171,
    name = "要来一点红茶吗？呵呵~这可是我在上一个圈落中发现的顶级红茶，虽说无法和地面相比，但在深渊中能有这样的品质，已经非常不错了。"
  },
  [85031171] = {
    id = 85031171,
    name = "如果没有经历那场变故，我也无法成为探险者，也不会对世间的人情冷暖有那么深刻的体会......呵呵~有时候，挫折和苦难或许并非全是坏事。"
  },
  [85031172] = {
    id = 85031172,
    name = "大家的过去或许差异很大，但我们现在的身份却并无不同。我们都在这深渊中追逐着自己想要的一切，或是财富，或是名望，亦或者......只是对未知的憧憬。"
  },
  [85041171] = {
    id = 85041171,
    name = "无论面对怎样的挫折苦难，只要能坚持、不放弃，必然会有新的收获。"
  },
  [85041172] = {
    id = 85041172,
    name = "我成为探险者的过程并不顺利，所以才要更珍惜这来之不易的机会，为理想全力以赴！"
  },
  [85041173] = {
    id = 85041173,
    name = "我的理想并没有那么崇高，只是在追寻深渊中的未知，追寻属于自己的未来。"
  },
  [85051171] = {
    id = 85051171,
    name = "我曾拥有令无数人仰慕的生活，可是在那样的生活中，我并没有找到自己想要的一切。"
  },
  [85051172] = {
    id = 85051172,
    name = "当我第一次知晓深渊时便对它充满了向往......不过，身为贵族的父母并不愿意他们的女儿憧憬那样危险、混乱的世界。"
  },
  [85051173] = {
    id = 85051173,
    name = "从决定成为探险者的那一刻起，我便告诉自己，无论前路多么曲折艰险，也绝对不要放弃。"
  },
  [85051174] = {
    id = 85051174,
    name = "深渊中不仅有美好和神秘，也有凶险和危机，没人知道下一刻会有怎样的际遇。也正因为如此，它才更令人着迷。"
  },
  [85051175] = {
    id = 85051175,
    name = "前行的路上必然还有许多风浪，但只要能克服恐惧，胸怀希望，无论怎样的逆境都只会让我们更加强大。"
  },
  [85061171] = {
    id = 85061171,
    name = "我的弓弦已调整完毕。"
  },
  [85071171] = {
    id = 85071171,
    name = "瞄准——"
  },
  [85071172] = {id = 85071172, name = "嘿！"},
  [85071173] = {id = 85071173, name = "哈！"},
  [85081171] = {
    id = 85081171,
    name = "没有后退可言。"
  },
  [85081172] = {
    id = 85081172,
    name = "全神贯注。"
  },
  [85091171] = {
    id = 85091171,
    name = "箭啊，见证我的意志吧！"
  },
  [85091172] = {
    id = 85091172,
    name = "由我来开辟前行之路。"
  },
  [85101171] = {id = 85101171, name = "呃啊。"},
  [85101172] = {id = 85101172, name = "呃。"},
  [85101173] = {id = 85101173, name = "呀啊。"},
  [85111171] = {
    id = 85111171,
    name = "只是一点小麻烦而已。"
  },
  [85121171] = {
    id = 85121171,
    name = "我的决心......还不够吗......"
  },
  [85131171] = {
    id = 85131171,
    name = "我的弓弦正在鸣奏胜利的乐章。"
  },
  [85141171] = {
    id = 85141171,
    name = "如此剧烈的风雨，连弓弦也忍不住悲泣。"
  },
  [85151171] = {
    id = 85151171,
    name = "生日是非常特别的日子，不管怎样都应该好好庆祝才行！嗯......做个蛋糕吧！——请放心，我小时候专门修行过厨艺，这方面还是很有自信的。"
  },
  [85161171] = {
    id = 85161171,
    name = "曾经，新年对我而言意味着数不清的宴会、数不清的应酬，现在终于不用再勉强自己去做那些事了。今天我们就按照自己喜欢的方式来庆祝吧！"
  },
  [85171171] = {
    id = 85171171,
    name = "只有这样的日子，才会觉得以前的厨艺修行有了用处，呵呵~这是我故乡风味的甜点，请试试吧，希望能合您的口味。"
  },
  [85181171] = {
    id = 85181171,
    name = "巡游的人群，嬉闹的孩子们......到处都好热闹。我从来不被允许参加这样的庆典，如此难得的机会，我不想错过，可以陪我去看看吗？"
  },
  [85191171] = {
    id = 85191171,
    name = "节日快乐~请问今天有时间吗？并不是什么要紧的事儿，而是我前段时间学会了一首曲子，想在今天为你演奏一下......"
  },
  [85011181] = {
    id = 85011181,
    name = "如果有什么事请尽管吩咐，只要是为了探险，我会全力以赴！——探险之外的事？我、我也会努力。"
  },
  [85021181] = {
    id = 85021181,
    name = "食物、淡水......呀，差点忘驱魔粉！这下物资都准备好了，我去通知大家，准备出发了~"
  },
  [85031181] = {
    id = 85031181,
    name = "能够像现在这样和大家一起探险，真像是做梦一样~拥有一起并肩作战的同伴、彼此分享见闻的朋友......如果是梦，真希望永远也不要醒来——啊！不好，锅里的食物！"
  },
  [85031182] = {
    id = 85031182,
    name = "“灾厄魔女”吗？次数多了，连自己也觉得似乎就是那样。如果不是遇到了大家，我根本无法走到现在。"
  },
  [85041181] = {
    id = 85041181,
    name = "利用深渊的力量成长这种事情竟然是真的，深渊中还有多少令人无法想象的事情呢。"
  },
  [85041182] = {
    id = 85041182,
    name = "我想和大家一起去往更多的地方，了解深渊更多的秘密，必须继续努力才行。"
  },
  [85041183] = {
    id = 85041183,
    name = "拥有了更强的力量，便也拥有了更多的责任，我已经做好了觉悟！"
  },
  [85051181] = {
    id = 85051181,
    name = "对我而言，这次机会来之不易，我一定会好好珍惜。"
  },
  [85051182] = {
    id = 85051182,
    name = "和大家成为同伴，我觉得非常开心，我会努力做的更好，不成为大家的拖累。"
  },
  [85051183] = {
    id = 85051183,
    name = "我现在已经可以做到许多以前无法做到的事了，但还不够，前路还有许多危险，不能就此懈怠。"
  },
  [85051184] = {
    id = 85051184,
    name = "不管做什么事，都要竭尽全力，就算，就算结果不一定让人满意，但至少也要让自己不会后悔。"
  },
  [85051185] = {
    id = 85051185,
    name = "书上说“宽容和善良，才是深渊中最强大的力量”，我一直不太明白，如今经历了诸多事情，终于有些理解这句话了。"
  },
  [85061181] = {
    id = 85061181,
    name = "今天会遇到些什么呢。"
  },
  [85071181] = {id = 85071181, name = "嘿咿。"},
  [85071182] = {id = 85071182, name = "哈啊~"},
  [85071183] = {id = 85071183, name = "哈！"},
  [85081181] = {
    id = 85081181,
    name = "一起努力吧。"
  },
  [85081182] = {
    id = 85081182,
    name = "全力以赴。"
  },
  [85091181] = {
    id = 85091181,
    name = "支援的事情，请交给我吧。"
  },
  [85091182] = {
    id = 85091182,
    name = "我会守护好大家的身后。"
  },
  [85101181] = {id = 85101181, name = "呃。"},
  [85101182] = {id = 85101182, name = "呃啊。"},
  [85101183] = {id = 85101183, name = "唔呃。"},
  [85111181] = {
    id = 85111181,
    name = "没，没关系，我还可以——"
  },
  [85121181] = {
    id = 85121181,
    name = "唔，如果我能再强一点......"
  },
  [85131181] = {
    id = 85131181,
    name = "大家彼此的信任，是战斗胜利的关键。"
  },
  [85141181] = {
    id = 85141181,
    name = "对不起......果然，我只会带来不幸......"
  },
  [85151181] = {
    id = 85151181,
    name = "老师，生日快乐！呵呵~大家已经准备好了蛋糕和派对，一起去庆祝吧！"
  },
  [85161181] = {
    id = 85161181,
    name = "以前新年的时候，只有蒂娜和我一起，现在能和这么多同伴一起度过，是我从未想过的事。"
  },
  [85171181] = {
    id = 85171181,
    name = "老，老师，这个——这是我自己做的一些甜点，希望老师可以喜欢。谢谢您长久以来的关照，希望您今后，也可以继续关照我。"
  },
  [85181181] = {
    id = 85181181,
    name = "今天好热闹啊，小莎她们等不及已经先出去玩了，我们也赶紧去看看吧，似乎会有很多有趣的事物。"
  },
  [85191181] = {
    id = 85191181,
    name = "以前在孤儿院的时候，每到这样的日子，院长都会组织大家一起坐在火炉旁聊天，分享故事，真是怀念啊。"
  },
  [85011191] = {
    id = 85011191,
    name = "听说这里管吃管住？哈哈，开玩笑的。我是茉莉，很高兴能收到你的邀请，今后请多多关照啦，要真的关照我哦！"
  },
  [85021191] = {
    id = 85021191,
    name = "这段时间好像没什么事，请不用担心，就算真发生了什么我们所不知道的情况，担心也没用。"
  },
  [85031191] = {
    id = 85031191,
    name = "和大家比起来我的实力并不算顶尖，但在深渊中生存的能力，我可不会输给任何人。就算什么也不带，把我扔在外面几个月也不会有问题——嗯？你那是什么眼神，不会真的有这个打算吧？！"
  },
  [85031192] = {
    id = 85031192,
    name = "呼~最近运气真差啊，探险没捞到什么好处就算了，玩游戏也一直在输，得想想办法才行......前些天好像碰到圣歌的人来着，对！去找她们帮忙！"
  },
  [85041191] = {
    id = 85041191,
    name = "竟然真的对我如此关照......这太令人意外，不，太令人感动了！"
  },
  [85041192] = {
    id = 85041192,
    name = "今后我们就是一条船上的同伴了，有福同享，有难同当！"
  },
  [85041193] = {
    id = 85041193,
    name = "没想到我也可以走到这一步，哈哈哈~属于探险者茉莉的时代，就要到来了！"
  },
  [85051191] = {
    id = 85051191,
    name = "老头经常对我说，人无法改变自己的天赋，但可以决定自己努力的程度。"
  },
  [85051192] = {
    id = 85051192,
    name = "不管有怎样的心愿和梦想，只有先想办法活下来，才有可能实现。"
  },
  [85051193] = {
    id = 85051193,
    name = "我所掌握的技巧只能算是些小聪明，想要在深渊活下去，还是得掌握更多能力才行。"
  },
  [85051194] = {
    id = 85051194,
    name = "我所做的一切，都是为了有一天能有足够的力量选择自己的道路。"
  },
  [85051195] = {
    id = 85051195,
    name = "老头希望我能过上安稳的生活，那种生活一定会非常舒适！可是现在的我，更喜欢现在这种和大家一起探险的日子。"
  },
  [85061191] = {
    id = 85061191,
    name = "希望幸运女神多多关照。"
  },
  [85071191] = {id = 85071191, name = "呀！"},
  [85071192] = {id = 85071192, name = "豁！"},
  [85071193] = {id = 85071193, name = "嘿！"},
  [85081191] = {
    id = 85081191,
    name = "别太得意了。"
  },
  [85081192] = {
    id = 85081192,
    name = "尽管来吧！"
  },
  [85091191] = {
    id = 85091191,
    name = "活着才能创造奇迹。"
  },
  [85091192] = {
    id = 85091192,
    name = "运气也是生存的关键。"
  },
  [85101191] = {id = 85101191, name = "呃。"},
  [85101192] = {id = 85101192, name = "唔呃。"},
  [85101193] = {id = 85101193, name = "呃啊。"},
  [85111191] = {
    id = 85111191,
    name = "好险，差点就完蛋了。"
  },
  [85121191] = {
    id = 85121191,
    name = "可恶，运气......真差啊......"
  },
  [85131191] = {
    id = 85131191,
    name = "哇哦，收获满满！这次运气不错嘛~"
  },
  [85141191] = {
    id = 85141191,
    name = "啊，运气真差~不过空手而归也是探险者的常态，只要我们还活着，就还有希望。"
  },
  [85151191] = {
    id = 85151191,
    name = "今天是你的生日？哇哦，那可得好好庆祝一下。我去做个蛋糕吧，虽然味道不一定能让你满意，但外形肯定没问题！哈哈哈~"
  },
  [85161191] = {
    id = 85161191,
    name = "欸？！今天居然没有行动？！呜呜呜，感谢幸运女神。以前我可没有休息的日子，就算是新年，也得为了食物奔波，不然就得饿肚子了。"
  },
  [85171191] = {
    id = 85171191,
    name = "初次见面说多多关照时，还以为只会是客套话，每想竟然真的对我这么关照。能和你相遇，来到这里，难道说.......我运气其实很不错？"
  },
  [85181191] = {
    id = 85181191,
    name = "儿时的我总会尽最大的努力去讨要糖果，然后小心翼翼的存放起来，之后再慢慢享用......呵呵，对那时候的我来说，糖果可是难得的珍品。"
  },
  [85191191] = {
    id = 85191191,
    name = "派发礼物的人选定好了吗？——有了？！那，那驯鹿呢？扮演驯鹿我也可以！只要能感受派发礼物的乐趣，就算扮演一棵树我也没问题！"
  },
  [85011201] = {
    id = 85011201,
    name = "本小姐便是莱娜，未来会名扬深渊的传奇探险者，把所有和传说相关的事都交给本小姐吧。"
  },
  [85021201] = {
    id = 85021201,
    name = "今天的计划会不会太简单了，完全没有能体现出传奇探险者实力的行动，把难度再提高两倍！"
  },
  [85031201] = {
    id = 85031201,
    name = "父母也好，身边的其他人也好，总是喜欢将我当作小孩子看待。哼，总有一天，我会证明自己，成为让谁也不敢小看的传奇探险者。"
  },
  [85031202] = {
    id = 85031202,
    name = "比起所谓的财富，深渊的混沌和未知才是本小姐所看重的，在这种世界，才有更大的可能缔造只属于本小姐的传说。"
  },
  [85041201] = {
    id = 85041201,
    name = "只要有缔造传说的可能，任何机会都不能错过。"
  },
  [85041202] = {
    id = 85041202,
    name = "只不过达到这种程度而已，比起我的目标，还差得远呢。"
  },
  [85041203] = {
    id = 85041203,
    name = "修行的过程虽然辛苦，但比起这样的辛苦，我更不想被忽视，被敷衍。"
  },
  [85051201] = {
    id = 85051201,
    name = "本小姐的未来只能由我自己选择，不需要任何人安排。"
  },
  [85051202] = {
    id = 85051202,
    name = "努力不一定会有收获，但不努力，一定什么也做不到！"
  },
  [85051203] = {
    id = 85051203,
    name = "那种努力后却得不到认可的心情，普通人是不会明白的，当然，本小姐也不需要人理解。"
  },
  [85051204] = {
    id = 85051204,
    name = "缔造传说，就必须做到普通人难以想象的事，为此付出一切我也愿意。"
  },
  [85051205] = {
    id = 85051205,
    name = "如果无法得到认可，不管多么优秀的表现都没有意义。可是，如果能得到认可，哪怕只有一个人，也会完全不同。"
  },
  [85061201] = {
    id = 85061201,
    name = "对手不会太弱吧？"
  },
  [85071201] = {id = 85071201, name = "嘿呀！"},
  [85071202] = {id = 85071202, name = "喝啊！"},
  [85071203] = {id = 85071203, name = "嘿咦！"},
  [85081201] = {
    id = 85081201,
    name = "愚蠢的家伙。"
  },
  [85081202] = {
    id = 85081202,
    name = "受死吧！"
  },
  [85091201] = {
    id = 85091201,
    name = "敢招惹本小姐，你们完了！"
  },
  [85091202] = {
    id = 85091202,
    name = "别以为可以逃掉！"
  },
  [85101201] = {id = 85101201, name = "呃。"},
  [85101202] = {id = 85101202, name = "呃啊。"},
  [85101203] = {id = 85101203, name = "诶呀。"},
  [85111201] = {
    id = 85111201,
    name = "哼，果然是个势均力敌的对手。"
  },
  [85121201] = {
    id = 85121201,
    name = "可恶......呜呜......可恶！"
  },
  [85131201] = {
    id = 85131201,
    name = "赢了！咳~这种程度的对手，还是无法体现本小姐真正的实力。"
  },
  [85141201] = {
    id = 85141201,
    name = "为什么会这么强......就算传奇探险者也不一定能应付。"
  },
  [85151201] = {
    id = 85151201,
    name = "诶？你说今天是你的生日？唔...嘛...看在你平时帮了本小姐不少忙的份上，今天的行动结束后，本小姐陪你去圈落中逛逛吧。"
  },
  [85161201] = {
    id = 85161201,
    name = "又过去了一年，无论实力还是阅历都有了不少收获，可是却并未做到任何能被称为传说的事......这样的我，在父母看来，依然还是个胡闹的小孩子吧......"
  },
  [85171201] = {
    id = 85171201,
    name = "本小姐知道今天是代表感谢的日子，但那又如何，和其他人做一样的事？不，本小姐从来就不会遵循常理，也不会做那些无趣又雷同的事。"
  },
  [85181201] = {
    id = 85181201,
    name = "竟然还在期待今天这种节日，真是幼稚，明明还有那么多的事要做，还特意去准备衣服......算了，既然都准备好了，那就去看看吧。"
  },
  [85191201] = {
    id = 85191201,
    name = "大人物的每一天都很充实，是没有心思考虑这种节日的。不过我还算不上大人物，所有趁现在享受一下节日也是理所当然的事。"
  },
  [85011211] = {
    id = 85011211,
    name = "您好，我是新加入探险队的赛格丽特，叫我丽特就行，如果有能帮上忙的地方请尽管吩咐。"
  },
  [85021211] = {
    id = 85021211,
    name = "关于接下来的行程，我有几个地方想再确认一下，请问现在有时间吗？放心，不会耽误太久。"
  },
  [85031211] = {
    id = 85031211,
    name = "每个人都在深渊追逐自己所渴求的存在，财富、名望、力量......我也不例外，不过在这些事物之外，我更想找到属于自己的未来。"
  },
  [85031212] = {
    id = 85031212,
    name = "来到深渊后我才发现这个世界远比想象中危险，为此我曾一度感到畏惧。多亏了同伴们的支持和鼓励，我才有了继续前行的勇气。"
  },
  [85041211] = {
    id = 85041211,
    name = "这是、对我的认可吗？谢谢，我会好好利用这份力量。"
  },
  [85041212] = {
    id = 85041212,
    name = "我能清晰感觉到自己力量的变化，但要实现我们的目标，只是这样还远远不够。"
  },
  [85041213] = {
    id = 85041213,
    name = "无论前路有多么危险我也不会畏惧，我会用自己的力量、开辟属于自己的未来！"
  },
  [85051211] = {
    id = 85051211,
    name = "最初我一直在各地游历，寻找历练自己的机会，然而收获并不理想。直到成为探险者之后，在一场场生死战斗中，我才感觉到了明显的进步。"
  },
  [85051212] = {
    id = 85051212,
    name = "深渊中的危险远比人们所描述的更加可怕，但和大家在一起，我便觉得无论怎样的危险也无需畏惧。"
  },
  [85051213] = {
    id = 85051213,
    name = "我在探险者学院学习的时间并不算长，但那段经历，在我成为探险者后发挥了极大的作用。"
  },
  [85051214] = {
    id = 85051214,
    name = "一个人无论怎样强大，所能做到的事终究有限，在这个变幻莫测的深渊中，值得信赖的同伴才是最可靠的存在。"
  },
  [85051215] = {
    id = 85051215,
    name = "多亏了大家的帮助我才能走到现在，我也想有足够的力量帮助大家，成为大家最可靠的同伴。"
  },
  [85061211] = {
    id = 85061211,
    name = "我会守护好所有同伴。"
  },
  [85071211] = {id = 85071211, name = "嘿！"},
  [85071212] = {id = 85071212, name = "呀啊！"},
  [85071213] = {id = 85071213, name = "哈！"},
  [85081211] = {
    id = 85081211,
    name = "决不后退。"
  },
  [85081212] = {
    id = 85081212,
    name = "到此为止。"
  },
  [85091211] = {
    id = 85091211,
    name = "我会清除所有阻碍！"
  },
  [85091212] = {
    id = 85091212,
    name = "见识一下我的意志吧！"
  },
  [85101211] = {id = 85101211, name = "唔啊。"},
  [85101212] = {id = 85101212, name = "欸~"},
  [85101213] = {id = 85101213, name = "呃啊。"},
  [85111211] = {
    id = 85111211,
    name = "你们、挡不住我。"
  },
  [85121211] = {
    id = 85121211,
    name = "如果......能再强一点......"
  },
  [85131211] = {
    id = 85131211,
    name = "大家都没事吧？全员平安才能算得上完美的胜利。"
  },
  [85141211] = {
    id = 85141211,
    name = "在大家安全撤离之前，我绝不会倒下。"
  },
  [85151211] = {
    id = 85151211,
    name = "今天是你的生日吗？不是？请不要拿这种事开玩笑，万一搞错了会很让人困扰的......我做了一个蛋糕，来试试吧~以前在家时，父母和弟弟的生日都是我在准备，口味应该不会太差。"
  },
  [85161211] = {
    id = 85161211,
    name = "据说在新年这天，如果能和家人朋友一起度过，接下来的一年一定会非常幸福。今天不管做任何事，我们都一起吧！"
  },
  [85171211] = {
    id = 85171211,
    name = "请问现在有空吗，来试试这个点心怎么样？——这是小时候母亲教我做的，她说在这种节日里，最美味的点心才能传达最真诚的谢意。"
  },
  [85181211] = {
    id = 85181211,
    name = "这会儿到处都在举办庆典，似乎很有趣的样子。一起去逛逛吗？我自从离家后便很少参加这种节日庆典，也不知道深渊中的庆典和地面有怎样的差别~"
  },
  [85191211] = {
    id = 85191211,
    name = "我离家之后，每年这个时候都会往家中寄送一些我现在所在地的特产，让父母知道我平安的同时也为他们送上一些特别的节日礼物。不知道今年的礼物有没有准时送到......"
  },
  [85011221] = {
    id = 85011221,
    name = "我是艾米莉，虽然还算不上精英探险者，但你可以提前以精英探险者的标准来要求我，我，我一定可以做到。"
  },
  [85021221] = {
    id = 85021221,
    name = "要成为精英探险者没有那么容易，我们今天也继续寻找线索吧。"
  },
  [85031221] = {
    id = 85031221,
    name = "现在的我确实没什么名气，但是，但是这只是现在而已！只要再给我一点时间，我一定能取得令人瞩目的功绩，让艾米莉这个名字传遍深渊。"
  },
  [85031222] = {
    id = 85031222,
    name = "故事里的主人公能遇到很多可以提升实力，获得名望的事件。我到深渊也有不短的时间了，却什么也没遇上，难道有什么地方弄错了？"
  },
  [85041221] = {
    id = 85041221,
    name = "这、这是精英探险者也会经历的吧？哈~哈~我准备好了。"
  },
  [85041222] = {
    id = 85041222,
    name = "我不是任何人的累赘，也不是任何麻烦和附庸，我是艾米莉，探险者艾米莉。"
  },
  [85041223] = {
    id = 85041223,
    name = "那些精英探险者所能做到的事，我也可以做到了，探险者艾米莉的传说，从现在开始！"
  },
  [85051221] = {
    id = 85051221,
    name = "只要是能提升实力的事，不管有多辛苦，我也一定会做好！"
  },
  [85051222] = {
    id = 85051222,
    name = "我所付出的一切努力，都是为了追上，不，是为了超越姐姐！"
  },
  [85051223] = {
    id = 85051223,
    name = "在某些人看来，我就是一个什么也做不好的笨蛋......哼，我才不会在意！我一定会取得成绩，让所有人刮目相看的。"
  },
  [85051224] = {
    id = 85051224,
    name = "有时候我会怀疑自己所做的一切是否有意义，怀疑成为精英探险者是否真的是我想要的未来。"
  },
  [85051225] = {
    id = 85051225,
    name = "每个成就非凡的探险者都有自己的道路，而非一味的追逐，可我却一直在追逐姐姐的背影......我终于明白了，现在开始我要全力寻找属于自己的未来。"
  },
  [85061221] = {
    id = 85061221,
    name = "希望这次能有特别的收获。"
  },
  [85071221] = {id = 85071221, name = "嘿！"},
  [85071222] = {id = 85071222, name = "咿呀！"},
  [85071223] = {id = 85071223, name = "哈！"},
  [85081221] = {
    id = 85081221,
    name = "别小看我。"
  },
  [85081222] = {
    id = 85081222,
    name = "放弃挣扎吧。"
  },
  [85091221] = {
    id = 85091221,
    name = "我也有值得信任的同伴。"
  },
  [85091222] = {
    id = 85091222,
    name = "胜利一定会属于我们。"
  },
  [85101221] = {id = 85101221, name = "诶~"},
  [85101222] = {id = 85101222, name = "诶呀。"},
  [85101223] = {id = 85101223, name = "呜哇~"},
  [85111221] = {
    id = 85111221,
    name = "精英探险者，绝不会轻易放弃。"
  },
  [85121221] = {
    id = 85121221,
    name = "果然......我什么也做不到......"
  },
  [85131221] = {
    id = 85131221,
    name = "这样的胜利还不足以被人传颂，得继续解决更多问题。"
  },
  [85141221] = {
    id = 85141221,
    name = "对不起......我果然就是姐姐说的那样，是个什么都做不好的笨蛋。"
  },
  [85151221] = {
    id = 85151221,
    name = "诶，今天生日？这、这么突然，什么都没有准备——就这样度过？不行。这么重要的日子，绝不能随意。请等一下，我先去做个蛋糕。"
  },
  [85161221] = {
    id = 85161221,
    name = "希望女神庇佑，今年能遇到更多提升实力、获得名望的机会......向女神祈祷是我新年时必须要做的事，受她的眷顾，这些年我虽然没有什么特别的奇遇，但一直都很顺利。"
  },
  [85171221] = {
    id = 85171221,
    name = "非常感谢你这段时间以来帮我寻找成为精英探险者的线索，如果有什么希望我做的事，请尽管开口，我、我会努力去做。"
  },
  [85181221] = {
    id = 85181221,
    name = "小时候，各种热闹的节日活动我都会和姐姐一起参加，可是稍大一些后，她就不肯再带着我，我便没有再参加过这些活动。"
  },
  [85191221] = {
    id = 85191221,
    name = "以前在这样的节日里我都会收到和姐姐一样的礼物，可是忘了从哪一次开始，我和她收到的礼物就不一样了......虽然新的礼物可能会更适合我，但是我更想要和姐姐一样的礼物。"
  },
  [85011231] = {
    id = 85011231,
    name = "我和那些普通的家伙们不一样，除了猎杀魔物的工作都不要来打扰我，不会做，也不想做。"
  },
  [85021231] = {
    id = 85021231,
    name = "来得正好，我刚把周围的魔物都清理了一遍，找个人去收拾一下现场吧。对了，建议找个胆大一点的人。"
  },
  [85031231] = {
    id = 85031231,
    name = "魔物就是魔物，无论拥有怎样的外表，怎样的性格，也无法改变它们是魔物的事实，对它们绝不能有任何怜悯。"
  },
  [85031232] = {
    id = 85031232,
    name = "冰冷的洞穴......无尽的黑暗......潮湿又灼热的空气......还有，还有母亲绝望的哭泣......只要一闭上眼，眼前就全是那个时候的场景......总有一天，我会杀尽所有魔物！所有！！"
  },
  [85041231] = {
    id = 85041231,
    name = "只要能更好的猎杀魔物，无论会付出怎样的代价，我都不会犹豫。"
  },
  [85041232] = {
    id = 85041232,
    name = "我曾差一点成为了魔物的食物，但现在，它们才是我的猎物！"
  },
  [85041233] = {
    id = 85041233,
    name = "撕碎所有的魔物，是我活着的意义，也是我现在唯一能为母亲做到的事。"
  },
  [85051231] = {
    id = 85051231,
    name = "若没有那场变故，这世上会少一个疯狂的探险者，也会少一个偏执的研究员。"
  },
  [85051232] = {
    id = 85051232,
    name = "没有比复仇更强的信念，也没有比和魔物厮杀更好的修行方式。"
  },
  [85051233] = {
    id = 85051233,
    name = "对那些侥幸从灾难中活下来的人而言，什么也做不到的活着才是最大的痛苦。"
  },
  [85051234] = {
    id = 85051234,
    name = "我准备来深渊时，父亲送给我这副他亲自打造的利爪，在这利爪上，我也能感受到他心中的痛苦与愤怒。"
  },
  [85051235] = {
    id = 85051235,
    name = "母亲大人，我错了吗？不会错的吧？在那最后一刻，你一定也希望能有人为你复仇吧......"
  },
  [85061231] = {
    id = 85061231,
    name = "我已经等不急了。"
  },
  [85071231] = {id = 85071231, name = "呀哈！"},
  [85071232] = {id = 85071232, name = "嘿咿！"},
  [85071233] = {id = 85071233, name = "哈呀！"},
  [85081231] = {id = 85081231, name = "去死！"},
  [85081232] = {
    id = 85081232,
    name = "撕碎你！"
  },
  [85091231] = {
    id = 85091231,
    name = "在恐惧中挣扎吧。"
  },
  [85091232] = {
    id = 85091232,
    name = "被撕碎是你们唯一的宿命。"
  },
  [85101231] = {id = 85101231, name = "呃啊-"},
  [85101232] = {id = 85101232, name = "呃。"},
  [85101233] = {id = 85101233, name = "呀啊。"},
  [85111231] = {
    id = 85111231,
    name = "熟悉的味道——"
  },
  [85121231] = {
    id = 85121231,
    name = "也算是......一种解脱......"
  },
  [85131231] = {
    id = 85131231,
    name = "不堪一击，这种程度的猎物可满足不了我。"
  },
  [85141231] = {
    id = 85141231,
    name = "放开我，就算死我也要拉它们一起——"
  },
  [85151231] = {
    id = 85151231,
    name = "生日？呵~也算是个难得的日子，一起去找个大猎物庆祝一下，目标随你选——领主级？我很期待，不过如果明年还想过生日，最好换一个。"
  },
  [85161231] = {
    id = 85161231,
    name = "大家都很享受新年嘛，真是不错~我也去找几个大家伙玩玩——别阻拦我，这种日子我对魔物的憎恨会更强烈，如果不宣泄出来，我也不知道自己会做出什么事情。"
  },
  [85171231] = {
    id = 85171231,
    name = "感谢的日子，呵~如果是以前，或许你可以期待一下我亲手制作的点心，但现在，我的双手都已被这利爪禁锢，除了猎杀魔物，什么也做不到。"
  },
  [85181231] = {
    id = 85181231,
    name = "大家都装扮得不错嘛，幽灵、巫师、魔物......呵呵，如果我不小心搞错了目标——不用紧张，人和魔物的气味完全不同，我只对真的魔物感兴趣。"
  },
  [85191231] = {
    id = 85191231,
    name = "自从那场变故之后，这样的节日对我而言已经失去了存在的意义......帮我找到新的意义？呵呵，说的真是轻松......好吧，今天我就和你一起，看看你能带我找到些什么。"
  },
  [85011241] = {
    id = 85011241,
    name = "在这里应该不会再遇到那个吵闹的家伙了吧？呼~我是探险者欧培拉，非常感谢你的邀请，请多多关照。"
  },
  [85021241] = {
    id = 85021241,
    name = "探险和商业运作在某些方面很相似，只不过商业运作失败后还有重来的机会，而探险......我们再确认一下近期的探险计划吧。"
  },
  [85031241] = {
    id = 85031241,
    name = "自由学派中大部分成员都是对深渊拥有自己独到见解或发现的探险者，而另外一些则是拥有特殊才能，同样渴求探索深渊秘密的人。"
  },
  [85031242] = {
    id = 85031242,
    name = "自由学派中有个吵闹的家伙，每次和她在一起的时候都觉得有些困扰，可是一段时间听不到她的声音，似乎又有些寂寞......"
  },
  [85041241] = {
    id = 85041241,
    name = "想要追寻深渊中的未知存在，力量是不可或缺的。"
  },
  [85041242] = {
    id = 85041242,
    name = "所谓的隐秘，便是隐藏于表象之下的真实，只有足够的严谨和细致，才能看穿那些虚伪的表象。"
  },
  [85041243] = {
    id = 85041243,
    name = "只要确定了自己的目标，就应该为之付出全力，即便最终无法实现，也绝不会因此后悔。"
  },
  [85051241] = {
    id = 85051241,
    name = "成为探险者其实并非我最初的目标，不过现在看来比起继承家族事务，还是探险者更加适合我。"
  },
  [85051242] = {
    id = 85051242,
    name = "想要追寻深渊中的未知总会遇到很多困难，虚假的情报、险恶的环境，以及凶猛的魔物。"
  },
  [85051243] = {
    id = 85051243,
    name = "同伴们在分享见闻时，偶尔也会发生争执。但无论有怎样的分歧，大家也都会在分享结束后和好。"
  },
  [85051244] = {
    id = 85051244,
    name = "力量只是追寻未知的手段，而智慧才是揭晓未知的钥匙，只有同时具备力量和智慧的人，才能发现这个世界更多的真相。"
  },
  [85051245] = {
    id = 85051245,
    name = "深渊中所隐藏的未知还有很多，无论如何追寻也难以全部揭晓，所以比起最终的答案，享受追寻的过程会更加重要。"
  },
  [85061241] = {
    id = 85061241,
    name = "准备完成，随时可以出发。"
  },
  [85071241] = {id = 85071241, name = "呵！"},
  [85071242] = {id = 85071242, name = "嘿咿！"},
  [85071243] = {id = 85071243, name = "呀啊！"},
  [85081241] = {
    id = 85081241,
    name = "别乱动。"
  },
  [85081242] = {
    id = 85081242,
    name = "瞄准——"
  },
  [85091241] = {
    id = 85091241,
    name = "能比我的箭更快吗？"
  },
  [85091242] = {
    id = 85091242,
    name = "绝不会让任何敌人逃掉。"
  },
  [85101241] = {id = 85101241, name = "唔。"},
  [85101242] = {id = 85101242, name = "呀~"},
  [85101243] = {id = 85101243, name = "呃啊。"},
  [85111241] = {
    id = 85111241,
    name = "没事，这种麻烦不算什么。"
  },
  [85121241] = {
    id = 85121241,
    name = "侥幸，终会断送一切......"
  },
  [85131241] = {
    id = 85131241,
    name = "人员、物资......战损、缴获......嗯，怎么算都是一场完胜。"
  },
  [85141241] = {
    id = 85141241,
    name = "如此周全的准备，也无法应付那些狡猾的对手吗......"
  },
  [85151241] = {
    id = 85151241,
    name = "探险途中确实无法安心享受生日，但这绝不是随便对待的理由。我会想办法筹备一些物资，今天的行动结束后一起庆祝吧。"
  },
  [85161241] = {
    id = 85161241,
    name = "学派每年都会举办“新年茶会”，这天往往是人最多、也是情报交流最多的一天，一起去吗？我觉得应该能收获一些你感兴趣的线索。"
  },
  [85171241] = {
    id = 85171241,
    name = "这束花好看吗？这是我一早去采摘的，据说这种花的花语代表着最真挚的感谢......它和今天的节日很相配吧？送给你。谢谢你长久以来的关照！"
  },
  [85181241] = {
    id = 85181241,
    name = "小时候，我虽然渴望参加这样热闹的庆典，但是为了追上姐姐我会放弃所有休息时间。现在和大家一起尽情享受节日后，我才发现自己错过了多少美好和快乐，今后我要更加努力找回来才行!"
  },
  [85191241] = {
    id = 85191241,
    name = "这种节日里，自由学派的成员也会相互交换礼物，不过我主要负责礼物的接收和寄送，确认参与人员之类的工作则是由其他人在负责。"
  },
  [85011251] = {
    id = 85011251,
    name = "你好，我是卡莎，那个，该怎么说呢......我想成为一名优秀的探险者，为此做什么都行，大概，大概就是这样。"
  },
  [85021251] = {
    id = 85021251,
    name = "..... .....你，你好。\n（呼~说出来了！今天有好好打招呼，离优秀的探险者又近了一步）"
  },
  [85031251] = {
    id = 85031251,
    name = "差不多到“冥想”的时间了，这是祖父传授我的一种修行方式，可以摒除杂念，集中注意力。\n（应该没说错话吧，好紧张......）"
  },
  [85031252] = {
    id = 85031252,
    name = "呵呵呵~今天的天气不错嘛。\n（祖父说如果找不到话题可以先谈论天气，可是为什么感觉更尴尬了？——啊，天气这么糟，惨了，我都在说些什么......）"
  },
  [85041251] = {
    id = 85041251,
    name = "我的父辈都是优秀的探险者，所以我也想要成为和他们一样优秀的人。"
  },
  [85041252] = {
    id = 85041252,
    name = "握住这柄长枪，我便能感受到无比的平静，心中也不会再有任何犹豫和畏惧。"
  },
  [85041253] = {
    id = 85041253,
    name = "我能感受到自己的成长，可是除了力量，不管哪方面，距离优秀都还有很大的差距。"
  },
  [85051251] = {
    id = 85051251,
    name = "（快想快想，这种时候该说点什么，唔，之前明明想好了。快说点什么吧，什么都行，不然好尴尬，啊，感谢，感谢的话）\n......谢谢。"
  },
  [85051252] = {
    id = 85051252,
    name = "这种程度的进步根本不算什么。\n（被夸奖时应该这样说吧......为什么大家说话可以那么轻松，我也好想能做到。）"
  },
  [85051253] = {
    id = 85051253,
    name = "不管做事、说话之前都要考虑清楚，这样才算得上优秀。\n（终于把自己的想法说出来了，呼~）"
  },
  [85051254] = {
    id = 85051254,
    name = "我想和大家一样，能从容不迫的战斗、交流......可是我只有在战斗时，才能不去考虑多余的事情。\n（感觉已经没有那么紧张了，加油。）"
  },
  [85051255] = {
    id = 85051255,
    name = "其实，我并非不想或不愿搭理别人，只是，常常在我想好要怎么表达后，一切都已经结束了。"
  },
  [85061251] = {
    id = 85061251,
    name = "如果只是战斗的话......我能做到。"
  },
  [85071251] = {id = 85071251, name = "嘿！"},
  [85071252] = {id = 85071252, name = "呀！"},
  [85071253] = {id = 85071253, name = "哈！"},
  [85081251] = {
    id = 85081251,
    name = "逃不掉！"
  },
  [85081252] = {
    id = 85081252,
    name = "有破绽！"
  },
  [85091251] = {
    id = 85091251,
    name = "已经没有机会了。"
  },
  [85091252] = {
    id = 85091252,
    name = "于此，献上终结。"
  },
  [85101251] = {id = 85101251, name = "呃。"},
  [85101252] = {id = 85101252, name = "呃啊~"},
  [85101253] = {id = 85101253, name = "唔呃-"},
  [85111251] = {
    id = 85111251,
    name = "别想干扰我。"
  },
  [85121251] = {
    id = 85121251,
    name = "......我似乎，分心了。"
  },
  [85131251] = {
    id = 85131251,
    name = "战斗不就是握紧武器，然后将一切交给身体么？\n（大家的表情好奇怪，难道我说了奇怪的话么？）"
  },
  [85141251] = {
    id = 85141251,
    name = "考虑得太多，反而没能把握好机会......"
  },
  [85151251] = {
    id = 85151251,
    name = "生日快乐。\n（很轻松就把礼物送出去了，做的不错！不过会不会说的太简单了......嗯，嗯，毕竟是优秀的探险者嘛，就应该是这样才对！）"
  },
  [85161251] = {
    id = 85161251,
    name = "新年快乐，今天有时间吧？不不不，那个、我的意思是如果有时间，可以一起去参加庆典吗？\n（明明已经练习了好多次，结果说出口还是紧张了......）"
  },
  [85171251] = {
    id = 85171251,
    name = "我做了些饼干，要试试么？\n（竟然真的吃了！糟了，忘了自己先尝尝，会不会太甜了，也忘了说感谢的话......完全没做好，明明准备了那么久......）"
  },
  [85181251] = {
    id = 85181251,
    name = "这种节日有些无聊啊~\n（好像说了很帅气的话！可是，好想和大家一起去巡游......但那样就没有优秀探险者的样子了，忍耐忍耐......）"
  },
  [85191251] = {
    id = 85191251,
    name = "由我来帮忙派发礼物么？\n（对优秀的探险者而言，承担派发礼物之类的工作是应该的吧，可是，要和那么多人说话......只能硬着头皮上了吗......）"
  },
  [85011261] = {
    id = 85011261,
    name = "我是杰西卡，还只能算得上新人，非常感谢您能同意我的申请，我会努力跟上大家的进度。"
  },
  [85021261] = {
    id = 85021261,
    name = "我的修行计划还差一点，今天的探险行动可以稍微等我一下吗？"
  },
  [85031261] = {
    id = 85031261,
    name = "人们常说，深渊是充满希望和奇迹的世界，每个人都能在这里找到属于自己的人生，我也可以吗......"
  },
  [85031262] = {
    id = 85031262,
    name = "我很羡慕大家能知道自己所追寻的事物，我虽然每天都在努力，但那只是为了能跟上大家行动的步伐，我其实并不明白自己这样做的意义。"
  },
  [85041261] = {
    id = 85041261,
    name = "对不起，我的进步太慢让您担心了——嗯，不是这样？"
  },
  [85041262] = {
    id = 85041262,
    name = "这样一来，我也有了独当一面的自信，请、请期待我的表现！"
  },
  [85041263] = {
    id = 85041263,
    name = "对我而言，努力是最容易做到的事，如果，如果我能找到努力的意义，或许会有更多收获。"
  },
  [85051261] = {
    id = 85051261,
    name = "其实我并不清楚成为探险者是否是自己真正的愿望，只是在什么也做不到的时候，遇到了成为探险者的契机。"
  },
  [85051262] = {
    id = 85051262,
    name = "作为探险者，我的天赋并不出众，所以只有付出比别人更多的努力，才能在这个世界生存下来。"
  },
  [85051263] = {
    id = 85051263,
    name = "战斗，是我现在唯一能做到的事，除了努力修行，我也不知道自己平时应该做些什么。"
  },
  [85051264] = {
    id = 85051264,
    name = "重复去做同一件事，我并不会觉得枯燥，只要是能通过“重复”做到的事，我都会做到极致。"
  },
  [85051265] = {
    id = 85051265,
    name = "虽然我还没有找到自己努力的意义，但我的内心告诉我现在所做的一切都是正确的，或许再有一些时日，我便能知道自己所渴求的存在。"
  },
  [85061261] = {
    id = 85061261,
    name = "让大家久等了。"
  },
  [85071261] = {id = 85071261, name = "咿呀！"},
  [85071262] = {id = 85071262, name = "哈！"},
  [85071263] = {id = 85071263, name = "嘿！"},
  [85081261] = {
    id = 85081261,
    name = "难不倒我。"
  },
  [85081262] = {
    id = 85081262,
    name = "接招吧！"
  },
  [85091261] = {
    id = 85091261,
    name = "我也有可以做到的事。"
  },
  [85091262] = {
    id = 85091262,
    name = "不会让你们逃掉！"
  },
  [85101261] = {id = 85101261, name = "嗯啊-"},
  [85101262] = {id = 85101262, name = "呃~"},
  [85101263] = {id = 85101263, name = "呃啊！"},
  [85111261] = {
    id = 85111261,
    name = "大家都在战斗，我也不能退缩！"
  },
  [85121261] = {
    id = 85121261,
    name = "什么也......做不到......"
  },
  [85131261] = {
    id = 85131261,
    name = "这段时间的努力没有白费，我也能跟上大家的步伐了。"
  },
  [85141261] = {
    id = 85141261,
    name = "我没能配合好大家的行动，抱歉......"
  },
  [85151261] = {
    id = 85151261,
    name = "请问今天是您的生日么？抱歉，我是不经意间听到的。那个，祝您生日快乐，我准备了一份生日礼物，希望您能喜欢。"
  },
  [85161261] = {
    id = 85161261,
    name = "对我来说，新年意味着全新的开始，无论之前遭受了多少挫折磨难，在这一天里我都会告诉自己已经结束了，然后鼓起勇气继续前行。"
  },
  [85171261] = {
    id = 85171261,
    name = "那个，我、我做了一些点心，请问您可以收下么？我也想趁着这样的节日，做些力所能及事感谢您对我的关照。当然，我知道这远远不够，我也会更努力回应您的期待。"
  },
  [85181261] = {
    id = 85181261,
    name = "我从小就羡慕那些在节日里身着盛装参加巡游的孩子，但我家里非常贫困......所以在那种时候我会非常渴望长大，渴望有钱穿上那些新奇的节日服装。"
  },
  [85191261] = {
    id = 85191261,
    name = "探险队的事务我已经处理完毕了，您只需要确认一下就好，今天请好好享受节日吧——欸，不用休息？不，那可不行，在这么重要日子里好好休息，也是一名优秀的队长该做的事儿。"
  },
  [85011271] = {
    id = 85011271,
    name = "感到荣幸吧，最优秀的探险者姬玛接受了你的邀请，希望你的表现也能配得上我的实力。"
  },
  [85021271] = {
    id = 85021271,
    name = "接下来的这些行动都可以围绕我来布置作战计划，不管同行成员的实力如何，我都能轻松搞定。"
  },
  [85031271] = {
    id = 85031271,
    name = "我已经到过许多地方，但依然没有西比尔的消息，以她的实力不应该那么默默无名才对，难道......不！我都能轻松应对的危机，她肯定也没问题！"
  },
  [85031272] = {
    id = 85031272,
    name = "和那些传奇探险者相比，无论是名气还是功绩我现在都远远比不上，不过我还有足够的时间，只要继续努力总有一天可以超越他们！"
  },
  [85041271] = {
    id = 85041271,
    name = "不管在哪，只要有足够的实力，就能获得足够的认可和名气。"
  },
  [85041272] = {
    id = 85041272,
    name = "在亲手击败西比尔之前，我会努力变得足够优秀，成为比任何人都厉害的探险者。"
  },
  [85041273] = {
    id = 85041273,
    name = "现在的我、有自信不会输给任何人，无论怎样的传奇我都会超越给你看！"
  },
  [85051271] = {
    id = 85051271,
    name = "西比尔是我的朋、不、是我的宿敌！必须亲手击败的宿敌！我来深渊便是为了找到她，然后击败她！"
  },
  [85051272] = {
    id = 85051272,
    name = "真正强大的人根本不会在意别人的看法，我们只需要按照自己的意志行动就足够了。"
  },
  [85051273] = {
    id = 85051273,
    name = "我和西比尔第一次交手时输的很惨，但那是因为她比我修行更久，所以并不算什么。以我现在的实力，再交手我一定不会输！"
  },
  [85051274] = {
    id = 85051274,
    name = "虽然成为探险者并非我最初的理想，但只要是我自己决定事，我就一定会全力做到最好！"
  },
  [85051275] = {
    id = 85051275,
    name = "只要坚持下去，我的名字一定能很快传遍深渊！呵呵，当西比尔发现我更晚来深渊、名气却比她还大的时候，会有怎样的反应呢？"
  },
  [85061271] = {
    id = 85061271,
    name = "把最强的交给我来对付！"
  },
  [85071271] = {id = 85071271, name = "哈！"},
  [85071272] = {id = 85071272, name = "嘿呀！"},
  [85071273] = {id = 85071273, name = "呀啊！"},
  [85081271] = {
    id = 85081271,
    name = "乖乖投降吧！"
  },
  [85081272] = {
    id = 85081272,
    name = "尝尝我的厉害！"
  },
  [85091271] = {
    id = 85091271,
    name = "我可不会手下留情，觉悟吧！"
  },
  [85091272] = {
    id = 85091272,
    name = "不管怎样挣扎也没用。"
  },
  [85101271] = {id = 85101271, name = "呜哇。"},
  [85101272] = {id = 85101272, name = "唔。"},
  [85101273] = {id = 85101273, name = "呀啊。"},
  [85111271] = {
    id = 85111271,
    name = "不过如此，我、我才不会认输。"
  },
  [85121271] = {
    id = 85121271,
    name = "决斗......无法完成了吗......"
  },
  [85131271] = {
    id = 85131271,
    name = "都是些不堪一击的对手，能被如此优秀的探险者击败是他们的荣幸~"
  },
  [85141271] = {
    id = 85141271,
    name = "怎么会......可恶！我才不会输！不会输！"
  },
  [85151271] = {
    id = 85151271,
    name = "生日快乐！嗯，你那是什么表情？哼，别愣着了，跟我走吧。去哪？当然是去庆祝生日！我会带你去过一个最棒的生日，好好期待吧！"
  },
  [85161271] = {
    id = 85161271,
    name = "每个新年我家都会举办派对，邀请周围的邻居一起庆祝新年，西比尔也会和她父母一起来参加。不过她总是表现得很拘谨，完全没有享受派对的样子。"
  },
  [85171271] = {
    id = 85171271,
    name = "那个、这段时间谢谢你的关照，不管是寻找西比尔的线索，还是寻找提升名气的方法，你都帮了不少忙......总、总之各种事都非常感谢。"
  },
  [85181271] = {
    id = 85181271,
    name = "这种幽灵的节日为什么大家要去外面游玩，明明呆在家里才是最好的吧？！我、我并不是害怕幽灵什么的，只要有足够的实力，不管幽灵还是什么都能轻松对付！大概。"
  },
  [85191271] = {
    id = 85191271,
    name = "这个发卡是西比尔临走前送给我的，我本来准备在那年的这个时候给她回礼，结果......哼，明明礼物都准备好了......我一定会找到她，堂堂正正的打败她之后，再把礼物送出去！"
  },
  [85011281] = {
    id = 85011281,
    name = "真理之钥，岚，我和那些普通探的险者不一样，如果没有重要的事请别打扰我。"
  },
  [85021281] = {
    id = 85021281,
    name = "准备出发的时候叫我就行，我会趁这段时间再做一些练习。休息？对我而言除了必要的睡觉时间，每一分钟都不能浪费。"
  },
  [85031281] = {
    id = 85031281,
    name = "如果能实现真理之钥的目标，我自然能成为传奇人物，无论地面还是深渊，都将传颂我们的名字。进行得怎样？很、很顺利，我们只要向着目标全力以赴肯定没问题！"
  },
  [85031282] = {
    id = 85031282,
    name = "对我而言没有什么比振兴家族更重要，所以加入真理之钥并非什么兴趣......最多、最多就一点点，但那也只是为了能更快成长，成为传奇人物。"
  },
  [85041281] = {
    id = 85041281,
    name = "如果把这种提升方式公之于众或许能提升影响力......探险者的常识？我、我当然知道。"
  },
  [85041282] = {
    id = 85041282,
    name = "我绝不会错过任何提升自己的机会，只有努力变得更强，我才能以自己的方式重现家族荣光。"
  },
  [85041283] = {
    id = 85041283,
    name = "不用付出任何代价便能获得好处，这种事我完全不信，所以有什么事尽管说，我会考虑的。"
  },
  [85051281] = {
    id = 85051281,
    name = "作为淑女，应当随时注意自己的形象，而不是像某些笨蛋一样，只顾着追求快乐，什么也不管。"
  },
  [85051282] = {
    id = 85051282,
    name = "想要实现目标并不容易，现在我能做的，便是将每件事都做到极致。"
  },
  [85051283] = {
    id = 85051283,
    name = "如果没有足够坚定的信念，很容易在追寻力量的过程中迷失自我。"
  },
  [85051284] = {
    id = 85051284,
    name = "深渊确实是个凶险的世界，但也因此隔绝了许多地面上的闲言碎语，我在这里不管做什么事都能更加专注。"
  },
  [85051285] = {
    id = 85051285,
    name = "父母常说的那种“满足”我或许已经有体会了，但我并不打算停下，如果能和大家一起见证目标实现，一定会更令人“满足”。"
  },
  [85061281] = {
    id = 85061281,
    name = "练习便是为了这一刻。"
  },
  [85071281] = {id = 85071281, name = "喝！"},
  [85071282] = {id = 85071282, name = "呀啊！"},
  [85071283] = {id = 85071283, name = "嘿！"},
  [85081281] = {
    id = 85081281,
    name = "放弃抵抗吧。"
  },
  [85081282] = {
    id = 85081282,
    name = "饶不了你！"
  },
  [85091281] = {
    id = 85091281,
    name = "我的剑，绝不会犹豫！"
  },
  [85091282] = {
    id = 85091282,
    name = "现在才想逃，已经晚了。"
  },
  [85101281] = {id = 85101281, name = "咿呀。"},
  [85101282] = {id = 85101282, name = "呃。"},
  [85101283] = {id = 85101283, name = "呃啊。"},
  [85111281] = {
    id = 85111281,
    name = "不过如此，我才不会畏惧。"
  },
  [85121281] = {
    id = 85121281,
    name = "怎么会......修行还不够吗......"
  },
  [85131281] = {
    id = 85131281,
    name = "都是意料之中的结果，并没有什么值得庆祝的地方。"
  },
  [85141281] = {
    id = 85141281,
    name = "可恶，连这样的对手都解决不了，练习必须加倍才行。"
  },
  [85151281] = {
    id = 85151281,
    name = "生日是今天吧？我想再确认一下。我已经通知大家了，她们应该都快到了。做什么？当然是庆祝生日，只有我们两的话会、会有些无聊吧？"
  },
  [85161281] = {
    id = 85161281,
    name = "每逢新年我也会和朋友们一起外出逛街......怎么，很奇怪吗？虽然因此休息了一天，但也了解了很多修行之外的趣事，并不算浪费时间。"
  },
  [85171281] = {
    id = 85171281,
    name = "那、那个，点心......啊？！你怎么自顾自就吃起来了......请不要勉强装作很好吃的样子......我知道味道可能还差很多......当然，如果你能喜欢......"
  },
  [85181281] = {
    id = 85181281,
    name = "有些笨蛋又在吵着要去参加庆典了，真是头疼，身为淑女就应该时刻注意自己的形象才行啊......算了，我们也一起去吧。"
  },
  [85191281] = {
    id = 85191281,
    name = "我正在学习同伴们分享给我的修行感悟，你要看看吗？看不懂？呵呵，很正常。我也是经过了一段时间后才明白她们想要表达的内容。"
  },
  [85011291] = {
    id = 85011291,
    name = "咳、咳咳~我是来自圣歌的颂道使，就是你在邀请我吗？很好，既然明白我的优秀，那便为我准备些可以展现实力的任务吧。"
  },
  [85021291] = {
    id = 85021291,
    name = "能被大家信任是我的荣幸，但因此就让我负责准备工作未免太大材小用了吧？多给我安排些可以展现实力的事啊！"
  },
  [85031291] = {
    id = 85031291,
    name = "不管公会还是高塔，都是一群只会说大话的家伙，但圣歌不一样，我们可是真正为了改变深渊在努力。"
  },
  [85031292] = {
    id = 85031292,
    name = "别觉得我个子不高就小瞧我，我可是天才！小时候村里那么多孩子，就只有我被邀请学习魔法~成为探险者后，我刚到港口便接到了圣歌的邀请~怎么样，不管怎么看都很厉害吧？！"
  },
  [85041291] = {
    id = 85041291,
    name = "这算是对我的认可吗？嘿嘿~这样一来便能让更多人明白我的优秀了吧？"
  },
  [85041292] = {
    id = 85041292,
    name = "和那些冥顽不灵的恶徒讲道理只是浪费口舌，绝对的实力才能让他们乖乖听话。"
  },
  [85041293] = {
    id = 85041293,
    name = "如果我的努力能为这个世界带来足够的变化，那么我一定可以成为最受瞩目的存在！"
  },
  [85051291] = {
    id = 85051291,
    name = "我所肩负的是改变深渊的艰巨使命，如果不是那种可以带来深远影响的事情，我很难展现自己的全力。"
  },
  [85051292] = {
    id = 85051292,
    name = "比起其他探险者，我的打扮已经如此特别了，可还是时常被人忽视，果然还是要再长高一点才行啊......"
  },
  [85051293] = {
    id = 85051293,
    name = "和大家相比我的个子确实不高，但那不算什么，真正的强大在于思想和信念！而且，我还在成长期，只要再等一段时间一定会有所变化。"
  },
  [85051294] = {
    id = 85051294,
    name = "大家都很和善，对我也很关照，可是、可是如果能不把我当作小孩子对待就更好了。"
  },
  [85051295] = {
    id = 85051295,
    name = "我一直按照自己的正义在行动，可是却并未给深渊带来什么明显的变化。或许、今后应该改变一下思路，多做些让人无法忽略的大事才行！"
  },
  [85061291] = {
    id = 85061291,
    name = "终于有重要的事了吗？"
  },
  [85071291] = {id = 85071291, name = "喝呃！"},
  [85071292] = {id = 85071292, name = "哈！"},
  [85071293] = {id = 85071293, name = "嘿咿！"},
  [85081291] = {
    id = 85081291,
    name = "太大意了！"
  },
  [85081292] = {
    id = 85081292,
    name = "别小瞧我！"
  },
  [85091291] = {
    id = 85091291,
    name = "见识一下我真正的实力。"
  },
  [85091292] = {
    id = 85091292,
    name = "没有什么可以难倒我！"
  },
  [85101291] = {id = 85101291, name = "呀啊。"},
  [85101292] = {id = 85101292, name = "呃呃。"},
  [85101293] = {id = 85101293, name = "呜啊。"},
  [85111291] = {
    id = 85111291,
    name = "太过分了，饶不了你！"
  },
  [85121291] = {
    id = 85121291,
    name = "呜呜......我还有好多事没做......"
  },
  [85131291] = {
    id = 85131291,
    name = "怎么样，我的表现还不错吧？对付这样的敌人，根本用不着全力~"
  },
  [85141291] = {
    id = 85141291,
    name = "可恶的家伙，下次我绝不会放过他们！"
  },
  [85151291] = {
    id = 85151291,
    name = "每个人在自己生日那天，都应该成为最受瞩目的存在。今天是你生日，我会努力让所有人都关注你，尽量任何事都听你的。当然，只是今天哦！"
  },
  [85161291] = {
    id = 85161291,
    name = "我看起来有稍微长高一点吗？没变化？唉~等下参加聚会时肯定又会被说“过了这么久还是老样子”之类的......你陪我一起去吧，至少这样能让我看起来显得更成熟一些。"
  },
  [85171291] = {
    id = 85171291,
    name = "给，这是我自己做的点心。味道不错？那、那是理所当然的，我从小就跟村子里的人学了很多事情，区区点心根本算不上什么。"
  },
  [85181291] = {
    id = 85181291,
    name = "准备好一起去参加节日庆典了吗？我每次去参加都会收到许多糖果，但其他人却往往一无所获，果然我的人气已经在不知不觉中提高了很多吧？！不知道今年会有怎样的收获呢，哈哈哈~"
  },
  [85191291] = {
    id = 85191291,
    name = "每年这个节日我们都会向大家派发物资，这可是提升名气的好机会，所以今年我争取到了最多的派发数量！......可以帮帮我吗？今天要派送的数量实在太多，已经快来不及了......"
  },
  [85011301] = {
    id = 85011301,
    name = "我是荒野游骑兵的骑士、云雀，虽然还只是见习骑士，但我很强的，所以不管什么事都可以让我帮忙！"
  },
  [85021301] = {
    id = 85021301,
    name = "我们今天去哪呢，要不要去更远的地方看看？再走远一点说不定就能发现什么好地方！"
  },
  [85031301] = {
    id = 85031301,
    name = "追寻宝物的过程并不轻松，但也没有想象的那么困难。只要能保持永不放弃的信念和积极乐观的心态，一定能有所收获。"
  },
  [85031302] = {
    id = 85031302,
    name = "荒野游骑兵中的每个成员都拥有骑士长给予的骑士封号，因为我历练不够，所以目前还只是见习骑士。嗯，她什么时候才会给我封号呢，真想要一个听起来就很厉害的~"
  },
  [85041301] = {
    id = 85041301,
    name = "每一个重要时刻，都需要背诵骑士律令！律令六，绝不觊觎任何有主的宝物。"
  },
  [85041302] = {
    id = 85041302,
    name = "这样的实力提升只要再多经历几次，我就一定能拥有属于我的骑士封号！"
  },
  [85041303] = {
    id = 85041303,
    name = "同伴们曾答应我，只要我变强就告诉我一些宝物的线索，以我现在的实力应该足够了吧！"
  },
  [85051301] = {
    id = 85051301,
    name = "祖父曾说过“深渊中遍地都是宝物”，现在已经探索过的地方宝物肯定都被大家捡走了，所以得去找没人探索过地方才行。"
  },
  [85051302] = {
    id = 85051302,
    name = "如果没有足够强大的力量，即便掌握了宝物的线索也很难得到它们。"
  },
  [85051303] = {
    id = 85051303,
    name = "寻找宝物的目的？我从没想过这样的问题，是啊，为什么呢？嗯......啊，我知道了，因为那是宝物，所以才要寻找！"
  },
  [85051304] = {
    id = 85051304,
    name = "故事的主人公总是在经历危险后获得丰厚的收获，危险啊......我到目前为止遇到的最大危险也就是和骑士长相遇那次了，结果还被她轻松解决......"
  },
  [85051305] = {
    id = 85051305,
    name = "真的会有“遍地都是宝物”的地方吗......不，祖父不会骗我，现在有这么多同伴一起努力，我一定会找到那个地方！"
  },
  [85061301] = {
    id = 85061301,
    name = "见习骑士、云雀，准备完毕。"
  },
  [85071301] = {id = 85071301, name = "呀！"},
  [85071302] = {id = 85071302, name = "嘿！"},
  [85071303] = {id = 85071303, name = "呼。"},
  [85081301] = {
    id = 85081301,
    name = "站住站住。"
  },
  [85081302] = {
    id = 85081302,
    name = "瞄准了哦~"
  },
  [85091301] = {
    id = 85091301,
    name = "小看我的家伙，一个都逃不掉。"
  },
  [85091302] = {
    id = 85091302,
    name = "已经没有后悔的机会了！"
  },
  [85101301] = {id = 85101301, name = "唔啊！"},
  [85101302] = {id = 85101302, name = "啊~"},
  [85101303] = {id = 85101303, name = "呃啊！"},
  [85111301] = {
    id = 85111301,
    name = "我才不怕......呜，好痛啊！"
  },
  [85121301] = {
    id = 85121301,
    name = "呜~还没有......找到那个地方...... "
  },
  [85131301] = {
    id = 85131301,
    name = "这样的对手根本不用封号骑士出手，我就能轻松解决！"
  },
  [85141301] = {
    id = 85141301,
    name = "等我成为封号骑士，一定会找回来！"
  },
  [85151301] = {
    id = 85151301,
    name = "生日快乐！嘿嘿~我准备了一个很多种口味的蛋糕，一起吃吧~啊，不对，要先许愿才行！祖父说过，在生日那天许下的愿望都会实现！"
  },
  [85161301] = {
    id = 85161301,
    name = "啊，又到新年了吗？！怎么办怎么办，我去年没有找到任何宝物，庆祝会上要怎么和大家说啊......要不你陪我一起去吧，好不好？"
  },
  [85171301] = {
    id = 85171301,
    name = "嘿嘿，为了感谢你对云雀的关照，我准备了一份超级特殊的礼物哦——藏宝图！这是我花光所有积蓄从黑市买来的，老板说绝对保真，以后要一起去哦！"
  },
  [85181301] = {
    id = 85181301,
    name = "我每年准备的恶作剧都吓不到人，反而经常被吓到，如果能想到些什么厉害的就好了......啊！今年我们一起恶作剧吧，这样一定可以赢过其他人！"
  },
  [85191301] = {
    id = 85191301,
    name = "我们庆祝这种节日的方式就是为彼此做一件事，队长有什么想要云雀做的吗？不管什么事都行，仅限今天哦！"
  },
  [85011311] = {
    id = 85011311,
    name = "我是来自深渊航线的辉夜，应您的邀约而来，从今天起将驻扎在您的探险队，为您提供全方位的探险支援服务。"
  },
  [85021311] = {
    id = 85021311,
    name = "近期的探险行程涉及到了几处比较危险的区域，我提议针对这些区域，制定一些更加完备的应对计划。"
  },
  [85031311] = {
    id = 85031311,
    name = "若我不曾见识过深渊的残酷、人心的阴暗，便不会有现在这样渴望守护安宁、守护善良和正义的决心。而正是这样的决心，让我选择了深渊航线。"
  },
  [85031312] = {
    id = 85031312,
    name = "深渊航线的行事准则之一便是“不得窥探客人的任何隐私”，任何违背之人都会受到组织的严惩。所以不管任何行动，我只需要知道自己的任务即可。"
  },
  [85041311] = {
    id = 85041311,
    name = "即便深渊中充斥着太多残酷的事，但依然有值得我们全力以赴的美好和希望。"
  },
  [85041312] = {
    id = 85041312,
    name = "守护，是我的任务，也是我的使命，为此，就算付出生命，我也在所不惜。"
  },
  [85041313] = {
    id = 85041313,
    name = "以我之剑，斩破长夜，以我之盾，护卫光明。——这便是，我的意志和信念！"
  },
  [85051311] = {
    id = 85051311,
    name = "对我而言，最难对付的敌人并非是凶猛的魔物，那些阴险狡诈、笑里藏刀的探险者才是最可怕的存在。"
  },
  [85051312] = {
    id = 85051312,
    name = "深渊中从事护卫支援事务的组织有很多，但像深渊航线一样拥有自己道德底线的却很少。"
  },
  [85051313] = {
    id = 85051313,
    name = "组织中的每个人都有各自的追求，但不管怎样的追求，在守护深渊秩序和正义这一点上，我们都一样。"
  },
  [85051314] = {
    id = 85051314,
    name = "我的父亲被利益熏心的友人算计永远离开了这个世界，我现在所做的一切，或许是对他最好的告慰。"
  },
  [85051315] = {
    id = 85051315,
    name = "我并不认为自己所做的一切很高尚，和那些追寻财富名望的人一样，我也只是全力追寻自己所信奉的存在而已。"
  },
  [85061311] = {
    id = 85061311,
    name = "要出发了吗？"
  },
  [85071311] = {id = 85071311, name = "哈咿！"},
  [85071312] = {id = 85071312, name = "咿啊！"},
  [85071313] = {id = 85071313, name = "嘿！"},
  [85081311] = {
    id = 85081311,
    name = "我，不会畏惧！"
  },
  [85081312] = {
    id = 85081312,
    name = "决不后退！"
  },
  [85091311] = {
    id = 85091311,
    name = "立于不败，方得全胜。"
  },
  [85091312] = {
    id = 85091312,
    name = "我的意志，绝不动摇！"
  },
  [85101311] = {id = 85101311, name = "呃。"},
  [85101312] = {id = 85101312, name = "唔呃。"},
  [85101313] = {id = 85101313, name = "呃啊。"},
  [85111311] = {
    id = 85111311,
    name = "这种情况我可经历了不少。"
  },
  [85121311] = {
    id = 85121311,
    name = "任务，要失败了吗......"
  },
  [85131311] = {
    id = 85131311,
    name = "这趟任务还算顺利，大家好好休息一下，下一次或许就没这么轻松了。"
  },
  [85141311] = {
    id = 85141311,
    name = "大家都没事吧？只要没有人员损失，我们就还有机会。"
  },
  [85151311] = {
    id = 85151311,
    name = "生日快乐~嗯，很惊讶？虽然我们的合约中并没有庆祝生日的条款，但我这样做不算违规吧？呵呵，一起去享受美食吧。"
  },
  [85161311] = {
    id = 85161311,
    name = "新年快乐，我正准备去巡逻——休息？呵呵，越是这种时候越需要警惕。当然，今天的巡逻应该会很轻松，要一起吗？"
  },
  [85171311] = {
    id = 85171311,
    name = "比起武器，厨房的工具实在太难掌控了，所以就、只能这样了......虽然这些点心看起来有些发黑，但味道肯定没问题，你不会拒绝吧？"
  },
  [85181311] = {
    id = 85181311,
    name = "真是热闹的节日，不过我暂时没空享受。啊，请别误会，不是什么紧急事件，只是、只是准备和同伴贩售一些节日商品......嗯？你想和我们一起？"
  },
  [85191311] = {
    id = 85191311,
    name = "我收到了母亲寄来的礼物，嗯，她也给你捎了一份，都是我故乡的特产。她在来信中表示非常认同我现在所做的一切，希望我能为了守护更多美好而努力......我还要再努力一点，才能回应她的期待！"
  },
  [85011321] = {
    id = 85011321,
    name = "探险者白鸢应您的邀约而来，非常感谢您对我的认可，希望我们在接下来的行程中合作愉快。"
  },
  [85021321] = {
    id = 85021321,
    name = "休息结束了吗？走吧。准备？呵呵，作为曾经的预备役骑士，不用特地为我预留准备时间。"
  },
  [85031321] = {
    id = 85031321,
    name = "我自幼跟随祖父进行骑士修行，无论武技、谋略、礼仪均有所涉猎，如果没有成为探险者，我现在应该能算得上一位优秀的骑士了吧？不过对我而言，无论成为怎样优秀的骑士也不会比现在更让我感到满足。"
  },
  [85031322] = {
    id = 85031322,
    name = "深渊固然残酷，但蕴藏着无数种可能性，它赋予了每一个探险者平等的机会，只要竭尽全力，任何人都可能在这里找到自己想要的一切。"
  },
  [85041321] = {
    id = 85041321,
    name = "不管对自己的实力有多么自信也绝不能懈怠，没有人知道下一刻会面对怎样的危险。"
  },
  [85041322] = {
    id = 85041322,
    name = "曾经，我的剑为了骑士的荣耀而挥舞，但现在，我只想为了自己的理想拼尽全力！"
  },
  [85041323] = {
    id = 85041323,
    name = "信念才是我们最强大的力量，若能拥有足够坚定的信念，无论面对怎样的困难都能无坚不摧。"
  },
  [85051321] = {
    id = 85051321,
    name = "最初我来深渊是为了完成最后的历练，然而我却在历练结束后选择了留下......那是我第一次违背约定......"
  },
  [85051322] = {
    id = 85051322,
    name = "我并不喜欢战斗，但很多时候根本没有选择的机会。所以每一次必须战斗时，我都会做好觉悟，全力以赴！"
  },
  [85051323] = {
    id = 85051323,
    name = "有人说我舍弃了骑士的荣耀，他们说的没错，但也不算全对。因为如果没有足够的信念，背负那样的荣耀只会为它蒙羞。"
  },
  [85051324] = {
    id = 85051324,
    name = "我们的人生和这个变换莫测的深渊非常相似，没有人知道前路会是怎样，不过不管是坎坷还是坦途，都只有前行的人才能知晓。"
  },
  [85051325] = {
    id = 85051325,
    name = "深渊中的种种经历，让我原本有些迷茫的内心变得更加动摇，不由得产生了想要自己决定未来的想法，所以这一次、我想为了自己选择的未来而努力。"
  },
  [85061321] = {
    id = 85061321,
    name = "我会速战速决。"
  },
  [85071321] = {id = 85071321, name = "哈啊！"},
  [85071322] = {id = 85071322, name = "呀！"},
  [85071323] = {id = 85071323, name = "嘿诶！"},
  [85081321] = {
    id = 85081321,
    name = "挡不住我。"
  },
  [85081322] = {id = 85081322, name = "退下！"},
  [85091321] = {
    id = 85091321,
    name = "精神不朽，信念长存！"
  },
  [85091322] = {
    id = 85091322,
    name = "我们的命运、由自己掌控！"
  },
  [85101321] = {id = 85101321, name = "呃~"},
  [85101322] = {id = 85101322, name = "呃啊。"},
  [85101323] = {id = 85101323, name = "咿呃。"},
  [85111321] = {
    id = 85111321,
    name = "一点意外而已，无需在意。"
  },
  [85121321] = {
    id = 85121321,
    name = "我的选择......错了吗......"
  },
  [85131321] = {
    id = 85131321,
    name = "全力以赴是对敌人的尊重，也可以减少一些不必要的痛苦。"
  },
  [85141321] = {
    id = 85141321,
    name = "我会争取足够的时间，大家先撤！"
  },
  [85151321] = {
    id = 85151321,
    name = "那个、今天是您的生日吧？请问您有什么想要的礼物吗？对不起，因为不清楚您想要的东西，所以没有提前准备礼物......或者、或者今天行动结束后可以耽误一下吗？我们一起去挑选吧！"
  },
  [85161321] = {
    id = 85161321,
    name = "以前新年是我唯一可以休息的日子，但对我而言即便休息也不知道该做些什么，无所事事的一天反而感觉非常漫长——诶，去逛新年庆典？那个、我从来没去过，没关系吗？"
  },
  [85171321] = {
    id = 85171321,
    name = "非常感谢您至今以来的关照和教诲！......太严肃了吗？对不起，我只是听说今天是代表感谢日子，所以......下次我一定会问清楚节日礼仪。"
  },
  [85181321] = {
    id = 85181321,
    name = "到处都在举办庆典，真热闹啊~我以前非常渴望参加这种庆典，但因为必须要完成修行任务所以无法参加。来到深渊后，却又觉得自己有些格格不入，不敢一个人去，那个、可以一起去看看吗？"
  },
  [85191321] = {
    id = 85191321,
    name = "自从决定留在深渊之后，每到这样的日子我都会为祖父寄送一份礼物。或许是出于愧疚，也或许是想告诉他我还平安，但不管怎样的心情，我都没有后悔过自己所做出的选择。"
  },
  [85011331] = {
    id = 85011331,
    name = "我是安伦，非常感谢您的邀请，呵呵呵~希望在此停留期间，我们能相处愉快。"
  },
  [85021331] = {
    id = 85021331,
    name = "嗯~睡了个好觉呢。深渊中随时可能发生战斗，所以我会好好把握可以休息的每一分钟。"
  },
  [85031331] = {
    id = 85031331,
    name = "我可没有在偷懒哦，只是趁着空隙休息一下而已~而且比起忙碌，我无所事事会更好吧？大家都平安无事才是我们共同的希望。"
  },
  [85031332] = {
    id = 85031332,
    name = "被叫做“圣女大人”的时候真的很有压力呢，毕竟我可没有传闻中那种救死扶伤、只身对抗邪恶的力量，不过我也希望有一天能成为那样的人。"
  },
  [85041331] = {
    id = 85041331,
    name = "力量没有善恶，只要使用者心存善意，它便代表着救赎和希望。"
  },
  [85041332] = {
    id = 85041332,
    name = "既然被大家称作圣女大人，我也不想辜负这份信任和期待，就稍微再努力一点吧。"
  },
  [85041333] = {
    id = 85041333,
    name = "对那些陷入困境的人而言，我们的举手之劳或许便能为他们带来救赎，这份救赎也许在有朝一日能孕育出更多的善意。"
  },
  [85051331] = {
    id = 85051331,
    name = "我时常对自己说“尽力就好”，可是每次看到求助者期待的眼神时，却不知道该怎样拒绝......"
  },
  [85051332] = {
    id = 85051332,
    name = "对我而言，变强是为了能做到更多的事，没想到在获得成长后，才发现自己似乎也有了更多责任。"
  },
  [85051333] = {
    id = 85051333,
    name = "被太多人期待，有时候会觉得很累，所以在能够休息时，才要尽情享受。"
  },
  [85051334] = {
    id = 85051334,
    name = "我很喜欢现在的一切，不是为了父母，也不是为了其他什么，只是单纯的喜欢我现在所拥有的这种生活。"
  },
  [85051335] = {
    id = 85051335,
    name = "我最初只是想找个不错的探险队暂留一段时间，让自己偷懒休息一下。可是现在，似乎暂时找不到继续漂泊的理由了。"
  },
  [85061331] = {
    id = 85061331,
    name = "啊~休息时间结束了吗。"
  },
  [85071331] = {id = 85071331, name = "嘿！"},
  [85071332] = {id = 85071332, name = "喝~"},
  [85071333] = {id = 85071333, name = "呀啊~"},
  [85081331] = {
    id = 85081331,
    name = "大家，拜托了！"
  },
  [85081332] = {
    id = 85081332,
    name = "乘胜追击。"
  },
  [85091331] = {
    id = 85091331,
    name = "拯救，是我的职责！"
  },
  [85091332] = {
    id = 85091332,
    name = "我绝不会放弃任何同伴。"
  },
  [85101331] = {id = 85101331, name = "咿呃~"},
  [85101332] = {id = 85101332, name = "啊~"},
  [85101333] = {id = 85101333, name = "呜啊~"},
  [85111331] = {
    id = 85111331,
    name = "不用担心我，大家务必小心。"
  },
  [85121331] = {
    id = 85121331,
    name = "可以......安心的休息了......"
  },
  [85131331] = {
    id = 85131331,
    name = "嗯，没人受伤吧？那打扫战场就拜托大家了，呵呵~"
  },
  [85141331] = {
    id = 85141331,
    name = "伤员由我负责，大家赶快撤离。"
  },
  [85151331] = {
    id = 85151331,
    name = "生日这种日子里就该放下所有事务，安心享受美食之类的吧？嗯，一起去圈落中逛逛吧，放心，如果有紧急的事情我们赶回来也来得及。"
  },
  [85161331] = {
    id = 85161331,
    name = "最后一次和父母庆祝新年时，我们之间还发生了一点不愉快的事......没能当面道歉，果然还是有些遗憾。我现在所能做的，只有守护更多人，让他们不再经历这样的遗憾。"
  },
  [85171331] = {
    id = 85171331,
    name = "非常感谢您这段时间以来的关照，我做了些点心，请尝一下吧~这可是我牺牲了不少休息时间才完成的，请务必收下~"
  },
  [85181331] = {
    id = 85181331,
    name = "真是热闹的节日，每个人的脸上都洋溢着幸福喜悦的笑容，呵呵~趁现在还没有什么紧急事件，一起去享受节日吧。"
  },
  [85191331] = {
    id = 85191331,
    name = "能够赶在今天之前把重要的事务都处理完毕真是太好了，如此一来，我们便可以稍微放松一下，享受这份难得的安宁了。"
  },
  [85011341] = {
    id = 85011341,
    name = "战斗的事尽管交给我，只要大家还在一起行动，我都会听从你的指挥。"
  },
  [85021341] = {
    id = 85021341,
    name = "这是接下来的行程么？我没有意见。"
  },
  [85031341] = {
    id = 85031341,
    name = "我不喜欢武力，但也并不排斥战斗，你不必有什么顾虑，如果发生战斗，能多照看一下安妮斯，就算是帮大忙了。"
  },
  [85031342] = {
    id = 85031342,
    name = "我和安妮斯一起长大，保护她是我的责任。可是，我虽然能暂时阻止那些胡说八道的人一时，却无法从根本上解决问题——"
  },
  [85041341] = {
    id = 85041341,
    name = "变强，总归不是坏事。"
  },
  [85041342] = {
    id = 85041342,
    name = "只要是敌人，不管是谁，我都不会犹豫。"
  },
  [85041343] = {
    id = 85041343,
    name = "无论多么强大的力量，也无法拯救一个人的灵魂。"
  },
  [85051341] = {
    id = 85051341,
    name = "深渊中的对手，比传闻中要棘手不少。"
  },
  [85051342] = {
    id = 85051342,
    name = "团队作战并非是我擅长的领域，不过我会尝试去做。"
  },
  [85051343] = {
    id = 85051343,
    name = "信任同伴并非是一件轻松的事，总之，我会尽力。"
  },
  [85051344] = {
    id = 85051344,
    name = "无论怎样的对手，都必须全力以赴，轻敌大意是探险者的大忌。"
  },
  [85051345] = {
    id = 85051345,
    name = "顾虑太多会让人迟疑，可是若没有顾虑，又失去了前行的动力。"
  },
  [85061341] = {id = 85061341, name = "走吧。"},
  [85071341] = {id = 85071341, name = "喝啊！"},
  [85071342] = {id = 85071342, name = "嘿~"},
  [85071343] = {id = 85071343, name = "呀啊！"},
  [85081341] = {
    id = 85081341,
    name = "速战速决。"
  },
  [85081342] = {
    id = 85081342,
    name = "再快一点！"
  },
  [85091341] = {
    id = 85091341,
    name = "反抗只是徒劳。"
  },
  [85091342] = {
    id = 85091342,
    name = "差不多，该结束了。"
  },
  [85101341] = {id = 85101341, name = "呃。"},
  [85101342] = {id = 85101342, name = "呃啊。"},
  [85101343] = {id = 85101343, name = "咿呃~"},
  [85111341] = {
    id = 85111341,
    name = "还差得远。"
  },
  [85121341] = {
    id = 85121341,
    name = "顾虑，太多了么......"
  },
  [85131341] = {
    id = 85131341,
    name = "别大意，还没到完全放松的时候。"
  },
  [85141341] = {
    id = 85141341,
    name = "安妮斯，队长，你们没事吧？！"
  },
  [85151341] = {
    id = 85151341,
    name = "生日是值得纪念的日子，不管怎样今天都应该放松一下，安妮斯也烤了蛋糕，一起庆祝？"
  },
  [85161341] = {
    id = 85161341,
    name = "以前新年，我大多数时候都会和安妮斯一起度过，这种日子并不适合孤零零一个人，要一起去找她么？"
  },
  [85171341] = {
    id = 85171341,
    name = "没想到今天居然是这种日子，非常感谢你的关照。这段时间以来，不管是我还是安妮斯，都让你费了不少心思，十分感谢！"
  },
  [85181341] = {
    id = 85181341,
    name = "这种热闹的日子，我并不太喜欢，当然，也算不上讨厌，只是，有些不擅长应付而已。"
  },
  [85191341] = {
    id = 85191341,
    name = "已经这个时节了么，有点想念地面上的食物了......今晚的伙食能稍微改善一下么？"
  },
  [85011351] = {
    id = 85011351,
    name = "我叫露米娅，是一名猎人——啊，不对，用你们的话说叫探险者，今天起正式加入你的探险队，请多多关照。"
  },
  [85021351] = {
    id = 85021351,
    name = "嘘~......啪！搞定，嘿嘿~我刚在蹲守一只飞虫，这种飞虫很难对付，只能抓住它停下来的瞬间，给予致命一击才行。"
  },
  [85031351] = {
    id = 85031351,
    name = "深渊和我长大的山林在地形上有些相似，但猎物完全不同，在我们那里可完全看不到长得这么奇怪的魔物。"
  },
  [85031352] = {
    id = 85031352,
    name = "“提尔”可是我花光狩猎多年的钱换来的，我见到它的第一眼就被迷住了！它不仅可以挥砍，还能化作一张血盆大口，嗷呜一口将猎物咬碎！非常棒！"
  },
  [85041351] = {
    id = 85041351,
    name = "猎人没有固定的套路，对付不同的猎物，需要掌握不同的方法。"
  },
  [85041352] = {
    id = 85041352,
    name = "“提尔”已经迫不及待了，让我们在深渊这个猎场中，尽情狩猎吧！"
  },
  [85041353] = {
    id = 85041353,
    name = "如果没有足够的实力，“猎人”也很有可能成为“猎物”。"
  },
  [85051351] = {
    id = 85051351,
    name = "猎人和探险者差别并不大，都是对付猎物，只不过猎物的种类稍微有些不同罢了。"
  },
  [85051352] = {
    id = 85051352,
    name = "无论猎物多么弱小，也必须全力以赴，轻敌大意可是猎人的大忌！"
  },
  [85051353] = {
    id = 85051353,
    name = "我在家乡已经找不到势均力敌的猎物了，所以当得知深渊的存在后，我便对这个“新猎场”充满了期待。"
  },
  [85051354] = {
    id = 85051354,
    name = "每个猎人的狩猎技巧、惯用武器都会有不同，对我来说，没有比“提尔”更完美的武器了！"
  },
  [85051355] = {
    id = 85051355,
    name = "比起我的家乡，深渊中多了不少危险，但也拥有更多乐趣。外形奇特、能力古怪的猎物，互相支持的同伴......我非常满意现在拥有的一切！"
  },
  [85061351] = {
    id = 85061351,
    name = "狩猎开始咯~"
  },
  [85071351] = {id = 85071351, name = "嘿咿！"},
  [85071352] = {id = 85071352, name = "哈！"},
  [85071353] = {id = 85071353, name = "哈啊！"},
  [85081351] = {
    id = 85081351,
    name = "接招吧！"
  },
  [85081352] = {
    id = 85081352,
    name = "发现弱点。"
  },
  [85091351] = {
    id = 85091351,
    name = "猎物逃不出猎人的掌心。"
  },
  [85091352] = {
    id = 85091352,
    name = "优秀的猎人可不会被迷惑。"
  },
  [85101351] = {id = 85101351, name = "唔。"},
  [85101352] = {id = 85101352, name = "哇啊~"},
  [85101353] = {id = 85101353, name = "呃唔。"},
  [85111351] = {
    id = 85111351,
    name = "忍耐，是猎人的基本修行！"
  },
  [85121351] = {
    id = 85121351,
    name = "......还是......太大意了么......"
  },
  [85131351] = {
    id = 85131351,
    name = "无论如何狡猾的猎物，也总会有藏不住尾巴的时候~"
  },
  [85141351] = {
    id = 85141351,
    name = "可恶，真是狡猾的家伙，下一次绝对不会放过它！"
  },
  [85151351] = {
    id = 85151351,
    name = "诶！你说今天生日？这么重要的日子，为什么不早点告诉我呢，啊，没时间了，我去看看还有些什么食材......呼~祈祷莫妮卡小姐没有全部吃掉。"
  },
  [85161351] = {
    id = 85161351,
    name = "我的家乡在山里，道路不是很方便，所以平时大家走动很少，只有新年的时候，周围的人们才会聚集到一起，享受食物和美酒，一起庆祝新年。"
  },
  [85171351] = {
    id = 85171351,
    name = "今天是代表感谢的日子......我大概知道了，和我们那感谢山神的日子一样吧？！每到山神庆典，大家都会把最好的猎物供奉给山神，我也去把最好的猎物拿来供奉给你~"
  },
  [85181351] = {
    id = 85181351,
    name = "好热闹的节日啊，圈落里似乎多了不少穿着奇怪服饰的人——欸，节日习俗吗？！竟然还有这么奇怪的习俗，外面的世界果然比山里麻烦多了。"
  },
  [85191351] = {
    id = 85191351,
    name = "请稍等，我在清点近期捕获的猎物。在我那每到这个时节大家就会把各自捕获的猎物拿出来，分一些给那些无法捕猎的人。现在我在这里，我也要把这些猎物分一些给大家。"
  },
  [85011361] = {
    id = 85011361,
    name = "你好，我是苏西，目前是一名自由探险者，虽然还算新人，那个......那个......总之，我会努力！"
  },
  [85021361] = {
    id = 85021361,
    name = "嗯......嗯？抱、抱歉！我看书太入迷了，没注意您已经来了。"
  },
  [85031361] = {
    id = 85031361,
    name = "我第一次看到深渊奇景时，便觉得所有书本中关于它的描述都太过苍白无力。那是一种无法用言语形容、不亲眼所见便绝对无法想象的梦幻场景。"
  },
  [85031362] = {
    id = 85031362,
    name = "我喜欢书本，它们就像我最好的朋友，给我讲述着关于这个世界的一切......可是，有一些事物，如果不能亲眼见识，终究还是会觉得遗憾。"
  },
  [85041361] = {
    id = 85041361,
    name = "原、原来还有这样的方法。我没在书上看到过，稍微有一点点紧张。"
  },
  [85041362] = {
    id = 85041362,
    name = "这种感觉......就好像亲眼见到书本中所描绘的那些胜景一样。"
  },
  [85041363] = {
    id = 85041363,
    name = "只要我能坚持下去，终有一天，一定能走遍深渊的每个角落。"
  },
  [85051361] = {
    id = 85051361,
    name = "我并没有具体的探险计划，只是凭着感觉在行动，这样，果然不行吗？"
  },
  [85051362] = {
    id = 85051362,
    name = "如果不能将所学的知识加以应用，那么无论读多少书，也不会有太大的不同。"
  },
  [85051363] = {
    id = 85051363,
    name = "每个人都有憧憬的事物，虽然我憧憬的存在可能和大家不同，但这份心情一定不会输给大家。"
  },
  [85051364] = {
    id = 85051364,
    name = "虽然深渊中的景色比书本中更加壮观，可是深渊中的危险，也远远超过了书本中的记录......"
  },
  [85051365] = {
    id = 85051365,
    name = "我不知道旅途的终点，也不知道准确的方向，但并非每一件事都要弄清楚之后才能去做，那样会少了很多意义。"
  },
  [85061361] = {
    id = 85061361,
    name = "我、我明白了。"
  },
  [85071361] = {id = 85071361, name = "喝啊！"},
  [85071362] = {id = 85071362, name = "嘿~"},
  [85071363] = {id = 85071363, name = "呀啊！"},
  [85081361] = {
    id = 85081361,
    name = "不要躲开哦。"
  },
  [85081362] = {
    id = 85081362,
    name = "我会努力的。"
  },
  [85091361] = {
    id = 85091361,
    name = "用这个决胜负吧。"
  },
  [85091362] = {
    id = 85091362,
    name = "应该不会打偏吧？"
  },
  [85101361] = {id = 85101361, name = "呜哇。"},
  [85101362] = {id = 85101362, name = "呀~"},
  [85101363] = {id = 85101363, name = "呃啊。"},
  [85111361] = {
    id = 85111361,
    name = "我、我还可以继续。"
  },
  [85121361] = {
    id = 85121361,
    name = "治疗的咒语，想不起来了......"
  },
  [85131361] = {
    id = 85131361,
    name = "赢了吗？好像是赢了吧？呼~好紧张。"
  },
  [85141361] = {
    id = 85141361,
    name = "到底......是什么地方没做好呢......"
  },
  [85151361] = {
    id = 85151361,
    name = "生日是今天吗？啊，我想起来了，之前大家秘密筹备的庆祝宴会，好像就是为今天准备的惊喜——啊！不不不！请忘了我刚说的话！什么都没有！"
  },
  [85161361] = {
    id = 85161361,
    name = "关于新年的来历，书本中有很多种记录呢，各地关于新年的故事传说也不一样，不过不管是怎样的传说记录，都代表着人们对新年的美好祈愿。"
  },
  [85171361] = {
    id = 85171361,
    name = "嗯...嗯......啊，抱歉，我、我刚又走神了。我在想要怎样表达谢意，可是，完全没有头绪......要不我们一起去圈落中看看吧，说不定会有发现。"
  },
  [85181361] = {
    id = 85181361,
    name = "这个节日不管哪一本书中的记载，都是和幽灵相关的呢，为什么大家不但不怕，反而还在一起狂欢呢——我、我并没有害怕。"
  },
  [85191361] = {
    id = 85191361,
    name = "我收到的第一份圣诞礼物便是一本关于深渊传说的画集，所以大概从那时候起，我便开始向往深渊了吧。"
  },
  [85011371] = {
    id = 85011371,
    name = "我并不清楚您在向我发出邀请时是怎样的心情，认真、无聊、同情，或者纯粹发错了？不过不管怎样，能收到您的邀请我很开心。"
  },
  [85021371] = {
    id = 85021371,
    name = "我近期实战方面的训练似乎有些不足，有些地方没能完全理解，如果可以的话我希望能为我增加一些实战方面的训练。"
  },
  [85031371] = {
    id = 85031371,
    name = "我修行武技的理由并非是自己没有魔法天赋，相反，我曾被称作魔法天才。只是命运——不，是我自己太看重虚名，自作自受......"
  },
  [85031372] = {
    id = 85031372,
    name = "一些人认为我来深渊是为了追寻让自己恢复的奇迹，虽说不完全对，但也没有全错。如果说我对那种奇迹没有一点兴趣肯定是假的，只是我现在更想要珍惜当下，努力超越上一秒的自己。"
  },
  [85041371] = {
    id = 85041371,
    name = "把一件力所能及的事做到极致，便是我现在唯一能做到的事。"
  },
  [85041372] = {
    id = 85041372,
    name = "我不会再逃避，也不会再放弃，我会靠自己的努力，全力追寻属于我的未来！"
  },
  [85041373] = {
    id = 85041373,
    name = "真正的奇迹只能由自己创造，一味追寻那虚无缥缈的存在只会一事无成。"
  },
  [85051371] = {
    id = 85051371,
    name = "曾经的我肯定无法想象现在的自己，那种迷失在荣誉光环中的人是不会相信他们没有经历过的事情。"
  },
  [85051372] = {
    id = 85051372,
    name = "我刚开始修行武技时，只是抱着试试看的心态，从没想过自己能到走到这一步。"
  },
  [85051373] = {
    id = 85051373,
    name = "从我无法继续学习魔法的那一刻起，我的人生就发生了巨大变化，当时我曾一度陷入绝望，但现在我早已释然。"
  },
  [85051374] = {
    id = 85051374,
    name = "我曾无比渴望获得别人的认可，但对现在的我而言，只要相信自己就足够了。"
  },
  [85051375] = {
    id = 85051375,
    name = "如果真的存在命运，我很想感谢它给了我一个重新审视自己的机会，若非如此，恐怕我仍未从那些虚伪的谎言中醒来。"
  },
  [85061371] = {
    id = 85061371,
    name = "我会全力以赴。"
  },
  [85071371] = {id = 85071371, name = "哈啊！"},
  [85071372] = {id = 85071372, name = "嘿呀！"},
  [85071373] = {id = 85071373, name = "呀！"},
  [85081371] = {
    id = 85081371,
    name = "交给我吧。"
  },
  [85081372] = {
    id = 85081372,
    name = "别妄想了！"
  },
  [85091371] = {
    id = 85091371,
    name = "只要相信，便有可能。"
  },
  [85091372] = {
    id = 85091372,
    name = "奇迹，应该由自己创造！"
  },
  [85101371] = {id = 85101371, name = "呃啊。"},
  [85101372] = {id = 85101372, name = "啊！"},
  [85101373] = {id = 85101373, name = "呃。"},
  [85111371] = {
    id = 85111371,
    name = "我绝不会退让。"
  },
  [85121371] = {
    id = 85121371,
    name = "没能......把握好机会......"
  },
  [85131371] = {
    id = 85131371,
    name = "战斗中没有侥幸，无论胜败都是我们实力的证明。"
  },
  [85141371] = {
    id = 85141371,
    name = "一时的胜败并不代表什么，任何时候都不能轻易放弃。"
  },
  [85151371] = {
    id = 85151371,
    name = "生日啊，那得准备蛋糕才行。不，你不用阻止我，不管什么事都绝不能敷衍，必须认真对待。我去准备一下，不会等太久。"
  },
  [85161371] = {
    id = 85161371,
    name = "以前每个新年我们一家人都会非常和睦的在一起庆祝，可是从我遭遇事故后就变了......大家虽然还会在一起庆祝，但那种气氛非常尴尬，令人想要逃避。"
  },
  [85171371] = {
    id = 85171371,
    name = "已经到了这个节日了吗，在这里的每一天我都感觉很充实，完全没觉察到......还好之前有准备食材，我先去做些点心，要甜一点还是淡一点呢？"
  },
  [85181371] = {
    id = 85181371,
    name = "这种热闹的节日我一度觉得只有普通人才会去庆祝，从来不屑于参加，现在看来我当初真是错过了好多乐趣，今年不能再错过了，一起去吧~"
  },
  [85191371] = {
    id = 85191371,
    name = "我很喜欢现在这样和大家一起分享礼物，庆祝节日的感觉，这是一种我被称为“魔法天才”根本无法感受到的幸福，呵呵~所幸我明白的还不算太晚。"
  },
  [85011381] = {
    id = 85011381,
    name = "您好，我是暗夜侍者的朗姆，今天起正式加入您的探险队，有什么麻烦尽管交给我吧。"
  },
  [85021381] = {
    id = 85021381,
    name = "探险的准备我已经做好了，出发前要再休息一下吗？——给，刚冲好的红茶，小心烫。"
  },
  [85031381] = {
    id = 85031381,
    name = "只要是小姐自己决定事，她就一定会做到极致。所以她既然选择了成立暗夜侍者，那么我们总有一天会成为名扬深渊的组织。哈~我也要努力才行，绝不能成为大家的拖累。"
  },
  [85031382] = {
    id = 85031382,
    name = "在许多传说中，深渊都是一个令人向往的世界，可是来到这里才发现它并没有那么美好。不过、我相信只要和大家在一起，就一定能收获我们所渴望的一切。"
  },
  [85041381] = {
    id = 85041381,
    name = "虽然还只是见习执事，但我所能提供的服务一定会让您满意！"
  },
  [85041382] = {
    id = 85041382,
    name = "竟然可以达到这样的程度，请期待执事朗姆、不，探险者朗姆的全力吧。"
  },
  [85041383] = {
    id = 85041383,
    name = "无论是侍者的工作还是探险者的事务，我感觉现在的自己一定能做得更加完美！"
  },
  [85051381] = {
    id = 85051381,
    name = "主家对我们一家人恩重如山，所以能成为执事，为主家效力是我的荣幸。"
  },
  [85051382] = {
    id = 85051382,
    name = "我渴望成为一名优秀的侍者，也渴望成为一名优秀的探险者，所以不管遇到怎样的困难，我也绝不会畏惧。"
  },
  [85051383] = {
    id = 85051383,
    name = "小姐其实非常善良，充满了正义，我记得她刚修行不久，便四处打击盗贼匪徒，帮助弱小，受到了许多人的称赞。"
  },
  [85051384] = {
    id = 85051384,
    name = "不管以前还是现在，我所能做到的事只有一件——解决麻烦！所以我会全力以赴，把这件事做到极致！"
  },
  [85051385] = {
    id = 85051385,
    name = "如果没有小姐和主家的关照，便不会有现在的我。所以在得知小姐要来深渊时，我便已做好了觉悟——即便付出生命也要护卫她的安全！"
  },
  [85061381] = {
    id = 85061381,
    name = "请交给我吧！"
  },
  [85071381] = {id = 85071381, name = "嘿！"},
  [85071382] = {id = 85071382, name = "呀！"},
  [85071383] = {id = 85071383, name = "喝！"},
  [85081381] = {
    id = 85081381,
    name = "饶不了你。"
  },
  [85081382] = {
    id = 85081382,
    name = "乖乖放弃吧！"
  },
  [85091381] = {
    id = 85091381,
    name = "我也有、必须要做到的事。"
  },
  [85091382] = {
    id = 85091382,
    name = "请见证我的觉悟！"
  },
  [85101381] = {id = 85101381, name = "呃啊~"},
  [85101382] = {id = 85101382, name = "唔。"},
  [85101383] = {id = 85101383, name = "噫呃。"},
  [85111381] = {
    id = 85111381,
    name = "一点意外而已，不要紧。"
  },
  [85121381] = {
    id = 85121381,
    name = "我、做到了吗......"
  },
  [85131381] = {
    id = 85131381,
    name = "我的表现还不错吧？呵呵，下一次我还能做得更好！"
  },
  [85141381] = {
    id = 85141381,
    name = "明明已经做足了准备，为什么会这样......还有什么地方没做好吗......"
  },
  [85151381] = {
    id = 85151381,
    name = "请问现在有空吗？我想和您确认一下庆祝会的出席名单。呵呵，今天是您的生日吧？所以我们为您准备了一个庆祝会，您只需要确认一些必要信息就好。"
  },
  [85161381] = {
    id = 85161381,
    name = "我们正在准备新年庆典，请问您有什么特别的需求吗？我想更多的了解大家的需求，让每一个参与庆典的人都能收获幸福和快乐。"
  },
  [85171381] = {
    id = 85171381,
    name = "您拿的甜点是别人送的吗？呵呵，我也在想要不要准备一点类似的谢礼。诶？是大小姐亲手做的？！大小姐竟、竟然会做甜点，一定很美味吧？！"
  },
  [85181381] = {
    id = 85181381,
    name = "大小姐很喜欢这种热闹的节日，我还是她学伴的时候，就曾帮她欺骗过护卫。可是在我被当家发现并责罚之后，大小姐就很少再让我帮忙......虽然知道她是不想连累我，但是......"
  },
  [85191381] = {
    id = 85191381,
    name = "节日快乐~呵呵，这是主家为大家准备的礼物，每个人都有！啊，快来不及了，我得赶紧去送下一份！"
  },
  [85011391] = {
    id = 85011391,
    name = "我是弗拉克，这是我的伙伴吉米，多亏了它我才能成为探险者，嘿嘿~吉米也来打个招呼吧。"
  },
  [85021391] = {
    id = 85021391,
    name = "吉米虽然很强，但如果不好好维护，关键时候无法发挥全力就麻烦了......嗯，好了，维护完成，随时可以出发。"
  },
  [85031391] = {
    id = 85031391,
    name = "许多人都觉得我没有继承父母强大的天赋非常可惜，可是我却不这么认为，正因为我天赋普通，我才能和吉米相遇，也才能成为无比特殊的探险者。"
  },
  [85031392] = {
    id = 85031392,
    name = "吉米是我跟随维克多大师学习后制作的“全能型”魔偶，除了能辅助我战斗之外，在侦察、储物、搬运之类的工作上也拥有非常出色的表现！"
  },
  [85041391] = {
    id = 85041391,
    name = "我想到了一个新的改进方案，可以让吉米变得更强！"
  },
  [85041392] = {
    id = 85041392,
    name = "超级吉米，登场！这段时间的调整让吉米变得更加完美了，请期待我们的表现吧！"
  },
  [85041393] = {
    id = 85041393,
    name = "利用深渊的力量来强化吉米，真是天才的想法！啊，真期待啊，吉米还能再一次蜕变吗。"
  },
  [85051391] = {
    id = 85051391,
    name = "我的力量并不出色，不足以支撑我成为探险者，但有了吉米的帮助，一切都不一样了。"
  },
  [85051392] = {
    id = 85051392,
    name = "优秀的工匠会根据所处环境的变化，调整修缮自身的不足。"
  },
  [85051393] = {
    id = 85051393,
    name = "维克多大师是技术方面的天才，作为弟子我也绝不能逊色于他，我会和吉米一起证明这一点！"
  },
  [85051394] = {
    id = 85051394,
    name = "吉米拥有让普通探险者难以企及的力量，但是如果太依赖于它，也并非完全是好事。至少，对我而言。"
  },
  [85051395] = {
    id = 85051395,
    name = "无论是技术还是力量，都只是探索深渊的助力，如果沉迷其中，遗忘了自己的初心，必然会迷失方向。"
  },
  [85061391] = {
    id = 85061391,
    name = "吉米，要出发咯。"
  },
  [85071391] = {id = 85071391, name = "嘿！"},
  [85071392] = {id = 85071392, name = "呀啊！"},
  [85071393] = {id = 85071393, name = "喝咿！"},
  [85081391] = {
    id = 85081391,
    name = "别小看我。"
  },
  [85081392] = {
    id = 85081392,
    name = "交给我吧。"
  },
  [85091391] = {
    id = 85091391,
    name = "吉米，拜托你了！"
  },
  [85091392] = {
    id = 85091392,
    name = "展现力量吧，超级吉米！"
  },
  [85101391] = {id = 85101391, name = "啊！"},
  [85101392] = {id = 85101392, name = "噫呃。"},
  [85101393] = {id = 85101393, name = "唔啊。"},
  [85111391] = {
    id = 85111391,
    name = "追着不放，真难缠。"
  },
  [85121391] = {
    id = 85121391,
    name = "果然......不能只靠吉米......"
  },
  [85131391] = {
    id = 85131391,
    name = "干的漂亮，吉米，真是完美的表现！"
  },
  [85141391] = {
    id = 85141391,
    name = "吉米都无法发挥全力，难道是哪里没有维护好？"
  },
  [85151391] = {
    id = 85151391,
    name = "生日！这可是值得纪念和庆祝的一天呢，先让吉米给您跳支舞吧！嘿嘿嘿~说起来，吉米的生日也快到了呢。"
  },
  [85161391] = {
    id = 85161391,
    name = "新年啊，如果今年能想到更多改进吉米的方案就好了。离开维克多大师也有不少时间了，可是我的技术进步还是太慢，今年得更加努力才行。"
  },
  [85171391] = {
    id = 85171391,
    name = "锵锵！来尝尝这个由吉米制作的吉米外形的点心——无论是外形还是制作人员都是全世界独一无二的，送给你！"
  },
  [85181391] = {
    id = 85181391,
    name = "每次这样的节日，吉米都是最受欢迎的呢~带着它去参加巡游，小孩子们都会围绕着吉米看个不停，嘿嘿~"
  },
  [85191391] = {
    id = 85191391,
    name = "今年派发礼物的人选定好了吗，让我试试怎么样？吉米可以扮演我的驯鹿，不仅可以装载很多礼物还能跑得飞快，就和故事中一样。"
  },
  [85011401] = {
    id = 85011401,
    name = "好久不见了哥哥，呵呵~姐妹们收到哥哥的邀请，就决定比赛看谁先赶过来，我是第几个到的呢？"
  },
  [85021401] = {
    id = 85021401,
    name = "陆大叔常说“天道酬勤”，就算是哥哥也不能随便荒废时间，尤其是在深渊中，每一秒都要好好珍惜。"
  },
  [85031401] = {
    id = 85031401,
    name = "希尔负责支援，艾莉亚负责诱敌......嗯，艾莉亚战斗时可不懂得什么叫停手，还是让她负责正面对抗吧......真希望她们能好好执行作战计划啊。"
  },
  [85031402] = {
    id = 85031402,
    name = "妹妹们都还不太懂事，不管是学习还是修行都需要督促，可是大叔和遥姐都有更重要的事要做，所以暂时就由我来监督调皮的妹妹们，这样一来也能让大叔他们不会因为大家的事分心。"
  },
  [85041401] = {
    id = 85041401,
    name = "知识非常重要，但只是读书还远远不够，还要懂得如何应用。"
  },
  [85041402] = {
    id = 85041402,
    name = "书籍是赛珂忠实的朋友，不管遇到怎样的问题，它们都能给出答案。"
  },
  [85041403] = {
    id = 85041403,
    name = "没有任何事可以成为荒废自己修行的借口，身为姐姐更应该以身作则。"
  },
  [85051401] = {
    id = 85051401,
    name = "姐妹们都想要到深渊见识一番，我便和大家一起来了，呵呵~我也想亲眼见识一下这个书本中的世界。"
  },
  [85051402] = {
    id = 85051402,
    name = "姐妹们都是我重要的家人，我需要足够的力量才能守护大家。"
  },
  [85051403] = {
    id = 85051403,
    name = "身为姐姐的压力其实很大，遥姐自然不必说，希尔、艾莉亚和蕾米几个人，在一些方面早已比我做的更好。"
  },
  [85051404] = {
    id = 85051404,
    name = "大叔和遥姐经常为了魔物的事情忙碌，我也希望能有更强的实力，可以为他们再分担一些。"
  },
  [85051405] = {
    id = 85051405,
    name = "有姐妹们陪在身边，能亲眼见识书中的世界......我已经很满足了，若说还有遗憾，大概就是哥哥和大叔的伴侣了。"
  },
  [85061401] = {
    id = 85061401,
    name = "大家按计划行动。"
  },
  [85071401] = {id = 85071401, name = "嘿咿~"},
  [85071402] = {id = 85071402, name = "咿哟！"},
  [85071403] = {id = 85071403, name = "哈~"},
  [85081401] = {
    id = 85081401,
    name = "这个怎么样？"
  },
  [85081402] = {id = 85081402, name = "看好咯~"},
  [85091401] = {
    id = 85091401,
    name = "这点问题交给我！"
  },
  [85091402] = {
    id = 85091402,
    name = "别小看知识的力量！"
  },
  [85101401] = {id = 85101401, name = "唔呃！"},
  [85101402] = {id = 85101402, name = "呃呃~"},
  [85101403] = {id = 85101403, name = "唔诶。"},
  [85111401] = {
    id = 85111401,
    name = "稍微，有点勉强了呢。"
  },
  [85121401] = {
    id = 85121401,
    name = "这可不是姐姐该有的样子......"
  },
  [85131401] = {
    id = 85131401,
    name = "虽然胜利了，但这次的作战计划还有不少地方可以继续完善。"
  },
  [85141401] = {
    id = 85141401,
    name = "如果出发前我能找遥姐和大叔能帮忙完善一下作战计划，结果或许会完全不同。"
  },
  [85151401] = {
    id = 85151401,
    name = "哥哥记得今天是什么日子吗？——没错，是哥哥的生日！就算身为队长，今天也应该给自己放个假好好庆祝一下吧？呵呵~走吧，姐妹们应该都到了。"
  },
  [85161401] = {
    id = 85161401,
    name = "每到新年大叔都会给我们做汤圆，今年估计他被什么事耽误了还没回来，就由我来给大家做吧，呵呵呵，毕竟这也算我们“家”的传承嘛。"
  },
  [85171401] = {
    id = 85171401,
    name = "哥哥来尝一下我新做的甜点。——味道怎么样，会不会太甜了？呵呵呵，你喜欢就最好了，还有很多请慢慢吃，我去给你倒杯茶。"
  },
  [85181401] = {
    id = 85181401,
    name = "今天不管怎么恶作剧都没关系吧？我也去准备一下，今天要让妹妹们知道她们平时那些恶作剧都只是小孩子的游戏，呵呵呵~"
  },
  [85191401] = {
    id = 85191401,
    name = "还相信会有神秘人半夜翻窗来送礼的妹妹们真是可爱啊，不过守护妹妹们的天真幻想，也是我们每一个“成熟的”家人应该做的事。"
  },
  [85011411] = {
    id = 85011411,
    name = "暗夜侍者龙舌兰，应您的邀约而来。那么，让我们先聊聊后续的计划如何？"
  },
  [85021411] = {
    id = 85021411,
    name = "想要在深渊中平安前行，除了细致的行程规划，还要学会随机应变才行。"
  },
  [85031411] = {
    id = 85031411,
    name = "让暗夜侍者成为深渊中的传奇、这并不是一件容易的事。不过“不容易”只是对普通人而言，只要我们全力以赴，一定可以做到。"
  },
  [85031412] = {
    id = 85031412,
    name = "深渊中的每个人都在追寻自己所渴求的一切，可是却只有少数人可以如愿。有时候，比起盲目的行动，缜密的计划才是成功的关键。"
  },
  [85041411] = {
    id = 85041411,
    name = "只要坚信自己所做的决定，便无所谓前路是坎坷还是坦途。"
  },
  [85041412] = {
    id = 85041412,
    name = "你已经展现了自己的诚意，那么、便让你见识一下我的意志吧！"
  },
  [85041413] = {
    id = 85041413,
    name = "无论作为探险者还是执事，我都没有止步的理由，为了实现我们的目标，我必须前进！"
  },
  [85051411] = {
    id = 85051411,
    name = "拥有贤明的雇主和足以施展才华的舞台是无数执事梦寐以求的事，而主家给我了这样的机会。"
  },
  [85051412] = {
    id = 85051412,
    name = "深渊中的危险远远不止魔物和环境，不过不管会有怎样的危险，也无法阻止我前行。"
  },
  [85051413] = {
    id = 85051413,
    name = "少爷非常宠溺大小姐，不管她有怎样的想法都会予以支持和肯定。"
  },
  [85051414] = {
    id = 85051414,
    name = "无论怎样动人的传说，都只属于那些最后活下来的人，而在那之前的无数挑战者，没人会记得他们的姓名。"
  },
  [85051415] = {
    id = 85051415,
    name = "在大小姐有需要时我自当全力以赴，在此之外，我也不会错过任何一个可以提升自己的机会。"
  },
  [85061411] = {
    id = 85061411,
    name = "希望都是些合格的对手。"
  },
  [85071411] = {id = 85071411, name = "哼！"},
  [85071412] = {id = 85071412, name = "嘿！"},
  [85071413] = {id = 85071413, name = "哈啊！"},
  [85081411] = {
    id = 85081411,
    name = "看招——"
  },
  [85081412] = {
    id = 85081412,
    name = "你、赢不了。"
  },
  [85091411] = {
    id = 85091411,
    name = "来吧，一决胜负！"
  },
  [85091412] = {
    id = 85091412,
    name = "由我来了结一切。"
  },
  [85101411] = {id = 85101411, name = "唔。"},
  [85101412] = {id = 85101412, name = "呃啊。"},
  [85101413] = {id = 85101413, name = "呃。"},
  [85111411] = {
    id = 85111411,
    name = "哼，不过如此。"
  },
  [85121411] = {
    id = 85121411,
    name = "太、轻敌了吗......"
  },
  [85131411] = {
    id = 85131411,
    name = "还算有些实力，不过也仅仅是有些实力罢了。"
  },
  [85141411] = {
    id = 85141411,
    name = "失败，没有借口。"
  },
  [85151411] = {
    id = 85151411,
    name = "今天行动结束后还有什么安排吗？有？还有哪些事今天必须完成，全部交给我吧。我们准备了一个生日庆祝会，如果您这位主角缺席就没有意义了。"
  },
  [85161411] = {
    id = 85161411,
    name = "从主家运来的物资都齐了。准备用来做什么？依照罗斯伯尔家的传统，我们准备在此举办新年庆典，这些物资如果全靠在深渊中采购要花很长时间，所以便直接从主家运送过来了。"
  },
  [85171411] = {
    id = 85171411,
    name = "什么事这么高兴？那些甜点？看起来像是可以随处买到的点心，有什么特别的吗？——大小姐亲自做的？！她竟然也会亲手做点心了吗......"
  },
  [85181411] = {
    id = 85181411,
    name = "每逢这种节日，主家的习俗便是全体出动，满城寻找大小姐的踪影。像是特别的捉迷藏活动？呵呵，或许吧。"
  },
  [85191411] = {
    id = 85191411,
    name = "节日快乐~这礼物是主家送来的，不必推辞，探险队中的每个人都有。在这种特别的节日里，为每个同伴送上祝福也是主家的习俗。"
  },
  [85011421] = {
    id = 85011421,
    name = "这份邀请上的地址是这里吗？呵呵~我就是莱克西，请多多指教啦~如果有什么有趣好玩的事请务必告诉我！"
  },
  [85021421] = {
    id = 85021421,
    name = "行程安排？这种事不必告诉我，对我而言不管去哪都是一件值得期待的事，带着未知前行、有时候会有更多惊喜~"
  },
  [85031421] = {
    id = 85031421,
    name = "深渊如同一个未曾开发的世界，除了浊炁和魔物，还有许多只属于这个世界的景色，如果能走遍深渊，必然可以收获许多前所未有的体验。"
  },
  [85031422] = {
    id = 85031422,
    name = "比起许多枯燥繁琐的工作，探险者的生活有趣多了~不过即便是那些枯燥繁琐的事务，也从来不会缺少愿意为之全力以赴的人。"
  },
  [85041421] = {
    id = 85041421,
    name = "这是给我的惊喜吗？不过还没能好好感受就结束了，下次再有这种事可以提前告诉我~"
  },
  [85041422] = {
    id = 85041422,
    name = "深渊就如同一个布满荆棘的花园，绝美的风景只属于那些不惧危险、敢于突破重重荆棘的人。"
  },
  [85041423] = {
    id = 85041423,
    name = "以我现在的实力足以去到很多地方，但想要走遍深渊，必须还要付出更多努力！"
  },
  [85051421] = {
    id = 85051421,
    name = "我曾跟随导师游历各地，许多名山大川和偏远荒地都留下了我们的足迹。"
  },
  [85051422] = {
    id = 85051422,
    name = "很久前我便在家里的研究资料中见识了不少魔物，我自认为对它们已经足够了解，但实际接触后我才发现自己的“了解”有多么片面~"
  },
  [85051423] = {
    id = 85051423,
    name = "父母一直希望我能继承他们的事业，可是我对那些枯燥的研究根本提不起兴趣。如果勉强自己去做不喜欢的事，我觉得并不会有太多意义。"
  },
  [85051424] = {
    id = 85051424,
    name = "在我看来真正的探险者应该是积极探寻未知存在、开拓这个世界的人，那些追逐名利财富的人更像是一味追逐利益的淘金者。"
  },
  [85051425] = {
    id = 85051425,
    name = "绝美的风景只是我追求的一部分，我真正渴望的、是把握好生命中的每一刻，热爱自己拥有的一切，尽情享受这世界上所有的美好！"
  },
  [85061421] = {
    id = 85061421,
    name = "我很期待今天的目的地。"
  },
  [85071421] = {id = 85071421, name = "呀！"},
  [85071422] = {id = 85071422, name = "嘿~"},
  [85071423] = {id = 85071423, name = "哈~"},
  [85081421] = {
    id = 85081421,
    name = "躲不掉的。"
  },
  [85081422] = {
    id = 85081422,
    name = "乖乖投降吧~"
  },
  [85091421] = {
    id = 85091421,
    name = "妨碍我、可算不上明智的决定。"
  },
  [85091422] = {
    id = 85091422,
    name = "不管怎么抵抗，都只是白费力气。"
  },
  [85101421] = {id = 85101421, name = "呜呃~"},
  [85101422] = {id = 85101422, name = "呀啊~"},
  [85101423] = {id = 85101423, name = "诶~"},
  [85111421] = {
    id = 85111421,
    name = "我可不会轻易放过找麻烦的家伙。"
  },
  [85121421] = {
    id = 85121421,
    name = "旅途......到此为止了吗......"
  },
  [85131421] = {
    id = 85131421,
    name = "沿途的阻碍都已清除完毕，稍微休整一下继续赶路吧~"
  },
  [85141421] = {
    id = 85141421,
    name = "旅途中难免遭遇各种意外，不必放在心上。"
  },
  [85151421] = {
    id = 85151421,
    name = "今天是你的生日？哇哦~那必须要好好庆祝一下！走吧，我带你去一个非常棒的地方，比起寻常的酒馆，在那里庆祝会有趣很多~"
  },
  [85161421] = {
    id = 85161421,
    name = "新年登山是我跟随导师修行时养成的习惯，在新年里登高望远会有一种和平时完全不同的感觉！一起去吗？走嘛~那种快乐只有亲自体验之后才能明白。"
  },
  [85171421] = {
    id = 85171421,
    name = "哼哼~看！——是的，一束花，但它可不是普通的花。这种花盛放的时间非常短暂，但却代表坚韧与永恒，很厉害吧？！送给你，我觉得它和你很配！"
  },
  [85181421] = {
    id = 85181421,
    name = "对那些关于节日的传说我很感兴趣，不过、能和大家一起享受节日会让我感到更加幸福！庆典已经快开始了，我们也准备一下出发吧！"
  },
  [85191421] = {
    id = 85191421,
    name = "节日快乐！——节日礼物？呵呵，我当然有准备，不过嘛、寻常礼物感觉没什么新意，等今天行动结束，我带你去见识一个特别的礼物！"
  },
  [85011431] = {
    id = 85011431,
    name = "拯救那些身陷困境之人总是令人愉悦，您一定也是这样认为的吧？呵呵~我便是圣歌的赦罪人，应您的邀约而来~"
  },
  [85021431] = {
    id = 85021431,
    name = "无法帮上大家的每一刻都让我感到无比煎熬......有什么我能做的吗？不管什么事都可以——"
  },
  [85031431] = {
    id = 85031431,
    name = "有人觉得圣歌的理念和行动太过天真，也有人觉得圣歌所做的一切非常高尚，呵呵~不管天真也好，高尚也罢，我们所追寻的只会是我们所信仰的正义，而不是别人对我们的看法。"
  },
  [85031432] = {
    id = 85031432,
    name = "为什么大家都不愿意让我帮忙呢？难道真如那些人所说，我看上去不像是什么正经人？真是遗憾啊，明明是那么令人愉悦的事......"
  },
  [85041431] = {
    id = 85041431,
    name = "不管别人如何评说，我也无法容忍自己对那些陷入困境的人坐视不理。"
  },
  [85041432] = {
    id = 85041432,
    name = "为了消弭这深渊中的苦难，我会解决所有罪恶的源头——"
  },
  [85041433] = {
    id = 85041433,
    name = "实现正义和美好的手段有很多，比如、将所有不懂爱的存在抹除......呵呵，开玩笑的，不用紧张~"
  },
  [85051431] = {
    id = 85051431,
    name = "每当想到深渊中还有那么多需要被救赎的人，我的心中便会涌现出难以压抑的情绪。"
  },
  [85051432] = {
    id = 85051432,
    name = "作为圣歌的成员，我们虽然有着共同的信念，但成员之间彼此的行事作风却存在不少分歧，所以在非必要时我们很少一起行动。"
  },
  [85051433] = {
    id = 85051433,
    name = "弱小即是罪过，如果没有足够的实力，无论如何强烈、崇高的信念，听起来甚至还不如那些醉汉口中的胡话动听。"
  },
  [85051434] = {
    id = 85051434,
    name = "贯彻正义，守护美好和希望这种事绝非几句话就能做到，有时候不得不使用一些特别的手段......"
  },
  [85051435] = {
    id = 85051435,
    name = "我并不在意自己所作的一切是否能算得上高尚，但如果能为这个世界带来一些变化，哪怕只有一点点，也足以令我感到前所未有的满足。"
  },
  [85061431] = {
    id = 85061431,
    name = "这些小事就交给我吧~"
  },
  [85071431] = {id = 85071431, name = "呀咿！"},
  [85071432] = {id = 85071432, name = "嘿呀！"},
  [85071433] = {id = 85071433, name = "呵呵呵~"},
  [85081431] = {
    id = 85081431,
    name = "能挡得住吗？"
  },
  [85081432] = {
    id = 85081432,
    name = "结局、已经注定。"
  },
  [85091431] = {
    id = 85091431,
    name = "谁也无法从这里逃走~"
  },
  [85091432] = {
    id = 85091432,
    name = "我会消弭所有罪恶的根源。"
  },
  [85101431] = {id = 85101431, name = "呃啊~"},
  [85101432] = {id = 85101432, name = "唔啊~"},
  [85101433] = {id = 85101433, name = "哈啊~"},
  [85111431] = {
    id = 85111431,
    name = "久违的感觉~"
  },
  [85121431] = {
    id = 85121431,
    name = "什么......也帮不上了......"
  },
  [85131431] = {
    id = 85131431,
    name = "这点小事还无法让我满足~还有什么我能为您做的吗？"
  },
  [85141431] = {
    id = 85141431,
    name = "被失败孕育出的胜利会更加美味~"
  },
  [85151431] = {
    id = 85151431,
    name = "这是专属于你的节日，生日快乐~呵呵，今天有任何心愿都可以告诉我，只要不违背我们所信仰的正义，我都会为你实现~"
  },
  [85161431] = {
    id = 85161431,
    name = "又到参加新年聚会的日子，不知道其他人都到了没......我们也抓紧时间出发吧，免得晚了又被埋怨，而且早一点到可以听到更多有趣的故事~"
  },
  [85171431] = {
    id = 85171431,
    name = "非常感谢你这段时间以来的关照，我准备了一点小礼物，希望你能喜欢——雕像？不，这是我按照自己形象制作的特别点心，你、会喜欢的吧？"
  },
  [85181431] = {
    id = 85181431,
    name = "今天是幽灵的节日吧？呵呵~幽灵鬼魅都是邪祟，必须予以驱逐~一起去外面走走吧，我们可不能让邪祟打扰了庆典。"
  },
  [85191431] = {
    id = 85191431,
    name = "又到了这个向大家派发礼物的节日~不过每年都会有人拒绝我的礼物，我看起来就那么奇怪吗？喂，今年能陪我一起去吗？这样一来说不定就不会有人拒绝我们了。"
  },
  [85011441] = {
    id = 85011441,
    name = "您好，我是浮光，在深渊航线中还只能算得上新人，非常感谢您的信任和邀约，我会好好珍惜这次机会。"
  },
  [85021441] = {
    id = 85021441,
    name = "最近的行动中我有了不少收获，可是一时还无法完全理解，如果能有机会多验证一下，或许能有新的领悟。"
  },
  [85031441] = {
    id = 85031441,
    name = "婆婆让我出来寻找自己的幸福，可我并不知道幸福是什么，也不该如何寻找，迷茫之际，我遇到了深渊航线的大家......我想深渊航线或许是命运给予我寻找幸福的线索，所以便毫不犹豫的加入了。"
  },
  [85031442] = {
    id = 85031442,
    name = "组织中明明规定了“只要接受了邀约，无论怎样的任务都不能逃避和畏惧”，可是我在执行一些任务时，还是会觉得害怕或紧张......"
  },
  [85041441] = {
    id = 85041441,
    name = "虽然我还不懂幸福是什么，但那些会让人感到难过的事一定不是幸福。"
  },
  [85041442] = {
    id = 85041442,
    name = "只要战斗就可以了吗？如果只要战斗下去，就能明白幸福的含义，那么我、会全力以赴！"
  },
  [85041443] = {
    id = 85041443,
    name = "深渊航线的大家都拥有只有她们才能做到的事，我也想找到只有自己才能做到的事。"
  },
  [85051441] = {
    id = 85051441,
    name = "我一直和婆婆一起生活，她离开之后，我便失去了所有牵绊，只能迷茫的在深渊中游荡。"
  },
  [85051442] = {
    id = 85051442,
    name = "深渊航线中的生活总是很忙碌，我不讨厌忙碌，如果我的努力能守护大家的幸福，我也会感到满足。"
  },
  [85051443] = {
    id = 85051443,
    name = "我曾询问深渊航线的大家什么是幸福，可是每个人的回答都不一样。虽然没能得到想要的答案，但我至少明白了幸福不止一种。"
  },
  [85051444] = {
    id = 85051444,
    name = "我很喜欢现在所拥有的一切，这让我第一次对力量有了渴望，我渴望拥有足以守护我现在重要事物的力量。"
  },
  [85051445] = {
    id = 85051445,
    name = "有时候太过偏执的渴望反而会迷惑自己的内心，或许从我和大家相遇的那一刻起，我就已经找到了属于自己的幸福。"
  },
  [85061441] = {
    id = 85061441,
    name = "我会珍惜这次行动的机会。"
  },
  [85071441] = {id = 85071441, name = "呵！"},
  [85071442] = {id = 85071442, name = "呀！"},
  [85071443] = {id = 85071443, name = "哈！"},
  [85081441] = {
    id = 85081441,
    name = "专注——"
  },
  [85081442] = {
    id = 85081442,
    name = "全力以赴。"
  },
  [85091441] = {
    id = 85091441,
    name = "我会证明自己的实力。"
  },
  [85091442] = {
    id = 85091442,
    name = "这是只有我才能做到的事！"
  },
  [85101441] = {id = 85101441, name = "呀啊。"},
  [85101442] = {id = 85101442, name = "呃。"},
  [85101443] = {id = 85101443, name = "啊~"},
  [85111441] = {
    id = 85111441,
    name = "这是，无法阻止我的。"
  },
  [85121441] = {
    id = 85121441,
    name = "这......并不是我想要的结局......"
  },
  [85131441] = {
    id = 85131441,
    name = "真是一次完美的行动，我也获得了一些新的感悟。"
  },
  [85141441] = {
    id = 85141441,
    name = "非常抱歉......我没能把握好这次行动的机会。"
  },
  [85151441] = {
    id = 85151441,
    name = "婆婆说过，生日是非常重要的日子，这一天中只能做开心的事。请问，有什么事能让您觉得开心呢？"
  },
  [85161441] = {
    id = 85161441,
    name = "以前新年时，我都会和婆婆一起做许愿签，然后把心愿写在上面挂在高高的地方。婆婆说，挂得越高，愿望便会越容易实现。"
  },
  [85171441] = {
    id = 85171441,
    name = "请问、可以收下这些点心吗？只是想感谢您长期以来对我的关照。当然，我知道这远远不够，可是，如果您能收下的话......"
  },
  [85181441] = {
    id = 85181441,
    name = "我并不讨厌这种热闹的节日，只是不知道该怎样才能融入大家......只需要跟上巡游的人群就可以了吗？真的吗？那、那我去试试。"
  },
  [85191441] = {
    id = 85191441,
    name = "以前每逢这种节日，我的父母都会在家中举办酒会，我会趁大家跳舞时拼命的吃很多食物，因为当酒会结束后，我们会有很长时间无法填饱肚子。"
  },
  [85011451] = {
    id = 85011451,
    name = "我是星云，听深渊航线的同伴说有赚钱，咳~呵呵呵，听说这里正在招募支援探险的人员？这方面的经验我还算丰富，请多多指教。"
  },
  [85021451] = {
    id = 85021451,
    name = "今天的行程是怎样安排的呢，沿途上有听说宝物或者圣物的传闻吗？嘿嘿，我只是好奇，毕竟如果能发现些好东西，大家都能收获更多嘛~"
  },
  [85031451] = {
    id = 85031451,
    name = "深渊航线的宗旨是为客人提供最完备的护卫支援服务，在平时没有指定任务的时候大家可以自行探险，相当于可以拿多份酬劳！可是组织中的大家对任务之外的赚钱活动都不感兴趣，稍微有点可惜。"
  },
  [85031452] = {
    id = 85031452,
    name = "他们总是成天和我念叨“钱财都是身外之物”“组织规定不得觊觎客人的任何财物”之类的话，我自然明白。可是我家里很穷，祖辈都只是普通生意人，如果我不努力挣钱早晚会流落街头。"
  },
  [85041451] = {
    id = 85041451,
    name = "赚钱和帮助别人都是很快乐的事，如果在赚钱的同时还能帮到别人，那样就能拥有双倍的快乐。"
  },
  [85041452] = {
    id = 85041452,
    name = "现在我能胜任那些酬劳更高的工作了吧？呵呵，开玩笑的，赚钱固然重要，但对现在的客人负责更加重要。"
  },
  [85041453] = {
    id = 85041453,
    name = "要实现目标就不能满足于现状。呼~加油星云，再努力一点，一定可以做到！"
  },
  [85051451] = {
    id = 85051451,
    name = "我成为探险者之后给自己定了一个小目标，先赚1个亿！嗯？为什么你的眼神那么奇怪，难道很离谱吗?"
  },
  [85051452] = {
    id = 85051452,
    name = "比起其他组织，深渊航线的规定虽然不算多，但是却极其严格，我的任务酬劳几乎都变成了罚金！"
  },
  [85051453] = {
    id = 85051453,
    name = "混乱的深渊确实是个赚钱的好地方，但想要安全的赚钱却不容易，所以我对组织想要守护深渊秩序的目的非常赞同！"
  },
  [85051454] = {
    id = 85051454,
    name = "父亲常和我念叨“商人也必须要有自己的底线和信念”，我虽然想赚钱，但任何会损害大家利益、影响任务的事我绝不会去做。"
  },
  [85051455] = {
    id = 85051455,
    name = "我很像一个财迷吧？嘿嘿，我知道大家对我看法，请不用在意。我确实渴望赚钱，但我也渴望能在这个世界找到属于自己的未来。"
  },
  [85061451] = {
    id = 85061451,
    name = "走吧，我已经准备好了。"
  },
  [85071451] = {id = 85071451, name = "哟咿~"},
  [85071452] = {id = 85071452, name = "哈！"},
  [85071453] = {id = 85071453, name = "呀！"},
  [85081451] = {
    id = 85081451,
    name = "别分心。"
  },
  [85081452] = {
    id = 85081452,
    name = "尝尝这个！"
  },
  [85091451] = {
    id = 85091451,
    name = "见识一下我的全力吧！"
  },
  [85091452] = {
    id = 85091452,
    name = "趁早投降会更好哦~"
  },
  [85101451] = {id = 85101451, name = "呃啊~"},
  [85101452] = {id = 85101452, name = "呜哇。"},
  [85101453] = {id = 85101453, name = "呀~"},
  [85111451] = {
    id = 85111451,
    name = "挣钱的工作，从来都不会轻松。"
  },
  [85121451] = {
    id = 85121451,
    name = "我的...... 1个亿......"
  },
  [85131451] = {
    id = 85131451,
    name = "胜利是我们的呢！呐呐，打扫战场的事可以交给我吗？"
  },
  [85141451] = {
    id = 85141451,
    name = "再让我试一次吧，绝不能让它们夺走我的酬劳！"
  },
  [85151451] = {
    id = 85151451,
    name = "今天是你的生日吧？嘿嘿，快来看，我准备了一个超豪华的蛋糕！嗯？客人的隐私......我只是偶然听到的，不算窥探吧？！而且，我钱全都用来买这个超豪华蛋糕了，已经没钱交罚金了，呜呜——"
  },
  [85161451] = {
    id = 85161451,
    name = "我在家时，新年就是大家聚在一起算账的日子。在我的记忆中，好像从没听父亲说过赚钱，几乎每个新年都是在他和长辈们的叹息声中度过。"
  },
  [85171451] = {
    id = 85171451,
    name = "怎样做才能表达最高的谢意呢？果然还是做一份超豪华的“至尊点心”吧，从材料到制作工具，全部挑选顶级的，这样一定能传达心意！"
  },
  [85181451] = {
    id = 85181451,
    name = "锵锵~节日快乐！快看，换装道具，糖果，我准备了非常多，在节日里贩售特定商品一定可以大赚一笔！要不......你来帮帮我？利润......五五分？"
  },
  [85191451] = {
    id = 85191451,
    name = "这一堆礼物都是父亲托人送来的，每个人都有。哈啊~明明我已经成为探险者这么久了，可是却完全没赚到钱......不过如果能有重新选择的机会，我依然会选择探险者，不然就会失去和大家成为同伴的机会。"
  },
  [85011461] = {
    id = 85011461,
    name = "呀嚯~我是岫，大名鼎鼎的真理之钥的成员——欸？没听过？！真是没办法，让我好好和你介绍一下，可千万别吓着了。"
  },
  [85021461] = {
    id = 85021461,
    name = "我们今天去哪里呢？周围基本都已经探索过了，要不要去远一点的地方看看？"
  },
  [85031461] = {
    id = 85031461,
    name = "“探寻魔法和武技的根源”这种事并没有想象中那么容易，我们目前收获非常有限......不过我相信只要和大家在一起，不管怎样的困难都难不倒我们！"
  },
  [85031462] = {
    id = 85031462,
    name = "我刚到深渊就发现朋友们都加入了真理之钥，所以我也就主动申请了！不管什么时候，能和朋友在一起才是最快乐的吧？！嘿嘿~"
  },
  [85041461] = {
    id = 85041461,
    name = "这种体验太棒了，我们再来一次吧！暂时还不行？"
  },
  [85041462] = {
    id = 85041462,
    name = "再一次完成了蜕变，如果能继续下去，说不定我会成为真理之钥中最先发现武技奥秘的人！"
  },
  [85041463] = {
    id = 85041463,
    name = "我所有的力量都是为了追寻幸福和快乐，无论成长到什么地步，这一点永远不会改变。"
  },
  [85051461] = {
    id = 85051461,
    name = "所谓的随机应变就是完全不去考虑计划，想到什么就做什么，对吧？呵呵，这种事我最擅长了！"
  },
  [85051462] = {
    id = 85051462,
    name = "多亏了同伴们的支持我才能走到现在，今后我也要努力成为大家的依靠才行！"
  },
  [85051463] = {
    id = 85051463,
    name = "我毕业时老师们给我的评价都非常“糟糕”，如果他们知道我现在正为了一个“伟大的目标”而努力，会是怎样的心情呢？"
  },
  [85051464] = {
    id = 85051464,
    name = "深渊中的生活比地面辛苦很多，但我并不后悔自己的选择，如果没有成为探险者便不会拥有现在的一切。"
  },
  [85051465] = {
    id = 85051465,
    name = "很多时候我们所遇到的困难都不是自己能决定的，但我们可以决定自己面对困难时的心情，无论何时也不能忘了这一点。"
  },
  [85061461] = {
    id = 85061461,
    name = "要去新的地方了吗？"
  },
  [85071461] = {id = 85071461, name = "咻~"},
  [85071462] = {id = 85071462, name = "嘿咿~"},
  [85071463] = {id = 85071463, name = "哟咿~"},
  [85081461] = {
    id = 85081461,
    name = "瞄准......射击！"
  },
  [85081462] = {
    id = 85081462,
    name = "站好别动。"
  },
  [85091461] = {
    id = 85091461,
    name = "一起解决吧！"
  },
  [85091462] = {
    id = 85091462,
    name = "我可不会输给大家~"
  },
  [85101461] = {id = 85101461, name = "呀。"},
  [85101462] = {id = 85101462, name = "啊~"},
  [85101463] = {id = 85101463, name = "呃啊~"},
  [85111461] = {
    id = 85111461,
    name = "有些小看敌人了。"
  },
  [85121461] = {
    id = 85121461,
    name = "不能......和大家一起了......"
  },
  [85131461] = {
    id = 85131461,
    name = "不管是“老对手”还是“新朋友”，都无法阻止我们前进。"
  },
  [85141461] = {
    id = 85141461,
    name = "啊，真是不甘心......不过我已经掌握了敌人的弱点。"
  },
  [85151461] = {
    id = 85151461,
    name = "锵锵~生日快乐！怎么知道的？嘿嘿，秘密！我和大家一起准备了蛋糕和食物，等你来了就能开始庆祝会啦~"
  },
  [85161461] = {
    id = 85161461,
    name = "这种热闹的日子最适合和大家一起外出游玩了，你也是这样想的，对吧？嘿嘿，我这就去通知大家！"
  },
  [85171461] = {
    id = 85171461,
    name = "请看看这是什么！——点心？不，它可不是普通的点心，而是被赋予了快乐和幸福魔法的点心！送给你，希望今后你也能快乐和幸福哦。"
  },
  [85181461] = {
    id = 85181461,
    name = "去年这时，我和大家比赛看谁能拿到更多的糖果，结果输的好惨......今年我一定要赢回来！"
  },
  [85191461] = {
    id = 85191461,
    name = "好叻，写完啦！我刚才在写修行感悟呢，这可是真理之钥成员之间专属的节日礼物，很厉害哦！"
  },
  [85011471] = {
    id = 85011471,
    name = "只要有宝物的地方，就有荒野游骑兵的身影！荒野游骑兵、辉煌骑士猎鹰、参上！指挥的工作就拜托你了，可以吧指挥官~"
  },
  [85021471] = {
    id = 85021471,
    name = "近期行程中的几处目的地好像都比较危险，嗯~果然需要把准备工作做得更完善一点，毕竟有备无患，哈哈哈~"
  },
  [85031471] = {
    id = 85031471,
    name = "追寻宝物是一件无比浪漫的事，不过也是一件无比危险的事，毕竟在深渊中，没有人知道危险什么时候来临~所以不管怎样的行动，都必须小心谨慎，绝不能轻敌大意！"
  },
  [85031472] = {
    id = 85031472,
    name = "不管是做事目的还是行事作风，荒野游骑兵和其他寻宝组织完全不同，我们的虽然也在寻宝，但大家追寻的都不是那种传统意义上的宝物，我们渴求的是更有意义的存在。"
  },
  [85041471] = {
    id = 85041471,
    name = "咳咳~骑士律令第二条，在任何情况下都必须坚守安全第一的原则！"
  },
  [85041472] = {
    id = 85041472,
    name = "所谓的辉煌骑士，便是如同太阳一般耀眼的存在！怎么样，现在的我足够耀眼了吧？！"
  },
  [85041473] = {
    id = 85041473,
    name = "以我现在的力量，足以前往这片大地的尽头！太夸张了？不，就算到不了尽头，也差不了多少。"
  },
  [85051471] = {
    id = 85051471,
    name = "当我决定成为探险者时，父亲便带我来深渊修行历练，他是位非常老练的探险者，我现在掌握的所有技巧几乎都是他传授的。"
  },
  [85051472] = {
    id = 85051472,
    name = "寻宝人必须清楚勇敢和鲁莽的区别，否则在追寻宝物的过程中，很容易让自己陷入绝境。"
  },
  [85051473] = {
    id = 85051473,
    name = "有时候一瞬间的犹豫就可能造成无法挽回的后果，所以在该放弃时绝不能犹豫，这个世界上没有任何事物能比得上人的生命。"
  },
  [85051474] = {
    id = 85051474,
    name = "如果拥有足够的力量，自然可以更好的保障安全，可是如果一味的依靠力量，反而会干扰自己的判断。"
  },
  [85051475] = {
    id = 85051475,
    name = "不少人都喜欢为了一些无谓的事情消沉沮丧，真奇怪啊~难道消沉沮丧就能解决问题吗？唉~人生的时间并不多，浪费在这些事上太可惜了。"
  },
  [85061471] = {
    id = 85061471,
    name = "辉煌骑士，领命！"
  },
  [85071471] = {id = 85071471, name = "哈！"},
  [85071472] = {id = 85071472, name = "呀！"},
  [85071473] = {id = 85071473, name = "喝啊！"},
  [85081471] = {
    id = 85081471,
    name = "只有这点程度吗。"
  },
  [85081472] = {
    id = 85081472,
    name = "哈哈，再来再来。"
  },
  [85091471] = {
    id = 85091471,
    name = "一个敌人也别想从我这越过！"
  },
  [85091472] = {
    id = 85091472,
    name = "见识一下辉煌骑士真正的实力吧！"
  },
  [85101471] = {id = 85101471, name = "哇！"},
  [85101472] = {id = 85101472, name = "唔。"},
  [85101473] = {id = 85101473, name = "呃。"},
  [85111471] = {
    id = 85111471,
    name = "只是皮外伤而已，不用担心。"
  },
  [85121471] = {
    id = 85121471,
    name = "虽然早有觉悟，可是......"
  },
  [85131471] = {
    id = 85131471,
    name = "大家都平安无事，真是完美！去看看有没有什么好东西吧~"
  },
  [85141471] = {
    id = 85141471,
    name = "这里交给我，大家快撤！"
  },
  [85151471] = {
    id = 85151471,
    name = "锵锵~生日快乐！虽然很想给你准备礼物来着，可是我翻遍了仓库也没找到合适的......不如这样吧，我先欠你一份礼物，如果你有任何想要的都可以告诉我，我一定会帮你弄来！"
  },
  [85161471] = {
    id = 85161471,
    name = "大家已经把新年庆祝会的地点定好了， 我们一起过去吧——不方便？怎么会！你既然做了我的指挥官，我们自然就是一伙了吧？！"
  },
  [85171471] = {
    id = 85171471,
    name = "这个、送给你！没错，就是一把锁。不过这可不是普通的锁，据说这种造型的锁能带来最紧密牢靠的关系，很适合我们吧？！"
  },
  [85181471] = {
    id = 85181471,
    name = "每年骑士长都会弄出一些新的恶作剧，让人防不胜防！不行，今年不能再输了，否则又要被她嘲笑，帮我想想办法吧，指挥官！"
  },
  [85191471] = {
    id = 85191471,
    name = "在今天这种节日里，我们每个人都要为组织中其他成员做一件事。今年我准备把父亲传授我的鉴宝技巧分享给大家，不过展示这些技巧需要一些宝物做示范......指挥官能帮帮我吗？！"
  },
  [85011481] = {
    id = 85011481,
    name = "你好，听闻这里正在招募探索深渊的同伴？我是舒芙蕾，来自自由学派，希望今后能一起探寻深渊中那些传闻。"
  },
  [85021481] = {
    id = 85021481,
    name = "我准备了红茶，要试试吗？呵呵~这是我用学派茶会上学到的“泡茶诀窍”泡制的，味道应该会很不错~"
  },
  [85031481] = {
    id = 85031481,
    name = "比起其他组织，自由学派更像一个“同好会”，成员间并没有任何从属关系，除了举办“学派茶会”时一些同伴会聚到一起分享情报外，平时很少联系。"
  },
  [85031482] = {
    id = 85031482,
    name = "组织中看起来很凶的人？呵呵~你说的应该是欧培拉小姐。她其实非常热情、善良，只是对自己要求太严格了，不习惯展露自己的内心。如果你见过她和自己闹别扭的样子，一定会被她的可爱迷住。"
  },
  [85041481] = {
    id = 85041481,
    name = "我渴望探寻深渊中的那些传闻，化解那些因此诞生的无谓纷争。"
  },
  [85041482] = {
    id = 85041482,
    name = "每个人都有追寻自己理想的权力，但如果在追寻理想时选择背弃希望和正义，那样的理想我绝不认同！"
  },
  [85041483] = {
    id = 85041483,
    name = "探寻深渊中的传闻，也是在探寻我自己的未来，无论前路会有多少曲折坎坷我也不会畏惧。"
  },
  [85051481] = {
    id = 85051481,
    name = "小时候我一直生活在阁楼之中，对外面的世界充满了向往，可是在见识过之后，才发现和想象中的巨大差别。"
  },
  [85051482] = {
    id = 85051482,
    name = "善良，不是懦弱，宽容，也并非放纵。拥有足够的力量，才能贯彻自己的信念！"
  },
  [85051483] = {
    id = 85051483,
    name = "自由学派的同伴们对于理想所展现出来的热情和执着，是激励我继续前行的动力。"
  },
  [85051484] = {
    id = 85051484,
    name = "宽容是这个世界上最强大的力量，它足以拯救一个人，甚至这个世界。"
  },
  [85051485] = {
    id = 85051485,
    name = "尽管混乱与纷争是深渊中现在的主旋律，但我坚信终有一天可以看到这个世界充满秩序与和平。"
  },
  [85061481] = {
    id = 85061481,
    name = "我明白我们前行的方向。"
  },
  [85071481] = {id = 85071481, name = "嘿——"},
  [85071482] = {id = 85071482, name = "呀咿~"},
  [85071483] = {id = 85071483, name = "哈！"},
  [85081481] = {
    id = 85081481,
    name = "集中精神。"
  },
  [85081482] = {
    id = 85081482,
    name = "沉着应对。"
  },
  [85091481] = {
    id = 85091481,
    name = "大家相互依靠，便足以战胜一切。"
  },
  [85091482] = {
    id = 85091482,
    name = "没有什么可以阻挡我们的信念。"
  },
  [85101481] = {id = 85101481, name = "呃啊~"},
  [85101482] = {id = 85101482, name = "呃。"},
  [85101483] = {id = 85101483, name = "咿呀。"},
  [85111481] = {
    id = 85111481,
    name = "前行之路，难免会有荆棘坎坷。"
  },
  [85121481] = {
    id = 85121481,
    name = "请继续追寻希望......不要悲伤......"
  },
  [85131481] = {
    id = 85131481,
    name = "无论怎样的战斗都是残酷的，但为了守护秩序与和平，我们别无选择。"
  },
  [85141481] = {
    id = 85141481,
    name = "大家没事吧？无论怎样的险境，我们也绝不能放弃希望。"
  },
  [85151481] = {
    id = 85151481,
    name = "请问今天有什么特殊安排吗？我准备了些蛋糕和食物，一起庆祝生日吧！诶，谁的生日？难道你把自己的生日忘记了吗？呵呵~"
  },
  [85161481] = {
    id = 85161481,
    name = "新年快乐~请问今天能和我出去一趟吗？呵呵，我想去参加学派的“新年茶会”，但是一个人去稍微有些寂寞，如果你能一起......呵呵，非常感谢~"
  },
  [85171481] = {
    id = 85171481,
    name = "母亲说如果想对一个人传达最真挚的感谢，最好的方法便是准备足够丰盛的美食。呵呵~所以这些美食都是为你一个人准备的，请慢慢品尝。"
  },
  [85181481] = {
    id = 85181481,
    name = "节日庆典似乎已经开始了，要去看看吗？呵呵，我当然也会一起参加，这种热闹的节日我可不想错过。"
  },
  [85191481] = {
    id = 85191481,
    name = "自由学派中参加交换礼物的人变多之后，我对收到的礼物就越来越期待了。由于大家的喜好不同，所以礼物种类很多，许多都是想象不到的存在~"
  },
  [85011491] = {
    id = 85011491,
    name = "初次见面，叫我缇米就好~虽然目前还只是一名不太成熟的探险者，但我正在努力学习各种事情，要多多指导我哦。"
  },
  [85021491] = {
    id = 85021491,
    name = "我听大家讲了不少对付魔物的技巧，今天的行动正好可以派上用场！"
  },
  [85031491] = {
    id = 85031491,
    name = "为了成为探险者，我可是付出了非常非常多的努力，所以我会珍惜这次历练自己的机会，顺便游历深渊，感受这个与众不同的世界。"
  },
  [85031492] = {
    id = 85031492,
    name = "作为家族中唯一的女孩，一直备受长辈的关照。那种感觉虽然很幸福，但有时候也会有些困扰......因为一直在他们的庇护下生活，我将永远无法长大。"
  },
  [85041491] = {
    id = 85041491,
    name = "啊，又见识了新的东西，果然加入探险队是一个明智的选择。"
  },
  [85041492] = {
    id = 85041492,
    name = "新鲜的事物层出不穷，新的对手也更加强大，缇米也要再努力一点，才能从容应对~"
  },
  [85041493] = {
    id = 85041493,
    name = "缇米虽然是女孩子，但在修行上，家族中的同辈没人能比得上我！"
  },
  [85051491] = {
    id = 85051491,
    name = "我的故乡在离深渊很遥远的地方，这次出来，也是我第一次独自出远门。"
  },
  [85051492] = {
    id = 85051492,
    name = "我从小就跟随家族中那些实力出色的探险者一起修行，是他们告诉了我关于深渊的一切。"
  },
  [85051493] = {
    id = 85051493,
    name = "离开家时，我和父母约定好一定会平安回去，所以在游历之外我也会努力提升实力，遵守这个约定。"
  },
  [85051494] = {
    id = 85051494,
    name = "大家都说缇米是修行的天才，天才是没有极限的，所以缇米也没有！"
  },
  [85051495] = {
    id = 85051495,
    name = "无论怎样的事都应该去尝试、经历，只有不怕失败，经历足够的风雨，才能变得成熟，对吧？嘻嘻~"
  },
  [85061491] = {
    id = 85061491,
    name = "没问题哦~"
  },
  [85071491] = {id = 85071491, name = "来咯~"},
  [85071492] = {id = 85071492, name = "哈！"},
  [85071493] = {id = 85071493, name = "嘿~"},
  [85081491] = {
    id = 85081491,
    name = "不准躲开哦~"
  },
  [85081492] = {
    id = 85081492,
