  [85121511] = {
    id = 85121511,
    name = "令人......讨厌的味道......"
  },
  [85131511] = {
    id = 85131511,
    name = "虽然没有发现新的食材有些可惜，但胜利还是令人高兴。"
  },
  [85141511] = {
    id = 85141511,
    name = "失败的味道......下次，不会再失手了......"
  },
  [85151511] = {
    id = 85151511,
    name = "生日......原来是这样的日子么......抱歉，我没有这样的经历，导师也没教过我这方面的事情。我该做些什么呢，去抓一只新的魔物可以么？"
  },
  [85161511] = {
    id = 85161511,
    name = "新年快乐，这是一些金币，请自由使用吧——嗯，不是这样？以前新年时，大人们都是这样告诉我的。"
  },
  [85171511] = {
    id = 85171511,
    name = "听说今天是代表感谢的日子，所以我去抓了几只从没见过的魔物。一起享用吧，我会用新学的烹饪方式来处理，不会中毒的。"
  },
  [85181511] = {
    id = 85181511,
    name = "今天圈落里好多人，我只是路过，就有人塞给了我一堆糖果......原来是这样么，糖果很好吃，我很喜欢这个节日。"
  },
  [85191511] = {
    id = 85191511,
    name = "听说今天会有会有一头鹿拉着一位老人跑遍世界，那头鹿是魔物么？可以跑这么远，味道应该会不错。"
  },
  [85011521] = {
    id = 85011521,
    name = "闪开闪开——耶！我是第一个到的吗，哥哥？我和姐妹们在比赛看谁能先到哥哥这里，没人比我快吧？！"
  },
  [85021521] = {
    id = 85021521,
    name = "遥姐和赛珂姐好像不在，很好，我要趁这段时间研究新的武技，等我能打赢她们的时候应该就可以不用读书了！"
  },
  [85031521] = {
    id = 85031521,
    name = "我可是姐妹中最厉害的，每次战斗我都会冲在最前面，就算有遥姐和大叔参与的战斗，依然会由我来负责正面对抗！哼哼哼我厉害吧~"
  },
  [85031522] = {
    id = 85031522,
    name = "其实我并不讨厌读书，只是我抵抗不了书本中的魔法，每次都会被它催眠。比如这样——随便翻开一本书，从第一个字开始看，三、二、一......"
  },
  [85041521] = {
    id = 85041521,
    name = "我的武技研究有了新的突破，离创造出属于我的武技又近了一点！"
  },
  [85041522] = {
    id = 85041522,
    name = "我已经领悟了大叔所说的“武技的真谛”，见识一下我自创的武技吧！"
  },
  [85041523] = {
    id = 85041523,
    name = "我又创造出了新的武技，比之前的厉害十倍，不，百倍！哈哈哈哈~"
  },
  [85051521] = {
    id = 85051521,
    name = "我原本以为成为探险者就可以不用读书了，结果还是被追着学习......"
  },
  [85051522] = {
    id = 85051522,
    name = "大叔说最强的武技需要靠自己领悟，可是我感觉每次创造的新武技都会比以前的更厉害！"
  },
  [85051523] = {
    id = 85051523,
    name = "希尔和蕾米经常和我比试，每次都是我赢，等遥姐下次回来，我也要和她比试一下！"
  },
  [85051524] = {
    id = 85051524,
    name = "最近希尔和蕾米都在认真读书，难道她们可以抵御书本中的催眠魔法？不行，我也不能输给她们，看书看书......"
  },
  [85051525] = {
    id = 85051525,
    name = "如果有一天，我的实力能超过大叔和遥姐，就可以由我来保护大家，到时候大家只要做自己想做的事情就可以了。"
  },
  [85061521] = {
    id = 85061521,
    name = "只管冲就行了吧？！"
  },
  [85071521] = {id = 85071521, name = "嘿咿！"},
  [85071522] = {id = 85071522, name = "咿啊！"},
  [85071523] = {id = 85071523, name = "哈！"},
  [85081521] = {
    id = 85081521,
    name = "不堪一击！"
  },
  [85081522] = {
