  [86407001] = {
    id = 86407001,
    name = "公会情报部突然找到了你，让你帮忙寻找他们消失的调查员，这与一个神秘的村庄有关。"
  },
  [86407002] = {
    id = 86407002,
    name = "你找到公会情报屋时，竟然遇到了侦探马尔可，她也在打听村庄的秘密。"
  },
  [86407003] = {
    id = 86407003,
    name = "你和马尔可伪装进入村庄，开始调查事件，打听到三年前曾有探险者来，但那名探险者最后失踪了，你忽然想起了同样发生在三年前的事件。"
  },
  [86407004] = {
    id = 86407004,
    name = "你与马尔可到达那名英雄曾经战斗的地方，发现了一把光辉之剑，而这把剑竟然在原地铸就了一方魔法阵。"
  },
  [86407005] = {
    id = 86407005,
    name = "你们到圈落发现了更多的失踪者，但大多数人认为是魔物所为。"
  },
  [86407006] = {
    id = 86407006,
    name = "进入山林里，马尔可找错了路，你们不得不面对一个大麻烦。"
  },
  [86407007] = {
    id = 86407007,
    name = "逃到战场遗址处时，魔物忽然退去了，原来这个魔法阵仍然在运作，并囊括了整个村庄，你忽然窥得了某些真相。"
  },
  [86407008] = {
    id = 86407008,
    name = "你们暗中村庄调查，发现了与村庄神秘交易的黑衣人，你与马尔可分头行动，回来时却发现马尔可失踪了。"
  },
  [86407009] = {
    id = 86407009,
    name = "你在后山山顶发现大量的尸骨及祭台，也找到了被抓的马尔可，这时村民启动了魔法阵。"
  },
  [86407010] = {
    id = 86407010,
    name = "魔物闯入村庄，村民变回了劫匪，你和马尔可隔岸观火，看着那群恶人自食其果。"
  },
  [86420001] = {
    id = 86420001,
    name = "一个神秘的黑衣人找到你，想跟你卖点消息。"
  },
  [86420002] = {
    id = 86420002,
    name = "金发女孩莱娜在据点挑战“长得好看的家伙”，你被她看到后，成了下一个挑战的对象，但她似乎有其他目的。"
  },
  [86420003] = {
    id = 86420003,
    name = "你与莱娜去山洞取她藏起来的宝图，发现莱娜与众不同的战斗方式。"
  },
  [86420004] = {
    id = 86420004,
    name = "你们根据线索到达废弃的据点，在魔物战斗后发现一具尸体，这时莱娜要求你去搜尸。"
  },
  [86420005] = {
    id = 86420005,
    name = "你们去往河边一无所获，你察觉藏宝图路线的不同，似乎是走错了。"
  },
  [86420006] = {
    id = 86420006,
    name = "你们穿越山坳时，碰到一队探险者，而这队探险者竟然是伪装的劫匪。"
  },
  [86420007] = {
    id = 86420007,
    name = "你与莱娜终于到了藏宝图指示的地点，在战胜魔物后，发现了宝物竟然。"
  },
  [86420008] = {
    id = 86420008,
    name = "你们成功逃出山谷，莱娜与你约定下一次同行。"
  },
  [86446001] = {
    id = 86446001,
    name = "你在魔物手中救下酷似探险者的少年埃拉，埃拉的神色却很慌忙，在跟踪他后，竟然发现了还有一个人。"
  },
  [86446002] = {
    id = 86446002,
    name = "埃拉偷偷进入一个地方消失了，而少女紧跟进入，你打算也进去看看。"
  },
  [86446003] = {
    id = 86446003,
    name = "你进入一个叫西默蒂瑞的村庄，这里与世隔绝，从未受到魔物侵扰，引得岫接连感叹，但这里的人似乎并不欢迎外来人。"
  },
  [86446004] = {
    id = 86446004,
    name = "原来岫一直在寻找世外桃源，在感叹西默蒂瑞美好的时刻，有孩童出现，让你与岫去山里找埃拉，但无意中你们发现了村庄的重大秘密。"
  },
  [86446005] = {
    id = 86446005,
    name = "你与岫在神殿找到了埃拉，原来多年前村庄发生了意外，关闭了于外界的通道，同时时间停滞了，村民们不老不死，但埃拉觉得这是一种病。"
  },
  [86446006] = {
    id = 86446006,
    name = "村长在山脚下等你们，告诉了你们异常残酷的真相，而埃拉认为村民有权知道这样的真相。"
  },
  [86446007] = {
    id = 86446007,
    name = "你们找到医师询问如何治疗病症，医师索要大量欧泊，埃拉送上了全部的积蓄。"
  },
  [86446008] = {
    id = 86446008,
    name = "进入黑森林后，你发现有人跟踪你们，但并没有找到人。"
  },
  [86446009] = {
    id = 86446009,
    name = "你们回到村庄，村民们已得知了真相，埃拉将药物交给了村长，村长率先做出了选择，但也有村民不愿意改变现状，认为一切是神的恩赐，一场争议在所难免。"
  },
  [86446010] = {
    id = 86446010,
    name = "你们快速赶赴祭坛，原来黑衣人追源晶意外发现了光阴沙漏，在双方争抢过程中，沙漏出现了破损，所有人都即将面临最后的抉择。"
  },
  [86446011] = {
    id = 86446011,
    name = "埃拉最终选择离开了西默蒂瑞，他感谢你们，去追求新的梦想，岫也对更多的未知心生向往。"
  },
  [86416001] = {
    id = 86416001,
    name = "你从魔物手里救下了一名商人，他告诉你前方据点发生了抢劫和袭击事件，劝你绕道而行。"
  },
  [86416002] = {
    id = 86416002,
    name = "你遇到一名浑身是血的女人正要对小女孩下手，赶走她后才发现是一场误会。"
  },
  [86416003] = {
    id = 86416003,
    name = "来到据点后，你听到路人讨论嚣张的劫匪，夜晚休息时听到外面有人战斗，于是追了出去。"
  },
  [86416004] = {
    id = 86416004,
    name = "你跟着人影追出据点，发现白衣女人在战斗中陷入困境，于是帮助她击退了黑衣人，并得知了她的名字......"
  },
  [86416005] = {
    id = 86416005,
    name = "你与薇一同帮助据点击退了魔物，而因为魔物袭击据点，薇而担心她的朋友海伦，你决定与薇一起去看看。"
  },
  [86416006] = {
    id = 86416006,
    name = "你们赶到海伦藏身点小木屋，发现海伦失踪了，木屋一片狼藉......"
  },
  [86416007] = {
    id = 86416007,
    name = "你们根据线索到了山林，救下一名正在被黑衣人袭击的探险者，而探险者被追杀是因为目睹一群黑衣人抓了一个女孩......"
  },
  [86416008] = {
    id = 86416008,
    name = "你们在河谷追上黑衣人救下了被绑的女孩，可惜不是海伦，于是开始逼问黑衣人......"
  },
  [86416009] = {
    id = 86416009,
    name = "你们到达了黑衣人的巢穴，却被黑衣人组织发现了，危难之际，薇做出了一个大胆的决定......"
  },
  [86416010] = {
    id = 86416010,
    name = "被薇转移后，你抓了个黑衣人，质问他们的目的，并想办法营救薇与海伦......"
  },
  [86416011] = {
    id = 86416011,
    name = "你听见路人讨论附近魔物蠢蠢欲动，威胁到附近据点，决定去森林探查情况......"
  },
  [86416012] = {
    id = 86416012,
    name = "你到山上，打算将魔物引到黑衣人的巢穴......"
  },
  [86416013] = {
    id = 86416013,
    name = "魔物冲入了据点，你趁乱将薇和海伦一起救了出来......"
  },
  [86416014] = {
    id = 86416014,
    name = "三人返回小屋，双方相互感谢，薇准备带走海伦寻找治疗她的办法。"
  },
  [86427001] = {
    id = 86427001,
    name = "一个受伤的人向你求助，得知对方竟然是从地下决斗场逃出来的......"
  },
  [86427002] = {
    id = 86427002,
    name = "你将受伤的人送到圈落，公会的人找到了你，请求你的协助调查地下决斗场......"
  },
  [86427003] = {
    id = 86427003,
    name = "你在地下决斗场遇到了一名赌徒，他告诉了你许多规矩，而这时，赌徒压中的参赛者姬玛出场了......"
  },
  [86427004] = {
    id = 86427004,
    name = "你向姬玛询问关于药物的事，得知她只是来此寻人。而这时，从决斗场逃出来的人被抓了回去，且被灌下了某种吸引浊炁的药物......"
  },
  [86427005] = {
    id = 86427005,
    name = "姬玛带你回据点并提供了情报，同时也让你帮他一起打听西比尔的事情，你们决定过几日后再次前往决斗场......"
  },
  [86427006] = {
    id = 86427006,
    name = "你们发现地下决斗场已经关闭，探查后发现了仍有留守人，在你们的逼问下，得知了一些线索......"
  },
  [86427007] = {
    id = 86427007,
    name = "你们根据线索见到了两名黑衣人，悄悄跟上后打晕了两人，并救出了他们绑架的人，知道了新的决斗场地点。"
  },
  [86427008] = {
    id = 86427008,
    name = "你们走入山腹中，发现了更加残忍的死斗场，而曾经和姬玛对战的比斗者也在，并以饮下药剂的方式获胜了，你们在决斗比赛高潮时溜入后台，找到了幕后老板......"
  },
  [86427009] = {
    id = 86427009,
    name = "你们挟持老板逃离了现场后，姬玛也从他嘴里知道了西比尔的下落，老板并没有否认想把西比尔也制成堕落者的想法，遭到姬玛的暴打......"
  },
  [86431001] = {
    id = 86431001,
    name = "你在路上突然遭遇埋伏，而这群埋伏的人发现攻击的对象好像不该是你......"
  },
  [86431002] = {
    id = 86431002,
    name = "雨中，你再次碰到那群黑衣人，而他们正在欺负一个女孩......"
  },
  [86431003] = {
    id = 86431003,
    name = "你与辉夜到黑衣人提供的位置，但黑衣人组织已人去楼空......"
  },
  [86431004] = {
    id = 86431004,
    name = "两人到了贫民窟，却发现了黑衣人组织造成的惨状，这时驻守在贫民窟的黑衣人回来了......"
  },
  [86431005] = {
    id = 86431005,
    name = "你们在山林追丢了黑衣人，发现那些魔物受某讯号控制着，而魔物也开始听从讯号袭击你们......"
  },
  [86431006] = {
    id = 86431006,
    name = "辉夜为收集情报，带你到深渊航线的临时歇脚处休息，在这里，你了解了辉夜做这份工作的原因......"
  },
  [86431007] = {
    id = 86431007,
    name = "你们在山谷遭到黑衣组织的包围和绝杀，黑衣人头目竟然献祭了队员，想和辉夜同归于尽，而你也陷入了困境......"
  },
  [86431008] = {
    id = 86431008,
    name = "你们失去了黑衣人头目的踪影，在途中碰到公会成员，他说看到了一个非常危险的人......"
  },
  [86431009] = {
    id = 86431009,
    name = "黑衣人被你们打败后变成了堕落者，你与辉夜只能将其击杀......"
  },
  [86470001] = {
    id = 86470001,
    name = "在野外，尼莉莎看到咕咕，大家都跟着咕咕追了上去......"
  },
  [86470002] = {
    id = 86470002,
    name = "你们追着小魔物到一处结界包围的山谷里，这里看起来一直有人居住，小魔物一口吃掉了屋顶的魔法石逃走了......"
  },
  [86470003] = {
    id = 86470003,
    name = "你和尼莉莎、少女一起去了圈落，寻找材料却一无所获，这时少女突然想起......"
  },
  [86470004] = {
    id = 86470004,
    name = "你们去往废弃的矿场，发现矿场已经坍塌了......"
  },
  [86470005] = {
    id = 86470005,
    name = "你和伊涅利丝将材料运送到山谷后，大家合力重铸木屋，伊涅利丝也说起了一些往事......"
  },
  [86470006] = {
    id = 86470006,
    name = "众人决定去圈落筹备物资，你看到花种，跟几人商量后，决定去买花种......"
  },
  [86470007] = {
    id = 86470007,
    name = "你们几人去往森林采集花种，合力杀掉魔物，采集种子后，却发生了意外......"
  },
  [86470008] = {
    id = 86470008,
    name = "你们回到山谷，用药剂溶解了黏液，开始布置剩下的装饰，在空地上种下了花种......"
  },
  [86461001] = {
    id = 86461001,
    name = "你正打算去和同伴们汇合，在路上遇到村民呼救，有魔物破坏他们的农作物，你随即前往，村民继续找人求救。"
  },
  [86461002] = {
    id = 86461002,
    name = "你来到村落和村民一起抵抗魔物，僵持之际遇到黑羽相助，击退魔物后，你和黑羽打算帮村民修复损毁的防御设施"
  },
  [86461003] = {
    id = 86461003,
    name = "你与村民一同来到附近森林获取材料，在获取材料时遇到魔物，与村民们配合解决了危机。"
  },
  [86461004] = {
    id = 86461004,
    name = "返回村庄后，你与黑羽说话期间，魔物再次来袭，你与黑羽展现出了相当高的默契，面对大批魔物，你们陷入困境时，同伴们出现了......"
  },
  [86461005] = {
    id = 86461005,
    name = "众人和村民来到山谷，发现很多魔物聚集，打算返回村庄再做打算，村民不小心被魔物发现，你为了救人，被迫与他人分散。"
  },
  [86461006] = {
    id = 86461006,
    name = "你与村民躲入山洞，却见到了巨型魔物，面对魔物的穷追不舍，你带着村民苦苦鏖战，正当村民绝望的时候，黑羽找到了你。"
  },
  [86461007] = {
    id = 86461007,
    name = "村民为了报答你和黑羽开了一场晚会，得知附近圈落准备举办收获祭，黑羽邀请你一起参加。"
  },
  [86432001] = {
    id = 86432001,
    name = "一个公会的人濒死前跑向你，朝你求救，你发现这人已经浊炁侵蚀了......"
  },
  [86432002] = {
    id = 86432002,
    name = "你在前往据点的路上遇到了白鸢，她以骑士的身份劝你这个普通探险者不要去据点，你提出帮忙后，却被她拒绝，这时出现了“变异”的魔物......"
  },
  [86432003] = {
    id = 86432003,
    name = "你和白鸢路过发现一个刚搭起不久的临时营地，原来营地中都是白鸢家族的人，而那个爆炸的据点是白鸢家族长居的据点，人们知道白鸢是来救他们的，给白鸢提了很多无理的要求。"
  },
  [86432004] = {
    id = 86432004,
    name = "在做好防护措施后，你与白鸢进入了据点，根据线索找到了爆炸发生地，这时有一只行动迅速的堕落者出现了。"
  },
  [86432005] = {
    id = 86432005,
    name = "你与白鸢回到临时营地，告诉大家据点有堕落者，据点短时间内不适合生活，众人迷茫无助，让白鸢帮忙解决问题。"
  },
  [86432006] = {
    id = 86432006,
    name = "你们再次到达据点，在那片炸碎的房屋附近寻找线索，发现这个地方是据点管理的办事处，这时堕落者再次出现，阻止你们靠近......"
  },
  [86432007] = {
    id = 86432007,
    name = "你带着白鸢来到山洞，听白鸢断断续续的喊祖父，发现她神志不清，竟然受到了盒子中的东西影响......"
  },
  [86432008] = {
    id = 86432008,
    name = "你们回到临时营地后，据点负责人向白鸢索要盒子，被白鸢拒绝。"
  },
  [86432009] = {
    id = 86432009,
    name = "白鸢对负责人所说的话产生了迷茫，你细心安慰后，她对人生的方向有所明悟。"
  },
  [86432010] = {
    id = 86432010,
    name = "你悄悄找到据点负责人，从他的嘴里撬出了关于黑衣人的消息，但负责人所知甚少，最后不了了之。"
  },
  [86455001] = {
    id = 86455001,
    name = "你在途中帮人脱困，那人告诉你他正打算采集材料，制作庆典所用的天灯。"
  },
  [86455002] = {
    id = 86455002,
    name = "你在途中遇到一女子沐浴，正打算悄悄离开之际，突然杀出两人袭击女子......"
  },
  [86455003] = {
    id = 86455003,
    name = "鬼魅带你去山里，让你帮忙采集一些驱散魔物的药草，在赶走魔物摘取药草时，突然有其他人出现抢夺药草......"
  },
  [86455004] = {
    id = 86455004,
    name = "鬼魅告诉你埃兰据点每年都会为祭祀牺牲女孩献祭宝物，护佑据点平安，而宝物里实际束缚了一只魔物。"
  },
  [86455005] = {
    id = 86455005,
    name = "你和鬼魅来到据点后，一路听到了很多人对鬼魅的误解，鬼魅并不在意，打算与你一明一暗分开寻找线索。"
  },
  [86455006] = {
    id = 86455006,
    name = "鬼魅找了偏僻的地方准备放出魔物，打开时发现神石是假的，鬼魅中了迷雾，祭司带人包围了你们。"
  },
  [86455007] = {
    id = 86455007,
    name = "你带着鬼魅在湖边休息，鬼魅恢复后感谢你的相助，但却决定与你分道扬镳......"
  },
  [86455008] = {
    id = 86455008,
    name = "你们伪装回到据点，找机会把矿石粉末放进火石里，等待祭祀的开始......"
  },
  [86455009] = {
    id = 86455009,
    name = "你和鬼魅离开了据点，忽然想送给她一件礼物......"
  },
  [86480001] = {
    id = 86480001,
    name = "你们找了宛如镜子的宝物，鬼侍希望你们将宝物卖给她，而在此时，异变突生......"
  },
  [86480002] = {
    id = 86480002,
    name = "你醒来后发现自己成为了普尔兰伯爵的继承人，脑中也混进一段奇怪的记忆，树下的小女孩叫你哥哥，让你去筹备宴会......"
  },
  [86480003] = {
    id = 86480003,
    name = "你和柯莱特尔一起回家筹备宴会，她趁你忙的时候跑不见了，就在你忙得晕头转向的时候，耳边传来了妹妹的呼救声......"
  },
  [86480004] = {
    id = 86480004,
    name = "柯莱特尔偷跑出去给家人准备礼物，不敢让父母知道。你找到她回家后，一起挨了父亲的训斥，在半夜睡觉的时候，你又听到了她的声音。"
  },
  [86480005] = {
    id = 86480005,
    name = "次日，你处理事务时，查到仇家会在宴会有动作。加强了布防之后，柯莱特尔邀你一起喝下午茶，一个小小的恶作剧随之“扑面而来”......"
  },
  [86480006] = {
    id = 86480006,
    name = "宴会开始后，仇家果然找上了门，在你的布局下，完美解决了这次危机，却在这时，听见了柯莱特尔的求救声......"
  },
  [86480007] = {
    id = 86480007,
    name = "你一路不断和柯莱特尔聊天，在诛杀背叛者后，将柯莱特尔救了出来，而这时，梦境出现了支离的迹象......"
  },
  [86480008] = {
    id = 86480008,
    name = "破碎的镜子导致你和鬼侍昏睡后，同伴们来找维克多大师求救，维克多大师的首徒弗拉克愿意出手相助，将你和鬼侍救醒，前提是完成他的要求。"
  },
  [86700001] = {
    id = 86700001,
    name = "清剿魔物"
  },
  [86700002] = {
    id = 86700002,
    name = "散落的线索"
  },
  [86700003] = {
    id = 86700003,
    name = "物资搜索"
