  },
  [86751107] = {
    id = 86751107,
    name = "人们齐聚在祭祀仪式上，对着天空高唱颂歌，就能得到力量。"
  },
  [86751108] = {
    id = 86751108,
    name = "它像一座高峰，无数人奋力爬上顶端，越靠近巅峰，越容易跌落。"
  },
  [86751109] = {
    id = 86751109,
    name = "随意捏于掌中柔韧的傀儡，却能不受控制地独舞。"
  },
  [86751110] = {
    id = 86751110,
    name = "永不静止的时空里，它缓缓运行着独有的轨道，那是众生的命运。"
  },
  [86751111] = {
    id = 86751111,
    name = "神鸟的幼雏不幸身亡，无处安放的羽翼被镶嵌在金徽之上。"
  },
  [86751112] = {
    id = 86751112,
    name = "一道道惊雷将坚石切割成苍鹰的模样，它振翅欲飞，却又被岁月凝固，成为永续的守望。"
  },
  [86751113] = {
    id = 86751113,
    name = "昔日的信仰皆为尘土，鲜血被赋予了全新的意义。"
  },
  [86751114] = {
    id = 86751114,
    name = "它以力量回馈，轻而易举地开出诱人的价码。"
  },
  [86751115] = {
    id = 86751115,
    name = "忘记痛苦吧，忘记那些怀念的记忆，你将一往无前。"
  },
  [86751116] = {
    id = 86751116,
    name = "戴上它，任何弱点都将无所遁形。"
  },
  [86751117] = {
    id = 86751117,
    name = "不知来处的甲胄，轻便又坚固。"
  },
  [86751118] = {
    id = 86751118,
    name = "认清自己，人类已不再是野兽。"
  },
  [86751119] = {
    id = 86751119,
    name = "特殊的晶矿之芯，具有催化魔法，高效转换的能力。"
  },
  [86751120] = {
    id = 86751120,
    name = "它在空中肆意起舞，聚起风雨迎面而来。"
  },
  [86751121] = {
    id = 86751121,
    name = "传闻鎏金色的圆规铸就了天地秩序，拟定了世界的规则。"
  },
  [86751122] = {
    id = 86751122,
    name = "一支化为金石的羽毛，浸润着月色与梦境。从某天起，深渊再也没有鸟儿飞过。"
  },
  [86751123] = {
    id = 86751123,
    name = "一面照进人心的镜子，放大了心绪的阴暗面。你在笑，镜子里的人在哭。"
  },
  [86751124] = {
    id = 86751124,
    name = "远古凶镰的尖刺，至今也一样锋芒四射。"
  },
  [86751125] = {
    id = 86751125,
    name = "带着天地之力的方锤，带给对手不可抗拒的噩梦。"
  },
  [86751126] = {
    id = 86751126,
    name = "握住它，你将更加渴望血液与杀戮。"
  },
  [86751127] = {
    id = 86751127,
    name = "极为罕见的液态流晶体，可吸收于法杖中，提升施法速度。"
  },
  [86751128] = {
    id = 86751128,
    name = "以湖心之羽制作的长弓，轻如羽快如风。"
  },
  [86751129] = {
    id = 86751129,
    name = "能将力量蓄积的轮盘，正一刻不停地旋转着。"
  },
  [86751130] = {
    id = 86751130,
    name = "据说解开它，就能打开某处隐秘的大门。"
  },
  [86751131] = {
    id = 86751131,
    name = "它向出征的勇士致意，赐予其祝福。"
  },
  [86751132] = {
    id = 86751132,
    name = "有人说里面封印了一只恶魔，待它释放，必将危害人间。"
  },
  [86751133] = {
    id = 86751133,
    name = "当它倾倒而出，一切会变慢得如泉水一般缓慢。"
  },
  [86751134] = {
    id = 86751134,
    name = "一把锁，锁住邪恶的灵魂与其疯狂的欲求。"
  },
  [86751135] = {
    id = 86751135,
    name = "宛如冰晶中囚禁的幽梦，凝结着岁月的孤寂与哀愁。"
  },
  [86751136] = {
    id = 86751136,
    name = "它避开时代的漩涡，静观岁月流转，守护那抹不灭的星光。"
  },
  [86751137] = {
    id = 86751137,
    name = "渺渺空灵的曲调，流淌过鲜血的战场，洗涤杀戮与仇恨。"
  },
  [86751138] = {
    id = 86751138,
    name = "当你展开它，能感受到灵魂被安抚。"
  },
  [86751139] = {
    id = 86751139,
    name = "沉寂于地下的魔晶重现天日，照亮冰冷的黑夜。"
  },
  [86751140] = {
    id = 86751140,
    name = "古老的祭司向光明祷告，呢喃吟咏被时间埋没，传奇的故事世代相传。"
  },
  [86751141] = {
    id = 86751141,
    name = "当善良被腐蚀，丑恶就成了释放欲望的毒蛇。"
  },
  [86751142] = {
    id = 86751142,
    name = "传闻里面封印了罪恶，不时会跳动，证明它还活着。"
  },
  [86751143] = {
    id = 86751143,
    name = "真正的恐惧源于未知，所有的生物对未知的想象都太过浅薄。"
  },
  [86751144] = {
    id = 86751144,
    name = "亘古的魔法被勾绘施加在披风上，给予后人久远的祝福。"
  },
  [86751145] = {
    id = 86751145,
    name = "万物更迭不息，星辰流转无常，时光不朽，它亦如初。"
  },
  [86751146] = {
    id = 86751146,
    name = "只要怀抱希望，死去的意志也会在你的内心复活。"
  },
  [86751147] = {
    id = 86751147,
    name = "传说中的言灵师送给爱人的定情之物，也是幸运的象征。紫晶不毁，我情不灭。"
  },
  [86751148] = {
    id = 86751148,
    name = "一对神乎其神的晶石，在其中一块发生变化时，另一块也互相感应般随之变化。"
  },
  [86751149] = {
    id = 86751149,
    name = "古老的禁术，将灵魂中的恶念封入魔偶中，烧毁主人指向的一切。"
  },
  [86751150] = {
    id = 86751150,
    name = "如蛇类般冰冷粗糙的雕塑，瓦解着敌人抵抗的意志。"
  },
  [86751151] = {
    id = 86751151,
    name = "迷局禁锢着记忆，你掌控着整个疆域。"
  },
  [86751152] = {
    id = 86751152,
    name = "以辰星的名义，构筑一场盛大的梦境。"
  },
  [86751153] = {
    id = 86751153,
    name = "受到毒咒的人偶，迫不及待地要把痛苦分享给对方品尝。"
  },
  [86751154] = {
    id = 86751154,
    name = "能将身形与外貌一同遮蔽的斗篷，使魔物分辨不出光源与气味。"
  },
  [86751155] = {
    id = 86751155,
    name = "遗落在尘世的宝珠，成就了预言的灵摆，昭示灾厄与不详。"
  },
  [86751156] = {
    id = 86751156,
    name = "悠扬悦耳的天籁，为战士注入勇气与希望。"
  },
  [86751157] = {
    id = 86751157,
    name = "戴上它，就能拥有神赐之力。"
  },
  [86751158] = {
    id = 86751158,
    name = "能够穿透黑暗的提灯，照亮未知的领域。"
  },
  [86751159] = {
    id = 86751159,
    name = "能在黑暗中发光的细沙，及其容易附在魔法箭矢之上。"
  },
  [86751160] = {
    id = 86751160,
    name = "它锐利如刃，丝丝入扣，织就毁灭的囚笼。"
  },
  [86751161] = {
    id = 86751161,
    name = "它力聚千钧，束缚万物，将敌人禁锢在黑暗之中。"
  },
  [86751162] = {
    id = 86751162,
    name = "当烛火燃尽，黑夜会重新降临。"
  },
  [86751163] = {
    id = 86751163,
    name = "世界掉入深渊的牢笼，就让它来编织新的美梦。"
  },
  [86751164] = {
    id = 86751164,
    name = "命运在回环往复中螺旋而上，永不停息。"
  },
