  [86771001] = {
    id = 86771001,
    name = "需「辉夜」出战才会生效\n1、战斗开始后，「辉夜」获得以自身100%%基础攻击力伤害的反伤效果，持续整场战斗。\n2、战斗开始后，「辉夜」受到攻击时，发动攻击方有10%%的概率陷入眩晕状态，持续1秒。"
  },
  [86771002] = {
    id = 86771002,
    name = "需「星云」出战才会生效\n1、战斗开始后，「星云」为敌方全体目标，附加30%%当前攻击力的木系持续伤害效果，持续整场战斗。\n2、「星云」在施放总攻技时，使敌方全体目标防御力降低30%%，持续整个总攻阶段。"
  },
  [86771003] = {
    id = 86771003,
    name = "需「月蚀」出战才会生效\n1、战斗开始后，「月蚀」为我方全体成员，基础攻速提升100%%，持续30秒。\n2、战斗开始后，「月蚀」为我方全体「猎影」成员，附加攻击时无视敌方目标10%%防御力的效果，持续整场战斗。"
  },
  [86771004] = {
    id = 86771004,
    name = "战斗开始后，每隔15秒，我方全体成员，获得100%%免伤效果，持续5秒，免伤效果结束后，重新计时。"
  },
  [86771005] = {
    id = 86771005,
    name = "战斗开始时，敌方全体目标，受到等同于各自生命上限20%%的伤害。"
  },
  [86771006] = {
    id = 86771006,
    name = "战斗开始后，每隔15秒，基于战场中我方当前攻击力最高的成员的当前攻击力，对敌方全体目标造成50%%的水属性伤害，并有50%%的概率使受到攻击的敌方目标陷入冰冻状态，持续2秒。"
  },
  [86771007] = {
    id = 86771007,
    name = "战斗开始后，每隔4秒，我方全体成员恢复等同于自身4%%生命上限的生命值。"
  },
  [86771008] = {
    id = 86771008,
    name = "战斗开始后，每隔10秒，生成以我方全体成员20%%生命上限的等量护盾值，并基于我方当前攻击力最高的角色的当前攻击力，对敌方全体目标造成50%%的火属性伤害。"
  },
  [86771009] = {
    id = 86771009,
    name = "战斗开始后，我方全体成员，每次普攻时无视敌方目标20%%的当前防御力。"
  },
  [86771010] = {
    id = 86771010,
    name = "战斗开始后，每拥有50货券，我方全体成员造成的伤害提升10%%。"
  },
  [86771011] = {
    id = 86771011,
    name = "每消耗10点荣誉，我方全体成员，暴击伤害提升5%%。"
  },
  [86781001] = {
    id = 86781001,
    name = "“只要深渊仍有罪恶存在，那么我便没有收鞘的理由。”"
  },
  [86781002] = {
    id = 86781002,
    name = "星云小姐的家族徽记，虽然本人表示只是普通物品，但拥有如此强大的力量，绝不可能普通吧？"
  },
  [86781003] = {
    id = 86781003,
    name = "月蚀前往深渊前，父母送给她的饯别礼物——是和解，是传承，或者只是希望与关怀。"
  },
  [86781004] = {
    id = 86781004,
    name = "是永生，亦是坟墓。"
  },
  [86781005] = {
    id = 86781005,
    name = "古老而神秘的天平，据传可以衡量一切存在，无论是物质还是能量，无论是生命还是情感。"
  },
  [86781006] = {
    id = 86781006,
    name = "在这片领域中，它便是唯一的主宰。"
  },
  [86781007] = {
    id = 86781007,
    name = "当光线穿过水晶，映照在宝石上，仿佛能窥见另一个世界。"
  },
  [86781008] = {
    id = 86781008,
    name = "以谎言蛊惑人心，以谎言吞噬灵魂。"
  },
  [86781009] = {
    id = 86781009,
    name = "宛如一切邪恶的汇聚，宛如一切灾祸的根源。"
  },
  [86781010] = {
    id = 86781010,
    name = "多一点，再多一点......无休无止，永无止尽。"
  },
  [86781011] = {
    id = 86781011,
    name = "是烈阳坠落，步入黑夜的黄昏，抑或黑夜破晓，走向新生的黎明。"
  },
