  [80270101] = {
    id = 80270101,
    name = "得知你在打听炎日洞穴，酒保指了指桌上的三杯烈酒，示意你意思一下。"
  },
  [80270102] = {
    id = 80270102,
    name = "一位行商热情地向你推销自己的莹草，你购买后发现品质低劣，想要找他退货，他却瞬间变脸，拒不承认。"
  },
  [80270103] = {
    id = 80270103,
    name = "一辆满载货物的推车卡在了巷道中，推车的主人看上去愁眉苦脸，不知如何是好。"
  },
  [80270104] = {
    id = 80270104,
    name = "药商拼命兜售着自己的“神药”，宣称只要涂抹身上便能避火。为了证明自己所言非虚，甚至当场在助手身上点燃了火焰。"
  },
  [80270105] = {
    id = 80270105,
    name = "一家店铺正在出售一种价格低廉且便于携带的罐头。你仔细观察了一番，发现这些罐头虽然实惠，但品相却并不怎么样。"
  },
  [80270106] = {
    id = 80270106,
    name = "为获取更多情报，你前往拜访圈落中的能人异士。他们并未拒绝你的请求，只是提出了一个有些奇怪的条件。"
  },
  [80270107] = {
    id = 80270107,
    name = "一名拾荒者自告奋勇为你充当向导，出于谨慎，你拒绝了他的请求。但他并不死心，甚至掏出一枚源晶，表示只要你同意他一起行动，他便告诉你地下据点的秘密。"
  },
  [80270108] = {
    id = 80270108,
    name = "街角出现了一栋全新的房屋，你没忍住多打量了几眼，没想到引起了房屋主人的注意。"
  },
  [80270109] = {
    id = 80270109,
    name = "一伙强盗趁你们在夜里休息时发起偷袭，幸好你及时发现并立即组织了反击。"
  },
  [80270110] = {
    id = 80270110,
    name = "行至半道，大雨倾盆，你躲入了附近山洞。山洞中遗留着几具枯骨，似乎在诉说山洞深处的恐怖。"
  },
  [80270111] = {
    id = 80270111,
    name = "荒野中突然出现了一阵氤氲的气息，似乎正酝酿着某种危险的存在。"
  },
  [80270112] = {
    id = 80270112,
    name = "一条船搁浅在河滩上，看上去已经腐朽破败，似乎并无什么价值。"
  },
  [80270113] = {
    id = 80270113,
    name = "林地中长满了色彩各异的蘑菇，看上去异常美丽。对于如何处置这些蘑菇，你陷入了犹豫。"
  },
  [80270114] = {
    id = 80270114,
    name = "一位身着破旧皮衣的猎人坐在巨石上，你主动上前搭话，他却并不搭理你，甚至表示自己不想和弱者说话。"
  },
  [80270115] = {
    id = 80270115,
    name = "巨大的路牌倒在地上，上面的内容已难以看清，你只得凭经验判断接下来前行的方向。"
  },
  [80270116] = {
    id = 80270116,
    name = "表面雕刻着复杂图案的陶罐挺立在河滩旁，看上去极为古怪。"
  },
  [80270117] = {
    id = 80270117,
    name = "一头巨大的生物浮在河面，正顺着水流向你飘来。它的身体表面能看到不少伤痕，似乎受了重伤。"
  },
  [80270118] = {
    id = 80270118,
    name = "神秘的浓雾瞬间笼罩四野，你根本看不清周围。是冒险前进还是原地休整，你必须立即作出选择。"
  },
  [80270119] = {
    id = 80270119,
    name = "一座巍峨的雕像坐落在一个古老的祭坛上，表面爬满了藤蔓和苔藓。祭坛周围散落着诸多物件，似乎正引诱外来者进入其中。"
  },
  [80270120] = {
    id = 80270120,
    name = "夜里，你正在河滩上扎营休整。摇曳的篝火吸引了另一队探险者的注意，他们来到了你身前。"
  },
  [80270121] = {
    id = 80270121,
    name = "你登上山顶，远眺目的地所在。那里烟雾弥漫，似乎正酝酿着新的风暴，必须抓紧时间了。"
  },
  [80270122] = {
    id = 80270122,
    name = "蜿蜒小道的尽头，几根原木按规律插在地面，圆木表层镌刻着古老、复杂的符号。这里似乎是一个仍在继续运转的魔法阵。"
  },
  [80270123] = {
    id = 80270123,
    name = "破烂的推车旁，一名受伤的行商趴在地上，见你出现，他拼命向你呼救。不远处，一头魔物也发现了你，它仰天咆哮，似乎在警告你别靠近它的猎物。"
  },
  [80270124] = {
    id = 80270124,
    name = "逆流的熔岩如同倒悬的火焰瀑布，有人说这里是被诅咒之地，也有人说，这是深渊自己的法则。"
  },
  [80270125] = {
    id = 80270125,
    name = "巨大的树木横亘在峡谷中，将来往通道阻塞。这种程度的障碍无法阻止你前行的脚步。"
  },
  [80270126] = {
    id = 80270126,
    name = "一个红色的小家伙从你眼前闪过，看起来很像你记忆中的某种生物，于是你寻着它的踪迹跟了上去。"
  },
  [80270127] = {
    id = 80270127,
    name = "地图上所标记的道路消失了，取而代之的是一个蜿蜒曲折的迷宫。"
  },
  [80270128] = {
    id = 80270128,
    name = "一柄法杖直插大地，表面布满了灰尘，看起来宛如被束缚在这片土地之上，又好似封锁这片土地的钥匙。"
  },
  [80270129] = {
    id = 80270129,
    name = "冶炼台、钩锁、破碎的试剂瓶......这片废墟或许曾是一个极为重要的所在。"
  },
  [80270130] = {
    id = 80270130,
    name = "一片绚丽的花海出现在了你眼前，和风浮动，花海泛起涟漪。在这番令人沉醉的美景下，大地突然震颤。"
  },
  [80270131] = {
    id = 80270131,
    name = "几池热气腾腾的温泉出现在你眼前，对远途的探险者来说，这或许是最好的慰藉。"
  },
  [80270132] = {
    id = 80270132,
    name = "诸多火山岩石散落在浅溪中，这些岩石表面可以看到细密的金色纹路，属于难得一见的天然矿物。"
  },
  [80270133] = {
    id = 80270133,
    name = "一位游商冒着风险在危险区域兜售自己的货物，他看起来对自己的商品非常自信。"
  },
  [80270134] = {
    id = 80270134,
    name = "一群魔物正在慌乱的奔逃。究竟是怎样的危险，能让这群凶悍的存在也感到畏惧？"
  },
  [80270135] = {
    id = 80270135,
    name = "隐匿于林中的魔物在夜色下露出了凶戾的面孔，不等你作出反应便汹涌袭来。"
  },
  [80270136] = {
    id = 80270136,
    name = "清点随行人员时你发现数量不对，不知何时开始，你的队伍中出现了多余的“人”。"
  },
  [80270137] = {
    id = 80270137,
    name = "这废弃的瞭望塔虽然终会坍塌，但在那一刻到来前，即便已经倾斜，它仍坚守着自己的职责。"
  },
  [80270138] = {
    id = 80270138,
    name = "自贫瘠石缝中长出的小花，或许它便是这片破碎大地新生的希望。"
  },
  [80270139] = {
    id = 80270139,
    name = "你从魔物口中救下了一名路人，交谈中得知他曾是一位向导，或许可以从他那里探听一些需要的信息。"
  },
  [80270140] = {
    id = 80270140,
    name = "枯木林中尚有魔力残存，你清查一番后发现了一个破损的法阵。"
  },
  [80270141] = {
    id = 80270141,
    name = "休整也是远途的重要一环，充足的休息才能走得更远。"
  },
  [80270142] = {
    id = 80270142,
    name = "巨大的骸骨匍匐在荒野上，大半身躯都被泥土和火山灰掩埋。"
  },
  [80270143] = {
    id = 80270143,
    name = "巨大的船只搁浅在码头，船身已经破损，满是岁月的痕迹。不过舱室似乎还算完好，或许能发现些有用的东西。"
  },
  [80270144] = {
    id = 80270144,
    name = "你隐约听到了人类婴儿的哭声，在如此凶险的荒野怎么可能存在人类婴儿？必然有诈。"
  },
  [80270145] = {
    id = 80270145,
    name = "废弃的码头旁仍能看到一些被遗弃的船只，这些残破的存在似乎在诉说着这里曾经的繁华。"
  },
  [80270146] = {
    id = 80270146,
    name = "你遇到了一名独行的探险者，他对火山表现出浓厚兴趣，想向你打听火山有关的情况。"
  },
  [80270147] = {
    id = 80270147,
    name = "小屋中的物件散落各处，看得出当初的撤离非常匆忙，不过也正因如此才能找到些有用的线索。"
  },
  [80270148] = {
    id = 80270148,
    name = "夜幕降临，火山口喷出的熔岩划破夜空，宛如飞逝的流星。"
  },
  [80270149] = {
    id = 80270149,
    name = "风暴突然降临荒滩，你立即招呼众人进入搁浅的大船中躲避。"
  },
  [80270150] = {
    id = 80270150,
    name = "巨大的兽骨宛如桥梁，横跨沼泽。不知是深渊的怜悯，还是某位先行者的温柔。"
  },
  [80270151] = {
    id = 80270151,
    name = "街道两侧的石壁上刻画着各种符号，似涂鸦，又似文字。或许是此前生活在这里的人拼命想留下些什么。"
  },
  [80270152] = {
    id = 80270152,
    name = "废墟深处突然响起一阵时断时续的音乐，这里似乎还有幸存者？"
  },
  [80270153] = {
    id = 80270153,
    name = "连接暗河两岸的，只有几块悬浮的木板。这种东西不管怎么看，也绝对算不上“桥”吧。"
  },
  [80270154] = {
    id = 80270154,
    name = "巨大的石碑倒在地上，将所镌刻的文字隐藏了起来。或许是运气不好，也或许是有人刻意为之。"
  },
  [80270155] = {
    id = 80270155,
    name = "一堆物资散落在地，表面满是灰尘，看样子是人们在撤离时遗弃的。不过这么久了竟然没有被魔物破坏，稍微有些可疑。"
  },
  [80270156] = {
    id = 80270156,
    name = "书架上还存放着一些书籍。毁灭来临时，或许人们选择了携带比这些书本更重要的东西。"
  },
  [80270157] = {
    id = 80270157,
    name = "书库中许多书本已经被搬走了，看来在转移时也有人不愿舍弃这些人类文明的结晶。"
  },
  [80270158] = {
    id = 80270158,
    name = "夜宿时，一阵阴风卷灭了篝火，不远处一个幽灵般的人影向你们飘来。"
  },
  [80270159] = {
    id = 80270159,
    name = "在街道中走了很久，你再一次回到了原点。不知何时，你已陷入这个街道迷宫之中。"
  },
  [80270160] = {
    id = 80270160,
    name = "古老的魔法阵上堆满了各种魔物的遗骸，宛如正在进行一场盛大而诡异的仪式。"
  },
  [80270161] = {
    id = 80270161,
    name = "周围黑雾萦绕，不知不觉间，你已走入了魔物的包围圈。"
  },
  [80270162] = {
    id = 80270162,
    name = "穿越密林时，一片黑影从天而降。对于到访的探险者，魔物的“热情”超乎了你的想象。"
  },
  [80270163] = {
    id = 80270163,
    name = "陡峭的崖壁高耸入云，几乎无法攀爬。想要前进，得考虑一些别的方式了。"
  },
  [80270164] = {
    id = 80270164,
    name = "一团篝火在夜色极为显眼，只是你想要接近时，却发现无论如何也无法缩短与它之间的距离。"
  },
  [80270165] = {
    id = 80270165,
    name = "一个鬼鬼祟祟的男人找到了你，极力向你推销一颗据说极其贵重的石头。"
  },
  [80270166] = {
    id = 80270166,
    name = "道路被树枝和倒下的树木阻断，旁边能看到一些凌乱的痕迹。这些痕迹并不像魔物所留下的。"
  },
  [80270167] = {
    id = 80270167,
    name = "你救下了一名独行的魔法师，他拿出一件贵重的物品向你表达谢意。"
  },
  [86800001] = {
    id = 86800001,
    name = "一点小意外而已，无碍。"
  },
  [86800002] = {
    id = 86800002,
    name = "是乐园......也是囚笼......"
  },
  [86800003] = {
    id = 86800003,
    name = "就算已经感受过无数次，这种感觉依然令人陶醉~"
  },
  [86800004] = {
    id = 86800004,
    name = "这里作为每个新手探险者必经的区域，只有顺利通过考核的探险者才能在之后更加危险的区域活下来。"
  },
  [86800101] = {
    id = 86800101,
    name = "人迹罕至的冰原深处，是属于巨兽的国度，任何闯入者都将承受它们的怒火。"
  },
  [86800102] = {
    id = 86800102,
    name = "隐匿于森林深处的迷宫，无人知晓其真正面目，如果贸然进入，要么迷失其中，要么成为食物。"
  },
  [86800105] = {
    id = 86800105,
    name = "炽热的炎流将天空和大地也烫成黑色，这里成为了人类的绝地，也成为了魔物的乐园。"
  },
  [86800201] = {
    id = 86800201,
    name = "魔物蛰伏了起来，与周围环境融为一体。即便是经验老道的探险者，如果不够仔细，也很容易落入它们的陷阱。"
  },
  [86800202] = {
    id = 86800202,
    name = "一阵大风吹来，卷起漫天烟尘，根本看不清前路，你只得凭感觉行动。待烟尘消散，几头魔物出现在了你眼前。"
  },
  [86800203] = {
    id = 86800203,
    name = "几头魔物蹲守在前方隘口，不时打量四周，看样子它们正在等候主动上门的猎物。"
  },
  [86800204] = {
    id = 86800204,
    name = "沉睡的魔物慵懒地匍匐在荒原上，不时翻动身躯，十分惬意。你本打算绕过它，但在看到它那自在舒适的样子后不由停下了脚步。"
  },
  [86800205] = {
    id = 86800205,
    name = "道路旁残存着一些奇特的痕迹，你追寻着这些痕迹发现了魔物栖息之地。"
  },
  [86800206] = {
    id = 86800206,
    name = "几头魔物将道路占据，与它们对抗得花费不少力气，不过如果绕路，又得花费更多时间。"
  },
  [86800207] = {
    id = 86800207,
    name = "一些魔物游荡在素材周围，时而咆哮时而低吼，看样子它们并不欢迎到访者。"
  },
  [86800208] = {
    id = 86800208,
    name = "从魔物盘踞的地方传来一股异样的气息，这种气息大概率是某种罕见的素材，当然，也有可能只是单纯的陷阱。"
  },
  [86800209] = {
    id = 86800209,
    name = "魔物有时候也会利用素材引诱猎物，只是比起探险者设置的陷阱，它们的诱饵太明显了，恐怕只有菜鸟才会上当。"
  },
  [86800210] = {
    id = 86800210,
    name = "几头魔物似乎在争夺什么，彼此嘶吼对峙，眼看冲突一触即发，“您”这位突然出现的旁观者引起了它们注意。"
  },
