  },
  [120000004] = {
    id = 120000004,
    name = "随便走走。"
  },
  [111070001] = {
    id = 111070001,
    name = "遥远的旷野上，似有歌声传来。"
  },
  [111070002] = {
    id = 111070002,
    name = "「这迈向死亡的世界，黑潮漫延；\n萌芽的大地，崩坏的终点；」"
  },
  [111070003] = {
    id = 111070003,
    name = "「千星坠落，残留的火焰；\n染血深渊，不归的开端；\n水之彼岸，遗迹的碎片;」"
  },
  [111070004] = {
    id = 111070004,
    name = "「绝境的杀戮，魔王的盛典；\n祭祀的过客，流浪的衣冠......」"
  },
  [111070005] = {
    id = 111070005,
    name = "歌声还在继续。"
  },
  [111070006] = {
    id = 111070006,
    name = "你缓缓走近。白衣斗篷的青年坐在大树的阴翳下，透明的指甲轻叩着琉特琴弦，与弦音和声的，是诗人编织的诗句......"
  },
  [111070007] = {
    id = 111070007,
    name = "「是杀伐，是背叛，是欢喜与哀怨；」"
  },
  [111070008] = {
    id = 111070008,
    name = "「腐朽的传说......」"
  },
  [111070009] = {
    id = 111070009,
    name = "那声音沉静如水，悠悠吟唱着。"
  },
  [111070010] = {
    id = 111070010,
    name = "曲子弹奏到尾声，他抬起头对你唱出了最后一句。"
  },
  [111070011] = {
    id = 111070011,
    name = "「消失于终焉之前。」"
  },
  [111070012] = {
    id = 111070012,
    name = "「......」"
  },
  [111070013] = {
    id = 111070013,
    name = "吟游诗人慢慢起身，抱琴微笑。"
  },
  [111070014] = {
    id = 111070014,
    name = "「客人听了我的歌，不打算打赏吗？」"
  },
  [111070015] = {
    id = 111070015,
    name = "「的确是一首不错的曲子，但你并不是为我唱的。我为何要付钱？」"
  },
  [111070016] = {
    id = 111070016,
    name = "「为谁而唱的不重要，你听到了什么才重要。」"
  },
  [111070017] = {
    id = 111070017,
    name = "「......」"
  },
  [111070018] = {
    id = 111070018,
    name = "你回想起他的歌声，歌词里有关于绝境之地的秘密。难道这个人知道些什么？"
  },
  [111070019] = {
    id = 111070019,
    name = "「你唱词里所说的地方，有的我去过了，有的还没有......」"
  },
  [111070020] = {
    id = 111070020,
    name = "吟游诗人静静地看着你，你正打算支付打赏，一块欧泊掉入诗人手中。"
  },
  [111070021] = {
    id = 111070021,
    name = "「这是我打赏你的。」"
  },
  [111070022] = {
    id = 111070022,
    name = "少女踏着靴子高傲地走上前。"
  },
  [111070023] = {
    id = 111070023,
    name = "「我也听到你的歌了。你唱的是不是那些谜题？你怎么会知道那些传说？」"
  },
  [111070024] = {
    id = 111070024,
    name = "「呵呵，你旁边这位才是我今天的第一位听众。」"
  },
  [111070025] = {
    id = 111070025,
    name = "少女这时转过身，看向了你。"
  },
  [111070026] = {
    id = 111070026,
    name = "半片镜片下，她的眼神锐利，将你从头打量到脚，让你有一种被扒开的感觉。"
  },
  [111070027] = {
    id = 111070027,
    name = "「衣物整洁，五官英俊，气质沉稳。剑鞘有轻度磨损，但保护得很好，补给也充足......初步判断，你是自由探险者。」"
  },
  [111070028] = {
    id = 111070028,
    name = "「你是？」"
  },
  [111070029] = {
    id = 111070029,
    name = "「初次见面，我叫马尔可，是一个侦探。」"
  },
  [111070030] = {
    id = 111070030,
    name = "「侦探啊......」"
  },
  [111070031] = {
    id = 111070031,
    name = "「不错，刚才我发现了一个很重要的线索。」"
  },
  [111070032] = {
    id = 111070032,
    name = "「你刚刚没有付费吧，所以现在是我和他的交流时间。」"
  },
  [111070033] = {
    id = 111070033,
    name = "你看了一眼吟游诗人，这个人似乎知道很多秘密，但......"
  },
  [111070034] = {
    id = 111070034,
    name = "思前想后，你让出了位置。"
  },
  [111070035] = {
    id = 111070035,
    name = "「唱歌的，你刚才提到的绝境，是绝境之地吗？魔王，深渊里真的有魔王的存在？还有传说中的遗迹，你知道在什么地方？另外那个祭祀，是指的那个黑色祭坛吗？还有——」"
  },
  [111070036] = {
    id = 111070036,
    name = "「呵呵呵呵......」"
  },
  [111070037] = {
    id = 111070037,
    name = "面对马尔可一连串的提问，歌者笑了笑，慢悠悠地说道。"
  },
  [111070038] = {
    id = 111070038,
    name = "「我只是将听过的传说唱了出来，至于为什么会知道，大概是我走过了太多的地方。而那些谜题的答案，不是该交给你们探险者去寻找吗？」"
  },
  [111070039] = {
    id = 111070039,
    name = "马尔可皱了皱眉：的确，这个人唱了，又好像什么也没唱。"
  },
  [111070040] = {
    id = 111070040,
    name = "叮叮咚咚~白袍歌者又弹起了琉特琴，哼起了悠扬的旋律。听起来像是随心哼唱的小调，舒缓惬意，光影在他身上流淌，他渐渐远去。"
  },
  [111070041] = {
    id = 111070041,
    name = "「我的欧泊！」"
  },
  [111070042] = {
    id = 111070042,
    name = "等歌声消失，马尔可才反应过来。"
  },
  [111070043] = {
    id = 111070043,
    name = "「算了，就当打赏他的了。」"
  },
  [111070044] = {
    id = 111070044,
    name = "马尔可转过头来看着你。"
  },
  [111070045] = {
    id = 111070045,
    name = "「你刚才是不是也听出来了什么？」"
  },
  [111070046] = {
    id = 111070046,
    name = "「嗯，他唱了很多深渊的未解之谜。」"
  },
  [111070047] = {
    id = 111070047,
    name = "「对吧！果然是这样，你也对这些秘密感兴趣？」"
  },
  [111070048] = {
    id = 111070048,
    name = "「呃......」"
  },
  [111070049] = {
    id = 111070049,
    name = "「难得遇到一个同道中人。」"
  },
  [111070050] = {
    id = 111070050,
    name = "马尔可抬了抬那半片眼镜。"
  },
  [111070051] = {
    id = 111070051,
    name = "「看你的资质，勉强可以做我的助手。要不要跟我一起去寻找这些谜题的答案？」"
  },
  [111070052] = {
    id = 111070052,
    name = "「？？？」"
  },
  [111070053] = {
    id = 111070053,
    name = "你好奇地看了她一眼，她盯了一阵，忽然避开了你的视线。"
  },
  [111070054] = {
    id = 111070054,
    name = "「咳，本侦探不会强求的。我还忙着去调查一件事。」"
  },
  [111070055] = {
    id = 111070055,
    name = "马尔可将一块徽章拍在你的手心里。"
  },
  [111070056] = {
    id = 111070056,
    name = "「这是我的侦探徽章，你要是发现了什么新的秘密和线索，记得及时通知我，我走了。」"
  },
  [111070057] = {
    id = 111070057,
    name = "马尔可朝前迈步而去，身影逐渐没于丛林间。"
  },
  [111070058] = {
    id = 111070058,
    name = "「......奇怪的女孩。」"
  },
  [111070059] = {
    id = 111070059,
    name = "行至半途，你看到有人匆匆跑来。"
  },
  [111070060] = {
    id = 111070060,
    name = "（这人身着公会的服饰.....这么着急，难道公会出了什么事？）"
  },
  [111070061] = {
    id = 111070061,
    name = "公会成员见到你，面露喜色，在你面前站定。"
  },
  [111070062] = {
    id = 111070062,
    name = "呼......呼，总算，总算找到您了。"
  },
  [111070063] = {
    id = 111070063,
    name = "......找我？（怎么觉得又有麻烦上身了......）"
  },
  [111070064] = {
    id = 111070064,
    name = "无论如何，请您先看看这个......"
  },
  [111070065] = {
    id = 111070065,
    name = "公会成员拿出一封信，上面刻印着情报部的徽章，落款留名，是一个陌生的名字：维弗。"
  },
  [111070066] = {
    id = 111070066,
    name = "维弗是我们的情报调查员。他失踪了，这封信是他留下的最后一封信。"
  },
  [111070067] = {
    id = 111070067,
    name = "我没有见过这个人，也没有听说过。"
  },
  [111070068] = {
    id = 111070068,
    name = "不不，您误会了。"
  },
  [111070069] = {
    id = 111070069,
    name = "维弗在信中说，他发现了一座突然出现的村庄。可是，在他寄出这封信后就再也没有了回音，后来我们派出了其他调查员寻找维弗的下落，但什么也没有找到。"
  },
  [111070070] = {
    id = 111070070,
    name = "突然出现的村庄？"
  },
  [111070071] = {
    id = 111070071,
    name = "是的，深渊里出现村庄，公会有责任查明情况。后来情报部也成立了调查组去调查，可惜一无所获。"
  },
  [111070072] = {
    id = 111070072,
    name = "深渊里出现临时据点也不是什么令人奇怪的事吧。那座村庄是小型的圈落吗，没有向你们公会报备？"
  },
  [111070073] = {
    id = 111070073,
    name = "其实......现在的探险者越来越多，临时据点也增加得很快，加上公会没有足够的人手去安排和调查，建立临时据点，已经不需要向公会报备了。"
  },
  [111070074] = {
    id = 111070074,
    name = "不过那座村庄不同，去过的调查员都认为那就是普通的一个村庄。它和大陆上的村庄一样，完全没有圈落那样坚固的防御墙。"
  },
  [111070075] = {
    id = 111070075,
    name = "如果没有防御工事的话......岂不是很容易就被魔物袭击？"
  },
  [111070076] = {
    id = 111070076,
    name = "没错，奇怪的地方正是在这里！"
  },
  [111070077] = {
    id = 111070077,
    name = "这座村庄竟然能在深渊里存活这么久，一定有它不为人知的秘密。部长大人说，如果是您的话，一定能找到事实的真相！"
  },
  [111070078] = {
    id = 111070078,
    name = "公会让我做的只有这个吗？"
  },
  [111070079] = {
    id = 111070079,
    name = "如果可以的话，也请您在调查村庄的同时，帮忙寻找一下维弗的下落。"
  },
  [111070080] = {
    id = 111070080,
    name = "好吧，不过我本事有限，也许会辜负伽丝茉阁下的期待。"
  },
  [111070081] = {
    id = 111070081,
    name = "呵呵，您真是太谦虚了。"
  },
  [111070082] = {
    id = 111070082,
    name = "对于我们来说，您能接下这个任务，就离真相只有一步之遥了。"
  },
  [111070083] = {
    id = 111070083,
    name = "没那么夸张......"
  },
  [111070084] = {
    id = 111070084,
    name = "如果您需要帮助的话，可以到据点或圈落，那里有我们其他的成员，他们都会无条件配合您的调查。"
  },
  [111070085] = {
    id = 111070085,
    name = "嗯，我知道了。"
  },
  [111070086] = {
    id = 111070086,
    name = "公会成员伸手弯腰，朝你行了个大礼。"
  },
  [111070087] = {
    id = 111070087,
    name = "那么，祝您一切顺利。"
  },
  [111070088] = {
    id = 111070088,
    name = "他露出真诚的笑容后转身离开了。"
  },
  [111070089] = {
    id = 111070089,
    name = "你拿着当前唯一的线索——维弗的信件，深深叹了口气。"
  },
  [111070090] = {
    id = 111070090,
    name = "我就知道，果然是个麻烦啊，这个村庄也不知道在哪里，总之先去附近的据点打听一下吧。"
  },
  [111070091] = {
    id = 111070091,
    name = "到了，先找到公会的人吧。一般来说，他们的聚集地在......"
  },
  [111070092] = {
    id = 111070092,
    name = "你环视四周，作为固定的据点，这里有许多买卖的房屋，其中也有专门收集贩卖消息的情报屋。"
  },
  [111070093] = {
    id = 111070093,
    name = "你的视线略过武器店、药店，最后停留在一间不起眼的小屋门口。在确认了屋檐上公会情报部的符号后，你走了进去。"
  },
  [111070094] = {
    id = 111070094,
    name = "这位小姐，你再不离开，就别怪我们不客气了！"
  },
  [111070095] = {
    id = 111070095,
    name = "你打算怎么不客气？"
  },
  [111070096] = {
    id = 111070096,
    name = "你刚进入屋内，就看见了柜台前争执的两人。一名是身着公会制服的青年， 而另一位少女看着有些熟悉。"
  },
  [111070097] = {
    id = 111070097,
    name = "（马尔可？）"
  },
  [111070098] = {
    id = 111070098,
    name = "你忽然想起了她的名字。公会成员如同抓住了一根救命稻草，立刻向你走来。"
  },
  [111070099] = {
    id = 111070099,
    name = "请问这位先生，您要买消息还是要卖消息？"
  },
  [111070100] = {
    id = 111070100,
    name = "我来调查维弗的事。"
  },
  [111070101] = {
    id = 111070101,
    name = "维弗？你有什么信物吗？"
  },
  [111070102] = {
    id = 111070102,
    name = "你将维弗的信件递给了他，他神色一变，而后惊喜万分。"
  },
  [111070103] = {
    id = 111070103,
    name = "啊，您就是部长大人说的那位先生吗？！部长大人吩咐我们一定要全力配合您！"
  },
  [111070104] = {
    id = 111070104,
    name = "等一等，是我先来的，你应该先完成跟我的交易吧！"
  },
  [111070105] = {
    id = 111070105,
    name = "马尔可转过身，看见你时愣了愣。"
  },
  [111070106] = {
    id = 111070106,
    name = "是你......备用助手！"
  },
  [111070107] = {id = 111070107, name = "......"},
  [111070108] = {
    id = 111070108,
    name = "你在公会有熟人？太好了，你让他们把情报卖给我吧。"
  },
  [111070109] = {
    id = 111070109,
    name = "您认识马尔可小姐？"
  },
  [111070110] = {
    id = 111070110,
    name = "只是一面之缘。既然马尔可小姐着急买情报，那么女士优先吧。"
  },
  [111070111] = {
    id = 111070111,
    name = "你示意公会成员打发走马尔可，但他似乎非常为难。"
  },
  [111070112] = {
    id = 111070112,
    name = "怎么回事，做个生意还扭扭捏捏的。"
  },
  [111070113] = {
    id = 111070113,
    name = "那个，部长大人嘱咐过，有关这方面的消息，只能交给这位先生。"
  },
  [111070114] = {
    id = 111070114,
    name = "只能交给他？"
  },
  [111070115] = {
    id = 111070115,
    name = "马尔可转头看向了你，忽然笑了笑。"
  },
  [111070116] = {
    id = 111070116,
    name = "你也要调查那个村庄？"
  },
  [111070117] = {
    id = 111070117,
    name = "不，我只是帮他们找人。"
  },
  [111070118] = {
    id = 111070118,
    name = "找人啊......不管怎么说，我们的目的是一样的。所以告诉我，和告诉我的备用助手没什么区别。"
  },
  [111070119] = {
    id = 111070119,
    name = "啊，这个......"
  },
  [111070120] = {
    id = 111070120,
    name = "不一样，如果他因为破坏规则受到了处罚，是不是也由马尔可小姐来承担责任呢？"
  },
  [111070121] = {id = 111070121, name = "......"},
  [111070122] = {
    id = 111070122,
    name = "总之，你不要再想方设法打听了！在我这里你是得不到任何关于村庄的消息的！"
  },
  [111070123] = {
    id = 111070123,
    name = "行吧，那我就不打扰了。"
  },
  [111070124] = {
    id = 111070124,
    name = "马尔可意味深长地看了你一眼就离开了，公会情报人员长舒了一口气。"
  },
  [111070125] = {
    id = 111070125,
    name = "总算走了......"
  },
  [111070126] = {
    id = 111070126,
    name = "她经常来这里吗？"
  },
  [111070127] = {
    id = 111070127,
    name = "马尔可小姐来这里好几次了，每次都会打听一些奇奇怪怪的情报。也不知道都是从哪里听来的传闻，她说让她最感兴趣的，就是深渊十大未解之谜。"
  },
  [111070128] = {
    id = 111070128,
    name = "十大未解之谜？"
  },
  [111070129] = {
    id = 111070129,
    name = "我在情报部待了这么多年，也没听说过，八成是她道听途说的。这次她不知道从哪里听说了光辉之剑，就跑到我这里打听关于村庄的消息。"
  },
  [111070130] = {
    id = 111070130,
    name = "光辉之剑？"
  },
  [111070131] = {
    id = 111070131,
    name = "是的，据说就在维弗发现的村庄。后来调查组前去调查过，那把所谓的光辉之剑，其实就是为村民赶走魔物的英雄的剑而已。"
  },
  [111070132] = {
    id = 111070132,
    name = "这样啊，不过这些消息也不是什么秘密。为什么不能让她知道？"
  },
  [111070133] = {
    id = 111070133,
    name = "呵呵，公会有公会的规矩。我能在这里待这么久，全靠这个。"
  },
  [111070134] = {
    id = 111070134,
    name = "他闭着嘴巴，用手横划拉紧的动作。"
  },
  [111070135] = {
    id = 111070135,
    name = "那如果她从其他方面得到了消息呢？"
  },
  [111070136] = {
    id = 111070136,
    name = "那就不是公会能管的事了。"
  },
  [111070137] = {
    id = 111070137,
    name = "公会成员哈哈一笑，递给了你一张地图。"
  },
  [111070138] = {
    id = 111070138,
    name = "这是当年调查组去往村庄调查绘制的地图。听说那些村民不欢迎外来者，也许做一些伪装会更好进入。"
  },
  [111070139] = {
    id = 111070139,
    name = "这张地图，应该才是马尔可小姐想要的东西。"
  },
  [111070140] = {
    id = 111070140,
    name = "呵呵，这是情报部的前辈留下的珍贵成果。"
  },
  [111070141] = {
    id = 111070141,
    name = "那就希望前辈留下的成果，能带我寻找到真相吧。"
  },
  [111070142] = {
    id = 111070142,
    name = "那么，我们就在此静候佳音。"
  },
  [111070143] = {
    id = 111070143,
    name = "你出来后，果然在街对面看到了等候的马尔可。她看到你出现，瞬间精神焕发。"
  },
  [111070144] = {
    id = 111070144,
    name = "你来啦，我们快出发吧，我知道那个村庄在哪儿！"
  },
  [111070145] = {id = 111070145, name = "......"},
  [111070146] = {
    id = 111070146,
    name = "嗯？你还在等什么？"
  },
  [111070147] = {
    id = 111070147,
    name = "我想问问，马尔可小姐为什么想要调查那个村庄？"
  },
  [111070148] = {
    id = 111070148,
    name = "嗯？为什么？"
  },
  [111070149] = {
    id = 111070149,
    name = "你不认为突然出现的村庄特别奇怪、神秘吗？在深渊里，竟然有村庄能成功存活下来，这难道不是一个令人想要勘探的谜题？"
  },
  [111070150] = {
    id = 111070150,
    name = "果然是这样......"
  },
  [111070151] = {
    id = 111070151,
    name = "马尔可不断催促你出发。"
  },
  [111070152] = {
    id = 111070152,
    name = "快走吧，我已经迫不及待了。"
  },
  [111070153] = {
    id = 111070153,
    name = "无须查看地图，马尔可带着你顺利地找到了村庄。"
  },
  [111070154] = {
    id = 111070154,
    name = "就是那里。"
  },
  [111070155] = {
    id = 111070155,
    name = "果然是一个村庄。"
  },
  [111070156] = {
    id = 111070156,
    name = "清澈的小河对岸，坐落在山脚下一户又一户的人家，炊烟袅袅。偶尔几名村民来来往往和谐安宁，竟然和大陆的村庄别无二致。"
  },
  [111070157] = {
    id = 111070157,
    name = "这种村庄，竟然能够在深渊里存活，的确是很离奇的一件事。"
  },
  [111070158] = {
    id = 111070158,
    name = "我已经迫不及待想要解开这个谜题了！"
  },
  [111070159] = {
    id = 111070159,
    name = "等等，没有人会欢迎挖掘自己秘密的人。我想，我们需要伪装一下。"
  },
  [111070160] = {
    id = 111070160,
    name = "唔，你说得有道理。"
  },
  [111070161] = {
    id = 111070161,
    name = "马尔可抬了抬半片眼镜，眼镜反射出一道流光。"
  },
  [111070162] = {
    id = 111070162,
    name = "我有一个办法。"
  },
  [111070163] = {
    id = 111070163,
    name = "一段时间后......"
  },
  [111070164] = {
    id = 111070164,
    name = "站住，不许进去！"
  },
  [111070165] = {id = 111070165, name = "欸？"},
  [111070166] = {
    id = 111070166,
    name = "马尔可一副被吓到的模样，可怜兮兮地问道。"
  },
  [111070167] = {
    id = 111070167,
    name = "为什么呀？"
  },
  [111070168] = {
    id = 111070168,
    name = "反正，大......大哥说了，不能随便放人进去！"
  },
  [111070169] = {
    id = 111070169,
    name = "学妹，你怎么到这里来了？"
  },
  [111070170] = {
    id = 111070170,
    name = "呜呜，学长。"
  },
  [111070171] = {
    id = 111070171,
    name = "怎么回事，有人欺负你吗？"
  },
  [111070172] = {
    id = 111070172,
    name = "马尔可小心翼翼地看了一眼守卫。"
  },
  [111070173] = {
    id = 111070173,
    name = "你欺负她？"
  },
  [111070174] = {id = 111070174, name = "哈？"},
  [111070175] = {
    id = 111070175,
    name = "他不要我进这个据点，呜呜。"
  },
  [111070176] = {
    id = 111070176,
    name = "胡说，我们这个不是据——"
  },
  [111070177] = {
    id = 111070177,
    name = "守卫说到一半突然停顿，看向了村外走来的两人。"
  },
  [111070178] = {id = 111070178, name = "大哥！"},
  [111070179] = {
    id = 111070179,
    name = "那两人看着像是刚狩猎回村的猎户，神色轻松自然。"
  },
  [111070180] = {
    id = 111070180,
    name = "这是怎么回事？"
  },
  [111070181] = {
    id = 111070181,
    name = "守卫把来龙去脉给他讲述了一遍。"
  },
  [111070182] = {
    id = 111070182,
    name = "我是这个村子的村长，你们两个是学生？"
  },
  [111070183] = {
    id = 111070183,
    name = "马尔可似乎被吓到了，躲在了你身后。"
  },
  [111070184] = {
    id = 111070184,
    name = "是的，我们是学院的学生。为了完成个人实践测试的课题不知不觉就走到了这里，现在我们都有点累了，想找个据点休息一下。"
  },
  [111070185] = {
    id = 111070185,
    name = "学长，这个人好可怕，我不想进去了。而且这里看起来好破旧，一点也没有圈落安全~"
  },
  [111070186] = {id = 111070186, name = "......"},
  [111070187] = {
    id = 111070187,
    name = "可是圈落离这里还有很远。"
  },
  [111070188] = {
    id = 111070188,
    name = "哼，没有一个地方能比我们这里更安全！"
  },
  [111070189] = {
    id = 111070189,
    name = "村长笑了两声，突然拍了拍你的肩。"
  },
  [111070190] = {
    id = 111070190,
    name = "这位小兄弟的装备很齐全嘛！"
  },
  [111070191] = {id = 111070191, name = "......"},
  [111070192] = {
    id = 111070192,
    name = "那是因为...我立志毕业之后要做一名探险者。"
  },
  [111070193] = {
    id = 111070193,
    name = "哈哈，好！有志气。这里离圈落确实很远，你们想进就进去吧。"
  },
  [111070194] = {
    id = 111070194,
    name = "你愿意让我们进去？"
  },
  [111070195] = {
    id = 111070195,
    name = "村长什么也没说，笑着放你和马尔可进入了村庄。"
  },
  [111070196] = {
    id = 111070196,
    name = "（小声）大哥，就这么放他们进去，不会有问题吧？"
  },
  [111070197] = {
    id = 111070197,
    name = "大惊小怪。以前公会的人都来过，也没查到什么，何况是这两个连据点和村子都分不清楚的学生。"
  },
  [111070198] = {
    id = 111070198,
    name = "进入村庄后，马尔可立刻变回了平时的状态。"
  },
  [111070199] = {
    id = 111070199,
    name = "这里的浊炁变淡了一些......"
  },
  [111070200] = {
    id = 111070200,
    name = "是啊，看来这个村庄还有很多秘密。"
  },
  [111070201] = {
    id = 111070201,
    name = "作为助手，能发现不同寻常的秘密，是一个侦探的基本准则，虽然还差很远，但你有随机应变的能力。"
  },
  [111070202] = {id = 111070202, name = "嗯？"},
  [111070203] = {
    id = 111070203,
    name = "竟然想到伪装身份进入村庄，你还是有成为正式助手的资质的。"
  },
  [111070204] = {id = 111070204, name = "......"},
  [111070205] = {
    id = 111070205,
    name = "唉，像我这样聪明过人的侦探，想找到一个心仪的助手，真是太难了。"
  },
  [111070206] = {id = 111070206, name = "......"},
  [111070207] = {
    id = 111070207,
    name = "村庄里的房屋错落有致，可每一间房门都紧紧关闭着，只偶尔看到远处有人在搬运什么东西...马尔可走到一间房屋前停下了。"
  },
  [111070208] = {
    id = 111070208,
    name = "这是......仓库？"
  },
  [111070209] = {
    id = 111070209,
    name = "拥有仓库的据点在深渊里几乎不存在，而这里竟然有仓库，这难道不是值得留意之处吗？"
  },
  [111070210] = {
    id = 111070210,
    name = "不止仓库，这里的一切，都没有被破坏过的痕迹......"
  },
  [111070211] = {
    id = 111070211,
    name = "听到身后的脚步声，你们停止了讨论。"
  },
  [111070212] = {
    id = 111070212,
    name = "学长，这里和大陆的村庄很像啊~"
  },
  [111070213] = {
    id = 111070213,
    name = "嗯，看来据点的多样性也可以纳入研究的课题里。"
  },
  [111070214] = {
    id = 111070214,
    name = "这里为什么和其他据点不一样，不怕被魔物袭击吗？"
  },
  [111070215] = {
    id = 111070215,
    name = "身后传来了村长的笑声。"
  },
  [111070216] = {
    id = 111070216,
    name = "其实我们也是看中了这里的地理位置。这里背山靠水，魔物想进来也不容易，所以我们才决定在这里常住的。而且那件事之后，魔物已经很少来村里了，大家也能安安稳稳地过上几年。"
  },
  [111070217] = {
    id = 111070217,
    name = "那件事，是什么事？"
  },
  [111070218] = {
    id = 111070218,
    name = "三年前有个探险者帮过我们，否则村庄早就像其他据点一样，被魔物毁灭了。哦，这件事附近都可以打听到。"
  },
  [111070219] = {
    id = 111070219,
    name = "是不是那把光辉之剑的传说？！"
  },
  [111070220] = {
    id = 111070220,
    name = "哦？你听说过那把剑啊。"
  },
  [111070221] = {
    id = 111070221,
    name = "没错，光辉之剑是那位探险者留下的剑，当年的战场就在这座山的背后。"
  },
  [111070222] = {
    id = 111070222,
    name = "为了保护陌生人与魔物斗争...光辉之剑的主人真是了不起。他叫什么名字啊？"
  },
  [111070223] = {
    id = 111070223,
    name = "唉，这正是让我们可惜的地方，他没有留下名字就走了。"
  },
  [111070224] = {
    id = 111070224,
    name = "......好想亲眼看看那把传说中的剑啊。"
  },
  [111070225] = {
    id = 111070225,
    name = "面对马尔可的试探，村长眯了眯眼睛，随后，他扬了扬手。"
  },
  [111070226] = {
    id = 111070226,
    name = "去吧，沿着河边走到尽头就能到了，我还有点事，就先走了，你们随便看看吧。不过，尽量在日落之前回来哦，晚上的深渊，可是非常危险的。"
  },
  [111070227] = {
    id = 111070227,
    name = "嗯，谢谢您的提醒。"
  },
  [111070228] = {
    id = 111070228,
    name = "村长离开之后，马尔可还在沉思中。"
  },
  [111070229] = {
    id = 111070229,
    name = "不去看看吗，那把剑。"
  },
  [111070230] = {
    id = 111070230,
    name = "别打扰我，我在思考问题。"
  },
  [111070231] = {
    id = 111070231,
    name = "什么问题？"
  },
  [111070232] = {
    id = 111070232,
    name = "他说得很有道理，这里地势易守难攻，魔物的确很难侵入，但魔物一次没来过，还是令人难以置信。"
  },
  [111070233] = {
    id = 111070233,
    name = "你呢，有没有发现任何奇怪的地方？"
  },
  [111070234] = {
    id = 111070234,
    name = "其实他的话并不值得相信。他说的三年前......让我想起另一件让人觉得巧合的事。"
  },
  [111070235] = {
    id = 111070235,
    name = "果然，你知道更多的情报，快说来听听。"
  },
  [111070236] = {
    id = 111070236,
    name = "三年前，某学院开展了个人实践测试，其中一个学生在途中失踪了。而失踪的学生在实践结束之后也没有回到学院，之后也没有任何消息。"
  },
  [111070237] = {
    id = 111070237,
    name = "难道这个学生就是帮他们的探险者？"
  },
  [111070238] = {
    id = 111070238,
    name = "目前没有证据证明学生就是探险者。深渊危机重重，每天失踪的人不知凡几，也许真的只是巧合而已。"
  },
  [111070239] = {
    id = 111070239,
    name = "有质疑的精神，却不妄下定论，你确实有当助理的天赋。"
  },
  [111070240] = {id = 111070240, name = "......"},
  [111070241] = {
    id = 111070241,
    name = "不管怎样，先去看看光辉之剑吧。"
  },
  [111070242] = {
    id = 111070242,
    name = "我正有此意。"
  },
  [111070243] = {
    id = 111070243,
    name = "你们沿着河边走着，越到尽头越是萧条，直到寸草不生。"
  },
  [111070244] = {
    id = 111070244,
    name = "腐朽折断的刀柄剑戟插满黑红色的地面，肃杀萧索，当厮杀怒吼都消失，留下的只有沉寂的大地。"
  },
  [111070245] = {
    id = 111070245,
    name = "战场的中央，一把完整的大剑直插大地，剑柄处缠绕着手腕粗的铁链，延伸至四方地底，不知通向何处。"
  },
  [111070246] = {
    id = 111070246,
    name = "这就是那把光辉之剑？！"
  },
  [111070247] = {
    id = 111070247,
    name = "看来三年前这里经历过很惨烈的战斗。"
  },
  [111070248] = {
    id = 111070248,
    name = "又是来游历的人啊。"
  },
  [111070249] = {
    id = 111070249,
    name = "清瘦的老头拿着一把长长的扫把，在一旁躬身扫地。"
  },
  [111070250] = {
    id = 111070250,
    name = "哇，你什么时候出现的？"
  },
  [111070251] = {
    id = 111070251,
    name = "我一直在这里，是你们看得太专注了。"
  },
  [111070252] = {
    id = 111070252,
    name = "一直？你一直在这里打扫？"
  },
  [111070253] = {
    id = 111070253,
    name = "是的，大家说过的，这里是和探险者一起战斗过的地方，要随时保持这里的干净。"
  },
  [111070254] = {
    id = 111070254,
    name = "可是这里是村外吧，魔物不会来吗？"
  },
  [111070255] = {
    id = 111070255,
    name = "呵呵，多亏了那个探险者啊，自从那次魔物被杀光之后，就再也没有魔物来过了。"
  },
  [111070256] = {
    id = 111070256,
    name = "这把剑为什么会在这里？"
  },
  [111070257] = {
    id = 111070257,
    name = "是那位探险者留下的。"
  },
  [111070258] = {
    id = 111070258,
    name = "曾经有人想拔出那把剑，不过，从来没有人成功过。"
  },
  [111070259] = {
    id = 111070259,
    name = "那这把剑的主人现在在哪里？"
  },
  [111070260] = {
    id = 111070260,
    name = "当时她说她从圈落来，这里只是她考验的一部分，在那之后，她应该回到圈落了吧。"
  },
  [111070261] = {
    id = 111070261,
    name = "村民将落叶扫在一起，装进了一个口袋里。"
  },
  [111070262] = {
    id = 111070262,
    name = "这是用来做什么的？"
  },
  [111070263] = {
    id = 111070263,
    name = "他们把山上的莹草移植下来，想试着在村里种莹草，这些正好用来作为莹草的肥料。"
  },
  [111070264] = {
    id = 111070264,
    name = "对了，你刚才说的圈落，在哪个方向？"
  },
  [111070265] = {
    id = 111070265,
    name = "村民麻利地把落叶打包好，他提上了扫把，指了指河的对岸。"
  },
  [111070266] = {
    id = 111070266,
    name = "就在那边。你们要是去圈落，就早点出发吧，路程可不短啊。"
  },
  [111070267] = {
    id = 111070267,
    name = "村民走后，马尔可走到了那把大剑的面前，她试着敲了敲那把剑，纹丝不动。"
  },
  [111070268] = {
    id = 111070268,
    name = "果然如此！这把剑是被锁在这里的，我在剑中感受到了魔法力！"
  },
  [111070269] = {
    id = 111070269,
    name = "你走到马尔可的身边，轻触铁链，你感受到了魔法的流动，很快你发现，那些魔法的流动是有规律可寻的。"
  },
  [111070270] = {
    id = 111070270,
    name = "难道是......"
  },
  [111070271] = {
    id = 111070271,
    name = "铁链四周延伸的地面上，每一处地面都刻着奇异的纹路，魔法在某种规则下悄然运行着，与你的猜测不谋而合。"
  },
  [111070272] = {
    id = 111070272,
    name = "是魔法阵。"
  },
  [111070273] = {
    id = 111070273,
    name = "这里有魔法阵？是什么魔法阵？！"
  },
  [111070274] = {
    id = 111070274,
    name = "我也不太清楚，这种阵法，我从来没有见过，但这种魔法阵给我的感觉很不舒服。"
  },
  [111070275] = {
    id = 111070275,
    name = "也许这就是那个探险者做的呢，她用自己的武器做了这样的一个魔法阵，这样就能抵御魔物的入侵。看来你之前的情报与此事无关啊。"
  },
  [111070276] = {
    id = 111070276,
    name = "什么情报？"
  },
  [111070277] = {
    id = 111070277,
    name = "当然是学院学生失踪的情报！这种魔法阵，连我、我们都不知道，学院的学生，又怎么能做到呢？"
  },
  [111070278] = {
    id = 111070278,
    name = "（......那可不一定。）"
  },
  [111070279] = {
    id = 111070279,
    name = "说不定那个学生早已死在了魔物手里。"
  },
  [111070280] = {
    id = 111070280,
    name = "（真的没有联系吗......）"
  },
  [111070281] = {
    id = 111070281,
    name = "你仔细观察着那把剑。这是大陆很流行的大剑，学院曾经有一段时间也配备过相同的，但这并不能证明什么。"
  },
  [111070282] = {
    id = 111070282,
    name = "这个村庄的秘密，真是越来越有意思了！"
  },
  [111070283] = {
    id = 111070283,
    name = "（还是没有维弗的线索，看来需要收集更多的情报。）"
  },
  [111070284] = {
    id = 111070284,
    name = "我决定立刻前往圈落，收集多方的信息再整合判断。"
  },
  [111070285] = {id = 111070285, name = "嗯。"},
  [111070286] = {
    id = 111070286,
    name = "对了，你不是要找那个叫维弗的吗，怎么不去找人问问？"
  },
  [111070287] = {
    id = 111070287,
    name = "既然我们以伪装的身份进了村庄，就不能和公会有联系。有些事，适合暗中探查比较好。"
  },
  [111070288] = {id = 111070288, name = "......"},
  [111070289] = {
    id = 111070289,
    name = "要寻得事件的真相，有时候就要有主动出击的胆量，你虽然有做助手的天赋，但在这方面明显不足。想要成为我的助手，还差得远呢。"
  },
  [111070290] = {id = 111070290, name = "......"},
  [111070291] = {
    id = 111070291,
    name = "终于到了。呼，赶在了天黑之前。"
  },
  [111070292] = {
    id = 111070292,
    name = "这一路上的魔物太多了，直到圈落附近才逐渐消失。"
  },
  [111070293] = {
    id = 111070293,
    name = "在深渊，除了圈落，可没有绝对安全的地方。不过在大型兽潮来临的时候，这样的圈落也没有能完全抵挡的能力。"
  },
  [111070294] = {
    id = 111070294,
    name = "你说得不错，所以村庄——"
  },
  [111070295] = {
    id = 111070295,
    name = "所以我们趁现在街上还有人，多去收集一下情报吧。"
  },
  [111070296] = {
    id = 111070296,
    name = "马尔可自顾自地打断了你的话，走向圈落门前的守卫。你默默地观察人来人往的圈落城门，发现出入的几乎都是探险者。"
  },
  [111070297] = {
    id = 111070297,
    name = "这些人，都知道那个村庄的存在吗......"
  },
  [111070298] = {
    id = 111070298,
    name = "过了好一会儿，马尔可才结束了与守卫的谈话。在进入圈落时，她回头看了你一眼，催促你跟上。"
  },
  [111070299] = {
    id = 111070299,
    name = "我问过了，天黑之前，圈落会关闭城门。另外，还有一件事。"
  },
  [111070300] = {
    id = 111070300,
    name = "马尔可在圈落里左右环顾，突然，她看到了什么，露出了志在必得的笑容。"
  },
  [111070301] = {
    id = 111070301,
    name = "是那个吧......"
  },
  [111070302] = {
    id = 111070302,
    name = "她走近附近的城墙，望向上面一排排的寻人启事。"
  },
  [111070303] = {
    id = 111070303,
    name = "竟然这么多？"
  },
  [111070304] = {
    id = 111070304,
    name = "没错，这些都是近两年圈落的失踪者。虽然在深渊里人总是会发生各种意外消失，但这样频繁地消失，就显得非常蹊跷。"
  },
  [111070305] = {
    id = 111070305,
    name = "唔，我看看，这上面没有你要找的人啊。公会把消息瞒得很严呢~"
  },
  [111070306] = {id = 111070306, name = "......"},
  [111070307] = {
    id = 111070307,
    name = "疑点越来越多了。这些寻人启事，完全透露了失踪者和村庄之间的联系。"
  },
  [111070308] = {
    id = 111070308,
    name = "你也认为失踪者和村庄有关？"
  },
  [111070309] = {id = 111070309, name = "当然！"},
  [111070310] = {
    id = 111070310,
    name = "你看这个，失踪者和同伴发生争执，一气之下离开了圈落；还有这个，失踪者父母惨死，郁郁出走......这些，都说明一个问题！"
  },
  [111070311] = {
    id = 111070311,
    name = "那就是失踪者厌倦了探险，想暂时寻找安宁的地方，而那个村庄就是这样的一个避世之所！所以，那些人不是失踪，而是变成了神秘村庄的村民！"
  },
  [111070312] = {id = 111070312, name = "......"},
  [111070313] = {
    id = 111070313,
    name = "我想我们还是去收集更多的线索吧。"
  },
  [111070314] = {
    id = 111070314,
    name = "等等，你是在质疑我的判断吗？！"
  },
  [111070315] = {
    id = 111070315,
    name = "当然不是，我只是在想如何才能找到维弗。"
  },
  [111070316] = {
    id = 111070316,
    name = "这时，旁边路过一对中年夫妻。他们看着你们，露出了同情的眼神。"
  },
  [111070317] = {
    id = 111070317,
    name = "小伙子，你的同伴也失踪了啊？"
  },
  [111070318] = {
    id = 111070318,
    name = "找不到的啦~魔物那么凶猛，在深渊里失踪，多半就是被吃掉啦。"
  },
  [111070319] = {
    id = 111070319,
    name = "魔物？圈落附近的那些魔物，都是低级魔物吧？"
  },
  [111070320] = {
    id = 111070320,
    name = "那是从旁边山上溜出来的啦~"
  },
  [111070321] = {
    id = 111070321,
    name = "真正凶猛的魔物都在山上。而且它们没过多久就要袭击一次圈落，搞得这里乌烟瘴气的。"
  },
  [111070322] = {
    id = 111070322,
    name = "那为什么没有探险队上山剿灭魔物呢？"
  },
  [111070323] = {
    id = 111070323,
    name = "又不是公会那种大组织，都是零零散散几个人，谁会轻易去送死呀~你要找的人，十有八九是被魔物吃掉咯。"
  },
  [111070324] = {
    id = 111070324,
    name = "那座山在哪里？"
  },
  [111070325] = {
    id = 111070325,
    name = "你们想去山里找人？"
  },
  [111070326] = {
    id = 111070326,
    name = "哎呀，奉劝你们别去了~年纪轻轻，死了多可惜。"
  },
  [111070327] = {
    id = 111070327,
    name = "两人叹息着走了。"
  },
  [111070328] = {
    id = 111070328,
    name = "不告诉我吗？哼，我总有办法能找到答案。"
  },
  [111070329] = {
    id = 111070329,
    name = "马尔可皱了皱眉，向你伸出了手。"
  },
  [111070330] = {id = 111070330, name = "什么？"},
  [111070331] = {
    id = 111070331,
    name = "公会的人不是给了你一张地图吗？"
  },
  [111070332] = {
    id = 111070332,
    name = "你知道的还挺多。"
  },
  [111070333] = {
    id = 111070333,
    name = "你将维弗绘制的地图展开，指了指圈落与村庄之间的山。"
  },
  [111070334] = {
    id = 111070334,
    name = "附近只有这一座山，我们来的路上，也遇到了不少的魔物。"
  },
  [111070335] = {
    id = 111070335,
    name = "不对。你看这座山，明显离村庄更近，可魔物不去袭击就近的村庄，反而以防范更严密的圈落为目标，这是为什么？"
  },
  [111070336] = {
    id = 111070336,
    name = "（总算发现了疑点......）"
  },
  [111070337] = {
    id = 111070337,
    name = "既然在村庄查不到什么，那就探查这座山吧！"
  },
  [111070338] = {
    id = 111070338,
    name = "......只有我们两个？山上的魔物也许不是我们能应付的。"
  },
  [111070339] = {
    id = 111070339,
    name = "身为侦探，就要有一查到底的决心和义无反顾的勇气！这是寻找真相的必要条件。"
  },
  [111070340] = {
    id = 111070340,
    name = "这也是你挑选助手的条件吗？"
  },
  [111070341] = {id = 111070341, name = "当然。"},
  [111070342] = {
    id = 111070342,
    name = "马尔可往圈落外走去，你叹了口气跟了上去。"
  },
  [111070343] = {
    id = 111070343,
    name = "天快黑了，要查清真相也不急于一时，明早再去吧。"
  },
  [111070344] = {
    id = 111070344,
    name = "你的意思是同意和我一起去山上了？不错，你的敏锐度很高，要不要考虑成为我的临时助手？"
  },
  [111070345] = {id = 111070345, name = "......"},
  [111070346] = {
    id = 111070346,
    name = "不用太感激，对于临时助手合理的提议，本侦探还是愿意采纳的。走吧，先去休息，明天再上山。"
  },
  [111070347] = {
    id = 111070347,
    name = "这里的浊炁好浓郁，不愧是魔物喜欢的地方。"
  },
  [111070348] = {
    id = 111070348,
    name = "零零散散的魔物，到山脚下数量开始增多了。"
  },
  [111070349] = {
    id = 111070349,
    name = "这些低级魔物，以我们的能力，轻而易举就能解决掉。"
  },
  [111070350] = {
    id = 111070350,
    name = "马尔可率先进了山，你跟在她身后，静静观察四周，发现了一些端倪。"
  },
  [111070351] = {
    id = 111070351,
    name = "（如果失踪者在山里失踪，那么这里多少都会有人类活动的痕迹，可是......）"
  },
  [111070352] = {
    id = 111070352,
    name = "吼！！！"
  },
  [111070353] = {
    id = 111070353,
    name = "前方响起了一阵阵低吼声，周围的树上爬满了密密麻麻的魔物，它们张牙舞爪地对你们咆哮着，一起扑了上来！"
  },
  [111070354] = {id = 111070354, name = "快逃！"},
  [111070355] = {
    id = 111070355,
    name = "山林深处，你们不停奔跑，时不时往身后的魔物群扔出一道魔法。"
  },
  [111070356] = {
    id = 111070356,
    name = "不是那边，下山在这个方向！"
  },
  [111070357] = {
    id = 111070357,
    name = "慌乱中，你喊停了马尔可，她伸出法杖击倒一个魔物，神色严肃。"
  },
  [111070358] = {
    id = 111070358,
    name = "临阵脱逃从来不在本侦探的选择范围内，在没有找到真相之前，我是不会离开的！"
  },
  [111070359] = {
    id = 111070359,
    name = "如果真相不在这里，就这样死掉你也不会甘心吧！"
  },
  [111070360] = {
    id = 111070360,
    name = "本侦探的直觉不会错！村庄的秘密，一定和这座山的魔物有关！"
  },
  [111070361] = {
    id = 111070361,
    name = "这些魔物数量太多了，它们能长期栖居在这片山林，一定有首领。如果碰上没法对付的——"
  },
  [111070362] = {
    id = 111070362,
    name = "你要是害怕，我一个人去！"
  },
  [111070363] = {
    id = 111070363,
    name = "马尔可扔出一记魔法砸在那群低级魔物头上，接着冲进了更深的林子里。"
  },
  [111070364] = {
    id = 111070364,
    name = "真是我行我素的家伙......"
  },
  [111070365] = {id = 111070365, name = "......"},
  [111070366] = {
    id = 111070366,
    name = "追上马尔可的时候，她正被一只魔物拦了下来，而这只魔物散发出来的威压感，几乎让马尔可喘不过气。她吃力地应付着这只魔物，即使知道不敌也没有半点退缩的意思。"
  },
  [111070367] = {
    id = 111070367,
    name = "你跳上树干，借力腾空来到马尔可身边。紧跟着窜上来不少魔物，嘶吼着朝你和马尔可冲来，你转身护住马尔可，几剑解决了那些魔物。而此时头目级魔物抬头咆哮一声，朝马尔可怒奔而来！"
  },
  [111070368] = {id = 111070368, name = "！！！"},
  [111070369] = {id = 111070369, name = "快逃！"},
  [111070370] = {
    id = 111070370,
    name = "等等，走这边！"
  },
  [111070371] = {
    id = 111070371,
    name = "那边是——"
  },
  [111070372] = {
    id = 111070372,
    name = "就是这边！"
  },
  [111070373] = {
    id = 111070373,
    name = "马尔可引着魔物头也不回地冲向村庄的方向。"
  },
  [111070374] = {
    id = 111070374,
    name = "身手不错嘛！"
  },
  [111070375] = {
    id = 111070375,
    name = "你配合地清除两人身后的低级魔物，魔物尸体堵在头目级魔物前进的路上，但也拖延不了多少时间......"
  },
  [111070376] = {
    id = 111070376,
    name = "这样下去不是办法，到不了山下我们就会被它追上！"
  },
  [111070377] = {
    id = 111070377,
    name = "马尔可突然停了下来，她的前方是一条深不见底的沟壑。"
  },
  [111070378] = {
    id = 111070378,
    name = "呼......好宽的裂谷，这就是魔物不曾袭击村庄的原因吗？"
  },
  [111070379] = {
    id = 111070379,
    name = "也许是，但我们现在要考虑的问题不是这个......"
  },
  [111070380] = {
    id = 111070380,
    name = "和低级魔物战斗了许久，你们两人的魔力都消耗得非常严重。此时魔物首领逼近，飞扑而上，马尔可有一瞬间的怔愣。"
  },
  [111070381] = {id = 111070381, name = "躲开！"},
  [111070382] = {
    id = 111070382,
    name = "长剑挡住了利爪的进攻，头目级魔物嘶吼一声！尖爪并成一个锥体，刺入了你的肩膀！"
  },
  [111070383] = {
    id = 111070383,
    name = "轰！！！"
  },
  [111070384] = {
    id = 111070384,
    name = "绚烂的魔法在空中骤然炸开，马尔可一声呵斥，全力击中了魔物首领的眼睛！魔物首领痛得在地上滚了两圈，马尔可看到你的伤，瞳孔紧缩。"
  },
  [111070385] = {
    id = 111070385,
    name = "你没事吧？！"
  },
  [111070386] = {
    id = 111070386,
    name = "肩头不断渗出鲜血，顺着手臂滴到了地面上。头目级魔物首领不安分地刨着地面，变得更加兴奋。"
  },
  [111070387] = {
    id = 111070387,
    name = "先离开这里......"
  },
  [111070388] = {
    id = 111070388,
    name = "你艰难地拿出了地图，扔给马尔可，目光一直盯着魔物首领。"
  },
  [111070389] = {
    id = 111070389,
    name = "这条沟壑维弗画出来过，等会儿我去引诱魔物，你来寻路，现在只能想办法先逃出去！"
  },
  [111070390] = {
    id = 111070390,
    name = "马尔可看着负伤的你冲向魔物群，猛地回过神来，她双手颤抖地展开地图，咬紧嘴唇，急速地判断着路线。"
  },
  [111070391] = {
    id = 111070391,
    name = "找到了！走这边！"
  },
  [111070392] = {
    id = 111070392,
    name = "她用魔法击杀掉角落的一只魔物，魔物首领狂吼一声，猛锤地面！"
  },
  [111070393] = {
    id = 111070393,
    name = "浊炁在它身躯中爆发出来，迅速充斥着整片山林，马尔可立刻撑起了临时的防御光幕。"
  },
  [111070394] = {
    id = 111070394,
    name = "快过来！"
  },
  [111070395] = {
    id = 111070395,
    name = "你勉强在地上翻滚一圈，和马尔可汇合在一起，光幕随即碎裂！"
  },
  [111070396] = {
    id = 111070396,
    name = "马尔可一把抓住你的手腕，带你逃离了出去！"
  },
  [111070397] = {
    id = 111070397,
    name = "就快到了！"
  },
  [111070398] = {
    id = 111070398,
    name = "你们一路狂奔，总算看到了昔日的战场遗址，魔物首领仍在身后锲而不舍地追逐。"
  },
  [111070399] = {
    id = 111070399,
    name = "看来它被你彻底激怒了啊......"
  },
  [111070400] = {
    id = 111070400,
    name = "你们站在大剑旁边，马尔可捏紧了手中的法杖，做好了迎战的准备。"
  },
  [111070401] = {
    id = 111070401,
    name = "可下一秒，那魔物首领竟然在停在了原地，它左右转了一圈，不甘心地狂吼了一阵，竟然原路跑回了山。"
  },
  [111070402] = {
    id = 111070402,
    name = "怎么......回事？"
  },
  [111070403] = {
    id = 111070403,
    name = "难道是它感受到了昔日同伴的鲜血。意识到这里是个危险的地方，所以它不敢过来，灰溜溜撤回去了？"
  },
  [111070404] = {
    id = 111070404,
    name = "......那个，它看起来像是看不见我们。"
  },
  [111070405] = {
    id = 111070405,
    name = "你忍不住提醒了一下马尔可，她怔怔地望着大剑，忽然放声笑了起来。"
  },
  [111070406] = {
    id = 111070406,
    name = "原来如此！原来如此！"
  },
  [111070407] = {
    id = 111070407,
    name = "她脸上的血迹未干，身上衣衫也被划破，此时她彻底放松了紧绷的神经，坐在了地上。因为疲惫她连手指也动不了了，但依旧嘴角上翘，愉悦地笑着。"
  },
  [111070408] = {
    id = 111070408,
    name = "因为她接触到了事件真相的一部分。"
  },
  [111070409] = {
    id = 111070409,
    name = "......至今为止我查探了许多个事件，这是最惊心动魄、也是最令我意外的一次，本以为会白白地浪费侦查的时间，没想到竟然意外收获。"
  },
  [111070410] = {
    id = 111070410,
    name = "她抬眼望向你，眼神带着认可和欣赏。"
  },
  [111070411] = {
    id = 111070411,
    name = "多谢你了。没有你，我恐怕走不出那个山林。"
  },
  [111070412] = {
    id = 111070412,
    name = "马尔可笑着擦了擦嘴角的血。"
  },
  [111070413] = {
    id = 111070413,
    name = "这就是有助手一起行动的感觉吗......和一个人的确不同。我果然是缺了一个，像你这样优秀的助手啊......"
  },
  [111070414] = {id = 111070414, name = "......"},
  [111070415] = {
    id = 111070415,
    name = "那你说的意外收获是......"
  },
  [111070416] = {
    id = 111070416,
    name = "嗯？身为我的临时助手，你竟然没发现？"
  },
  [111070417] = {
    id = 111070417,
    name = "助手什么的......我只是想听听你意外得到的结论。"
  },
  [111070418] = {
    id = 111070418,
    name = "当然是这个魔法阵，是它屏蔽了魔物的感知！没想到竟然不是残留的阵法，而是一直在运行着！"
  },
  [111070419] = {
    id = 111070419,
    name = "你将手扶在大剑上，感受着其中的魔法波动。"
  },
  [111070420] = {
    id = 111070420,
    name = "这个魔法阵，不光笼罩了这部分区域，应该连整座村庄都囊括了。"
  },
  [111070421] = {
    id = 111070421,
    name = "（要长期运转这样一个阵法，通常会付出巨大的代价......）"
  },
  [111070422] = {
    id = 111070422,
    name = "你忽然明白那些探险者为什么会失踪了。"
  },
  [111070423] = {
    id = 111070423,
    name = "没错，所以在村庄里，一定有一个操控魔法阵的人！那些村民一定有秘密瞒着我们。嗯？你怎么了？看起来很生气的样子？"
  },
  [111070424] = {
    id = 111070424,
    name = "没什么，我们去村庄吧。"
  },
  [111070425] = {
    id = 111070425,
    name = "好，出发！"
  },
  [111070426] = {
    id = 111070426,
    name = "等等，最好别让村民们看见我们现在这副模样。"
  },
  [111070427] = {
    id = 111070427,
    name = "你说的对。隐藏起来才能探听到更多的秘密！走吧。"
  },
  [111070428] = {
    id = 111070428,
    name = "天色渐晚，你们拖着脚步到达村口的时候，守卫已经不见了，只有远处的几间房屋透出昏黄的灯光。"
  },
  [111070429] = {
    id = 111070429,
    name = "果然，守卫知道村庄是绝对安全的，才这样松懈。"
  },
  [111070430] = {
    id = 111070430,
    name = "他们就不怕被打劫吗？"
  },
  [111070431] = {
    id = 111070431,
    name = "......也许他们自己就是呢？"
  },
  [111070432] = {
    id = 111070432,
    name = "嗯？你嘀嘀咕咕在说什么呢？"
  },
  [111070433] = {
    id = 111070433,
    name = "没什么。为什么天还没完全黑，就没有人外出了？"
  },
  [111070434] = {
    id = 111070434,
    name = "没错，这点很奇怪。"
  },
  [111070435] = {
    id = 111070435,
    name = "你打算从哪里开始调查？"
  },
  [111070436] = {
    id = 111070436,
    name = "那边那间最大的房子亮着灯。"
  },
  [111070437] = {
    id = 111070437,
    name = "小心点，不要惊动他们。"
  },
  [111070438] = {
    id = 111070438,
    name = "这种小事不用你提醒我也知道。"
  },
  [111070439] = {
    id = 111070439,
    name = "马尔可放轻了脚步，刚走到房屋附近，房门突然打开了！你们二人连忙躲在掩体后面。"
  },
  [111070440] = {
    id = 111070440,
    name = "房子里走出来一个全身黑袍的人，看不清容貌，随后此人快步离开了村庄。"
  },
  [111070441] = {
    id = 111070441,
    name = "（小声）神神秘秘，肯定有秘密。"
  },
  [111070442] = {
    id = 111070442,
    name = "那黑衣人走后，马尔可用手肘碰了碰你。"
  },
  [111070443] = {
    id = 111070443,
    name = "你去跟踪那个黑衣人，我去听听他们在说什么。"
  },
  [111070444] = {
    id = 111070444,
    name = "那你别轻举妄动，小心一点。"
  },
  [111070445] = {
    id = 111070445,
    name = "眨眼之间，黑衣人已经失去了踪影，你迅速跟了上去。"
  },
  [111070446] = {
    id = 111070446,
    name = "你这个伤员才更应该小心。"
  },
  [111070447] = {id = 111070447, name = "......"},
  [111070448] = {
    id = 111070448,
    name = "马尔可靠近房间，听到了里面的说话声。"
  },
  [111070449] = {
    id = 111070449,
    name = "老大，这家伙这次竟然拿了这么多欧泊，说好的条件还加码，真当我们好欺负！"
  },
  [111070450] = {
    id = 111070450,
    name = "好不容易兄弟们能过点安生日子，他多要一个人，我们就给他多找一个人。"
  },
  [111070451] = {
    id = 111070451,
    name = "能一个人探险的都有点本事，其他探险队我们也不好下手，每个月能抓到一个已经是极限了啊。"
  },
  [111070452] = {
    id = 111070452,
    name = "这也是没有办法的事，谁让我们受制于人，等到我们能掌握那个魔法阵的能力之时......"
  },
  [111070453] = {
    id = 111070453,
    name = "早知道前两天来的那两个学生就该......"
  },
  [111070454] = {id = 111070454, name = "不行！"},
  [111070455] = {
    id = 111070455,
    name = "一个学生失踪还可以说是偶然，要是这两人也失踪，只会引起学院的怀疑。之前好不容易瞒过公会，不能再惹上更大的麻烦。"
  },
  [111070456] = {
    id = 111070456,
    name = "......可这日子就要到了，上哪儿找祭品啊？"
  },
  [111070457] = {
    id = 111070457,
    name = "明天你去圈落附近看看，总能找到落单的人，先维持魔法阵再说！"
  },
  [111070458] = {
    id = 111070458,
    name = "轰！一团火球冲破房门，房里的人一涌而出，马尔可怒气冲冲站在房门前。"
  },
  [111070459] = {id = 111070459, name = "......"},
  [111070460] = {
    id = 111070460,
    name = "你一路跟踪黑衣人，在即将出村的时候，黑衣人突然停下了。你立即藏起来屏住了呼吸，察觉到黑衣人离开后，你缓缓站起了身。"
  },
  [111070461] = {
    id = 111070461,
    name = "（消失了......很警觉的人，看样子他已经发现被跟踪了。算了，关于维弗的下落，这个村庄里应该有答案。）"
  },
  [111070462] = {
    id = 111070462,
    name = "追踪黑衣人失败，你返回寻找马尔可。"
  },
  [111070463] = {
    id = 111070463,
    name = "村里像是经过了一场大战，到处是被魔法烧毁的痕迹，马尔可也失去了踪影。"
  },
  [111070464] = {
    id = 111070464,
    name = "（不妙啊......）"
  },
  [111070465] = {
    id = 111070465,
    name = "身后窸窸窣窣传来了声音，你迅速隐蔽，一群村民拿着武器从仓库里走了出来。"
  },
  [111070466] = {
    id = 111070466,
    name = "大哥，那女的还有个同伙！"
  },
  [111070467] = {
    id = 111070467,
    name = "（马尔可被抓了？）"
  },
  [111070468] = {
    id = 111070468,
    name = "你躲避着村民，悄悄窥察这座村庄的每一寸角落。"
  },
  [111070469] = {
    id = 111070469,
    name = "没想到那女人不是学生，竟然还冒充学院的人，等抓到那男的再一并送到后山~呵呵呵，正愁没有交差的货，就送上门了。那男人一定还在这附近，给我仔细地找！"
  },
  [111070470] = {id = 111070470, name = "是！"},
  [111070471] = {
    id = 111070471,
    name = "（原来马尔可不仅暴露了身份，还揭穿了他们的假面具......）"
  },
  [111070472] = {
    id = 111070472,
    name = "（后山......看来维弗的尸体就要找到了。）"
  },
  [111070473] = {
    id = 111070473,
    name = "你暗中跟随几个村民到了后山。一根石柱高高伫立在悬崖边，马尔可正被锁链绑缚在石柱上，满脸愤怒。"
  },
  [111070474] = {
    id = 111070474,
    name = "你们这群罪大恶极的匪徒，我一定会曝光你们的罪行！如果你们不想被公会执法的话，就停止作恶，立刻放了我！"
  },
  [111070475] = {
    id = 111070475,
    name = "（还挺有精神的嘛。）"
  },
  [111070476] = {
    id = 111070476,
    name = "马尔可的话惹得村民一阵发笑。"
  },
  [111070477] = {
    id = 111070477,
    name = "好啊，你去地狱里曝光吧，他们都等着你呢。"
  },
  [111070478] = {
    id = 111070478,
    name = "村长意有所指地看着马尔可的脚下。石柱的四周，是堆积的皑皑白骨，少说也有几十架，马尔可的脸不禁沉了下去。"
  },
  [111070479] = {
    id = 111070479,
    name = "启动魔法阵！"
  },
  [111070480] = {
    id = 111070480,
    name = "村长一声令下，几个人在四周地面上放下了魔法石，一只携着魔法石的利箭飞出，箭矢往石柱上高处的一处凹槽射去！"
  },
  [111070481] = {
    id = 111070481,
    name = "（没办法了！）"
  },
  [111070482] = {
    id = 111070482,
    name = "箭矢落入石柱之前，一颗飞石将其击落。众人大惊，马尔可却露出了惊喜的神色。"
  },
  [111070483] = {
    id = 111070483,
    name = "是谁？！！"
  },
  [111070484] = {
    id = 111070484,
    name = "快阻止他们！这是一个献祭活人来运转魔法的邪恶阵法！那些失踪者，都是这样被害的！"
  },
  [111070485] = {
    id = 111070485,
    name = "在那里！！"
  },
  [111070486] = {
    id = 111070486,
    name = "村民们发现了你，而此时你已经站到了马尔可身后，举起了长剑。"
  },
  [111070487] = {
    id = 111070487,
    name = "侦探小姐，你对待真相的态度，真是令人钦佩。"
  },
  [111070488] = {
    id = 111070488,
    name = "哐！锁链竟然丝毫未损。"
  },
  [111070489] = {
    id = 111070489,
    name = "这个锁链和剑上的锁链一样，砍不断的！"
  },
  [111070490] = {
    id = 111070490,
    name = "你回头看向悬崖下，发现那柄长剑所在的魔法阵正对着这根石柱，此时石柱发出微光，与剑光遥相呼应。"
  },
  [111070491] = {
    id = 111070491,
    name = "竟然是连锁在一起的子母魔法阵......可是阵眼在哪里？"
  },
  [111070492] = {
    id = 111070492,
    name = "既然你送上门，那就和她一起去死吧！"
  },
  [111070493] = {
    id = 111070493,
    name = "村长站在魔法阵外，阴狠地补上了第二箭！石柱顶端被箭矢射中的一刹那，魔法阵爆发出了巨大的力量！"
  },
  [111070494] = {id = 111070494, name = "啊啊！"},
  [111070495] = {
    id = 111070495,
    name = "整个魔法阵瞬间笼罩在黑沉沉的浓雾中，无数怨灵在阵中肆虐飘荡，产生重重幻象。"
  },
  [111070496] = {
    id = 111070496,
    name = "无数幽灵尸骸从地底爬出，抓着马尔可的脚，幽灵一路啃噬着，几乎将她吞没！"
  },
  [111070497] = {
    id = 111070497,
    name = "你挥剑向那些幽灵尸骸斩去，而剑却像斩了空气般，只有轻微的气流波动。突然，几只尸骸扑向了你，令你浑身一颤！"
  },
  [111070498] = {
    id = 111070498,
    name = "身体内大量生机在迅速流逝，那些幽灵如跗骨之蛆，怎么甩也甩不掉！"
  },
  [111070499] = {
    id = 111070499,
    name = "（看来攻击对这些幽灵无效，必须先终止阵法的运行，否则......）"
  },
  [111070500] = {
    id = 111070500,
    name = "哈哈哈哈！只要施放魔法，就会逐渐被幽灵吞噬，直到化成一堆白骨！"
  },
  [111070501] = {
    id = 111070501,
    name = "去死吧！知道这个村庄的秘密的外人，统统都得死！"
  },
  [111070502] = {
    id = 111070502,
    name = "你抬起头，看向高高的石柱，一层魔法纹路发出闪耀的光，注入束缚的锁链中。"
  },
  [111070503] = {
    id = 111070503,
    name = "原来如此，这个石柱才是魔法阵的阵眼，崖下的剑是辅助阵法。"
  },
  [111070504] = {
    id = 111070504,
    name = "接着，你把所有的魔力灌注在长剑上，举剑斩向了石柱！"
  },
  [111070505] = {
    id = 111070505,
    name = "你要干什么！！"
  },
  [111070506] = {
    id = 111070506,
    name = "咔嚓、咔嚓、咔嚓......石柱出现了裂痕，魔法阵的光也逐渐暗淡了下来。"
  },
  [111070507] = {
    id = 111070507,
    name = "大大大哥！他、他在砍柱子，柱子、柱子要裂了！！"
  },
  [111070508] = {
    id = 111070508,
    name = "竟然敢破坏魔法阵，你去杀了他！"
  },
  [111070509] = {
    id = 111070509,
    name = "大哥，进入魔法阵我会死的！"
  },
  [111070510] = {
    id = 111070510,
    name = "少废话！"
  },
  [111070511] = {
    id = 111070511,
    name = "村长一脚将身边的村民踹进了魔法阵，那些幽灵闻到恐惧的负面情绪，张牙舞爪地扑了上去！"
  },
  [111070512] = {
    id = 111070512,
    name = "大哥救我，救我！"
  },
  [111070513] = {
    id = 111070513,
    name = "村民根本顾不上杀你，他疯狂拍打着魔法阵屏障！"
  },
  [111070514] = {
    id = 111070514,
    name = "救我，快救我啊啊啊啊！"
  },
  [111070515] = {
    id = 111070515,
    name = "啊！！！"
  },
  [111070516] = {
    id = 111070516,
    name = "撕心裂肺的惨叫声招来了更多的幽灵，它们一点点将村民啃噬殆尽，只留下了一堆白骨...而魔法阵恢复了一点光亮。"
  },
  [111070517] = {
    id = 111070517,
    name = "这样魔法阵，绝对不能存在！"
  },
  [111070518] = {
    id = 111070518,
    name = "你用力再次往同一个方向斩去，终于斩断了那条魔法纹路。光芒消失，你反手削掉了锁链的连接部分，在马尔可失重滑落的瞬间，你接住她滚向了一边。"
  },
  [111070519] = {id = 111070519, name = "轰！"},
  [111070520] = {
    id = 111070520,
    name = "石柱轰然倒塌，魔法纹路迸发出几道细碎的闪电，击得碎石飞溅。与此同时，那些幽灵也全都消失了。"
  },
  [111070521] = {
    id = 111070521,
    name = "踉跄着起身的村长，额头被碎石砸伤了一大片，他歇斯底里地愤怒吼叫着："
  },
  [111070522] = {
    id = 111070522,
    name = "杀了他们！给我杀了他们！"
  },
  [111070523] = {
    id = 111070523,
    name = "村民们步步逼近，你摇了摇怀中半昏半醒的马尔可。"
  },
  [111070524] = {id = 111070524, name = "醒醒！"},
  [111070525] = {
    id = 111070525,
    name = "兜里......药剂......"
  },
  [111070526] = {
    id = 111070526,
    name = "你拿出药喂在她嘴里。突然，身旁的石柱訇然碎裂，露出了一张娟秀的、惨白的、女孩的脸。"
  },
  [111070527] = {
    id = 111070527,
    name = "学院的服饰......是那个学生！"
  },
  [111070528] = {
    id = 111070528,
    name = "村民被石柱中突然出现的尸体吓了一跳，这时尸体倏然睁开了双眼！"
  },
  [111070529] = {
    id = 111070529,
    name = "怎......怎么回事？你怎么会活着？！明明、明明......"
  },
  [111070530] = {
    id = 111070530,
    name = "尸体全身以肉眼可见的速度蔓延成了青灰色，她双目赤白，表情狰狞，嘶吼着对准最近的村民一拳砸了下去，村民被一拳击开，躺在地上吐出大口大口的鲜血！"
  },
  [111070531] = {
    id = 111070531,
    name = "堕......堕落者！"
  },
  [111070532] = {
    id = 111070532,
    name = "竟然用堕落者作为阵眼，用活人作为祭品来延续阵法，丧尽天良啊......"
  },
  [111070533] = {id = 111070533, name = "嘶......"},
  [111070534] = {
    id = 111070534,
    name = "地面的魔法阵纹路骤然大亮，墨黑色的气体凝结成了一柄虚无的剑，堕落者将剑牢牢抓在手中。"
  },
  [111070535] = {
    id = 111070535,
    name = "剑攻向村长，触地的瞬间爆发出极强的气压，让人几乎喘不过气来。村长连滚带爬地躲过了攻击，仓皇地往山下跑去。"
  },
  [111070536] = {
    id = 111070536,
    name = "其他村民见村长逃了，连忙作鸟兽散，只留下几个无法动弹的村民，均被她一一杀死。而后，她看向了山顶上仅剩的两人——你和马尔可。"
  },
  [111070537] = {
    id = 111070537,
    name = "三年前，一名学生参加了学院的个人实践。测试过程中，她路过了一个看起来像村庄的匪窝，而这时，魔物袭击了村庄......"
  },
  [111070538] = {
    id = 111070538,
    name = "马尔可缓缓站起来，拾起她遗落的法杖，死死盯着眼前浑身浴血的堕落者。"
  },
  [111070539] = {
    id = 111070539,
    name = "学生和村民进行了很长很长时间的战斗，她帮这群劫匪打退了魔物。但是，她没想到的是，她的善意不但没有得到感恩，自己反而被永远封锁在了石柱里，成为了魔法阵中最重要的阵眼。"
  },
  [111070540] = {
    id = 111070540,
    name = "而属于她的武器就在离她不远的地方。变成了束缚她的囚笼，让她无法逃脱......"
  },
  [111070541] = {
    id = 111070541,
    name = "吼！！！"
  },
  [111070542] = {
    id = 111070542,
    name = "堕落者发出一声撕裂的吼叫，扑向马尔可，你一剑拦下了它的进攻，与其缠斗起来。"
  },
  [111070543] = {
    id = 111070543,
    name = "从那以后，这块依山傍水的匪窝，演变为神秘的村庄。那些劫匪借着村民的身份，无恶不作。他们每一个月会杀死一个独行的探险者，来补充魔法阵运行的能量。"
  },
  [111070544] = {
    id = 111070544,
    name = "就这样，一个又一个人失踪了，但从来没有人怀疑过这里，因为这里只是平静祥和的村庄。可谁能想到呢？这样让人羡慕的世外桃源，全是建立在无辜者的血肉之上！"
  },
  [111070545] = {
    id = 111070545,
    name = "法杖对准了正在和你激战的堕落者。"
  },
  [111070546] = {
    id = 111070546,
    name = "如今终于真相大白。满怀怨气的堕落者啊，就此安息吧！"
  },
  [111070547] = {
    id = 111070547,
    name = "一道净化之光在法杖顶端出现，光束击中了堕落者手中的剑。"
  },
  [111070548] = {
    id = 111070548,
    name = "啊！！！"
  },
  [111070549] = {
    id = 111070549,
    name = "你趁机一剑刺穿了它的心脏，堕落者的身体陷入僵直，她的面容逐渐蜕变为年轻秀丽的容颜。"
  },
  [111070550] = {
    id = 111070550,
    name = "这本应该是一个温柔的女孩，可如今，她的眼中只有滔天的恨意和不甘。"
  },
  [111070551] = {
    id = 111070551,
    name = "这是一个不公平的世界。总有人妄图利用别人的善良来欺骗、倾轧、掠夺，然后在受害者的尸体上，构建他们的乐园。无论你怎么抗争，都无法摆脱这一切......"
  },
  [111070552] = {
    id = 111070552,
    name = "女生的眼中滑出了泪水。"
  },
  [111070553] = {
    id = 111070553,
    name = "你怨恨，不甘，还有遗憾。可是，你所有的怨恨和遗憾变成了魔法阵运行的关键，你越是怨恨，越是不甘，魔法阵就会更牢固地守护着村庄，直到你的灵魂碎裂，消磨殆尽......"
  },
  [111070554] = {
    id = 111070554,
    name = "在夹缝中求生，没有人理会，没有人救赎，很痛苦吧，不过没关系，我们查到真相了。"
  },
  [111070555] = {
    id = 111070555,
    name = "我们会结束这一切，还你一个公道。"
  },
  [111070556] = {
    id = 111070556,
    name = "女生愣愣地看着你们，腐败的身体逐步消散。"
  },
  [111070557] = {
    id = 111070557,
    name = "乌云终究会散去，他们会得到应有的惩罚。"
  },
  [111070558] = {
    id = 111070558,
    name = "女生眼神恍惚，仅剩的意识让她泪流满面，而后泪水随身体一起化为了乌有。"
  },
  [111070559] = {
    id = 111070559,
    name = "「谢谢...... 」"
  },
  [111070560] = {
    id = 111070560,
    name = "空中传来女生的声音，温和而平静。"
  },
  [111070561] = {id = 111070561, name = "......"},
  [111070562] = {
    id = 111070562,
    name = "走吧，魔法阵破了，这里也不安全了。"
  },
  [111070563] = {
    id = 111070563,
    name = "马尔可少有的沉默，跟在你身后下了山。"
  },
  [111070564] = {
    id = 111070564,
    name = "等等，我记得不是这条路。"
  },
  [111070565] = {
    id = 111070565,
    name = "那边是回村的路。"
  },
  [111070566] = {
    id = 111070566,
    name = "那些匪徒呢，不管了？我们刚刚还答应了她要惩治他们。"
  },
  [111070567] = {
    id = 111070567,
    name = "呵呵，难道你想帮他们杀魔物吗？"
  },
  [111070568] = {
    id = 111070568,
    name = "马尔可怔了一下，轻咳了一声，与你并肩而行。"
  },
  [111070569] = {
    id = 111070569,
    name = "咳，本侦探只是想看看那些家伙的下场。"
  },
  [111070570] = {
    id = 111070570,
    name = "那我倒是知道一个地方，跟我来。"
  },
  [111070571] = {
    id = 111070571,
    name = "又是地图上写的？"
  },
  [111070572] = {
    id = 111070572,
    name = "算是吧。"
  },
  [111070573] = {id = 111070573, name = "......"},
  [111070574] = {
    id = 111070574,
    name = "马尔可沉默了好一阵，突然小声说了一句。"
  },
  [111070575] = {id = 111070575, name = "谢谢。"},
  [111070576] = {
    id = 111070576,
    name = "嗯，你说什么？"
  },
  [111070577] = {
    id = 111070577,
    name = "没、没什么。你这个临时助手非常优秀，我已经开始考虑长期和你合作了。"
  },
  [111070578] = {
    id = 111070578,
    name = "不不，按照侦探小姐的要求，我还差得远呢。"
  },
  [111070579] = {
    id = 111070579,
    name = "那......那是之前我没有认清你的本质，妄自下了定论，我跟你道歉。"
  },
  [111070580] = {
    id = 111070580,
    name = "不要紧。相反，你一往无前的正义感和对真相的追求，都令我刮目相看。"
  },
  [111070581] = {
    id = 111070581,
    name = "马尔可罕见地红了红脸，忽然想起了什么。"
  },
  [111070582] = {
    id = 111070582,
    name = "说起来，这些劫匪是从哪里知道的这么邪恶的阵法。"
  },
  [111070583] = {
    id = 111070583,
    name = "你不是偷听他们说话了吗？"
  },
  [111070584] = {
    id = 111070584,
    name = "是那个黑衣人！"
  },
  [111070585] = {
    id = 111070585,
    name = "没错，那些人还提到了交易的事，一定是黑衣人和那些匪徒做了交易。可是黑衣人又是从哪里来的魔法阵呢？有什么目的？"
  },
  [111070586] = {
    id = 111070586,
    name = "马尔可突然停下，拿出一本笔记。她划去了一行，又增加了几行字。"
  },
  [111070587] = {
    id = 111070587,
    name = "在写什么？"
  },
  [111070588] = {
    id = 111070588,
    name = "深渊十大未解之谜。村庄的秘密已经解开，可我闻到了更大的阴谋的味道。"
  },
  [111070589] = {
    id = 111070589,
    name = "咦，第一个是？"
  },
  [111070590] = {
    id = 111070590,
    name = "当然是深渊十大未解之谜之首了！"
  },
  [111070591] = {
    id = 111070591,
    name = "绝境之地啊......"
  },
  [111070592] = {
    id = 111070592,
    name = "是的，你也知道这个大名鼎鼎的地方吧！总有一天我会解开它的秘密！"
  },
  [111070593] = {id = 111070593, name = "......"},
  [111070594] = {
    id = 111070594,
    name = "远处传来了魔物群的吼叫声。"
  },
  [111070595] = {
    id = 111070595,
    name = "啊，天黑了，我们赶紧下山吧。"
  },
  [111070596] = {
    id = 111070596,
    name = "你找的这个地方，的确很适合侦查村庄的情形。"
  },
  [111070597] = {
    id = 111070597,
    name = "下山以后，你带着马尔可绕到了河边的隐秘位置。"
  },
  [111070598] = {
    id = 111070598,
    name = "夜色中，上百只魔物狂奔淌过了河。它们冲入了村庄，在各个房屋间窜行，火把倒在田间，火势蔓延到每个角落。匪徒们奋起抵抗，凄厉的惨叫声不绝于耳。"
  },
  [111070599] = {
    id = 111070599,
    name = "这个神秘的村庄，竟然就这样毁灭了。"
  },
  [111070600] = {
    id = 111070600,
    name = "觉得可惜？"
  },
  [111070601] = {
    id = 111070601,
    name = "不，一点也不。"
  },
  [111070602] = {
    id = 111070602,
    name = "这些匪徒死不悔改，让他们得到惩罚，才能使那些怀抱善意的探险者更相信这个世界吧。可是为什么......深渊已经很残酷了，为什么还会有人去残害同类呢？"
  },
  [111070603] = {
    id = 111070603,
    name = "都是人心中的贪欲作祟吧。欲望是吞噬人类灵魂的洪水猛兽，然而欲望从不会消失。"
  },
  [111070604] = {
    id = 111070604,
    name = "你说的没错。"
  },
  [111070605] = {
    id = 111070605,
    name = "马尔可远观了一阵，觉得索然无味。"
  },
  [111070606] = {
    id = 111070606,
    name = "不想看下去了，没什么可看的，走了。"
  },
  [111070607] = {
    id = 111070607,
    name = "马尔可走了几步，见你仍在原地，她回头叫你。"
  },
  [111070608] = {id = 111070608, name = "嗯？"},
  [111070609] = {
    id = 111070609,
    name = "我要去下一个地方侦查了。你有兴趣吗？"
  },
  [111070610] = {
    id = 111070610,
    name = "感谢邀请。不过我得去一趟公会，把这件事的前因后果告诉他们。"
  },
  [111070611] = {
    id = 111070611,
    name = "本侦探可不是随便邀请人的，你竟然都没有考虑就拒绝了。"
  },
  [111070612] = {id = 111070612, name = "......"},
  [111070613] = {
    id = 111070613,
    name = "真是可惜。你是一个很优秀的助手，下一次的案件侦破没有你真是遗憾。"
  },
  [111070614] = {
    id = 111070614,
    name = "你也是一个优秀的侦探，我很荣幸这一次能成为你的助手。"
  },
  [111070615] = {
    id = 111070615,
    name = "马尔可盯着你真诚的脸确认了好一会儿，露出了难得的微笑。"
  },
  [111070616] = {
    id = 111070616,
    name = "再见了。"
  },
  [111070617] = {
    id = 111070617,
    name = "期待和你的下次合作。"
  },
  [111070618] = {
    id = 111070618,
    name = "她转身潇洒地离开，风吹得披风飒飒作响，身后的河对岸火光飞扬，血色漫天。"
  },
  [111160001] = {
    id = 111160001,
    name = "那边有什么声音？"
  },
  [111160002] = {
    id = 111160002,
    name = "好像......是有人，不，是和魔物在战斗。"
  },
  [111160003] = {
    id = 111160003,
    name = "魔物的声音越来越近，与此同时你听到了重物被打落的声音。"
  },
  [111160004] = {
    id = 111160004,
    name = "怎么又......又过来了！别过来，别过来！！"
  },
  [111160005] = {
    id = 111160005,
    name = "救......救命啊！"
  },
  [111160006] = {
    id = 111160006,
    name = "庞大的阴影逐渐笼罩了那人，他绝望地闭上了眼。"
  },
  [111160007] = {id = 111160007, name = "躲开！"},
  [111160008] = {
    id = 111160008,
    name = "魔物被你轻松斩杀，地上的人背着空空的行囊，惊魂甫定站了起来。"
  },
  [111160009] = {
    id = 111160009,
    name = "得......得救了吗？"
  },
  [111160010] = {
    id = 111160010,
    name = "你没事吧？"
  },
  [111160011] = {
    id = 111160011,
    name = "呼，你真厉害啊，这么大一只魔物，竟然就这样杀掉了！"
  },
  [111160012] = {
    id = 111160012,
    name = "（这只是初级的魔物啊......）"
  },
  [111160013] = {
    id = 111160013,
    name = "你一个人，还是早点回圈落吧。"
  },
  [111160014] = {
    id = 111160014,
    name = "唉，这次亏大了。"
  },
  [111160015] = {id = 111160015, name = "什么？"},
  [111160016] = {
    id = 111160016,
    name = "我是个商人，最近物资吃紧，我的生意也不好做，以为到据点能赚一笔回圈落，没想到竟然在据点里面被袭击了！"
  },
  [111160017] = {
    id = 111160017,
    name = "这下货物全没了，哎......"
  },
  [111160018] = {
    id = 111160018,
    name = "在据点被魔物袭击吗？的确，据点没有圈落那样的防卫——"
  },
  [111160019] = {
    id = 111160019,
    name = "不，我是被人袭击的！那个该死的家伙，竟然打劫我！可惜我没看清那家伙的样子，就让他跑掉了！"
  },
  [111160020] = {
    id = 111160020,
    name = "人？这样啊......不过以你的战斗力，就算看清了，也没有办法对付劫匪。"
  },
  [111160021] = {
    id = 111160021,
    name = "唉，你说的没错。你也是要去前面的据点吗？"
  },
  [111160022] = {
    id = 111160022,
    name = "看情况吧。"
  },
  [111160023] = {
    id = 111160023,
    name = "真羡慕啊，你这样有本事的探险者，想去哪里都可以。看在你救过我的份上，好心提醒你也别去那个据点了，我听说可不止我一个人被抢呢。"
  },
  [111160024] = {
    id = 111160024,
    name = "你不想找回你的货物吗？"
  },
  [111160025] = {
    id = 111160025,
    name = "丢了就丢了，比赔上性命要好。"
  },
  [111160026] = {
    id = 111160026,
    name = "你倒是想得开。"
  },
  [111160027] = {
    id = 111160027,
    name = "想不开也不能改变什么。总之谢谢了，救命恩人！趁天还没黑，我得快点赶回圈落了。"
  },
  [111160028] = {
    id = 111160028,
    name = "他加快脚步离开了。"
  },
  [111160029] = {
    id = 111160029,
    name = "唔，有劫匪存在的据点吗......"
  },
  [111160030] = {
    id = 111160030,
    name = "前方有女孩的哭声。"
  },
  [111160031] = {
    id = 111160031,
    name = "小孩子？难道又是魔物！"
  },
  [111160032] = {
    id = 111160032,
    name = "你毫不犹豫地寻声跑去，一名小女孩坐在地上惊恐大哭，在她面前站着一个浑身是血的女人正缓缓抬起了手......"
  },
  [111160033] = {
    id = 111160033,
    name = "住手！！"
  },
  [111160034] = {id = 111160034, name = "！！！"},
  [111160035] = {
    id = 111160035,
    name = "女人的武技不低，她敏捷地躲过了你的攻击，并且出手快速急迫，在战斗的间隙，你看清了她的脸。"
  },
  [111160036] = {
    id = 111160036,
    name = "鲜血顺着额头留下，割裂了她的面容，只有一双眼睛凌厉又冷漠。"
  },
  [111160037] = {
    id = 111160037,
    name = "呜......呜......哇！"
  },
  [111160038] = {
    id = 111160038,
    name = "她出其不意地朝小女孩方向转去，你以为她要伤害小女孩，立刻出手阻拦，她却趁这个空隙立刻逃去了另一个方向，没等你追上已经失去了踪影。"
  },
  [111160039] = {
    id = 111160039,
    name = "地上的小女孩仍旧哭哭啼啼的，你叹了口气，走到她身边蹲下。"
  },
  [111160040] = {
    id = 111160040,
    name = "小朋友，你没受伤吧？"
  },
  [111160041] = {
    id = 111160041,
    name = "呜呜，没有......我和大家玩捉迷藏......呜呜......"
  },
  [111160042] = {
    id = 111160042,
    name = "捉迷藏吗......没事了，刚才那个坏人已经走了。"
  },
  [111160043] = {
    id = 111160043,
    name = "呜呜......大姐姐不是坏人啊。"
  },
  [111160044] = {
    id = 111160044,
    name = "什么？那你为什么哭......"
  },
  [111160045] = {
    id = 111160045,
    name = "因为我碰见魔物了，大姐姐杀掉了魔物。可是，大姐姐的样子看起来好吓人，呜呜......"
  },
  [111160046] = {
    id = 111160046,
    name = "原来是她救了你啊。"
  },
  [111160047] = {
    id = 111160047,
    name = "呜呜，我再也不敢出据点了，外面好可怕......"
  },
  [111160048] = {
    id = 111160048,
    name = "我送你回去吧。"
  },
  [111160049] = {
    id = 111160049,
    name = "嗯，谢谢大哥哥。"
  },
  [111160050] = {
    id = 111160050,
    name = "你带着小女孩回到据点，两三个小朋友围了上来，叽叽喳喳把小女孩拉走了。"
  },
  [111160051] = {
    id = 111160051,
    name = "大哥哥，再见！"
  },
  [111160052] = {
    id = 111160052,
    name = "小女孩和你挥了挥手，和玩伴们离开了。"
  },
  [111160053] = {
    id = 111160053,
    name = "捉迷藏啊，真是怀念呢。既然已经来了据点，就顺道休息一晚吧......啊，那边有个餐馆。"
  },
  [111160054] = {
    id = 111160054,
    name = "叮铃——"
  },
  [111160055] = {
    id = 111160055,
    name = "你打开餐馆的门，里面人声鼎沸，对于你的到来没有人在意。你找了个角落的位置，要了一些食物，慢条斯理地吃着，听人们闲聊。"
  },
  [111160056] = {
    id = 111160056,
    name = "依我看，那个小贼本事肯定不高，不然怎么只敢在夜里干偷鸡摸狗的勾当！"
  },
  [111160057] = {
    id = 111160057,
    name = "不一定，至少有一样本事一定不错。"
  },
  [111160058] = {id = 111160058, name = "什么？"},
  [111160059] = {
    id = 111160059,
    name = "逃跑的本事啊！这么多人被抢了，到现在居然还没人抓到他。"
  },
  [111160060] = {
    id = 111160060,
    name = "一个小贼怎么敢在据点里抢劫一次又一次？那些家伙根本不止一个人吧。"
  },
  [111160061] = {
    id = 111160061,
    name = "你看到还有其他的人？"
  },
  [111160062] = {
    id = 111160062,
    name = "我猜的啊~啧，这据点太不安全了，我得先走了。"
  },
  [111160063] = {
    id = 111160063,
    name = "哼，我们一定要抓到那个小贼！"
  },
  [111160064] = {
    id = 111160064,
    name = "餐馆里的人来来往往，都在讨论近来劫匪猖獗的事。你听来听去，也没有更多相关的消息，只是心下多了一份警惕。"
  },
  [111160065] = {
    id = 111160065,
    name = "天快黑了，今晚就在这里休息吧。"
  },
  [111160066] = {id = 111160066, name = "......"},
  [111160067] = {
    id = 111160067,
    name = "入夜，旅店客房。你正在浅眠，屋外出现了几声响动。"
  },
  [111160068] = {id = 111160068, name = "......"},
  [111160069] = {
    id = 111160069,
    name = "嘭！一声闷响。接着，一连串细细的脚步声从附近路过。"
  },
  [111160070] = {
    id = 111160070,
    name = "别走，站住！"
  },
  [111160071] = {
    id = 111160071,
    name = "女声清越急促，你听见她匆匆追了出去。附近的开门声此起彼伏，有骂吵人安眠的，也有以为魔物来袭的。"
  },
  [111160072] = {
    id = 111160072,
    name = "你打开了门，看到了远处的一道白影。白影在茫茫黑夜中忽隐忽现，越来越远。"
  },
  [111160073] = {
    id = 111160073,
    name = "......反正也睡不着了，去看看吧。"
  },
  [111160074] = {
    id = 111160074,
    name = "竟然还有帮手吗？无论你们有多少人，今天都必须留下！"
  },
  [111160075] = {
    id = 111160075,
    name = "一道弧光凭空而起，女人手持法杖，和两个黑衣人激烈地战斗着。"
  },
  [111160076] = {id = 111160076, name = "可恶！"},
  [111160077] = {
    id = 111160077,
    name = "两人一边躲避她的魔法，一边使用短剑攻击。那短剑漆黑，藏在黑袍子里，很难被人察觉。"
  },
  [111160078] = {id = 111160078, name = "唔......"},
  [111160079] = {
    id = 111160079,
    name = "她受伤了？"
  },
  [111160080] = {
    id = 111160080,
    name = "你悄悄靠近他们，找了一块大石头作为掩体。那些黑衣人把脸蒙得很严实，什么也看不清。"
  },
  [111160081] = {
    id = 111160081,
    name = "（这些人鬼鬼祟祟，不敢露出真面目......会不会就是那些劫匪？）"
  },
  [111160082] = {
    id = 111160082,
    name = "去死吧！"
  },
  [111160083] = {
    id = 111160083,
    name = "匕首刺向了女人的面门，你拾起地上的石子扔了出去！"
  },
  [111160084] = {id = 111160084, name = "铛！"},
  [111160085] = {
    id = 111160085,
    name = "石子击中黑衣人的手腕，短剑落在地上。"
  },
  [111160086] = {id = 111160086, name = "谁？！"},
  [111160087] = {id = 111160087, name = "？！"},
  [111160088] = {
    id = 111160088,
    name = "你从石头后走了出来，女人侧过身看了你一眼，朦胧的月光下，她明丽的脸上染了一层清辉。"
  },
  [111160089] = {
    id = 111160089,
    name = "她眼神警惕，直到你将武器对准了黑衣人，她才露出了讶异的神色。"
  },
  [111160090] = {
    id = 111160090,
    name = "哼，多管闲事！"
  },
  [111160091] = {
    id = 111160091,
    name = "以多欺少很不公平呐。我加入就正好啊~"
  },
  [111160092] = {id = 111160092, name = "......"},
  [111160093] = {
    id = 111160093,
    name = "两个黑衣人同时冲向了女人，你即刻加入战局，一剑划去，挡住了两人的攻击。黑衣人被震退好几步，他不可思议看着自己被划烂的衣服，停下了攻击。"
  },
  [111160094] = {
    id = 111160094,
    name = "算你运气！"
  },
  [111160095] = {
    id = 111160095,
    name = "黑衣人恶狠狠地瞪了你们一眼。他和同伴交换了个眼神，快速地消失在黑夜里。"
  },
  [111160096] = {
    id = 111160096,
    name = "别走！呃......"
  },
  [111160097] = {
    id = 111160097,
    name = "你追了两步，完全看不到黑衣人的踪迹。"
  },
  [111160098] = {
    id = 111160098,
    name = "竟然消失了......"
  },
  [111160099] = {id = 111160099, name = "......"},
  [111160100] = {
    id = 111160100,
    name = "他们是什么人？"
  },
  [111160101] = {
    id = 111160101,
    name = "不知道。你是？对了，我叫薇。"
  },
  [111160102] = {
    id = 111160102,
    name = "薇朝你疏离地笑了笑。她白皙的左肩被长发掩盖，一道暗红的血顺着发丝流向手臂，但她的表情没有任何变化，看来并不想人注意到她的伤痕。"
  },
  [111160103] = {
    id = 111160103,
    name = "......刚才谢谢你，我先回去了。"
  },
  [111160104] = {
    id = 111160104,
    name = "（是不想让我察觉到吗？可是如果不治疗的话.......）"
  },
  [111160105] = {
    id = 111160105,
    name = "你受伤了，尽快治疗吧。"
  },
  [111160106] = {id = 111160106, name = "！！！"},
  [111160107] = {
    id = 111160107,
    name = "那柄短剑刺向你的时候，我看见了。还是尽快治疗吧，夜里的浊炁很重，如果不尽快治疗，伤口很容易被侵蚀。"
  },
  [111160108] = {
    id = 111160108,
    name = "这点小伤，我......"
  },
  [111160109] = {
    id = 111160109,
    name = "我可以帮忙。"
  },
  [111160110] = {
    id = 111160110,
    name = "......我......好吧。"
  },
  [111160111] = {
    id = 111160111,
    name = "薇局促地拿出一瓶药粉递给你，她轻轻拨开了长发，露出肩臂的血痕，眼光低垂。"
  },
  [111160112] = {
    id = 111160112,
    name = "......那就拜托你了。"
  },
  [111160113] = {
    id = 111160113,
    name = "还好，伤口并不是很深，这些药粉足够了。"
  },
  [111160114] = {id = 111160114, name = "嗯。"},
  [111160115] = {
    id = 111160115,
    name = "伤口很快处理好了，薇再次感谢了你。"
  },
  [111160116] = {
    id = 111160116,
    name = "举手之劳。"
  },
  [111160117] = {
    id = 111160117,
    name = "你们向据点走去。"
  },
  [111160118] = {
    id = 111160118,
    name = "我找上那个黑衣人，是因为海伦。"
  },
  [111160119] = {id = 111160119, name = "海伦？"},
  [111160120] = {
    id = 111160120,
    name = "海伦是我的朋友。她......遇到了一些事，和那些黑衣人有关。没想到黑衣人竟然不止一个人。"
  },
  [111160121] = {
    id = 111160121,
    name = "那些黑衣人手段很多，不被人注意的短剑，诡异的逃走方式，很难让人察觉到他们的真实意图，这些特征，都和传言中的劫匪高度重合。"
  },
  [111160122] = {
    id = 111160122,
    name = "薇小姐，你的朋友，是被抢劫了吗？"
  },
  [111160123] = {
    id = 111160123,
    name = "不，是其他的事。"
  },
  [111160124] = {
    id = 111160124,
    name = "这样啊......下次和他们交手的话，还是小心一点比较好。"
  },
  [111160125] = {
    id = 111160125,
    name = "我知道了，前面就是据点，我们——"
  },
  [111160126] = {
    id = 111160126,
    name = "那是什么！！！"
  },
  [111160127] = {
    id = 111160127,
    name = "轰隆隆的沉闷声传来，地面震动，数十只魔物冲向了据点！"
  },
  [111160128] = {
    id = 111160128,
    name = "是魔物袭击！"
  },
  [111160129] = {
    id = 111160129,
    name = "薇毫无犹豫，快步赶了过去。"
  },
  [111160130] = {
    id = 111160130,
    name = "吼——！！"
  },
  [111160131] = {
    id = 111160131,
    name = "赶上了！"
  },
  [111160132] = {
    id = 111160132,
    name = "受到了魔物影响，附近的浊炁不断翻滚，探险者们大声呼喊，纷纷冲出据点和魔物开始了战斗。站在最前方的是薇，她高举法杖，迅捷优雅，魔法的能量鼓动，把宽大的衣摆吹得猎猎飞舞。"
  },
  [111160133] = {id = 111160133, name = "吼！！"},
  [111160134] = {
    id = 111160134,
    name = "该死的魔物，这边还有！"
  },
  [111160135] = {
    id = 111160135,
    name = "另一边又冲上来了一群魔物，薇击杀了最近的一波，正打算赶去支援。"
  },
  [111160136] = {
    id = 111160136,
    name = "薇小姐！"
  },
  [111160137] = {
    id = 111160137,
    name = "唔......那边还有魔物！"
  },
  [111160138] = {
    id = 111160138,
    name = "你的伤口......"
  },
  [111160139] = {
    id = 111160139,
    name = "刚止血的伤口又开始流血，顺着指尖落在地上。薇的目光很坚定，没有退却的意思。"
  },
  [111160140] = {
    id = 111160140,
    name = "交给我吧，都是一些低等魔物。"
  },
  [111160141] = {
    id = 111160141,
    name = "据点里还有很多没有战斗力的人，不能让魔物闯进去！"
  },
  [111160142] = {id = 111160142, name = "好！"},
  [111160143] = {
    id = 111160143,
    name = "最后一只魔物倒下，你甩了甩剑上的血迹。"
  },
  [111160144] = {
    id = 111160144,
    name = "呼，全部解决了。"
  },
  [111160145] = {
    id = 111160145,
    name = "听动静，这一次的袭击是暂时结束了。"
  },
  [111160146] = {
    id = 111160146,
    name = "发财了！又是一笔值钱的材料啊！"
  },
  [111160147] = {
    id = 111160147,
    name = "探险者们开始分捡魔物的材料，你重新帮薇处理好了伤口。"
  },
  [111160148] = {
    id = 111160148,
    name = "你不需要吗？那些材料。"
  },
  [111160149] = {
    id = 111160149,
    name = "不用了，我要离开一趟。"
  },
  [111160150] = {
    id = 111160150,
    name = "你去哪儿？夜晚魔物猖獗，外面实在太危险了。"
  },
  [111160151] = {
    id = 111160151,
    name = "这些魔物总是在夜里突袭，我担心海伦。"
  },
  [111160152] = {
    id = 111160152,
    name = "你的朋友不在据点里吗？"
  },
  [111160153] = {
    id = 111160153,
    name = "没有。她......她出了一点状况，没住在据点。既然据点这边的魔物清理完毕了，我想去她那里看一看。"
  },
  [111160154] = {
    id = 111160154,
    name = "那我和你一起吧。"
  },
  [111160155] = {
    id = 111160155,
    name = "这个......"
  },
  [111160156] = {
    id = 111160156,
    name = "如果再碰到黑衣人或者魔物，我们相互也好有个照应啊。"
  },
  [111160157] = {
    id = 111160157,
    name = "麻烦你了。"
  },
  [111160158] = {
    id = 111160158,
    name = "薇朝你露出了柔和的笑容，只是这笑容里多了一份担忧。"
  },
  [111160159] = {
    id = 111160159,
    name = "（是在担心她的朋友吗？）"
  },
  [111160160] = {
    id = 111160160,
    name = "这里短时间内应该不会有魔物再来了。我们走吧。"
  },
  [111160161] = {id = 111160161, name = "到了。"},
  [111160162] = {
    id = 111160162,
    name = "这里很隐蔽，小木屋临时搭建在树木和乱石后面，不特意寻找的话，很容易忽视。"
  },
  [111160163] = {
    id = 111160163,
    name = "那里就是海伦住的地方，魔物应该没有来过，不过......门怎么是开着的？"
  },
  [111160164] = {
    id = 111160164,
    name = "薇脸色一变，加快了脚步。"
  },
  [111160165] = {
    id = 111160165,
    name = "海伦......海伦！！！"
  },
  [111160166] = {
    id = 111160166,
    name = "没有人应答。薇率先跑进了小木屋。"
  },
  [111160167] = {
    id = 111160167,
    name = "怎么回事？海伦？海伦！"
  },
  [111160168] = {
    id = 111160168,
    name = "（发生了什么？）"
  },
  [111160169] = {
    id = 111160169,
    name = "你跟着薇进入了小木屋，里面到处是细碎的木屑。房门、窗框、破裂的木床摇摇欲坠，石桌上还有食物残渣......简直像被人洗劫过一样。"
  },
  [111160170] = {
    id = 111160170,
    name = "怎么了，薇小姐？"
  },
  [111160171] = {
    id = 111160171,
    name = "海伦......海伦不见了。"
  },
  [111160172] = {
    id = 111160172,
    name = "这是怎么回事，这间木屋......"
  },
  [111160173] = {
    id = 111160173,
    name = "这间小木屋是我无意中发现的，那件事发生后，我就把海伦一直安顿在这里。"
  },
  [111160174] = {
    id = 111160174,
    name = "那件事？"
  },
  [111160175] = {
    id = 111160175,
    name = "是关于海伦的事，其实她......她被浊炁侵蚀了。"
  },
  [111160176] = {
    id = 111160176,
    name = "什么？！"
  },
  [111160177] = {
    id = 111160177,
    name = "薇脸上露出了歉意。"
  },
  [111160178] = {
    id = 111160178,
    name = "抱歉......那......"
  },
  [111160179] = {
    id = 111160179,
    name = "等等。只要治疗及时，是可以抑制浊炁侵蚀的吧？她是怎么被侵蚀的？"
  },
  [111160180] = {
    id = 111160180,
    name = "......海伦是被人坑害的，那些黑衣人，就是害海伦被侵蚀的罪魁祸首。海伦和他们接触过，并且得到了一件宝物。"
  },
  [111160181] = {id = 111160181, name = "宝物？"},
  [111160182] = {
    id = 111160182,
    name = "从那以后，我发现她有了被侵蚀的迹象。我追问了很多次，她才痛苦地告诉了我这件事。"
  },
  [111160183] = {
    id = 111160183,
    name = "我只能让她远离人群，暂时把她安置到了这里。"
  },
  [111160184] = {
    id = 111160184,
    name = "一旦被浊炁侵蚀，最后都变成堕落者......"
  },
  [111160185] = {id = 111160185, name = "不。"},
  [111160186] = {
    id = 111160186,
    name = "我相信海伦，至少她现在还能保持清醒！她每天会自己摘莹草来抑制侵蚀的速度，她还说，要和我一起找到罪魁祸首，找到治疗她身体的办法！"
  },
  [111160187] = {
    id = 111160187,
    name = "啊，对......对不起......我太激动了。"
  },
  [111160188] = {
    id = 111160188,
    name = "没关系，我能理解。现在海伦不见了，你知道她会去哪儿吗？"
  },
  [111160189] = {
    id = 111160189,
    name = "她不会轻易出门的，也不会让房间变成这样糟糕，就算看到人，她也会躲得远远的。她知道，自己的身份绝对不能被人发现。"
  },
  [111160190] = {
    id = 111160190,
    name = "心念一闪，你觉得有什么被忽略了......"
  },
  [111160191] = {
    id = 111160191,
    name = "我怀疑是那群黑衣人，这里都是人为破坏的痕迹，你认为呢？"
  },
  [111160192] = {
    id = 111160192,
    name = "（到底是什么被忽略了......）"
  },
  [111160193] = {
    id = 111160193,
    name = "你陷入了思考，连薇叫了你好几声都没听见。"
  },
  [111160194] = {id = 111160194, name = "什么？"},
  [111160195] = {
    id = 111160195,
    name = "薇神色黯然，目光又垂了下去。"
  },
  [111160196] = {
    id = 111160196,
    name = "你还是不能接受吧......自己好心帮助的人竟然和快要成为堕落者的人为伍，这样的心情，我能理解的......"
  },
  [111160197] = {
    id = 111160197,
    name = "向你隐瞒了这件事，我很抱歉。是我太自私了，没有告诉你真相。"
  },
  [111160198] = {id = 111160198, name = "诶？"},
  [111160199] = {
    id = 111160199,
    name = "这种事，想隐瞒是人之常情吧，换做是我，也不可能相信第一次就认识的人。你能这样坦荡地告诉我，我很高兴。"
  },
  [111160200] = {
    id = 111160200,
    name = "高兴......？因为我的感觉告诉我，你是能让我相信的人。"
  },
  [111160201] = {
    id = 111160201,
    name = "哈哈。是像海伦一样被相信着吗？"
  },
  [111160202] = {id = 111160202, name = "嗯。"},
  [111160203] = {
    id = 111160203,
    name = "如果海伦知道你这样坚持，也一定有信心对抗浊炁的侵蚀的。"
  },
  [111160204] = {
    id = 111160204,
    name = "薇浅浅地笑了，但眉宇间依旧浮现着担忧的神色。"
  },
  [111160205] = {
    id = 111160205,
    name = "不用太担心，也许海伦怕被发现，自己离开了呢。这里看起来很糟糕，或许只是黑衣人找不到海伦，生气地破坏了这里，我们再找一找她有没有留下什么信息吧。"
  },
  [111160206] = {
    id = 111160206,
    name = "薇点点头，四处查探了一番。"
  },
  [111160207] = {
    id = 111160207,
    name = "啊，你看！"
  },
  [111160208] = {
    id = 111160208,
    name = "这些是......莹草？"
  },
  [111160209] = {
    id = 111160209,
    name = "没错。海伦平时会用这些莹草抑制体内的浊炁，而这里的莹草......"
  },
  [111160210] = {
    id = 111160210,
    name = "都枯萎了。枯萎的莹草不再具有抑制浊炁的能力，难道她是去摘莹草了？然后有人闯进屋子，只是巧合而已。"
  },
  [111160211] = {
    id = 111160211,
    name = "希望如此。如今也没有别的线索，只能去莹草生长的地方看一看了。"
  },
  [111160212] = {
    id = 111160212,
    name = "附近的那座山吗？怪石嶙峋，很险峻的地方啊~我还是和你一起去吧。"
  },
  [111160213] = {
    id = 111160213,
    name = "你也相信海伦吗？"
  },
  [111160214] = {id = 111160214, name = "不。"},
  [111160215] = {id = 111160215, name = "你......"},
  [111160216] = {
    id = 111160216,
    name = "如果海伦真的变成堕落者了，我会毫不犹豫下手的。"
  },
  [111160217] = {id = 111160217, name = "......"},
  [111160218] = {
    id = 111160218,
    name = "当然，如果她像你说的那样，我也不会见死不救。"
  },
  [111160219] = {
    id = 111160219,
    name = "......我知道了。"
  },
  [111160220] = {
    id = 111160220,
    name = "薇和你一起离开了小木屋。"
  },
  [111160221] = {
    id = 111160221,
    name = "海伦告诉过我，这座山的魔物很稀少，暂时没有发现精英级以上的，以她的能力在这座山采集莹草是相对容易的事。"
  },
  [111160222] = {
    id = 111160222,
    name = "那么，我们可以先去莹草多的地方找找看。"
  },
  [111160223] = {
    id = 111160223,
    name = "我和海伦来过一次，这条路她比较熟悉。"
  },
  [111160224] = {
    id = 111160224,
    name = "那就由你带路——"
  },
  [111160225] = {
    id = 111160225,
    name = "啊、啊——！！救命！！"
  },
  [111160226] = {
    id = 111160226,
    name = "别跑，乖乖地去死吧！"
  },
  [111160227] = {id = 111160227, name = "！！！"},
  [111160228] = {
    id = 111160228,
    name = "有人在求救！"
  },
  [111160229] = {
    id = 111160229,
    name = "前方跌跌撞撞跑来了一个男人，身后一个黑衣人正提刀追。"
  },
  [111160230] = {
    id = 111160230,
    name = "黑衣人，又是他们！"
  },
  [111160231] = {
    id = 111160231,
    name = "先救人再......"
  },
  [111160232] = {
    id = 111160232,
    name = "话没说完，薇已经一道魔法击中了黑衣人。"
  },
  [111160233] = {
    id = 111160233,
    name = "救我，救我！"
  },
  [111160234] = {id = 111160234, name = "唔！"},
  [111160235] = {
    id = 111160235,
    name = "黑衣人猝不及防被袭击，他看了你们三人一眼，转身就逃。"
  },
  [111160236] = {
    id = 111160236,
    name = "又想跑！"
  },
  [111160237] = {
    id = 111160237,
    name = "你冲进山林追过去，薇也想跟上，却被商人一把拉住！"
  },
  [111160238] = {
    id = 111160238,
    name = "呼，别追了！他们有一伙人！"
  },
  [111160239] = {
    id = 111160239,
    name = "怎么回事？"
  },
  [111160240] = {
    id = 111160240,
    name = "这些黑衣人是人口贩子！他们抓了一个女的，被我看到了，要杀我灭口！还好我跑得快，又遇到了你们......"
  },
  [111160241] = {
    id = 111160241,
    name = "你说什么？！他们抓的，难道是海伦？"
  },
  [111160242] = {
    id = 111160242,
    name = "海伦是谁？"
  },
  [111160243] = {
    id = 111160243,
    name = "商人见薇没有回答，不停地拍着胸口。"
  },
  [111160244] = {
    id = 111160244,
    name = "妈的，这比遇到魔物凶险太多了！啊，你的同伴回来了。"
  },
  [111160245] = {
    id = 111160245,
    name = "见你出现，薇快步走向了你。"
  },
  [111160246] = {
    id = 111160246,
    name = "怎么样？你没事吧？"
  },
  [111160247] = {
    id = 111160247,
    name = "你摇了摇头，黑衣人脚步轻快，又有树林作为遮挡物，你追了好一阵，再次失去黑衣人的踪影，只能折返。"
  },
  [111160248] = {
    id = 111160248,
    name = "没抓到人，在河谷的方向消失了。我担心他有同伙，就先回来了。"
  },
  [111160249] = {
    id = 111160249,
    name = "他说那些黑衣人抓了个女孩，会不会是海伦？"
  },
  [111160250] = {
    id = 111160250,
    name = "麻烦你说清楚来龙去脉。"
  },
  [111160251] = {
    id = 111160251,
    name = "我......我也是无意中看到的。我采完莹草的时候，天已经黑了，我打算在河边洗个脸，就遇见几个黑衣人在河对面，绑着一个女孩在赶路......"
  },
  [111160252] = {
    id = 111160252,
    name = "我本来没打算管这个闲事的啊！谁知道他们居然发现了我，还朝我追了过来！然后、然后就碰到了你们。"
  },
  [111160253] = {
    id = 111160253,
    name = "那个女孩被绑去哪儿了？！"
  },
  [111160254] = {
    id = 111160254,
    name = "河谷，他们往河谷那边去了。"
  },
  [111160255] = {
    id = 111160255,
    name = "我追到河谷的时候，人也消失了。先跟上去看看！"
  },
  [111160256] = {
    id = 111160256,
    name = "好。时间紧迫，我们快点出发！"
  },
  [111160257] = {
    id = 111160257,
    name = "喂喂，你们救人救一半，不管我了？我怎么回圈落啊？！"
  },
  [111160258] = {
    id = 111160258,
    name = "......抱歉，我有更重要的事。"
  },
  [111160259] = {
    id = 111160259,
    name = "黑衣人离开了，你现在是安全的，自行返回即可。"
  },
  [111160260] = {
    id = 111160260,
    name = "你冷冷地看了商人一眼，跟上了薇。商人在你的注视下打了个寒噤，快速地离开了。"
  },
  [111160261] = {
    id = 111160261,
    name = "那个人说的河谷，就是这里吗？"
  },
  [111160262] = {
    id = 111160262,
    name = "没错，之前黑衣人就是在这附近消失的。"
  },
  [111160263] = {
    id = 111160263,
    name = "这边有足印！"
  },
  [111160264] = {
    id = 111160264,
    name = "应该是那个人逃跑时留下的。"
  },
  [111160265] = {
    id = 111160265,
    name = "我们顺着这个足印，就能找到他们吧。"
  },
  [111160266] = {
    id = 111160266,
    name = "嗯，我们小心行事，不要打草惊蛇。"
  },
  [111160267] = {id = 111160267, name = "好。"},
  [111160268] = {
    id = 111160268,
    name = "走了一会儿，你们听到前面有对话声。"
  },
  [111160269] = {id = 111160269, name = "嘘。"},
  [111160270] = {
    id = 111160270,
    name = "你朝薇比了个手势，她缓缓蹲在你旁边，轻声细语。"
  },
  [111160271] = {
    id = 111160271,
    name = "他们就在前面，海伦在吗？"
  },
  [111160272] = {
    id = 111160272,
    name = "等一等，听他们说的是什么。"
  },
  [111160273] = {
    id = 111160273,
    name = "呸！本来说杀了那小子，没想到被他跑了。"
  },
  [111160274] = {
    id = 111160274,
    name = "那家伙连武器都没有，就是个摘莹草的供货商。居然能让他跑了，你也太差劲了吧？"
  },
  [111160275] = {
    id = 111160275,
    name = "谁说我差劲了！要不是——"
  },
  [111160276] = {
    id = 111160276,
    name = "要不是什么？你被人发现了？！"
  },
  [111160277] = {
    id = 111160277,
    name = "当、当然没有！那家伙仗着熟悉这里的地势，把我绕晕了才跑掉了。"
  },
  [111160278] = {
    id = 111160278,
    name = "嗯，跑就跑了吧。那种废物也掀不起什么风浪，最重要的是不能被探险者发现了。"
  },
  [111160279] = {
    id = 111160279,
    name = "哈哈，哪有什么探险者。再说了，咱们不是正缺探险者吗？要是有探险者，正好一起抓了！"
  },
  [111160280] = {
    id = 111160280,
    name = "你就吹牛吧！这袋子都扛不起的身板，遇到厉害点的探险者还不吓得屁滚尿流......"
  },
  [111160281] = {
    id = 111160281,
    name = "两人拖起地上的一个大口袋，边说边走，声音越来越小。"
  },
  [111160282] = {
    id = 111160282,
    name = "......他们抓探险者做什么？"
  },
  [111160283] = {
    id = 111160283,
    name = "海伦......难道在那个袋子里？"
  },
  [111160284] = {
    id = 111160284,
    name = "你打算怎么救人？"
  },
  [111160285] = {
    id = 111160285,
    name = "只能麻烦你牵制住那个家伙了。"
  },
  [111160286] = {
    id = 111160286,
    name = "不是牵制，是制服，交给我吧。而且这些黑衣人一定有秘密，我有点好奇了。"
  },
  [111160287] = {
    id = 111160287,
    name = "那就拜托了。"
  },
  [111160288] = {
    id = 111160288,
    name = "你以极快的速度到达了黑衣人的身后，同时武器出鞘。"
  },
  [111160289] = {id = 111160289, name = "嗨。"},
  [111160290] = {
    id = 111160290,
    name = "是......是......你！！"
  },
  [111160291] = {
    id = 111160291,
    name = "嘭！！突袭的计划很成功。你势如破竹制服了一个黑衣人。"
  },
  [111160292] = {
    id = 111160292,
    name = "探——探——呜哇！"
  },
  [111160293] = {
    id = 111160293,
    name = "另一个黑衣人打算逃走，你一剑刺穿了他的小腿。"
  },
  [111160294] = {
    id = 111160294,
    name = "啊啊啊啊——！"
  },
  [111160295] = {
    id = 111160295,
    name = "寂静的山谷传出惨叫声。"
  },
  [111160296] = {
    id = 111160296,
    name = "不是海伦.....等等，你看这个女孩！"
  },
  [111160297] = {
    id = 111160297,
    name = "薇解开口袋，露出了一个陌生女孩的脸。她的体内散发出黑气，是被浊炁侵蚀的症状，无论怎么叫，她都没能醒过来。"
  },
  [111160298] = {
    id = 111160298,
    name = "又见面了啊。上次让你跑掉了，这一次可没那么好运了。"
  },
  [111160299] = {
    id = 111160299,
    name = "......放了我们，你们想要什么，我都给你们！我身上有好多欧泊，都给你！"
  },
  [111160300] = {
    id = 111160300,
    name = "闭嘴，你这个家伙！之前你遇到过他们？！你这个撒谎的怂货！"
  },
  [111160301] = {
    id = 111160301,
    name = "你们抓这些探险者做什么？海伦在哪里？！"
  },
  [111160302] = {
    id = 111160302,
    name = "你说的什么海伦，我不认识啊，你们是不是找错人了......"
  },
  [111160303] = {
    id = 111160303,
    name = "你一脚踩在他胸上，高高举起了拳头。"
  },
  [111160304] = {
    id = 111160304,
    name = "啊！我说，我说别打我！"
  },
  [111160305] = {
    id = 111160305,
    name = "不能说！你要说出去，老大不会放过我们！"
  },
  [111160306] = {
    id = 111160306,
    name = "咚！！薇一杖敲晕了他的头，俯视着这个怯懦的黑衣人。"
  },
  [111160307] = {
    id = 111160307,
    name = "他听不见了，你可以说了。"
  },
  [111160308] = {id = 111160308, name = "！！！"},
  [111160309] = {
    id = 111160309,
    name = "海伦在哪里？"
  },
  [111160310] = {
    id = 111160310,
    name = "我......我真的不认识海伦啊！"
  },
  [111160311] = {
    id = 111160311,
    name = "那就把你知道的都说出来。"
  },
  [111160312] = {
    id = 111160312,
    name = "你们抓探险者做什么？"
  },
  [111160313] = {
    id = 111160313,
    name = "这是老大让我们干的事，他说组织上要做试验品的。"
  },
  [111160314] = {
    id = 111160314,
    name = "试验品？"
  },
  [111160315] = {
    id = 111160315,
    name = "就......就是把老大给的宝物，想办法送到优秀的探险者手上，再、再把那些探险者抓起来。"
  },
  [111160316] = {
    id = 111160316,
    name = "你说的那个宝物......"
  },
  [111160317] = {
    id = 111160317,
    name = "就是能让探险者变成堕落者的东西——"
  },
  [111160318] = {id = 111160318, name = "咔嚓！"},
  [111160319] = {
    id = 111160319,
    name = "地面因魔力的涌动裂开了一道缝隙，薇正抿着嘴唇，紧握法杖，黑衣人一抖，慌忙求饶。"
  },
  [111160320] = {
    id = 111160320,
    name = "我知道的都告诉你们了！！你们放了我吧！"
  },
  [111160321] = {
    id = 111160321,
    name = "你们把人抓到哪儿去了？"
  },
  [111160322] = {
    id = 111160322,
    name = "呜......这个真的不能说......"
  },
  [111160323] = {
    id = 111160323,
    name = "那留着你们也没什么用了。"
  },
  [111160324] = {
    id = 111160324,
    name = "你伸手掐住他的脖子，手指逐渐用力。"
  },
  [111160325] = {
    id = 111160325,
    name = "在废墟！"
  },
  [111160326] = {
    id = 111160326,
    name = "黑衣人慌张地大叫，一股脑儿全说了出来。"
  },
  [111160327] = {
    id = 111160327,
    name = "出了这个河谷不远的地方，有一个废弃的据点！我们的人都在里面！绑来的那些探险者也都在里面！"
  },
  [111160328] = {
    id = 111160328,
    name = "嘭！！你松开手，一拳砸晕了这个黑衣人。"
  },
  [111160329] = {
    id = 111160329,
    name = "想不到，竟然有人特意去制造堕落者。这些人想干什么？"
  },
  [111160330] = {
    id = 111160330,
    name = "无论他们想干什么，我都不会让他们得逞。"
  },
  [111160331] = {
    id = 111160331,
    name = "那他们怎么办？"
  },
  [111160332] = {
    id = 111160332,
    name = "还有那个被抓的女孩——消失了？！"
  },
  [111160333] = {
    id = 111160333,
    name = "原本躺在口袋里昏睡的陌生女孩已经不见了。"
  },
  [111160334] = {
    id = 111160334,
    name = "看来是趁我们在审问的时候偷偷溜走了。"
  },
  [111160335] = {
    id = 111160335,
    name = "这两个人......暂时就别管了。"
  },
  [111160336] = {
    id = 111160336,
    name = "那我们快去救海伦小姐吧。"
  },
  [111160337] = {
    id = 111160337,
    name = "前面就是那个人说的废墟吧？房子被破坏得很严重。"
  },
  [111160338] = {
    id = 111160338,
    name = "是啊。大部分据点只有简单的防护，用来收容临时的探险者，不像圈落那样有防御的城墙和守卫。所以只要遭到魔物袭击，就很容易被摧毁。"
  },
  [111160339] = {
    id = 111160339,
    name = "据点一旦被废弃，再重建也会很难。没想到，有人会在这种地方把探险者变成堕落者，真是不可原谅。"
  },
  [111160340] = {
    id = 111160340,
    name = "把人变成堕落者，对这些人有什么好处？他们到底在进行什么危险的试验？"
  },
  [111160341] = {
    id = 111160341,
    name = "......如果他们的目的是这个，那海伦一定很危险！"
  },
  [111160342] = {
    id = 111160342,
    name = "薇深吸一口气，走向废弃的据点。废墟里一片残垣断壁，杂草丛生，连虫豸声都没有，安静得让你的神经一下崩紧了。"
  },
  [111160343] = {
    id = 111160343,
    name = "他们怎么还没把货带回来？你去看看！"
  },
  [111160344] = {
    id = 111160344,
    name = "（小声）有声音！"
  },
  [111160345] = {
    id = 111160345,
    name = "一间破旧的房屋里传出了说话声，接着，隐隐的火光照亮了屋子。薇想走上前，被你及时拦下。"
  },
  [111160346] = {
    id = 111160346,
    name = "现在还不知道情况，先不要打草惊蛇。"
  },
  [111160347] = {
    id = 111160347,
    name = "......只有过去，才能知道海伦在不在那里。"
  },
  [111160348] = {
    id = 111160348,
    name = "再等一等......"
  },
  [111160349] = {
    id = 111160349,
    name = "房屋的门打开了，里面出来了一个还没来得及遮面的黑衣人。他把斗篷搭了下去，朝门口走来，嘴里还发出不满的抱怨。"
  },
  [111160350] = {
    id = 111160350,
    name = "天还没亮呢就把我赶出来~那两个不知道哪儿快活去了，害我受这种罪......哈欠~"
  },
  [111160351] = {id = 111160351, name = "......"},
  [111160352] = {
    id = 111160352,
    name = "黑衣人离你们越来越近，薇不安地看着你，示意你先发制人。"
  },
  [111160353] = {
    id = 111160353,
    name = "你点点头，正打算行动的时候，薇已经出手了。你赶紧跟了上去，将黑衣人的嘴捂得死死的。"
  },
  [111160354] = {
    id = 111160354,
    name = "——唔唔唔唔！"
  },
  [111160355] = {
    id = 111160355,
    name = "黑衣人睁大了眼，脚下猛然踩下了什么机关——咔嚓。"
  },
  [111160356] = {
    id = 111160356,
    name = "——轰！废墟空隙的火把一瞬间被点亮了！"
  },
  [111160357] = {id = 111160357, name = "糟了！"},
  [111160358] = {
    id = 111160358,
    name = "下一秒，从几间房屋中冲出数十个黑衣人。随后，一个看起来像头目的人走了出来。"
  },
  [111160359] = {
    id = 111160359,
    name = "按理说不会有探险者来废弃的据点，你么...是迷路了？"
  },
  [111160360] = {
    id = 111160360,
    name = "是你们抓了海伦！把海伦交出来！"
  },
  [111160361] = {
    id = 111160361,
    name = "哦，原来是到这里来找人的。看来被你们知道了些什么，不过没关系，时间快到了......正好由你们来代替吧。"
  },
  [111160362] = {
    id = 111160362,
    name = "给我抓住他们！"
  },
  [111160363] = {
    id = 111160363,
    name = "黑衣人一拥而上，将你们团团围住。薇毫不留情地打晕了手里的黑衣人，魔法光晕环绕着法杖，做好了随时攻击的准备。"
  },
  [111160364] = {
    id = 111160364,
    name = "薇小姐，要不要先走？"
  },
  [111160365] = {
    id = 111160365,
    name = "眼前人数众多，你们二人合作也许能全身而退。"
  },
  [111160366] = {
    id = 111160366,
    name = "不救出海伦，我不会离开。"
  },
  [111160367] = {
    id = 111160367,
    name = "那我就陪你大战一场！"
  },
  [111160368] = {
    id = 111160368,
    name = "放马过来吧！"
  },
  [111160369] = {
    id = 111160369,
    name = "魔法光晕猛然扩散，将眼前的黑衣人群击飞！"
  },
  [111160370] = {
    id = 111160370,
    name = "你几乎同时出手，剑刃划破了包围圈，和薇冲出重围。薇又扔出一记魔法，却被几人组成的光盾抵挡。"
  },
  [111160371] = {
    id = 111160371,
    name = "她眼神坚定，继续挥动法杖，嘴里无声地念着魔法咒语。你在她身边，击退了一个又一个靠近的黑衣人。薇结束吟唱的刹那，燃烧的火柱将那道光盾直接击碎！"
  },
  [111160372] = {id = 111160372, name = "找死！"},
  [111160373] = {
    id = 111160373,
    name = "我去抓住他！"
  },
  [111160374] = {
    id = 111160374,
    name = "交给你了。"
  },
  [111160375] = {
    id = 111160375,
    name = "黑衣人们卷土重来，和薇交战在一起。你意识到必须在很短的时间里制服那个头目！"
  },
  [111160376] = {
    id = 111160376,
    name = "不自量力！"
  },
  [111160377] = {
    id = 111160377,
    name = "你和黑衣人头目激战，一剑划破了他的手臂！"
  },
  [111160378] = {
    id = 111160378,
    name = "哼！小瞧你们了，那就尝尝这个吧！"
  },
  [111160379] = {
    id = 111160379,
    name = "轰——！"
  },
  [111160380] = {
    id = 111160380,
    name = "眼前爆发的烟雾让你瞬间失去了目标，闻到一股刺鼻的味道，你的手脚突然乏力。你立刻屏住了呼吸，但也来不及了。"
  },
  [111160381] = {
    id = 111160381,
    name = "武器突破烟雾朝你袭来，明知道危机将近，身体却无法移动。你拼尽全力，避开了要害，肩膀还是受了伤。"
  },
  [111160382] = {
    id = 111160382,
    name = "结束了！！"
  },
  [111160383] = {
    id = 111160383,
    name = "你被人击倒在地，烟雾散去的时候，十几把武器已经对准了你。"
  },
  [111160384] = {id = 111160384, name = "危险！"},
  [111160385] = {
    id = 111160385,
    name = "电光火石之间，攻守逆转。薇见你被擒，果断挥动法杖，一道速成魔法在她周围笼罩了一层薄幕，刀剑不摧。"
  },
  [111160386] = {
    id = 111160386,
    name = "哈哈哈！你这是给自己建了一个牢房吗？看你能坚持多久！"
  },
  [111160387] = {
    id = 111160387,
    name = "（她想做什么？）"
  },
  [111160388] = {
    id = 111160388,
    name = "薇又在低声吟唱，四周不断有刀剑砍上防御幕，但她的眼神镇定，手上丝毫不乱。法杖临空打出一道又一道魔法，在防御光幕碎裂的瞬间，银色的光朝你的方向射出！"
  },
  [111160389] = {id = 111160389, name = "散开！"},
  [111160390] = {
    id = 111160390,
    name = "四周的人作鸟兽散，你抬头看向薇，看到她放松下来的表情。"
  },
  [111160391] = {
    id = 111160391,
    name = "轰——！"
  },
  [111160392] = {
    id = 111160392,
    name = "光芒落在身上的时候，你感觉到了一丝抽离感。下一秒，天旋地转，你发现你被转移到了据点外的安全地。"
  },
  [111160393] = {
    id = 111160393,
    name = "这个位置黑暗又隐蔽，正好在一块断墙的后面。你感受了一下身体的状况，凝滞感依然很严重。"
  },
  [111160394] = {
    id = 111160394,
    name = "（临时转移魔法......）"
  },
  [111160395] = {
    id = 111160395,
    name = "该死的女人，竟敢戏弄我！把她抓起来！！"
  },
  [111160396] = {
    id = 111160396,
    name = "别碰我，我跟你们走。"
  },
  [111160397] = {
    id = 111160397,
    name = "哼，要不是......我现在就杀了你！带走！"
  },
  [111160398] = {
    id = 111160398,
    name = "目睹薇被人绑走，你靠在墙后深吸了一口气。"
  },
  [111160399] = {
    id = 111160399,
    name = "（看来他们暂时不会要薇和海伦的性命。现在冲出去也是送死，等身体恢复了，再想办法救她们吧。）"
  },
  [111160400] = {
    id = 111160400,
    name = "等所有人都散了之后，你悄悄离开了据点。"
  },
  [111160401] = {id = 111160401, name = "......"},
  [111160402] = {
    id = 111160402,
    name = "与此同时，废弃的据点内部，薇被推进了一间地下室。"
  },
  [111160403] = {
    id = 111160403,
    name = "你不是想见海伦吗？就让你们临死前见一见。感激我的仁慈，好好享受你最后的时光吧！"
  },
  [111160404] = {
    id = 111160404,
    name = "给我把她带到最里面的房间！"
  },
  [111160405] = {
    id = 111160405,
    name = "是，老大！"
  },
  [111160406] = {
    id = 111160406,
    name = "......嘭！"
  },
  [111160407] = {
    id = 111160407,
    name = "大门关上了，昏沉的地底是一条狭窄的通道，两边都是嵌在墙里的铁栅栏。"
  },
  [111160408] = {
    id = 111160408,
    name = "薇缓缓地走下去，铁栅栏里的人都和她一样，双手被锁链锁住，有的趴在地上无法动弹，身上散发出浊炁；有的将手伸出栅栏外不断地挣扎，发出绝望的吼叫。"
  },
  [111160409] = {
    id = 111160409,
    name = "（堕落者......）"
  },
  [111160410] = {
    id = 111160410,
    name = "走快点！"
  },
  [111160411] = {
    id = 111160411,
    name = "薇被黑衣人推攘着，越走越深。她甚至看到了好几个已经死在了栅栏里，那些成为堕落者的探险者全身已经异变腐化，临死前的表情煎熬、痛苦，却又带着解脱。"
  },
  [111160412] = {
    id = 111160412,
    name = "啧，又失败了几个，一会儿还得搬出去，真恶心。"
  },
  [111160413] = {
    id = 111160413,
    name = "两人走到了尽头，黑衣人打开了牢笼，将薇推了进去。"
  },
  [111160414] = {
    id = 111160414,
    name = "祝你好运了，美人。"
  },
  [111160415] = {
    id = 111160415,
    name = "牢笼从背后锁上，脚步匆匆而去。薇并没有理会他，而是注视着角落里那个蜷坐在地上的身影。"
  },
  [111160416] = {
    id = 111160416,
    name = "海伦......是你吗？"
  },
  [111160417] = {
    id = 111160417,
    name = "人形的黑色轮廓缓缓动了，涣散的眼睛努力地看了过来，发出了微弱嘶哑的声音。"
  },
  [111160418] = {
    id = 111160418,
    name = "......小薇？"
  },
  [111160419] = {
    id = 111160419,
    name = "薇两步走了上去，几乎要站不稳。眼前的海伦抬起了脸，属于堕落者的浊炁和纹路将血肉完全包裹，只有一双眼露着微亮的光。"
  },
  [111160420] = {
    id = 111160420,
    name = "你来了，太好了......这个鬼地方......我快待不下去了......"
  },
  [111160421] = {
    id = 111160421,
    name = "......嗯。我就是来救你出去的。"
  },
  [111160422] = {
    id = 111160422,
    name = "......小薇。"
  },
  [111160423] = {
    id = 111160423,
    name = "你还好吗？"
  },
  [111160424] = {
    id = 111160424,
    name = "不太好，有时候迷迷糊糊的。"
  },
  [111160425] = {
    id = 111160425,
    name = "这到底怎么回事？那些黑衣人怎么会找到你？他们到底有什么目的？"
  },
  [111160426] = {
    id = 111160426,
    name = "你问那么多，我都不知道回答哪一个了。唔，就从我怎么被坑了说起吧......"
  },
  [111160427] = {
    id = 111160427,
    name = "海伦的话越说越顺，她皱了皱眉，扭曲的五官慢慢摆正了位置，说的话颠三倒四的，有时候又会忘记刚刚说过的事，薇大致梳理了一下海伦表达的意思。"
  },
  [111160428] = {
    id = 111160428,
    name = "你是说，那个宝物是你买的？所以你才被浊炁侵蚀了？"
  },
  [111160429] = {
    id = 111160429,
    name = "可是真的很便宜啊！我也没打算用，本来想送去净化的，哪知道拿着就......那些家伙真是太坏了！"
  },
  [111160430] = {
    id = 111160430,
    name = "哎，你这贪小便宜的性格......"
  },
  [111160431] = {
    id = 111160431,
    name = "知道啦，便宜没好货嘛。我一定改！吃了这么大的亏，我长记性了。"
  },
  [111160432] = {
    id = 111160432,
    name = "后来呢？"
  },
  [111160433] = {
    id = 111160433,
    name = "后来我就被抓了。他们把我扔在这里就走了，第一次，我很轻松就逃出了这里。本来想找个地方躲起来，然后就被你发现了......"
  },
  [111160434] = {
    id = 111160434,
    name = "躲起来自生自灭吗？或者等到彻底变成堕落者，让我亲手杀了你？"
  },
  [111160435] = {
    id = 111160435,
    name = "小薇，我知道错了......你别生气了......"
  },
  [111160436] = {
    id = 111160436,
    name = "那你又是怎么从木屋消失的？"
  },
  [111160437] = {
    id = 111160437,
    name = "我摘莹草的时候被发现了。他们追到了木屋，把我抓了过来。一会儿说要做什么试验，一会儿又说要做什么祭品......"
  },
  [111160438] = {
    id = 111160438,
    name = "总之就是离死不远了吧。"
  },
  [111160439] = {
    id = 111160439,
    name = "海伦的双眼出现了懊悔的神色。"
  },
  [111160440] = {
    id = 111160440,
    name = "其实，我能感受到身体在不断地变化，跟那些黑衣人说的差不多吧。如果当初听你的话，我也不会......"
  },
  [111160441] = {
    id = 111160441,
    name = "呜呜，小薇......你说你是来救我的，其实你也是被抓来的吧？你这个锁链，怎么解开啊？都是我的错，都是我的错......"
  },
  [111160442] = {
    id = 111160442,
    name = "海伦又开始语无伦次，薇轻轻拍了拍她的手。"
  },
  [111160443] = {
    id = 111160443,
    name = "来得及的，我们会一起出去。"
  },
  [111160444] = {
    id = 111160444,
    name = "而且，有一个人一定会来救我们的。虽然我们萍水相逢，但我相信他。"
  },
  [111160445] = {
    id = 111160445,
    name = "伤好得差不多了。"
  },
  [111160446] = {
    id = 111160446,
    name = "你没有走太远，一直躲在废弃据点附近的山洞里。"
  },
  [111160447] = {
    id = 111160447,
    name = "得先确认薇的安危。那里戒备森严，需要想个办法偷偷进去......"
  },
  [111160448] = {
    id = 111160448,
    name = "就在你沉思的时候，远处有细微的声音。你藏在洞里，露出了笑容。"
  },
  [111160449] = {
    id = 111160449,
    name = "真巧啊~瞌睡了正好有人送枕头。"
  },
  [111160450] = {
    id = 111160450,
    name = "一名黑衣人从废墟出来，他打着哈欠，一副睡眠不足的样子，完全没有发现你的存在。"
  },
  [111160451] = {
    id = 111160451,
    name = "在他经过山洞时，你将他打晕拖进了山洞，整个过程悄无声息。"
  },
  [111160452] = {
    id = 111160452,
    name = "竟然是这个家伙，先等他醒了再问出情况吧。"
  },
  [111160453] = {
    id = 111160453,
    name = "片刻后......"
  },
  [111160454] = {
    id = 111160454,
    name = "别装了，我看到你眨眼了。"
  },
  [111160455] = {
    id = 111160455,
    name = "嘿嘿......又是你啊。侠士，我能交代的都交代了啊。"
  },
  [111160456] = {
    id = 111160456,
    name = "你才从你们的大本营出来，有最新的情报吧？"
  },
  [111160457] = {
    id = 111160457,
    name = "英雄！人不是我抓的啊！我没动过手......"
  },
  [111160458] = {
    id = 111160458,
    name = "闭嘴，不想死就好好回答我。"
  },
  [111160459] = {
    id = 111160459,
    name = "是是......"
  },
  [111160460] = {
    id = 111160460,
    name = "她被关在哪里？"
  },
  [111160461] = {
    id = 111160461,
    name = "那个废墟之前有个地下室。我们发现之后，老大就把它改建成了牢房，专门......专门关押试验品......"
  },
  [111160462] = {
    id = 111160462,
    name = "还有呢，你们之前抓的人呢？"
  },
  [111160463] = {
    id = 111160463,
    name = "你是说那个叫海伦的？她扛住了很多次浊炁的侵蚀，可以说是一个合格的祭品了。"
  },
  [111160464] = {id = 111160464, name = "祭品？"},
  [111160465] = {
    id = 111160465,
    name = "是，过几天仪式就会开始。老大说，等成功了，就能向上面交代了。"
  },
  [111160466] = {
    id = 111160466,
    name = "上面......你们是什么组织？"
  },
  [111160467] = {
    id = 111160467,
    name = "我不知道。他们会去据点抢东西，我只是组织下面负责抓人的，没杀过人啊~"
  },
  [111160468] = {
    id = 111160468,
    name = "那些劫匪果然是你们。"
  },
  [111160469] = {
    id = 111160469,
    name = "我，我没参与过抢劫！"
  },
  [111160470] = {
    id = 111160470,
    name = "我只想混口饭吃，放过我吧......呜呜......"
  },
  [111160471] = {
    id = 111160471,
    name = "......闭嘴。祭品会怎么样？"
  },
  [111160472] = {
    id = 111160472,
    name = "所有的祭品都会在仪式上被献祭。老大说这些都是秘密，不能让人知道......"
  },
  [111160473] = {
    id = 111160473,
    name = "什么时候？"
  },
  [111160474] = {
    id = 111160474,
    name = "就在三天后。地下室有一个魔法阵，他们会启动仪式。"
  },
  [111160475] = {
    id = 111160475,
    name = "黑衣人又详细说了地下室的位置。"
  },
  [111160476] = {
    id = 111160476,
    name = "构造呢，关押的地点呢？"
  },
  [111160477] = {
    id = 111160477,
    name = "你再三询问，榨干了他知道的所有情报。"
  },
  [111160478] = {
    id = 111160478,
    name = "真的没有了？好好想想。"
  },
  [111160479] = {
    id = 111160479,
    name = "侠士，能说的我都说了啊！你现在就算打死我，我也说不出来了，看在我交代了这么多的份儿上，饶我一命啊！"
  },
  [111160480] = {
    id = 111160480,
    name = "想必你也不敢回去报信吧？让我知道了这么多秘密，你的老大应该不会放过你的。"
  },
  [111160481] = {
    id = 111160481,
    name = "呃！！！绝、绝对不会！"
  },
  [111160482] = {
    id = 111160482,
    name = "见你点了点头，黑衣人欣喜若狂地爬了起来，生怕你改变了主意，他转身就跑。接着，被你一掌敲晕了。"
  },
  [111160483] = {
    id = 111160483,
    name = "真是天真，我可什么都没有答应你啊。唔，只有三天的时间......在这里也无计可施，先去据点想想办法吧。"
  },
  [111160484] = {
    id = 111160484,
    name = "你跨过晕倒在地的黑衣人，脚步忽然停下，然后顺手把黑衣人的衣服扒了下来。"
  },
  [111160485] = {
    id = 111160485,
    name = "这身衣服我就借走了。"
  },
  [111160486] = {
    id = 111160486,
    name = "你进入了据点里人群聚集的酒馆，老板热情地招呼了你。"
  },
  [111160487] = {
    id = 111160487,
    name = "这里有探险队能雇佣吗？"
  },
  [111160488] = {
    id = 111160488,
    name = "能雇佣的探险队啊，以前有的，像深渊航线那样比较知名的探险队也来过这里。不过最近是没有啦~不然劫匪又怎么会那么猖狂？"
  },
  [111160489] = {
    id = 111160489,
    name = "这位客人，你是需要什么援护的服务吗？我认识几个靠谱的探险者可以介绍给你哦~"
  },
  [111160490] = {
    id = 111160490,
    name = "......没什么。"
  },
  [111160491] = {
    id = 111160491,
    name = "走出了酒馆，你又去了好几个地方，最后靠在了一处墙角，仔细看着来来往往的人。"
  },
  [111160492] = {
    id = 111160492,
    name = "那片废墟平时很少有人去，如果召集零散的探险者，很可能会走漏风声，导致营救计划失败。"
  },
  [111160493] = {
    id = 111160493,
    name = "如果用大量的药物或者魔法阵，必须要有足够的接应和准备时间，三天远远不够....."
  },
  [111160494] = {
    id = 111160494,
    name = "如果用自己做诱饵.....不行，我没去过地下室，这些计划漏洞都太多了......"
  },
  [111160495] = {id = 111160495, name = "......"},
  [111160496] = {
    id = 111160496,
    name = "你挫败地呼出一口气，思绪一团乱麻，漫无目的地在街道上行走。"
  },
  [111160497] = {
    id = 111160497,
    name = "听说了吗?有人见到密云森林上空又有黑雾聚集了！"
  },
  [111160498] = {
    id = 111160498,
    name = "什么？黑雾！难道魔物又要来了？"
  },
  [111160499] = {
    id = 111160499,
    name = "前两天有人去森林探险的时候，发现了很多魔物往一个方向聚集，像是冲着咱们这儿来的！"
  },
  [111160500] = {
    id = 111160500,
    name = "不会吧，难道这个据点也保不住了？"
  },
  [111160501] = {
    id = 111160501,
    name = "这里这么多探险者呢，他们也应该能察觉到吧。"
  },
  [111160502] = {
    id = 111160502,
    name = "哎......之前那个据点真可惜啊，被密云森林的魔物袭击之后，现在也只剩一片废墟了。"
  },
  [111160503] = {
    id = 111160503,
    name = "那这里也不能久待了，我先收拾东西走了。"
  },
  [111160504] = {id = 111160504, name = "......"},
  [111160505] = {
    id = 111160505,
    name = "魔物......突袭......如果协助者是魔物的话......也许可以利用这一点！"
  },
  [111160506] = {
    id = 111160506,
    name = "先去密云森林那边探查一下。"
  },
  [111160507] = {
    id = 111160507,
    name = "这就是他们说的密云森林吧，空气中的浊炁越来越浓厚了。"
  },
  [111160508] = {
    id = 111160508,
    name = "入夜，你将黑衣人的衣服套在身上，走进了森林。好几次靠着这身黑衣隐蔽了身形，避免了和魔物交战的可能。"
  },
  [111160509] = {
    id = 111160509,
    name = "没想到这件衣服还挺有用的。"
  },
  [111160510] = {
    id = 111160510,
    name = "你小心翼翼地走向黑雾的中心。越往深处走，浊炁愈浓，四周的魔物也变得密集了起来，还有很多魔物在陆陆续续朝这边聚集。"
  },
  [111160511] = {
    id = 111160511,
    name = "你悄然上了一颗树，围观着那群躁动不安的魔物。"
  },
  [111160512] = {
    id = 111160512,
    name = "恐怕要不了多久，这些魔物就会行动了。一旦“头羊”诞生，那剩下的魔物就会跟随头羊，再等一等吧......"
  },
  [111160513] = {
    id = 111160513,
    name = "黑雾弥漫，一只又一只的魔物出现了，他们挨挨挤挤地在你的脚下汇聚，你抬眼望去，竟然有上百只不止。"
  },
  [111160514] = {
    id = 111160514,
    name = "再这样下去不行。这些魔物为了生存之地会互相驱逐，或许会等不到“头羊”，它们就会自相残杀了。"
  },
  [111160515] = {
    id = 111160515,
    name = "......既然如此，那就我来做“头羊”吧。"
  },
  [111160516] = {
    id = 111160516,
    name = "你伸手摸出一枚魔法弹，扔向了魔物中间！"
  },
  [111160517] = {
    id = 111160517,
    name = "轰——！"
  },
  [111160518] = {
    id = 111160518,
    name = "吼——！！"
  },
  [111160519] = {
    id = 111160519,
    name = "就是这样，都跟我来吧！"
  },
  [111160520] = {
    id = 111160520,
    name = "魔物发出了愤怒的吼叫，它们察觉到了你，疯狂地撞击你所在的树，在树快断裂的时候，你一跃到了另一颗树上。"
  },
  [111160521] = {
    id = 111160521,
    name = "接着你跳下树，感受到身后地动山摇的追逐，你一刻不停地奔跑，计算着“逃跑”的方向和速度。"
  },
  [111160522] = {
    id = 111160522,
    name = "呼......呼......既不能甩掉魔物，也不能被追上，真是累死了！"
  },
  [111160523] = {
    id = 111160523,
    name = "经过好几次的转移和指引，你终于到达了森林的边缘，那些魔物始终跟在你的身后。"
  },
  [111160524] = {
    id = 111160524,
    name = "呼，这边就是废墟的附近了，总算把这群魔物成功引到了这个方向。"
  },
  [111160525] = {
    id = 111160525,
    name = "魔物兴奋地震天嘶吼，最前方的魔物身上被魔法烧得噼里啪啦，发红的眼睛死盯着你，朝你飞奔而来！"
  },
  [111160526] = {
    id = 111160526,
    name = "来吧，就在那边！"
  },
  [111160527] = {
    id = 111160527,
    name = "呼，就在前面！（地下室......在这个方向！）"
  },
  [111160528] = {
    id = 111160528,
    name = "废墟门口的黑衣人看你风风火火冲了过来。"
  },
  [111160529] = {id = 111160529, name = "谁？!"},
  [111160530] = {
    id = 111160530,
    name = "快、报告老大！魔物来了！快跑！"
  },
  [111160531] = {
    id = 111160531,
    name = "黑衣人被你的消息愣住，你趁机冲到了关押薇的房屋，黑衣人头目愤怒地看着撞开门的你。"
  },
  [111160532] = {
    id = 111160532,
    name = "做什么！这么没规矩！"
  },
  [111160533] = {
    id = 111160533,
    name = "你低着头，慌张地大喊出声。"
  },
  [111160534] = {
    id = 111160534,
    name = "好多只魔物追过来了！老大救命！"
  },
  [111160535] = {
    id = 111160535,
    name = "魔物？还是那么废物，连魔物都能吓到你！"
  },
  [111160536] = {
    id = 111160536,
    name = "你们几个，跟我出去看看。剩下的，看好祭品。"
  },
  [111160537] = {
    id = 111160537,
    name = "黑衣人头目出门了，你被另一个黑衣人拍了拍，颇有安慰的意思。你发现他有点眼熟，朝他笑了笑。"
  },
  [111160538] = {
    id = 111160538,
    name = "又见面了，真巧。"
  },
  [111160539] = {
    id = 111160539,
    name = "咚！你打晕了他，找到了地下室的位置，迅速钻了进去。"
  },
  [111160540] = {
    id = 111160540,
    name = "地下室的气息让你感到很不舒服，你皱眉前进，看到铁牢里被关着的一个又一个堕落者。"
  },
  [111160541] = {
    id = 111160541,
    name = "......这些，都是试验品吗？没有抑制药物，很快就会被侵蚀啊......"
  },
  [111160542] = {
    id = 111160542,
    name = "你不忍再看，径直走到了尽头。你斩断了挂在门上的锁，推了推门，却推不开。"
  },
  [111160543] = {id = 111160543, name = "谁？！"},
  [111160544] = {id = 111160544, name = "是我。"},
  [111160545] = {
    id = 111160545,
    name = "是你？！请稍等！"
  },
  [111160546] = {
    id = 111160546,
    name = "薇欣喜的声音从门后传来，你感受到了门上魔法的波动，随后，这扇门从里面缓缓打开。"
  },
  [111160547] = {
    id = 111160547,
    name = "地下室里没有灯光，能见度极低，你只能看到眼前隐约的轮廓。"
  },
  [111160548] = {
    id = 111160548,
    name = "那是......"
  },
  [111160549] = {
    id = 111160549,
    name = "靠坐在墙壁上边的人形黑影，身躯随着呼吸在微弱地起伏，一股令人不悦的、堕落者的味道从她身上弥漫而出。"
  },
  [111160550] = {
    id = 111160550,
    name = "是你要找的海伦？"
  },
  [111160551] = {
    id = 111160551,
    name = "海伦还没有变成堕落者，她还有意识！"
  },
  [111160552] = {
    id = 111160552,
    name = "时间紧迫，先出去再说！"
  },
  [111160553] = {id = 111160553, name = "好。"},
  [111160554] = {
    id = 111160554,
    name = "神情紧绷的薇松了口气，她转身背起了海伦，跟着你出了牢房，你回头看了一眼薇。"
  },
  [111160555] = {
    id = 111160555,
    name = "我来背吧。"
  },
  [111160556] = {
    id = 111160556,
    name = "不用了，海伦不会伤害我。"
  },
  [111160557] = {
    id = 111160557,
    name = "她抿着嘴，眼中的坚定从未改变。"
  },
  [111160558] = {
    id = 111160558,
    name = "也对，她要是咬我一口，我可不能向你保证什么。"
  },
  [111160559] = {id = 111160559, name = "......"},
  [111160560] = {
    id = 111160560,
    name = "——嘭！上方传来了剧烈的震动，你示意薇停下脚步。"
  },
  [111160561] = {
    id = 111160561,
    name = "我们等等再出去，我把魔物群引过来了。"
  },
  [111160562] = {id = 111160562, name = "......"},
  [111160563] = {
    id = 111160563,
    name = "你环视四周铁牢中的堕落者，眼神复杂。"
  },
  [111160564] = {
    id = 111160564,
    name = "啊，这里比我想象的，更像地狱。"
  },
  [111160565] = {
    id = 111160565,
    name = "......他们用卑鄙的手段，把这些人变成了堕落者，这种丧尽天良的组织，是应该消失的。"
  },
  [111160566] = {
    id = 111160566,
    name = "海伦身上，到处都是血痕......是自残的印记？"
  },
  [111160567] = {
    id = 111160567,
    name = "......她不想变成堕落者，更不想伤害我。"
  },
  [111160568] = {
    id = 111160568,
    name = "被侵蚀之后，却没有被欲望异化，还能撑到现在......我相信她不会沦为堕落者的。"
  },
  [111160569] = {id = 111160569, name = "谢谢。"},
  [111160570] = {
    id = 111160570,
    name = "轰隆隆——"
  },
  [111160571] = {
    id = 111160571,
    name = "地面上震动不断，你听到有人朝门口靠近，立刻卡死了地下室的门。"
  },
  [111160572] = {
    id = 111160572,
    name = "怎么打不开！啊，别过来、别过来！啊——！！！"
  },
  [111160573] = {
    id = 111160573,
    name = "吼——！！"
  },
  [111160574] = {
    id = 111160574,
    name = "一门之隔，魔物大口吞食着黑衣人的尸体，隔了许久，你才听到湿哒哒的脚步声远去。"
  },
  [111160575] = {
    id = 111160575,
    name = "附近都安静了，我先出去探探情况，你跟着我。"
  },
  [111160576] = {id = 111160576, name = "嗯。"},
  [111160577] = {
    id = 111160577,
    name = "出了地下室，你踢开黑衣人残留的尸体警惕地观察着四周。原本还没被破坏的房屋已经成了一片废墟，四处躺了几个黑衣人的尸体，也没有了魔物的影子。"
  },
  [111160578] = {
    id = 111160578,
    name = "看来魔物去追他们了，我们趁现在快离开！"
  },
  [111160579] = {
    id = 111160579,
    name = "去海伦的小木屋，她需要尽快治疗！"
  },
  [111160580] = {
    id = 111160580,
    name = "你扔掉黑衣人的衣服，朝她伸出了手。"
  },
  [111160581] = {
    id = 111160581,
    name = "放心交给我，你带路吧。"
  },
  [111160582] = {
    id = 111160582,
    name = "薇愣了一瞬，将海伦放了下来，没想到海伦竟原地跳了起来！"
  },
  [111160583] = {
    id = 111160583,
    name = "啊啊！小薇，原来我们已经到外面了吗？！"
  },
  [111160584] = {
    id = 111160584,
    name = "海伦，现在不是开玩笑的时候。"
  },
  [111160585] = {
    id = 111160585,
    name = "知道知道，我们快走吧！"
  },
  [111160586] = {
    id = 111160586,
    name = "海伦走在薇的另一边，她的余光时不时朝你看过来，似乎在躲避什么。"
  },
  [111160587] = {
    id = 111160587,
    name = "你什么时候醒的？"
  },
  [111160588] = {
    id = 111160588,
    name = "啊哈，就是刚刚啦！"
  },
  [111160589] = {
    id = 111160589,
    name = "海伦避开你的视线，又往前跑了好几步，那慌张的背影，和你曾见过的浑身是血的女人如出一辙。"
  },
  [111160590] = {
    id = 111160590,
    name = "原来是你！"
  },
  [111160591] = {id = 111160591, name = "不是！"},
  [111160592] = {id = 111160592, name = "......？"},
  [111160593] = {
    id = 111160593,
    name = "薇询问地看着你，你朝她耸耸肩，下巴指了指海伦逃走的方向。"
  },
  [111160594] = {id = 111160594, name = "！！！"},
  [111160595] = {
    id = 111160595,
    name = "糟了，她这样会吓到别人的！"
  },
  [111160596] = {
    id = 111160596,
    name = "啊，我的木屋！"
  },
  [111160597] = {
    id = 111160597,
    name = "我辛辛苦苦搭的桌子，还有我摘的药草，竟然被他们全毁了！！"
  },
  [111160598] = {
    id = 111160598,
    name = "小木屋里一片狼藉，海伦身体里的黑气翻滚剧烈，从皮肤里渐渐渗出，双眼也变得鲜红......"
  },
  [111160599] = {
    id = 111160599,
    name = "啊......我要杀光他们！"
  },
  [111160600] = {
    id = 111160600,
    name = "杀......不行......我要冷静......冷静......好疼......好痛苦......小......薇......"
  },
  [111160601] = {
    id = 111160601,
    name = "眼看海伦又要伤害自己，薇立刻朝她身上笼罩了一层魔法的薄雾，海伦暂时昏睡了过去。"
  },
  [111160602] = {id = 111160602, name = "......"},
  [111160603] = {
    id = 111160603,
    name = "安顿好海伦，薇郑重地向你道谢。"
  },
  [111160604] = {
    id = 111160604,
    name = "这一次，真的非常感谢你。"
  },
  [111160605] = {
    id = 111160605,
    name = "不用太客气。"
  },
  [111160606] = {
    id = 111160606,
    name = "我想我是幸运的，如果没有遇到你，恐怕海伦现在已经......我也......"
  },
  [111160607] = {
    id = 111160607,
    name = "之前在废墟里，你不是也用魔法阵救了我吗，我还没说谢谢你呢。"
  },
  [111160608] = {
    id = 111160608,
    name = "那，那是因为，我不能因为救朋友，而将另一个朋友置身于危险之中。"
  },
  [111160609] = {
    id = 111160609,
    name = "似乎意识到说了什么，薇的脸微微泛红。"
  },
  [111160610] = {
    id = 111160610,
    name = "啊......我的意思是，救海伦不是你的责任，你没有义务为这件事承担风险。"
  },
  [111160611] = {
    id = 111160611,
    name = "你都说了，我们是朋友，那一起承担风险也没什么不对。"
  },
  [111160612] = {
    id = 111160612,
    name = "即使在成为朋友之前，你也毫不犹豫地出手帮助了我。在这危机重重的深渊里，我一直很钦佩像你这样的人，能为了别人出手，真的非常难得。"
  },
  [111160613] = {
    id = 111160613,
    name = "......呵呵。那么，接下来，你打算怎么办？"
  },
  [111160614] = {
    id = 111160614,
    name = "等海伦醒后，我会带她离开，然后找到办法，治好她。"
  },
  [111160615] = {
    id = 111160615,
    name = "她的人生还很长，很多事还没来得及经历，我不能让她就这样堕落，死去。既然那些人能制造出让人堕落的药物，那么浊炁的侵蚀，就也一定有解决的办法。"
  },
  [111160616] = {
    id = 111160616,
    name = "要是还有什么需要我帮忙的，尽管提出来。"
  },
  [111160617] = {
    id = 111160617,
    name = "薇一怔，缓慢而坚定地摇了摇头。"
  },
  [111160618] = {
    id = 111160618,
    name = "你给的帮助已经足够了。不过我做这样的决定，你会不会认为我很天真？"
  },
  [111160619] = {
    id = 111160619,
    name = "不，我觉得你很有勇气。"
  },
  [111160620] = {
    id = 111160620,
    name = "这样不放弃地去相信一个人，就算她随时会成为一个堕落者，也不愿意离开，这样的坚持，真的很有勇气。"
  },
  [111160621] = {
    id = 111160621,
    name = "薇微微睁大了眼，随后，她露出一个发自内心的微笑。"
  },
  [111160622] = {
    id = 111160622,
    name = "我没有你想得这么厉害，海伦是意志非常坚定的女孩。"
  },
  [111160623] = {
    id = 111160623,
    name = "像她这样的人都在努力忍受痛苦，因为她的坚持，我才觉得，再艰难的处境也不算什么，我会找到治疗她的办法。"
  },
  [111160624] = {
    id = 111160624,
    name = "我相信，这些都是我修行中的一部分，也是我在修行旅途的一次历练，我会珍惜所经历的一切。"
  },
  [111160625] = {
    id = 111160625,
    name = "修行之路，漫漫长远。历练也好，羁绊也好，还有和你并肩作战的回忆......都是上天给予我的恩赐。"
  },
  [111160626] = {
    id = 111160626,
    name = "我知道了，希望你能早日达成所愿。"
  },
  [111160627] = {
    id = 111160627,
    name = "谢谢你。"
  },
  [111160628] = {
    id = 111160628,
    name = "再见了......那个，我们还会再见吗？"
  },
  [111160629] = {
    id = 111160629,
    name = "嗯，肯定还会再见的。"
  },
  [111160630] = {
    id = 111160630,
    name = "你离开了小屋，细微的天光穿过云层落在地上，阴翳四散，你踏上了新的路途。"
  },
  [111200001] = {
    id = 111200001,
    name = "喂，小子。"
  },
  [111200002] = {
    id = 111200002,
    name = "身边突然出现了一道声音。你停下脚步，狐疑地朝声音传来的方向看去。"
  },
  [111200003] = {id = 111200003, name = "谁？"},
  [111200004] = {
    id = 111200004,
    name = "附近的树后，站着一个黑色的身影。他伸出披着黑色外袍的手，朝你招了招。"
  },
  [111200005] = {id = 111200005, name = "？？？"},
  [111200006] = {
    id = 111200006,
    name = "那只手又招了招。你心中警惕，转身就走。"
  },
  [111200007] = {
    id = 111200007,
    name = "别着急走啊！"
  },
  [111200008] = {
    id = 111200008,
    name = "一身黑衣的人从树后慌张钻了出来，他拉了拉帽檐，神秘地压低了声音。"
  },
  [111200009] = {
    id = 111200009,
    name = "咳咳，这位独行的探险者哟~我这里有一个消息，你要不要买？"
  },
  [111200010] = {
    id = 111200010,
    name = "什么消息？"
  },
  [111200011] = {
    id = 111200011,
    name = "嘿嘿，你说在深渊，还能有什么消息？当然是那方面的。"
  },
  [111200012] = {
    id = 111200012,
    name = "那方面？"
  },
  [111200013] = {
    id = 111200013,
    name = "没错，就是那方面。"
  },
  [111200014] = {
    id = 111200014,
    name = "黑衣人鬼鬼祟祟地靠近你，你浑身上下写满了拒绝，并后退了一步。"
  },
  [111200015] = {
    id = 111200015,
    name = "站那说就行。到底是哪方面，说清楚。"
  },
  [111200016] = {
    id = 111200016,
    name = "就是——"
  },
  [111200017] = {
    id = 111200017,
    name = "黑衣人话说到一半，忽然听见附近传来说话声。"
  },
  [111200018] = {
    id = 111200018,
    name = "嘁。我先走了，下次再说！"
  },
  [111200019] = {id = 111200019, name = "喂！"},
  [111200020] = {
    id = 111200020,
    name = "说完黑衣人就一头钻进了林子里消失了。"
  },
  [111200021] = {
    id = 111200021,
    name = "神神秘秘的，到底是什么消息？"
  },
  [111200022] = {
    id = 111200022,
    name = "不一会儿，路上迎面走来了一群探险队。他们探讨着去哪里狩猎魔物的事，从你身边擦肩而过。"
  },
  [111200023] = {id = 111200023, name = "......"},
  [111200024] = {
    id = 111200024,
    name = "这些都是普通的探险者，那个黑衣人在躲什么？难道有什么隐情？"
  },
  [111200025] = {
    id = 111200025,
    name = "唔，不管了。先去前面的据点看看，顺便添置一些物资吧。"
  },
  [111200026] = {
    id = 111200026,
    name = "别过去那边！"
  },
  [111200027] = {
    id = 111200027,
    name = "怎么，拦我做什么？"
  },
  [111200028] = {
    id = 111200028,
    name = "那边有个疯疯癫癫的女人，专门找人打架。"
  },
  [111200029] = {
    id = 111200029,
    name = "女人？漂亮吗？"
  },
  [111200030] = {
    id = 111200030,
    name = "你还关心这个，瞧瞧我身上的伤，就是被她揍的。"
  },
  [111200031] = {
    id = 111200031,
    name = "嚯，你是偷鸡不成蚀把米吧？"
  },
  [111200032] = {
    id = 111200032,
    name = "你脑子里能不能想点儿正常的？那女人见人就挑战，还说输了就......"
  },
  [111200033] = {
    id = 111200033,
    name = "据点入口处，两个探险者边说边走远了。"
  },
  [111200034] = {
    id = 111200034,
    name = "人好少的样子，哪里会有物资补给呢......"
  },
  [111200035] = {
    id = 111200035,
    name = "此时又出现了几名探险者，他们有说有笑地去了一条街道。"
  },
  [111200036] = {
    id = 111200036,
    name = "在那边吧。"
  },
  [111200037] = {
    id = 111200037,
    name = "你跟着他们走进那条街，顺利地找到了店铺，选了几样常用的补给品，交给老板打包。"
  },
  [111200038] = {
    id = 111200038,
    name = "噢噢噢噢！"
  },
  [111200039] = {
    id = 111200039,
    name = "店铺对面的空地上围了一大群人，里面吵吵闹闹，还有兵器相交的声音。"
  },
  [111200040] = {
    id = 111200040,
    name = "那边发生了什么事？"
  },
  [111200041] = {
    id = 111200041,
    name = "一个女人，每天都在这儿比武呢。"
  },
  [111200042] = {
    id = 111200042,
    name = "是那个女人啊......"
  },
  [111200043] = {
    id = 111200043,
    name = "你也听说了啊，她每天只挑三个人打，今天这是第二个。"
  },
  [111200044] = {
    id = 111200044,
    name = "——嘭！"
  },
  [111200045] = {
    id = 111200045,
    name = "一道人影飞出人群落在地上，败者脸色惨白地爬了起来，二话不说转身走了。"
  },
  [111200046] = {
    id = 111200046,
    name = "被她打败了的人就这么算了？"
  },
  [111200047] = {
    id = 111200047,
    name = "倒也不是。"
  },
  [111200048] = {
    id = 111200048,
    name = "她每次找人打架之前，会先谈好条件，给人欧泊。名字说得挺好听，叫什么“切磋交手的礼金”。"
  },
  [111200049] = {
    id = 111200049,
    name = "拿钱找人切磋？这样的比武......一定很多人愿意吧。"
  },
  [111200050] = {
    id = 111200050,
    name = "那可不，这种陪练费谁不愿意赚？有的打输了，还想再打一次，被她拒绝了。在她手底下，只有一次机会。"
  },
  [111200051] = {
    id = 111200051,
    name = "只挑战没挑战过的人，这也算得上修行的一种。不过，到现在也没有能战胜她的人吗？"
  },
  [111200052] = {
    id = 111200052,
    name = "有肯定有，但前提是她看不看得上。她说她要挑战的人，首要条件是不能长得丑。"
  },
  [111200053] = {id = 111200053, name = "......"},
  [111200054] = {
    id = 111200054,
    name = "人群随之散去，一名执剑少女的身姿逐渐显露出来——金发飞扬，明丽动人，轻世傲物。"
  },
  [111200055] = {
    id = 111200055,
    name = "哼，没想到又是一个外强中干的银枪蜡烛头。"
  },
  [111200056] = {
    id = 111200056,
    name = "啧啧，有这样的容貌，要求对手长得好看一点也不过分吧。"
  },
  [111200057] = {id = 111200057, name = "......"},
  [111200058] = {
    id = 111200058,
    name = "商人把补给品交给了你，你正准备给钱的时候，一只纤细的手搭在了包袱上。"
  },
  [111200059] = {
    id = 111200059,
    name = "没见过你，新来的？"
  },
  [111200060] = {id = 111200060, name = "你......"},
  [111200061] = {
    id = 111200061,
    name = "带着武器，是探险者啊。这些补给品的钱本小姐付了，和我打一场吧！"
  },
  [111200062] = {
    id = 111200062,
    name = "她傲慢地抬起了下巴，理了理长发，等待你的回答。"
  },
  [111200063] = {
    id = 111200063,
    name = "（切磋的话，那就只是交流武技，不过......）"
  },
  [111200064] = {
    id = 111200064,
    name = "为什么想跟我打？"
  },
  [111200065] = {
    id = 111200065,
    name = "看在你长得不错的份上，给你一个挑战本小姐的机会。"
  },
  [111200066] = {id = 111200066, name = "......"},
  [111200067] = {
    id = 111200067,
    name = "犹犹豫豫的，难道这也是一个中看不中用的？"
  },
  [111200068] = {id = 111200068, name = "......"},
  [111200069] = {
    id = 111200069,
    name = "少女托着下巴小声嘀咕，仿佛在估量你的价值。"
  },
  [111200070] = {
    id = 111200070,
    name = "如果这位小姐想要切磋较量，我当然可以奉陪。不过，为什么要挑战我？"
  },
  [111200071] = {
    id = 111200071,
    name = "呵呵，打赢我就告诉你！"
  },
  [111200072] = {
    id = 111200072,
    name = "她傲然一笑，抬剑就朝你攻了过来！"
  },
  [111200073] = {
    id = 111200073,
    name = "切磋吗，抱歉，我还有事......"
  },
  [111200074] = {
    id = 111200074,
    name = "跟本小姐切磋是看得上你，有什么事比这个更重要？"
  },
  [111200075] = {
    id = 111200075,
    name = "（总有不好的预感，不拒绝的话一定会有麻烦......）"
  },
  [111200076] = {
    id = 111200076,
    name = "今日真的不行，不如改天再——"
  },
  [111200077] = {
    id = 111200077,
    name = "哼，磨磨蹭蹭的，看招！"
  },
  [111200078] = {
    id = 111200078,
    name = "她冷笑一声，抬剑就朝你攻了过来！"
  },
  [111200079] = {
    id = 111200079,
    name = "你迅速拿起武器抵挡了这一剑，金发少女见你迎战，神色变得严肃起来。"
  },
  [111200080] = {
    id = 111200080,
    name = "莱娜，请赐教。"
  },
  [111200081] = {
    id = 111200081,
    name = "自报家门后，她抬起了眼，双眸里尽是凛然的战意。你点了点头，示意切磋可以开始。"
  },
  [111200082] = {
    id = 111200082,
    name = "一开始，她的攻势就很猛烈。她的每次攻击都被你堪堪躲开，你像是用尽了全力才躲掉她的攻击，又像是算准了她攻击的方向，不愿意多耗一点力气。"
  },
  [111200083] = {
    id = 111200083,
    name = "有点本事啊，那么本小姐要动真格的了！"
  },
  [111200084] = {id = 111200084, name = "......"},
  [111200085] = {
    id = 111200085,
    name = "几个回合后，你们二人的距离不断被莱娜拉近，她露出了势在必得的笑容。"
  },
  [111200086] = {
    id = 111200086,
    name = "又过了好几个回合......"
  },
  [111200087] = {
    id = 111200087,
    name = "莱娜与你缠斗了好一阵，渐渐显露出了颓势。"
  },
  [111200088] = {
    id = 111200088,
    name = "（虽然武技还有待磨炼，但有这样的天赋和战意，已经有强者的雏形了，还是尽快结束战斗吧。）"
  },
  [111200089] = {
    id = 111200089,
    name = "等她再次攻过来的时候，你故意卖了个破绽，莱娜抓住机会，和你互相用剑指向了要害。"
  },
  [111200090] = {
    id = 111200090,
    name = "多谢莱娜小姐指教，这次切磋就到此为止吧。"
  },
  [111200091] = {
    id = 111200091,
    name = "不错，就是你了！"
  },
  [111200092] = {
    id = 111200092,
    name = "莱娜心情很好地收起了武器，扬了扬头。"
  },
  [111200093] = {id = 111200093, name = "什么？"},
  [111200094] = {
    id = 111200094,
    name = "莱娜看到四周好奇围观的人群，欲言又止。"
  },
  [111200095] = {
    id = 111200095,
    name = "你跟我来，这里人太多了。"
  },
  [111200096] = {
    id = 111200096,
    name = "莱娜拿起你的补给品就走，你莫名其妙地跟在她身后。旁边路人盯着你们议论纷纷，其中有一人声音不小不大，正好被你们听见。"
  },
  [111200097] = {
    id = 111200097,
    name = "又要长得好，又要武技高，这分明是在选未婚夫啊......"
  },
  [111200098] = {
    id = 111200098,
    name = "其他人纷纷恍然大悟：难怪条件那么苛刻。有了这个解释，莱娜之前奇怪的行为就说得通了。"
  },
  [111200099] = {
    id = 111200099,
    name = "你还在纳闷的时候，莱娜已经忍无可忍跑远了，你见状赶紧追了上去。"
  },
  [111200100] = {
    id = 111200100,
    name = "你看，被我说中害羞了吧......"
  },
  [111200101] = {id = 111200101, name = "......"},
  [111200102] = {
    id = 111200102,
    name = "你追到一处角落找到了莱娜，她看起来并没有被路人影响心情。"
  },
  [111200103] = {
    id = 111200103,
    name = "竟然这么快就能找到隐匿起来的我，这一项测试你也通过了。"
  },
  [111200104] = {
    id = 111200104,
    name = "（其实只是普通的追踪技能而已......）"
  },
  [111200105] = {
    id = 111200105,
    name = "莱娜小姐，你到底想和我说什么？"
  },
  [111200106] = {
    id = 111200106,
    name = "你之前是故意和我打成平手吧？武技过关，心思缜密，还懂得谦让，从各方面来讲，你都通过了本小姐的考验。"
  },
  [111200107] = {id = 111200107, name = "考验？"},
  [111200108] = {
    id = 111200108,
    name = "对，考验你有没有和我一起寻宝的资格。"
  },
  [111200109] = {
    id = 111200109,
    name = "不知怎地，你忽然松了一口气。"
  },
  [111200110] = {
    id = 111200110,
    name = "原来你是想寻找同伴一起寻宝，那为什么不公开招募呢？"
  },
  [111200111] = {
    id = 111200111,
    name = "这一次的宝藏只有我知道，在一个非常隐秘的地方......再说，本小姐就是要自己选同伴，那些长得丑的、性格恶劣的我才不要。"
  },
  [111200112] = {id = 111200112, name = "......"},
  [111200113] = {
    id = 111200113,
    name = "事不宜迟，现在就跟我出发去寻宝吧。"
  },
  [111200114] = {
    id = 111200114,
    name = "等等，我想问一些关于宝藏的事。"
  },
  [111200115] = {
    id = 111200115,
    name = "......问那么多做什么？能跟我一起寻宝，是你的荣耀。难道你还想要什么好处？"
  },
  [111200116] = {
    id = 111200116,
    name = "什么，没有好处的吗？！"
  },
  [111200117] = {
    id = 111200117,
    name = "那个......总之，那个宝藏我也不知道是什么啦，钱我已经预付给你了啊。"
  },
  [111200118] = {
    id = 111200118,
    name = "莱娜提了提手里的补给包，当下就把“切磋礼金”变成了“寻宝费”。"
  },
  [111200119] = {id = 111200119, name = "......"},
  [111200120] = {
    id = 111200120,
    name = "如果寻到宝藏的话，你打算怎么分配？"
  },
  [111200121] = {
    id = 111200121,
    name = "我只要对战斗有帮助的宝物，如果是别的，本小姐也不吝啬给你。"
  },
  [111200122] = {
    id = 111200122,
    name = "对战斗有帮助的宝物，这个定义很广泛，对我很不公平啊。"
  },
  [111200123] = {
    id = 111200123,
    name = "那你想怎么分配？"
  },
  [111200124] = {
    id = 111200124,
    name = "既然我们是伙伴，那么在行动之前，就要有作战计划，还要制定战利品的分配模式。比如我什么都不用做只需要战斗，那么就只能拿相关宝物三成的市场价，而宝物就归你所有。"
  },
  [111200125] = {
    id = 111200125,
    name = "不行！你什么都不做，我不如雇佣一个护卫。"
  },
  [111200126] = {
    id = 111200126,
    name = "没错。正因为大家是临时结伴的搭档，所以更需要就分配问题提前达成统一。"
  },
  [111200127] = {
    id = 111200127,
    name = "那个......通常那些临时的探险队是怎么分配的？"
  },
  [111200128] = {
    id = 111200128,
    name = "莱娜小姐听说过拍卖吧。"
  },
  [111200129] = {
    id = 111200129,
    name = "如果队里有多人都需求那件宝物的话，价高者得，得到宝物的人将拍卖的钱分给其他同伴，按照每人对寻宝的贡献，得到相应的酬金。"
  },
  [111200130] = {
    id = 111200130,
    name = "这个主意不错，就这么定了。出发吧！"
  },
  [111200131] = {id = 111200131, name = "等等。"},
  [111200132] = {
    id = 111200132,
    name = "还有什么？你好啰嗦啊。"
  },
  [111200133] = {
    id = 111200133,
    name = "关于寻宝途中作战分配的问题，我们可以先拟定总额是十成，最开始我们都是五五，谁行动得多，谁就拿得多。"
  },
  [111200134] = {
    id = 111200134,
    name = "啊，真麻烦。不过一开始五五我不同意，宝藏是我发现的，我要七成！"
  },
  [111200135] = {
    id = 111200135,
    name = "（没想到还是个精打细算的贵族小姐。不过好歹是个宝藏，去看一看也好。）"
  },
  [111200136] = {
    id = 111200136,
    name = "好，我没意见。"
  },
  [111200137] = {
    id = 111200137,
    name = "那就这么说好了，走吧，先跟我去取藏宝图。"
  },
  [111200138] = {
    id = 111200138,
    name = "藏宝图你没放在身上？"
  },
  [111200139] = {
    id = 111200139,
    name = "你傻吗？"
  },
  [111200140] = {
    id = 111200140,
    name = "这么重要的东西随身携带，万一被打劫了怎么办？所以为了以防万一，我放在了一个谁也找不到的地方。"
  },
  [111200141] = {
    id = 111200141,
    name = "......你说的也不无道理。"
  },
  [111200142] = {
    id = 111200142,
    name = "这就是你说的谁都找不到的地方？"
  },
  [111200143] = {id = 111200143, name = "......"},
  [111200144] = {
    id = 111200144,
    name = "我没说错啊。"
  },
  [111200145] = {
    id = 111200145,
    name = "虽然这里的隐蔽性很高，但是，魔物也太多了。"
  },
  [111200146] = {
    id = 111200146,
    name = "你们到达一个隐蔽的山洞门口，莱娜示意藏宝图就在山洞里。而此时，山洞门口魔物进进出出，完全将山洞占领了。"
  },
  [111200147] = {
    id = 111200147,
    name = "我把藏宝图放进去的时候，这些魔物明明不存在的。难道它们发现了我的宝图，在觊觎我的宝物？！"
  },
  [111200148] = {
    id = 111200148,
    name = "......这些魔物是从别的地方迁徙到这里的，它们应该是把这个山洞当成了新的巢穴。"
  },
  [111200149] = {
    id = 111200149,
    name = "那怎么取回我的藏宝图？它们不会把它吃掉、或者撕碎吧？！"
  },
  [111200150] = {
    id = 111200150,
    name = "......魔物不会认识藏宝图的，我们还是先侦查一下吧，山洞里面是什么状况，我们也不知道。"
  },
  [111200151] = {
    id = 111200151,
    name = "好啊，那你去吧，小心点哦。"
  },
  [111200152] = {
    id = 111200152,
    name = "为什么是我？"
  },
  [111200153] = {
    id = 111200153,
    name = "是你提的主意，难不成你还要本小姐想办法？"
  },
  [111200154] = {
    id = 111200154,
    name = "如果我想到了办法并且进去了，那分成方面......"
  },
  [111200155] = {
    id = 111200155,
    name = "这也要重新分成？"
  },
  [111200156] = {
    id = 111200156,
    name = "拿到藏宝图难道不是寻宝中很重要的一步吗？"
  },
  [111200157] = {
    id = 111200157,
    name = "等等，我想过了，既然我们是伙伴，当然要一起行动了。"
  },
  [111200158] = {
    id = 111200158,
    name = "嗯，我现在制定一下作战计划。"
  },
  [111200159] = {
    id = 111200159,
    name = "片刻后......"
  },
  [111200160] = {
    id = 111200160,
    name = "这么麻烦？不能直接杀掉魔物进去吗？"
  },
  [111200161] = {
    id = 111200161,
    name = "这一次只是进去探查情况，如果能顺利拿到藏宝图，也避免了无谓的战斗。"
  },
  [111200162] = {
    id = 111200162,
    name = "唔......那就这么说定了，你引走魔物，我去拿藏宝图。"
  },
  [111200163] = {id = 111200163, name = "......"},
  [111200164] = {
    id = 111200164,
    name = "快去呀~嗯？难道你想让我引走魔物？你知道我把藏宝图藏哪儿了吗？"
  },
  [111200165] = {
    id = 111200165,
    name = "好吧，我去把洞口的魔物引开，不过你也要小心，如果发现异常及时撤退回来。"
  },
  [111200166] = {
    id = 111200166,
    name = "好吧，你也是。别被这种低级魔物吃掉啊~实在不行就求救，本小姐会救你的。"
  },
  [111200167] = {
    id = 111200167,
    name = "你默默观察了片刻，在莱娜的不断催促下开始了行动。"
  },
  [111200168] = {
    id = 111200168,
    name = "低级魔物对人类的感知很迟钝，大多是靠气味与声音辨别方向。你从补给包中拿出一瓶吸引魔物的药水，将药水倒在旁边一棵树上，接着将瓶子砸向了洞口。"
  },
  [111200169] = {id = 111200169, name = "呜——"},
  [111200170] = {
    id = 111200170,
    name = "魔物们朝树的方向一涌而去，莱娜见魔物离开，迅速地钻进了山洞里。"
  },
  [111200171] = {
    id = 111200171,
    name = "吼——！！"
  },
  [111200172] = {
    id = 111200172,
    name = "洞里竟然传出了魔物的怒吼声！"
  },
  [111200173] = {
    id = 111200173,
    name = "里面还有魔物！"
  },
  [111200174] = {
    id = 111200174,
    name = "你连忙跑了进去，看见莱娜正愤怒地朝魔物扔出一件魔法道具，一瞬间将魔物炸成了无数碎块！"
  },
  [111200175] = {
    id = 111200175,
    name = "竟敢拿我的藏宝图当垫子，去死吧！"
  },
  [111200176] = {
    id = 111200176,
    name = "外面的魔物也快进来了，赶紧出去！"
  },
  [111200177] = {
    id = 111200177,
    name = "莱娜收起地上的一张羊皮卷，你们快速跑出山洞，这时，被引走的魔物群去而复返，朝你们狂奔而来！"
  },
  [111200178] = {
    id = 111200178,
    name = "诶，等一下！这些低级魔物杀掉就好了啊！"
  },
  [111200179] = {
    id = 111200179,
    name = "（洞里的精英级魔物已经被她杀掉了，暂时不会有头目级以上的魔物过来）"
  },
  [111200180] = {
    id = 111200180,
    name = "那就速战速决！"
  },
  [111200181] = {
    id = 111200181,
    name = "我早就说直接杀进来嘛~你还提什么侦查一下，最后还不是要全部处理掉。"
  },
  [111200182] = {id = 111200182, name = "......"},
  [111200183] = {
    id = 111200183,
    name = "莱娜连续抛出了几个魔法道具，道具炸弹炸得震天响，没等魔物靠近就几乎全被消灭了。"
  },
  [111200184] = {
    id = 111200184,
    name = "接着，你们将残存的魔物一一击杀。"
  },
  [111200185] = {
    id = 111200185,
    name = "哼哼。这一次本小姐的贡献多，分成该改一改了吧？"
  },
  [111200186] = {id = 111200186, name = "......"},
  [111200187] = {
    id = 111200187,
    name = "莱娜小姐猎杀魔物的方式真特别。"
  },
  [111200188] = {
    id = 111200188,
    name = "羡慕吧？我还有很多这样的魔法道具哦。"
